INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(1, 'Cách giải quyết tranh chấp đất đai', 'cach-giai-quyet-tranh-chap-dat-dai', 'Cach giai quyet tranh chap dat dai', 'Quý vị đang gặp phải vấn đề tranh chấp đất đai với ai đó, đang bối rối hoặc không biết  cách giải quyết tranh chấp đất đai hiện nay như thế nào. Hãy tham khảo ngay bài viết dưới đây, quý vị sẽ biết nên làm gì thế nào khi tranh chấp đất đai', '2017/07/09/2235-1499572975.jpg', 5, '<h2>&nbsp;</h2>\r\n&nbsp;\r\n\r\n<div><img alt=\"\" src=\"/uploads/images/cach-giai-quyet-tranh-chap-dat-dai.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n<strong><span style=\"font-size:18px\">►&nbsp;C&aacute;c dạng tranh chấp đất đai hiện nay</span></strong><br />\r\n<br />\r\n&diams; Tranh chấp về quyền sử dụng đất: l&agrave; những tranh chấp giữa c&aacute;c b&ecirc;n với nhau về việc ai c&oacute; quyền sử dụng hợp ph&aacute;p đối với một mảnh đất n&agrave;o đ&oacute;. Trong dạng tranh chấp n&agrave;y ch&uacute;ng ta thường gặp c&aacute;c loại tranh chấp về ranh giới đất; tranh chấp về quyền sử dụng đất, t&agrave;i sản gắn liền với đất trong c&aacute;c quan hệ ly h&ocirc;n, thừa kế; &nbsp;tranh chấp để đ&ograve;i lại đất (đất đ&atilde; cho người kh&aacute;c mượn sử dụng m&agrave; kh&ocirc;ng trả lại, hoặc tranh chấp giữa người d&acirc;n tộc thiểu số với người đi x&acirc;y dựng v&ugrave;ng kinh tế mới&hellip;)<br />\r\n<br />\r\n&diams; &nbsp;Tranh chấp về quyền, nghĩa vụ ph&aacute;t sinh trong qu&aacute; tr&igrave;nh sử dụng đất: dạng tranh chấp n&agrave;y thường xảy ra khi c&aacute;c chủ thể c&oacute; những giao dịch d&acirc;n sự về quyền sử dụng đất hoặc c&aacute;c tranh chấp li&ecirc;n quan đến việc bồi thường giải ph&oacute;ng mặt bằng, t&aacute;i định cư&hellip;<br />\r\n<br />\r\n&diams; &nbsp;Tranh chấp về mục đ&iacute;ch sử dụng đất: &nbsp;đ&acirc;y l&agrave; dạng tranh chấp &iacute;t gặp hơn, những tranh chấp n&agrave;y li&ecirc;n quan đến việc x&aacute;c định mục đ&iacute;ch sử dụng đất l&agrave; g&igrave;. Th&ocirc;ng thường những tranh chấp n&agrave;y cũng dễ c&oacute; cơ sở để giải quyết v&igrave; trong qu&aacute; tr&igrave;nh ph&acirc;n bổ đất đai cho c&aacute;c chủ thể sử dụng, Nh&agrave; nước đ&atilde; x&aacute;c định mục đ&iacute;ch sử dụng đất th&ocirc;ng qua quy hoạch sử dụng đất. Tranh chấp chủ yếu do người sử dụng đất sử dụng sai mục đ&iacute;ch so với khi được Nh&agrave; nước giao đất, cho thu&ecirc; đất.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Nguy&ecirc;n tắc giải quyết tranh chấp</strong></span>\r\n\r\n<ul>\r\n	<li>Thứ nhất, lu&ocirc;n đảm bảo nguy&ecirc;n tắc đất đai thuộc sở hữu to&agrave;n d&acirc;n v&agrave; Nh&agrave; nước l&agrave; đại diện chủ sở hữu.</li>\r\n	<li>Thứ hai, bảo đảm lợi &iacute;ch của người sử dụng đất, nhất l&agrave; lợi &iacute;ch kinh tế, khuyến kh&iacute;ch tự h&ograve;a giải trong nội bộ nh&acirc;n d&acirc;n.</li>\r\n	<li>Thứ ba, việc giải quyết nhằm mục đ&iacute;ch ổn định kinh tế, x&atilde; hội, gắn với ph&aacute;t triển sản xuất, mở mang ng&agrave;nh nghề, tạo điều kiện cho lao động c&oacute; việc l&agrave;m, ph&ugrave; hợp với đặc điểm v&agrave; quy hoạch của từng địa phương.</li>\r\n</ul>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Tr&igrave;nh tự giải quyết tranh chấp như thế n&agrave;o</strong></span><br />\r\n<br />\r\nTranh chấp đất đai (TCĐĐ) c&oacute; thể được giải quyết theo phương thức khởi kiện tại T&ograve;a &aacute;n c&oacute; thẩm quyền hoặc khiếu nại l&ecirc;n cơ quan nh&agrave; nước c&oacute; thẩm quyền. Tương ứng với mỗi phương thức, quy tr&igrave;nh, thủ tục giải quyết cũng kh&aacute;c nhau.<br />\r\n<br />\r\n&rArr; Giải quyết TCĐĐ theo tr&igrave;nh tự tố tụng (d&acirc;n sự): việc giải quyết TCĐĐ tại T&ograve;a &aacute;n được thực hiện theo quy định chung tại Bộ luật tố tụng d&acirc;n sự. Theo đ&oacute;, c&aacute; nh&acirc;n, cơ quan, tổ chức c&oacute; quyền tự m&igrave;nh hoặc th&ocirc;ng qua người đại diện hợp ph&aacute;p để khởi kiện vụ &aacute;n tại T&ograve;a &aacute;n c&oacute; thẩm quyền (T&ograve;a &aacute;n nơi c&oacute; bất động sản đ&oacute;). luat su uy tin<br />\r\n&nbsp;<br />\r\nNgười khởi kiện vụ &aacute;n gửi đơn khởi kiện v&agrave; t&agrave;i liệu, chứng cứ đến T&ograve;a &aacute;n c&oacute; thẩm quyền, thực hiện việc tạm ứng &aacute;n ph&iacute; v&agrave; ho&agrave;n chỉnh hồ sơ, đơn khởi kiện theo y&ecirc;u cầu của T&ograve;a &aacute;n. Khi T&ograve;a &aacute;n đ&atilde; thụ l&yacute; giải quyết vụ &aacute;n, sẽ tiến h&agrave;nh h&ograve;a giải để c&aacute;c đương sự thỏa thuận với nhau về việc giải quyết vụ &aacute;n. Kh&aacute;c với hoạt động h&ograve;a giải trước khi khởi kiện, đ&acirc;y l&agrave; giai đoạn bắt buộc trong qu&aacute; tr&igrave;nh giải quyết vụ &aacute;n d&acirc;n sự do ch&iacute;nh T&ograve;a &aacute;n chủ tr&igrave; v&agrave; tiến h&agrave;nh. Nếu h&ograve;a giải th&agrave;nh th&igrave; T&ograve;a &aacute;n sẽ lập bi&ecirc;n bản h&ograve;a giải th&agrave;nh, hết 07 ng&agrave;y m&agrave; c&aacute;c b&ecirc;n đương sự kh&ocirc;ng thay đổi &yacute; kiến th&igrave; tranh chấp ch&iacute;nh thức kết th&uacute;c. Nếu h&ograve;a giải kh&ocirc;ng th&agrave;nh th&igrave; T&ograve;a &aacute;n quyết định đưa vụ &aacute;n ra x&eacute;t xử. Ngay trong qu&aacute; tr&igrave;nh x&eacute;t xử, c&aacute;c đương sự vẫn c&oacute; thể thỏa thuận với nhau về việc giải quyết vụ &aacute;n. Nếu kh&ocirc;ng đồng &yacute; c&aacute;c b&ecirc;n vẫn c&oacute; quyền kh&aacute;ng c&aacute;o theo tr&igrave;nh tự ph&uacute;c thẩm.<br />\r\n&nbsp;<br />\r\n&rArr; Giải quyết TCĐĐ theo tr&igrave;nh tự h&agrave;nh ch&iacute;nh: tr&igrave;nh tự n&agrave;y sẽ được &aacute;p dụng đối với những tranh chấp m&agrave; c&aacute;c đương sự kh&ocirc;ng c&oacute; giấy tờ chứng minh quyền sử dụng đất theo quy định v&agrave; lựa chọn giải quyết tranh chấp tại UBND. Đối với TCĐĐ giữa hộ gia đ&igrave;nh, c&aacute; nh&acirc;n, cộng đồng d&acirc;n cư với nhau th&igrave; khiếu nại đến chủ thể c&oacute; thẩm quyền giải quyết l&agrave; Chủ tịch UBND cấp huyện. Nếu một b&ecirc;n hoặc c&aacute;c b&ecirc;n đương sự kh&ocirc;ng đồng &yacute; với quyết định giải quyết lần đầu th&igrave; c&oacute; quyền khiếu nại đến Chủ tịch UBND cấp tỉnh.<br />\r\n&nbsp;<br />\r\nĐối với tranh chấp giữa tổ chức, cơ sở t&ocirc;n gi&aacute;o, người Việt Nam định cư ở nước ngo&agrave;i, tổ chức nước ngo&agrave;i, c&aacute; nh&acirc;n nước ngo&agrave;i với nhau hoặc giữa c&aacute;c đối tượng đ&oacute; với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n, cộng đồng d&acirc;n cư th&igrave; c&aacute;c đương sự n&agrave;y c&oacute; quyền khiếu nại đến Chủ tịch UBND cấp tỉnh. Nếu một trong c&aacute;c b&ecirc;n đương sự kh&ocirc;ng đồng &yacute; với quyết định giải quyết lần đầu n&agrave;y th&igrave; c&oacute; quyền khiếu nại đến Bộ trưởng Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường để y&ecirc;u cầu giải quyết.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Thủ tục tranh chấp đất đai gồm những g&igrave;</strong></span><br />\r\n<br />\r\n<strong><span style=\"color:#ff0033\">Thủ tục h&ograve;a giải tranh chấp đất đai</span></strong><br />\r\n<br />\r\nTheo Điều 202 Luật Đất đai 2013 th&igrave; Nh&agrave; nước khuyến kh&iacute;ch c&aacute;c b&ecirc;n tranh chấp đất đai tự h&ograve;a giải hoặc giải quyết tranh chấp đất đai th&ocirc;ng qua h&ograve;a giải ở cơ sở.<br />\r\n<br />\r\nNếu c&aacute;c b&ecirc;n tranh chấp kh&ocirc;ng h&ograve;a giải được th&igrave; gửi đơn đến Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi c&oacute; đất tranh chấp để h&ograve;a giải. Chủ tịch Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; c&oacute; tr&aacute;ch nhiệm tổ chức việc h&ograve;a giải tranh chấp tại địa phương m&igrave;nh; trong qu&aacute; tr&igrave;nh tổ chức thực hiện phải phối hợp với Ủy ban Mặt trận Tổ quốc Việt Nam cấp x&atilde; v&agrave; c&aacute;c tổ chức th&agrave;nh vi&ecirc;n của Mặt trận, c&aacute;c tổ chức x&atilde; hội kh&aacute;c. Thủ tục h&ograve;a giải tranh chấp đất đai tại Ủy ban nh&acirc;n cấp x&atilde; được thực hiện trong thời hạn kh&ocirc;ng qu&aacute; 45 ng&agrave;y, kể từ ng&agrave;y nhận được đơn y&ecirc;u cầu giải quyết tranh chấp đất đai.<br />\r\n<br />\r\nViệc h&ograve;a giải phải được lập th&agrave;nh bi&ecirc;n bản c&oacute; chữ k&yacute; của c&aacute;c b&ecirc;n v&agrave; c&oacute; x&aacute;c nhận h&ograve;a giải th&agrave;nh hoặc h&ograve;a giải kh&ocirc;ng th&agrave;nh của Ủy ban nh&acirc;n d&acirc;n cấp x&atilde;. Bi&ecirc;n bản h&ograve;a giải được gửi đến c&aacute;c b&ecirc;n tranh chấp, lưu tại Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi c&oacute; đất tranh chấp.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>Thủ tục h&agrave;nh ch&iacute;nh giải quyết tranh chấp đất đai</strong></span><br />\r\n<br />\r\nTại Khoản 2 Điều 203 Luật Đất đai 2013 quy định: tranh chấp đất đai đ&atilde; được h&ograve;a giải tại Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; m&agrave; kh&ocirc;ng th&agrave;nh m&agrave; đương sự kh&ocirc;ng c&oacute; giấy chứng nhận hoặc kh&ocirc;ng c&oacute; một trong c&aacute;c loại giấy tờ quy định tại Điều 100 của Luật Đất đai 2013 th&igrave; đương sự c&oacute; thể lựa h&igrave;nh thức giải quyết tranh chấp đất đai l&agrave; nộp đơn y&ecirc;u cầu giải quyết tranh chấp tại Ủy ban nh&acirc;n d&acirc;n cấp c&oacute; thẩm quyền.<br />\r\n<br />\r\n<span style=\"color:#ff0033\"><strong>Thủ tục tố tụng giải quyết tranh chấp đất đai</strong></span><br />\r\n<br />\r\nTranh chấp đất đai đ&atilde; được h&ograve;a giải tại Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; m&agrave; kh&ocirc;ng th&agrave;nh m&agrave; đương sự c&oacute; giấy chứng nhận hoặc c&oacute; một trong c&aacute;c loại giấy tờ quy định tại Điều 100 của Luật Đất đai 2013 v&agrave; tranh chấp về t&agrave;i sản gắn liền với đất hoặc trường hợp đương sự kh&ocirc;ng c&oacute; giấy chứng nhận hoặc kh&ocirc;ng c&oacute; một trong c&aacute;c loại giấy tờ quy định tại Điều 100 của Luật Đất đai 2013 nhưng lựa chọn h&igrave;nh thức giải quyết tranh chấp đất đai l&agrave; khởi kiện d&acirc;n sự th&igrave; vụ việc do T&ograve;a &aacute;n nh&acirc;n d&acirc;n giải quyết theo quy định của ph&aacute;p luật về tố tụng d&acirc;n sự.<br />\r\n<br />\r\n<strong><span style=\"font-size:18px\">► Một số điểm mới của luật đất đai 2013</span></strong><br />\r\n<br />\r\nLuật Đất đai năm 2013 được Quốc hội kh&oacute;a XIII kỳ họp thứ 6 th&ocirc;ng qua ng&agrave;y 29/11/2013 v&agrave; c&oacute; hiệu lực thi h&agrave;nh từ ng&agrave;y 01/07/2014. Đạo luật n&agrave;y thay thế Luật Đất đai năm 2003 v&agrave; c&oacute; nhiều sửa đổi, bổ sung nhằm đ&aacute;p ứng y&ecirc;u cầu của qu&aacute; tr&igrave;nh đẩy mạnh to&agrave;n diện c&ocirc;ng cuộc đổi mới tạo nền tảng để đến năm 2020, nước ta cơ bản trở th&agrave;nh nước c&ocirc;ng nghiệp ph&aacute;t triển theo hướng hiện đại. Một trong những sửa đổi, bổ sung của Luật Đất đai năm 2013 v&agrave; c&aacute;c văn bản hướng dẫn thi h&agrave;nh thể hiện trong nội dung c&aacute;c quy định về giải quyết tranh chấp đất đai.<br />\r\n<br />\r\n<strong>Những sửa đổi, bổ sung của Luật Đất đai năm 2013 v&agrave; c&aacute;c văn bản hướng dẫn thi h&agrave;nh về h&ograve;a giải tranh chấp đất đai</strong><br />\r\n&nbsp;<br />\r\nH&ograve;a giải tranh chấp đất đai được quy định tại Điều 202 Luật Đất đai năm 2013 v&agrave; tại Điều 88 Nghị định số 43/2014/NĐ-CP của Ch&iacute;nh phủ ng&agrave;y 15/05/2014 quy định chi tiết thi h&agrave;nh một số điều của Luật Đất đai (sau đ&acirc;y gọi l&agrave; Nghị định số 43/2014/NĐ-CP). T&igrave;m hiểu nội dung quy định về h&ograve;a giải tranh chấp đất đai của c&aacute;c văn bản ph&aacute;p luật n&agrave;y cho thấy c&oacute; những sửa đổi, bổ sung cơ bản sau đ&acirc;y:<br />\r\n&nbsp;<br />\r\nThứ nhất, về thời hạn h&ograve;a giải, Luật Đất đai năm 2013 sửa đổi, bổ sung quy định về thời hạn h&ograve;a giải; theo đ&oacute;, thời hạn h&ograve;a giải kh&ocirc;ng qu&aacute; 45 ng&agrave;y, kể từ ng&agrave;y nhận được đơn y&ecirc;u cầu giải quyết tranh chấp đất đai.<br />\r\n&nbsp;<br />\r\nThứ hai, bổ sung quy định về tr&aacute;ch nhiệm của Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; (UBND cấp x&atilde;) trong việc thực hiện h&ograve;a giải, theo đ&oacute;, khi nhận được đơn y&ecirc;u cầu giải quyết tranh chấp đất đai, UBND cấp x&atilde; c&oacute; tr&aacute;ch nhiệm thực hiện c&aacute;c c&ocirc;ng việc sau:<br />\r\n&nbsp;<br />\r\nThẩm tra, x&aacute;c minh t&igrave;m hiểu nguy&ecirc;n nh&acirc;n ph&aacute;t sinh tranh chấp, thu thập giấy tờ, t&agrave;i liệu c&oacute; li&ecirc;n quan do c&aacute;c b&ecirc;n cung cấp về nguồn gốc đất, qu&aacute; tr&igrave;nh sử dụng đất v&agrave; hiện trạng sử dụng đất;<br />\r\n&nbsp;<br />\r\nTh&agrave;nh lập Hội đồng h&ograve;a giải tranh chấp đất đai để thực hiện h&ograve;a giải. Th&agrave;nh phần Hội đồng gồm: Chủ tịch hoặc Ph&oacute; Chủ tịch UBND l&agrave; Chủ tịch Hội đồng; đại diện Ủy ban Mặt trận Tổ quốc x&atilde;, phường, thị trấn; tổ trưởng tổ d&acirc;n phố đối với khu vực đ&ocirc; thị; trưởng th&ocirc;n, ấp đối với khu vực n&ocirc;ng th&ocirc;n; đại diện của một số hộ d&acirc;n sinh sống l&acirc;u đời tại x&atilde;, phường, thị trấn biết r&otilde; về nguồn gốc v&agrave; qu&aacute; tr&igrave;nh sử dụng đối với thửa đất đ&oacute;; c&aacute;n bộ địa ch&iacute;nh, c&aacute;n bộ tư ph&aacute;p x&atilde;, phường, thị trấn. T&ugrave;y từng trường hợp cụ thể, c&oacute; thể mời đại diện Hội N&ocirc;ng d&acirc;n, Hội Phụ nữ, Hội Cựu chiến binh, Đo&agrave;n Thanh ni&ecirc;n Cộng sản Hồ Ch&iacute; Minh;<br />\r\n&nbsp;<br />\r\nTổ chức cuộc họp h&ograve;a giải c&oacute; sự tham gia của c&aacute;c b&ecirc;n tranh chấp, th&agrave;nh vi&ecirc;n Hội đồng h&ograve;a giải tranh chấp đất đai v&agrave; người c&oacute; quyền lợi, nghĩa vụ li&ecirc;n quan.&nbsp;<br />\r\n&nbsp;<br />\r\nViệc h&ograve;a giải chỉ được tiến h&agrave;nh khi c&aacute;c b&ecirc;n tranh chấp đều c&oacute; mặt. Trường hợp một trong c&aacute;c b&ecirc;n tranh chấp vắng mặt đến lần thứ 2 th&igrave; được coi l&agrave; việc h&ograve;a giải kh&ocirc;ng th&agrave;nh.<br />\r\n&nbsp;<br />\r\nThứ ba, sửa đổi, bổ sung quy định kết quả h&ograve;a giải tranh chấp đất đai phải được lập th&agrave;nh bi&ecirc;n bản, gồm c&oacute; c&aacute;c nội dung: Thời gian v&agrave; địa điểm tiến h&agrave;nh h&ograve;a giải; th&agrave;nh phần tham dự h&ograve;a giải; t&oacute;m tắt nội dung tranh chấp thể hiện r&otilde; về nguồn gốc, thời điểm sử dụng đất đang tranh chấp, nguy&ecirc;n nh&acirc;n ph&aacute;t sinh tranh chấp (theo kết quả x&aacute;c minh, t&igrave;m hiểu); &yacute; kiến của Hội đồng h&ograve;a giải tranh chấp đất đai; những nội dung đ&atilde; được c&aacute;c b&ecirc;n tranh chấp thỏa thuận, kh&ocirc;ng thỏa thuận.<br />\r\n&nbsp;<br />\r\nBi&ecirc;n bản h&ograve;a giải phải c&oacute; chữ k&yacute; của Chủ tịch Hội đồng, c&aacute;c b&ecirc;n tranh chấp c&oacute; mặt tại buổi h&ograve;a giải, c&aacute;c th&agrave;nh vi&ecirc;n tham gia h&ograve;a giải v&agrave; phải đ&oacute;ng dấu của UBND cấp x&atilde;; đồng thời phải được gửi ngay cho c&aacute;c b&ecirc;n tranh chấp v&agrave; lưu tại UBND cấp x&atilde;.<br />\r\n&nbsp;<br />\r\nThứ tư, bổ sung quy định sau thời hạn 10 ng&agrave;y, kể từ ng&agrave;y lập bi&ecirc;n bản h&ograve;a giải th&agrave;nh m&agrave; c&aacute;c b&ecirc;n tranh chấp c&oacute; &yacute; kiến bằng văn bản về nội dung kh&aacute;c với nội dung đ&atilde; thống nhất trong bi&ecirc;n bản h&ograve;a giải th&agrave;nh, th&igrave; Chủ tịch UBND cấp x&atilde; tổ chức lại cuộc họp Hội đồng h&ograve;a giải để xem x&eacute;t giải quyết đối với &yacute; kiến bổ sung v&agrave; phải lập bi&ecirc;n bản h&ograve;a giải th&agrave;nh hoặc kh&ocirc;ng th&agrave;nh.<br />\r\n&nbsp;<br />\r\nThứ năm, bổ sung quy định trường hợp h&ograve;a giải kh&ocirc;ng th&agrave;nh hoặc sau khi h&ograve;a giải th&agrave;nh, m&agrave; c&oacute; &iacute;t nhất một trong c&aacute;c b&ecirc;n thay đổi &yacute; kiến về kết quả h&ograve;a giải, th&igrave; UBND cấp x&atilde; lập bi&ecirc;n bản h&ograve;a giải kh&ocirc;ng th&agrave;nh v&agrave; hướng dẫn c&aacute;c b&ecirc;n tranh chấp gửi đơn đến cơ quan c&oacute; thẩm quyền giải quyết tranh chấp tiếp theo.<br />\r\n<br />\r\n<strong>Những sửa đổi, bổ sung của Luật Đất đai năm 2013 v&agrave; c&aacute;c văn bản hướng dẫn thi h&agrave;nh về thẩm quyền giải quyết tranh chấp đất đai</strong><br />\r\n&nbsp;<br />\r\nT&igrave;m hiểu c&aacute;c quy định về thẩm quyền giải quyết tranh chấp đất đai của Luật Đất đai năm 2013 v&agrave; c&aacute;c văn bản thi h&agrave;nh, ch&uacute;ng ta c&oacute; thể r&uacute;t ra một số sửa đổi, bổ sung chủ y&ecirc;u sau đ&acirc;y:<br />\r\n&nbsp;<br />\r\nThứ nhất, sửa đổi, bổ sung quy định về thẩm quyền giải quyết tranh chấp đất đai đối với trường hợp tranh chấp đất đai đ&atilde; được h&ograve;a giải tại UBND cấp x&atilde; nhưng kh&ocirc;ng th&agrave;nh m&agrave; đương sự kh&ocirc;ng c&oacute; Giấy chứng nhận hoặc kh&ocirc;ng c&oacute; một trong c&aacute;c loại giấy tờ hợp lệ về quyền sử dụng đất quy định tại Điều 100 Luật Đất đai năm 2013; theo đ&oacute;, đối với trường hợp tranh chấp n&agrave;y th&igrave; đương sự chỉ được lựa chọn một trong hai h&igrave;nh thức giải quyết tranh chấp đất đai theo quy định sau đ&acirc;y:\r\n<ul>\r\n	<li>Nộp đơn y&ecirc;u cầu giải quyết tranh chấp tại UBND cấp huyện hoặc UBND cấp tỉnh</li>\r\n	<li>Khởi kiện tại T&ograve;a &aacute;n nh&acirc;n d&acirc;n c&oacute; thẩm quyền theo quy định của ph&aacute;p luật về tố tụng d&acirc;n sự.</li>\r\n</ul>\r\nThứ hai, sửa đổi, bổ sung quy định trường hợp đương sự lựa chọn giải quyết tranh chấp tại UBND cấp c&oacute; thẩm quyền th&igrave; việc giải quyết tranh chấp được thực hiện như sau:\r\n\r\n<ul>\r\n	<li>Trường hợp tranh chấp giữa hộ gia đ&igrave;nh, c&aacute; nh&acirc;n, cộng đồng d&acirc;n cư với nhau th&igrave; Chủ tịch UBND cấp huyện giải quyết; nếu kh&ocirc;ng đồng &yacute; với quyết định giải quyết th&igrave; c&oacute; quyền khiếu nại đến Chủ tịch UBND cấp tỉnh hoặc khởi kiện tại T&ograve;a &aacute;n nh&acirc;n d&acirc;n theo quy định của ph&aacute;p luật về tố tụng h&agrave;nh ch&iacute;nh.</li>\r\n	<li>Trường hợp tranh chấp m&agrave; một b&ecirc;n tranh chấp l&agrave; tổ chức, cơ sở t&ocirc;n gi&aacute;o, người Việt Nam định cư ở nước ngo&agrave;i, doanh nghiệp c&oacute; vốn đầu tư nước ngo&agrave;i th&igrave; Chủ tịch UBND cấp tỉnh giải quyết; nếu kh&ocirc;ng đồng &yacute; với quyết định giải quyết th&igrave; c&oacute; quyền khiếu nại đến Bộ trưởng Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường hoặc khởi kiện tại T&ograve;a &aacute;n nh&acirc;n d&acirc;n theo quy định của ph&aacute;p luật về tố tụng h&agrave;nh ch&iacute;nh.</li>\r\n</ul>\r\nThứ ba, bổ sung quy định người c&oacute; thẩm quyền giải quyết tranh chấp đất đai phải ra quyết định giải quyết tranh chấp. Quyết định giải quyết tranh chấp c&oacute; hiệu lực thi h&agrave;nh phải được c&aacute;c b&ecirc;n nghi&ecirc;m chỉnh chấp h&agrave;nh. Trường hợp c&aacute;c b&ecirc;n kh&ocirc;ng chấp h&agrave;nh sẽ bị cưỡng chế.<br />\r\n&nbsp;<br />\r\nThứ tư, bổ sung quy định thủ tục giải quyết tranh chấp đất đai thuộc thẩm quyền của Chủ tịch UBND cấp huyện, cấp tỉnh, bao gồm:\r\n<ul>\r\n	<li>Người c&oacute; đơn y&ecirc;u cầu giải quyết tranh chấp đất đai nộp đơn tại UBND cấp c&oacute; thẩm quyền</li>\r\n	<li>Chủ tịch UBND cấp c&oacute; thẩm quyền giao tr&aacute;ch nhiệm cơ quan tham mưu giải quyết</li>\r\n	<li>Cơ quan tham mưu c&oacute; nhiệm vụ thẩm tra, x&aacute;c minh vụ việc, tổ chức h&ograve;a giải giữa c&aacute;c b&ecirc;n tranh chấp, tổ chức cuộc họp c&aacute;c ban, ng&agrave;nh c&oacute; li&ecirc;n quan để tư vấn giải quyết tranh chấp đất đai (nếu cần thiết) v&agrave; ho&agrave;n chỉnh hồ sơ tr&igrave;nh Chủ tịch UBND c&ugrave;ng cấp ban h&agrave;nh quyết định giải quyết tranh chấp đất đai.</li>\r\n</ul>\r\nHồ sơ giải quyết tranh chấp đất đai bao gồm: Đơn y&ecirc;u cầu giải quyết tranh chấp đất đai; bi&ecirc;n bản h&ograve;a giải tại UBND cấp x&atilde;; bi&ecirc;n bản l&agrave;m việc với c&aacute;c b&ecirc;n tranh chấp v&agrave; người c&oacute; li&ecirc;n quan; bi&ecirc;n bản kiểm tra hiện trạng đất tranh chấp; bi&ecirc;n bản cuộc họp c&aacute;c ban, ng&agrave;nh c&oacute; li&ecirc;n quan để tư vấn giải quyết tranh chấp đất đai đối với trường hợp h&ograve;a giải kh&ocirc;ng th&agrave;nh; bi&ecirc;n bản h&ograve;a giải trong qu&aacute; tr&igrave;nh giải quyết tranh chấp; tr&iacute;ch lục bản đồ, hồ sơ địa ch&iacute;nh qua c&aacute;c thời kỳ li&ecirc;n quan đến diện t&iacute;ch đất tranh chấp v&agrave; c&aacute;c t&agrave;i liệu l&agrave;m chứng cứ, chứng minh trong qu&aacute; tr&igrave;nh giải quyết tranh chấp; b&aacute;o c&aacute;o đề xuất v&agrave; dự thảo quyết định giải quyết tranh chấp hoặc dự thảo quyết định c&ocirc;ng nhận h&ograve;a giải th&agrave;nh. Chủ tịch UBND cấp c&oacute; thẩm quyền ban h&agrave;nh quyết định giải quyết tranh chấp hoặc quyết định c&ocirc;ng nhận h&ograve;a giải th&agrave;nh, gửi cho c&aacute;c b&ecirc;n tranh chấp, c&aacute;c tổ chức, c&aacute; nh&acirc;n c&oacute; quyền v&agrave; nghĩa vụ li&ecirc;n quan.<br />\r\n&nbsp;<br />\r\nThứ năm, bổ sung quy định thủ tục giải quyết tranh chấp đất đai thuộc thẩm quyền của Bộ trưởng Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường, cụ thể:<br />\r\n<br />\r\nNgười c&oacute; đơn y&ecirc;u cầu giải quyết tranh chấp đất đai gửi đơn đến Bộ trưởng Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường .Sau khi nhận được đơn y&ecirc;u cầu giải quyết tranh chấp đất đai, Bộ trưởng Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường ph&acirc;n c&ocirc;ng đơn vị c&oacute; chức năng tham mưu giải quyết. Đơn vị được ph&acirc;n c&ocirc;ng giải quyết tiến h&agrave;nh thu thập, nghi&ecirc;n cứu hồ sơ; tổ chức h&ograve;a giải giữa c&aacute;c b&ecirc;n tranh chấp; trường hợp cần thiết tr&igrave;nh Bộ trưởng Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường quyết định th&agrave;nh lập đo&agrave;n c&ocirc;ng t&aacute;c để tiến h&agrave;nh thẩm tra, x&aacute;c minh vụ việc tại địa phương; ho&agrave;n chỉnh hồ sơ tr&igrave;nh Bộ trưởng Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường ban h&agrave;nh quyết định giải quyết tranh chấp đất đai<br />\r\n<br />\r\nHồ sơ giải quyết tranh chấp đất đai gồm: Đơn y&ecirc;u cầu giải quyết tranh chấp đất đai; bi&ecirc;n bản l&agrave;m việc với c&aacute;c b&ecirc;n tranh chấp, với c&aacute;c tổ chức, c&aacute; nh&acirc;n c&oacute; li&ecirc;n quan; bi&ecirc;n bản kiểm tra hiện trạng đất tranh chấp; bi&ecirc;n bản h&ograve;a giải trong qu&aacute; tr&igrave;nh giải quyết tranh chấp; tr&iacute;ch lục bản đồ, hồ sơ địa ch&iacute;nh qua c&aacute;c thời kỳ li&ecirc;n quan đến diện t&iacute;ch đất tranh chấp, hồ sơ, t&agrave;i liệu l&agrave;m chứng cứ, chứng minh trong qu&aacute; tr&igrave;nh giải quyết tranh chấp đất đai tại địa phương; b&aacute;o c&aacute;o đề xuất v&agrave; dự thảo quyết định giải quyết tranh chấp đất đai hoặc dự thảo quyết định c&ocirc;ng nhận h&ograve;a giải th&agrave;nh<br />\r\n<br />\r\nQuyết định giải quyết tranh chấp đất đai hoặc quyết định c&ocirc;ng nhận h&ograve;a giải th&agrave;nh được gửi cho c&aacute;c b&ecirc;n tranh chấp, c&aacute;c tổ chức, c&aacute; nh&acirc;n c&oacute; quyền v&agrave; nghĩa vụ li&ecirc;n quan.<br />\r\n&nbsp;<br />\r\nThứ s&aacute;u, sửa đổi, bổ sung quy định căn cứ để giải quyết tranh chấp đất đai trong trường hợp c&aacute;c b&ecirc;n tranh chấp kh&ocirc;ng c&oacute; giấy tờ về quyền sử dụng đất; cưỡng chế thi h&agrave;nh quyết định giải quyết tranh chấp đất đai, quyết định c&ocirc;ng nhận h&ograve;a giải th&agrave;nh.<br />\r\n<br />\r\nTheo đ&oacute;, căn cứ để giải quyết tranh chấp đất đai trong c&aacute;c trường hợp n&agrave;y bao gồm: Chứng cứ về nguồn gốc v&agrave; qu&aacute; tr&igrave;nh sử dụng đất do c&aacute;c b&ecirc;n tranh chấp đất đai đưa ra; thực tế diện t&iacute;ch đất m&agrave; c&aacute;c b&ecirc;n tranh chấp đang sử dụng ngo&agrave;i diện t&iacute;ch đất đang c&oacute; tranh chấp v&agrave; b&igrave;nh qu&acirc;n diện t&iacute;ch đất cho một nh&acirc;n khẩu tại địa phương; sự ph&ugrave; hợp của hiện trạng sử dụng thửa đất đang c&oacute; tranh chấp với quy hoạch, kế hoạch sử dụng đất đ&atilde; được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt; ch&iacute;nh s&aacute;ch ưu đ&atilde;i người c&oacute; c&ocirc;ng của Nh&agrave; nước; quy định của ph&aacute;p luật về giao đất, cho thu&ecirc; đất, c&ocirc;ng nhận quyền sử dụng đất.<br />\r\n&nbsp;<br />\r\nCăn cứ quy định về cưỡng chế thi h&agrave;nh quyết định h&agrave;nh ch&iacute;nh, UBND cấp tỉnh quy định chi tiết việc cưỡng chế thi h&agrave;nh quyết định giải quyết tranh chấp đất đai, quyết định c&ocirc;ng nhận h&ograve;a giải th&agrave;nh.', 0, NULL, NULL, 1, 0, 2, '2017-07-09 11:01:51', '2017-07-14 09:33:19', 2, 2),
(2, 'Tranh chấp quyền sử dụng đất có sổ đỏ như thế nào', 'tranh-chap-quyen-su-dung-dat-co-so-do-nhu-the-nao', 'Tranh chap quyen su dung dat co so do nhu the nao', 'Nếu quý vị đang băng khoăn không biết việc tranh chấp quyền sử dụng đất có sổ đỏ như thế nào. Hãy tham khảo bài viết dưới đây, chúng tôi sẽ chia sẻ hướng giải quyết cho quý vị tham khảo theo quy định hiện nay.', '2017/07/09/tranh-chap-quyen-su-dung-dat-co-so-do-nhu-the-nao-1499585030.png', 5, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"Tranh chấp quyền sử dụng đất có sổ đỏ như thế nào\" src=\"/uploads/images/tranh-chap-quyen-su-dung-dat-co-so-do-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Thế n&agrave;o l&agrave; tranh chấp đất đai</strong></span><br />\r\n<br />\r\nTranh chấp đất đai l&agrave; tranh chấp về quyền, nghĩa vụ của người sử dụng đất giữa hai hoặc nhiều b&ecirc;n trong quan hệ đất đai. L&agrave; mọi tranh chấp ph&aacute;t sinh trong quan hệ đất đai bao gồm tranh chấp về quyền sử dụng đất, tranh chấp về t&agrave;i sản gắn liền với đất, tranh chấp li&ecirc;n quan đến địa giới h&agrave;nh ch&iacute;nh, mục đ&iacute;ch sử dụng đất.<br />\r\n<br />\r\nĐất đang c&oacute; tranh chấp được hiểu l&agrave; loại đất m&agrave; giữa người sử dụng hợp ph&aacute;p đất đ&oacute; với c&aacute; nh&acirc;n kh&aacute;c, với Nh&agrave; nước (về vấn đề bồi thường đất) hoặc giữa những người sử dụng chung mảnh đất đ&oacute; với nhau đang c&oacute; tranh chấp về quyền sử dụng đất, về t&agrave;i sản gắn liền với đất, về ranh giới, về mục đ&iacute;ch sử dụng đất hoặc về quyền, nghĩa vụ ph&aacute;t sinh trong qu&aacute; tr&igrave;nh sử dụng đất.<br />\r\n<br />\r\n<strong><span style=\"font-size:18px\">►&nbsp;Giải quyết tranh chấp đất đai c&oacute; sổ đỏ như thế n&agrave;o</span></strong><br />\r\n<br />\r\nNh&agrave; nước lu&ocirc;n khuyến kh&iacute;ch c&aacute;c b&ecirc;n tranh chấp đất đai tự h&ograve;a giải hoặc giải quyết tranh chấp đất đai th&ocirc;ng qua h&ograve;a giải ở cơ sở. Tranh chấp đất đai m&agrave; c&aacute;c b&ecirc;n tranh chấp kh&ocirc;ng h&ograve;a giải được th&igrave; gửi đơn đến Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi c&oacute; đất tranh chấp để h&ograve;a giải.<br />\r\n<br />\r\nChủ tịch ủy ban nh&acirc;n d&acirc;n cấp x&atilde; c&oacute; tr&aacute;ch nhiệm tổ chức việc h&ograve;a giải tranh chấp đất đai tại địa phương m&igrave;nh; trong qu&aacute; tr&igrave;nh tổ chức thực hiện phải phối hợp với ủy ban mặt trận tổ quốc Việt Nam cấp x&atilde; v&agrave; c&aacute;c tổ chức th&agrave;nh vi&ecirc;n của mặt trận, c&aacute;c tổ chức x&atilde; hội kh&aacute;c. Thủ tục h&ograve;a giải tranh chấp đất đai tại ủy ban nh&acirc;n cấp x&atilde; được thực hiện trong thời hạn kh&ocirc;ng qu&aacute; 45 ng&agrave;y, kể từ ng&agrave;y nhận được đơn y&ecirc;u cầu giải quyết tranh chấp đất đai.<br />\r\n<br />\r\nViệc h&ograve;a giải phải được lập th&agrave;nh bi&ecirc;n bản c&oacute; chữ k&yacute; của c&aacute;c b&ecirc;n v&agrave; c&oacute; x&aacute;c nhận h&ograve;a giải th&agrave;nh hoặc h&ograve;a giải kh&ocirc;ng th&agrave;nh của Ủy ban nh&acirc;n d&acirc;n cấp x&atilde;. bi&ecirc;n bản h&ograve;a giải được gửi đến c&aacute;c b&ecirc;n tranh chấp, lưu tại ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi c&oacute; đất tranh chấp.<br />\r\n<br />\r\nĐối với trường hợp h&ograve;a giải th&agrave;nh m&agrave; c&oacute; thay đổi hiện trạng về ranh giới, người sử dụng đất th&igrave; Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; gửi bi&ecirc;n bản h&ograve;a giải đến ph&ograve;ng t&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường đối với trường hợp tranh chấp đất đai giữa hộ gia đ&igrave;nh, c&aacute; nh&acirc;n, cộng đồng d&acirc;n cư với nhau; gửi đến Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường đối với c&aacute;c trường hợp kh&aacute;c.<br />\r\n<br />\r\nPh&ograve;ng t&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường, sở t&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường tr&igrave;nh Ủy ban nh&acirc;n d&acirc;n c&ugrave;ng cấp quyết định c&ocirc;ng nhận việc thay đổi ranh giới thửa đất v&agrave; cấp mới Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất.<br />\r\n<br />\r\n<strong>Lưu &yacute;:&nbsp;Sau khi h&ograve;a giải kh&ocirc;ng th&agrave;nh th&igrave; đối với tranh chấp đất đai c&oacute; sổ đỏ sẽ gửi đơn l&ecirc;n T&ograve;a &aacute;n nh&acirc;n d&acirc;n y&ecirc;u cầu được giải quyết.</strong><br />\r\n<br />\r\nTranh chấp đất đai đ&atilde; được h&ograve;a giải tại ủy ban nh&acirc;n d&acirc;n cấp x&atilde; m&agrave; kh&ocirc;ng th&agrave;nh th&igrave; được giải quyết như sau:<br />\r\n<br />\r\nTranh chấp đất đai m&agrave; đương sự c&oacute; Giấy chứng nhận hoặc c&oacute; một trong c&aacute;c loại giấy tờ quy định tại Điều 100 của Luật n&agrave;y v&agrave; tranh chấp về t&agrave;i sản gắn liền với đất th&igrave; do T&ograve;a &aacute;n nh&acirc;n d&acirc;n giải quyết.<br />\r\n<br />\r\nTranh chấp đất đai m&agrave; đương sự kh&ocirc;ng c&oacute; Giấy chứng nhận hoặc kh&ocirc;ng c&oacute; một trong c&aacute;c loại giấy tờ quy định tại Điều 100 của Luật n&agrave;y th&igrave; đương sự chỉ được lựa chọn một trong hai h&igrave;nh thức giải quyết tranh chấp đất đai theo quy định sau đ&acirc;y:\r\n<ul>\r\n	<li>Nộp đơn y&ecirc;u cầu giải quyết tranh chấp tại Ủy ban nh&acirc;n d&acirc;n cấp c&oacute; thẩm quyền theo quy định tại khoản 3 điều n&agrave;y</li>\r\n	<li>Khởi kiện tại T&ograve;a &aacute;n nh&acirc;n d&acirc;n c&oacute; thẩm quyền theo quy định của ph&aacute;p luật về tố tụng d&acirc;n sự</li>\r\n</ul>\r\nTrường hợp đương sự lựa chọn giải quyết tranh chấp tại Ủy ban nh&acirc;n d&acirc;n cấp c&oacute; thẩm quyền th&igrave; việc giải quyết tranh chấp đất đai được thực hiện như sau:\r\n\r\n<ul>\r\n	<li>Trường hợp tranh chấp giữa hộ gia đ&igrave;nh, c&aacute; nh&acirc;n, cộng đồng d&acirc;n cư với nhau th&igrave; Chủ tịch Ủy ban nh&acirc;n d&acirc;n cấp huyện giải quyết; nếu kh&ocirc;ng đồng &yacute; với quyết định giải quyết th&igrave; c&oacute; quyền khiếu nại đến Chủ tịch Ủy ban nh&acirc;n d&acirc;n cấp tỉnh hoặc khởi kiện tại T&ograve;a &aacute;n nh&acirc;n d&acirc;n theo quy định của ph&aacute;p luật về tố tụng h&agrave;nh ch&iacute;nh</li>\r\n	<li>Trường hợp tranh chấp m&agrave; một b&ecirc;n tranh chấp l&agrave; tổ chức, cơ sở t&ocirc;n gi&aacute;o, người Việt Nam định cư ở nước ngo&agrave;i, doanh nghiệp c&oacute; vốn đầu tư nước ngo&agrave;i th&igrave; Chủ tịch Ủy ban nh&acirc;n d&acirc;n cấp tỉnh giải quyết; nếu kh&ocirc;ng đồng &yacute; với quyết định giải quyết th&igrave; c&oacute; quyền khiếu nại đến Bộ trưởng Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường hoặc khởi kiện tại T&ograve;a &aacute;n nh&acirc;n d&acirc;n theo quy định của ph&aacute;p luật về tố tụng h&agrave;nh ch&iacute;nh</li>\r\n</ul>\r\nNgười c&oacute; thẩm quyền giải quyết tranh chấp đất đai tại khoản 3 điều n&agrave;y phải ra quyết định giải quyết tranh chấp. Quyết định giải quyết tranh chấp c&oacute; hiệu lực thi h&agrave;nh phải được c&aacute;c b&ecirc;n tranh chấp nghi&ecirc;m chỉnh chấp h&agrave;nh. Trường hợp c&aacute;c b&ecirc;n kh&ocirc;ng chấp h&agrave;nh sẽ bị cưỡng chế thi h&agrave;nh.<br />\r\n<br />\r\nSau khi tham khảo b&agrave;i viết n&agrave;y, ch&uacute;ng t&ocirc;i hi vọng qu&yacute; vị sẽ biết được c&aacute;ch giải quyết tranh chấp đất với nhau. Nhưng ch&uacute;ng t&ocirc;i khuy&ecirc;n rằng n&ecirc;n h&ograve;a giải trước, một khi đ&atilde; l&ecirc;n đến t&ograve;a &aacute;n sẽ rất phiền phức v&agrave; nhiều vấn đề li&ecirc;n quan kh&aacute;c.', 0, NULL, NULL, 1, 0, 3, '2017-07-09 14:23:55', '2017-07-14 09:33:01', 2, 2),
(3, 'Chi phí sang tên sổ đỏ nhà đất và thủ tục như thế nào', 'chi-phi-sang-ten-so-do-nha-dat-va-thu-tuc-nhu-the-nao', 'Chi phi sang ten so do nha dat va thu tuc nhu the nao', 'Quý vị đang muốn sang tên sổ đỏ nhà đất cho người khác, nhưng không nắm được các giấy tờ cần chuẩn bị. Hãy tham khảo ngay bài viết dưới đây, quý vị sẽ biết chi phí sang tên sổ đỏ nhà đất và thủ tục như thế nào.', '2017/07/09/chi-phi-sang-ten-so-do-nha-dat-va-thu-tuc-nhu-the-nao-1499586598.png', 5, '<h2>&nbsp;</h2>\r\n&nbsp;\r\n\r\n<div style=\"text-align:center\"><img alt=\"Chi phí sang tên sổ đỏ nhà đất và thủ tục như thế nào\" src=\"/uploads/images/chi-phi-sang-ten-so-do-nha-dat-va-thu-tuc-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nTrước khi biết lệ ph&iacute; v&agrave; thủ tục như thế n&agrave;o, ch&uacute;ng t&ocirc;i muốn qu&yacute; vị biết thuế thu nhập c&aacute; nh&acirc;n khi t&aacute;ch thửa như thế n&agrave;o. Mời qu&yacute; vị tham khảo dưới đ&acirc;y để biết về thuế c&aacute; nh&acirc;n n&agrave;y.<br />\r\n<br />\r\nTheo Th&ocirc;ng tư số 113/2011/TT-BTC của Bộ T&agrave;i ch&iacute;nh, c&oacute; hai phương ph&aacute;p để t&iacute;nh thuế thu nhập c&aacute; nh&acirc;n khi chuyển nhượng, mua b&aacute;n nh&agrave; đất.<br />\r\n<br />\r\nPhương ph&aacute;p thứ nhất: Thuế suất l&agrave; 25% tr&ecirc;n thu nhập t&aacute;ch thửa (gi&aacute; chuyển nhượng - gi&aacute; vốn) khi người nộp thuế k&ecirc; khai được những h&oacute;a đơn, chứng từ hợp ph&aacute;p chứng minh được gi&aacute; mua v&agrave; c&aacute;c chi ph&iacute; cấp quyền sử dụng đất, quyền sở hữu nh&agrave;; chi ph&iacute; cải tạo đất, nh&agrave;; chi ph&iacute; x&acirc;y dựng<br />\r\n<br />\r\nPhương ph&aacute;p thứ hai: Trường hợp kh&ocirc;ng c&oacute; h&oacute;a đơn, chứng từ chứng minh được gi&aacute; mua v&agrave; c&aacute;c chi ph&iacute; li&ecirc;n quan th&igrave; &aacute;p dụng thuế suất 2% theo gi&aacute; chuyển nhượng thực tế ghi tr&ecirc;n hợp đồng t&aacute;ch thửa. Trong trường hợp tr&ecirc;n hợp đồng chuyển nhượng kh&ocirc;ng ghi gi&aacute; chuyển nhượng hoặc ghi gi&aacute; chuyển nhượng thấp hơn bảng gi&aacute; đất, gi&aacute; t&iacute;nh lệ ph&iacute; trước bạ nh&agrave; do UBND cấp tỉnh quy định th&igrave; gi&aacute; t&iacute;nh thuế lấy theo bảng gi&aacute; đất, gi&aacute; t&iacute;nh lệ ph&iacute; trước bạ nh&agrave; do UBND cấp tỉnh quy định.<br />\r\n<br />\r\nTheo khoản 1 điều 4 luật thuế thu nhập c&aacute; nh&acirc;n năm 2007 th&igrave; việc t&aacute;ch thửa giữa vợ với chồng; cha đẻ, mẹ đẻ với con đẻ; cha nu&ocirc;i, mẹ nu&ocirc;i với con nu&ocirc;i; cha chồng, mẹ chồng với con d&acirc;u; cha vợ, mẹ vợ với con rể; &ocirc;ng nội, b&agrave; nội với ch&aacute;u nội; &ocirc;ng ngoại, b&agrave; ngoại với ch&aacute;u ngoại; anh, chị, em ruột với nhau l&agrave; thu nhập được miễn thuế thu nhập c&aacute; nh&acirc;n. Trong trường hợp n&agrave;y, hồ sơ cần c&oacute; giấy tờ chứng minh quan hệ giữa người tiến h&agrave;nh t&aacute;ch thửa v&agrave; người nhận chuyển nhượng như: giấy khai sinh<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Lệ ph&iacute; trước bạ.</strong></span><br />\r\n<br />\r\nCũng như thuế thu nhập c&aacute; nh&acirc;n, lệ ph&iacute; trước bạ l&agrave; nghĩa vụ bắt buộc đối với thủ tục t&aacute;ch thửa đất. Mức ph&iacute; trước bạ được t&iacute;nh bằng 0,5% gi&aacute; chuyển nhượng ghi tr&ecirc;n hợp đồng t&aacute;ch thửa.<br />\r\n<br />\r\nSau khi đ&oacute;ng c&aacute;c loại ph&iacute; tr&ecirc;n v&agrave;o Ng&acirc;n s&aacute;ch Nh&agrave; nước, người nhận chuyển nhượng d&ugrave;ng phiếu thu ph&iacute; để ho&agrave;n thiện hồ sơ sang t&ecirc;n sổ đỏ, nộp tại Văn ph&ograve;ng Đăng k&yacute; đất đai.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Thủ tục sang t&ecirc;n như thế n&agrave;o</strong></span><br />\r\n<br />\r\nKhi chuyển nhượng quyền sử dụng đất từ người n&agrave;y sang người kh&aacute;c ch&uacute;ng ta cần tiến h&agrave;nh thủ tục đăng k&yacute; quyền sử dụng đất theo quy định của ph&aacute;p luật để bảo vệ quyền v&agrave; lợi &iacute;ch hợp ph&aacute;p của m&igrave;nh.<br />\r\n<br />\r\n1. Thẩm quyền giải quyết: UBND cấp huyện nơi c&oacute; nh&agrave; đất<br />\r\n<br />\r\n2. Đối tượng thực hiện: C&aacute; nh&acirc;n, hộ gia đ&igrave;nh<br />\r\n<br />\r\n3. C&aacute;ch thức thực hiện: Hồ sơ của bước trước l&agrave;m cơ sở để thực hiện c&aacute;c bước sau v&agrave; được bổ sung theo từng bước như sau:<br />\r\n<br />\r\nBước 1: C&aacute;c b&ecirc;n đến cơ quan c&ocirc;ng chứng lập hợp đồng chuyển nhượng, thừa kế, cho tặng. Trong thời hạn 10 ng&agrave;y kể từ ng&agrave;y lập hợp đồng, c&aacute;c b&ecirc;n phải k&ecirc; khai lệ ph&iacute; trước bạ, thuế thu nhập c&aacute; nh&acirc;n, nếu qu&aacute; thời hạn tr&ecirc;n sẽ bị phạt theo quy định của nh&agrave; nước.<br />\r\n<br />\r\nBước 2: K&ecirc; khai nghĩa vụ t&agrave;i ch&iacute;nh (tại UBND cấp huyện nơi c&oacute; nh&agrave;, đất)<br />\r\n<br />\r\n<strong>Th&agrave;nh phần hồ sơ gồm:</strong>\r\n\r\n<ul>\r\n	<li>Tờ khai lệ ph&iacute; trước bạ (02 bản do b&ecirc;n mua k&yacute;)</li>\r\n	<li>Tờ khai thuế thu nhập c&aacute; nh&acirc;n (02 bản do b&ecirc;n b&aacute;n k&yacute;. Ri&ecirc;ng trường hợp cho tặng 04 bản).</li>\r\n	<li>Hợp đồng c&ocirc;ng chứng đ&atilde; lập (01 bản ch&iacute;nh)</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất (sổ đỏ), quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản gắn liền với đất (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền)</li>\r\n	<li>CMND + Sổ hộ khẩu của cả b&ecirc;n mua v&agrave; b&ecirc;n b&aacute;n (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền)</li>\r\n	<li>Đối với trường hợp cho tặng, thừa kế phải c&oacute; giấy tờ chứng minh quan hệ nh&acirc;n th&acirc;n của người cho v&agrave; người nhận để được miễn thuế thu nhập c&aacute; nh&acirc;n.</li>\r\n</ul>\r\nThời hạn c&oacute; th&ocirc;ng b&aacute;o nộp thuế: 10 ng&agrave;y Sau khi c&oacute; th&ocirc;ng b&aacute;o th&igrave; người nộp thuế nộp tiền v&agrave;o ng&acirc;n s&aacute;ch nh&agrave; nước.<br />\r\n<br />\r\n<strong>Thuế chuyển dịch quyền sử dụng đất gồm:</strong>\r\n\r\n<ul>\r\n	<li>Thuế thu nhập c&aacute; nh&acirc;n: 2 %</li>\r\n	<li>Thuế trước bạ: 0,5 %</li>\r\n</ul>\r\nBước 3: K&ecirc; khai sang t&ecirc;n (tại UBND quận/huyện nơi c&oacute; nh&agrave;, đất)\r\n\r\n<ul>\r\n	<li>Đơn đề nghị đăng k&yacute; biến động (do b&ecirc;n b&aacute;n k&yacute;); Trong trường hợp c&oacute; thoả thuận trong hợp đồng về việc b&ecirc;n mua thực hiện thủ tục h&agrave;nh ch&iacute;nh th&igrave; b&ecirc;n mua c&oacute; thể k&yacute; thay.</li>\r\n	<li>Hợp đồng chuyển nhượng; hợp đồng tặng cho; hoặc văn bản thỏa thuận ph&acirc;n chia di sản; văn bản khai nhận di sản</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất (sổ đỏ), quyền sở hữu nh&agrave; v&agrave; t&agrave;i sản gắn liền với đất (bản gốc)</li>\r\n	<li>Giấy nộp tiền v&agrave;o ng&acirc;n s&aacute;ch nh&agrave; nước (bản gốc)</li>\r\n	<li>Bản sao CMND + Sổ hộ khẩu của b&ecirc;n nhận chuyển nhượng, thừa kế, cho tặng</li>\r\n	<li>Thời hạn sang t&ecirc;n: 15 ng&agrave;y</li>\r\n</ul>\r\n<strong>Lệ ph&iacute; sang t&ecirc;n gồm</strong>\r\n\r\n<ul>\r\n	<li>- Lệ ph&iacute; địa ch&iacute;nh: 15.000 đồng/trường hợp</li>\r\n	<li>- Lệ ph&iacute; thẩm định: Mức thu t&iacute;nh bằng 0,15% gi&aacute; trị (sang t&ecirc;n) chuyển nhượng (Tối thiểu 100.000 đồng đến tối đa kh&ocirc;ng qu&aacute; 5.000.000 đồng/trường hợp)</li>\r\n</ul>\r\nBước 4: Nộp đủ lệ ph&iacute; theo quy định v&agrave; nhận sổ đỏ.<br />\r\n<br />\r\nTr&ecirc;n đ&acirc;y l&agrave; những th&ocirc;ng tin về thủ tục sang t&ecirc;n, chi ph&iacute; cho mọi người tham khảo. Nếu qu&yacute; vị cần th&ecirc;m c&aacute;c th&ocirc;ng tin về luật nh&agrave; đất kh&aacute;c, h&atilde;y tham khảo tại chuy&ecirc;n mục Tư Vấn Luật tr&ecirc;n website của ch&uacute;ng t&ocirc;i.', 0, NULL, NULL, 1, 0, 4, '2017-07-09 14:53:42', '2017-07-14 09:32:45', 2, 2),
(4, 'Đất thổ cư có được phép đào ao hay không', 'dat-tho-cu-co-duoc-phep-dao-ao-hay-khong', 'Dat tho cu co duoc phep dao ao hay khong', 'Nếu quý vị có một mảnh đất và xây nhà trên đó, nhưng lại dư ra một ít đất và muốn đào ao để nuôi thủy sản. Nhưng quý vị lại thắc mắc việc đất thổ cư có được phép đào ao hay không. Vấn đề này chúng tôi sẽ chia sẻ câu trả lời cho quý vị tham khảo dưới đây.', '2017/07/09/dat-tho-cu-co-duoc-phep-dao-ao-hay-khong-1499588385.png', 5, '<h2>&nbsp;</h2>\r\n&nbsp;\r\n\r\n<div style=\"text-align:center\"><img alt=\"Đất thổ cư có được phép đào ao hay không\" src=\"/uploads/images/dat-tho-cu-co-duoc-phep-dao-ao-hay-khong.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nVề nguy&ecirc;n tăc th&igrave; người sử dụng đất phải sử dụng đất đ&uacute;ng mục đ&iacute;ch. Muốn sử dụng đất sang mục đ&iacute;ch kh&aacute;c th&igrave; phải xin ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất.<br />\r\n<br />\r\nNếu sử dụng đất sai mục đ&iacute;ch th&igrave; sẽ bị xử phạt h&agrave;nh ch&iacute;nh, thậm ch&iacute; c&oacute; thể bị thu hồi đất. Cụ thể luật đất đai năm 2013 quy định như sau:<br />\r\n<br />\r\n<strong>Điều 64. Thu hồi đất do vi phạm ph&aacute;p luật về đất đai</strong><br />\r\n<br />\r\n1. C&aacute;c trường hợp thu hồi đất do vi phạm ph&aacute;p luật về đất đai bao gồm:<br />\r\n<br />\r\na) Sử dụng đất kh&ocirc;ng đ&uacute;ng mục đ&iacute;ch đ&atilde; được Nh&agrave; nước giao, cho thu&ecirc;, c&ocirc;ng nhận quyền sử dụng đất v&agrave; đ&atilde; bị xử phạt vi phạm h&agrave;nh ch&iacute;nh về h&agrave;nh vi sử dụng đất kh&ocirc;ng đ&uacute;ng mục đ&iacute;ch m&agrave; tiếp tục vi phạm<br />\r\n<br />\r\nb) Người sử dụng đất cố &yacute; hủy hoại đất<br />\r\n<br />\r\nc) Đất được giao, cho thu&ecirc; kh&ocirc;ng đ&uacute;ng đối tượng hoặc kh&ocirc;ng đ&uacute;ng thẩm quyền<br />\r\n<br />\r\nd) Đất kh&ocirc;ng được chuyển nhượng, tặng cho theo quy định của Luật n&agrave;y m&agrave; nhận chuyển nhượng, nhận tặng cho<br />\r\n<br />\r\nđ) Đất được Nh&agrave; nước giao để quản l&yacute; m&agrave; để bị lấn, chiếm<br />\r\n<br />\r\ne) Đất kh&ocirc;ng được chuyển quyền sử dụng đất theo quy định của Luật n&agrave;y m&agrave; người sử dụng đất do thiếu tr&aacute;ch nhiệm để bị lấn, chiếm<br />\r\n<br />\r\ng) Người sử dụng đất kh&ocirc;ng thực hiện nghĩa vụ đối với Nh&agrave; nước v&agrave; đ&atilde; bị xử phạt vi phạm h&agrave;nh ch&iacute;nh m&agrave; kh&ocirc;ng chấp h&agrave;nh<br />\r\n<br />\r\nh) Đất trồng c&acirc;y h&agrave;ng năm kh&ocirc;ng được sử dụng trong thời hạn 12 th&aacute;ng li&ecirc;n tục; đất trồng c&acirc;y l&acirc;u năm kh&ocirc;ng được sử dụng trong thời hạn 18 th&aacute;ng li&ecirc;n tục; đất trồng rừng kh&ocirc;ng được sử dụng trong thời hạn 24 th&aacute;ng li&ecirc;n tục<br />\r\n<br />\r\ni) Đất được Nh&agrave; nước giao, cho thu&ecirc; để thực hiện dự &aacute;n đầu tư m&agrave; kh&ocirc;ng được sử dụng trong thời hạn 12 th&aacute;ng li&ecirc;n tục hoặc tiến độ sử dụng đất chậm 24 th&aacute;ng so với tiến độ ghi trong dự &aacute;n đầu tư kể từ khi nhận b&agrave;n giao đất tr&ecirc;n thực địa phải đưa đất v&agrave;o sử dụng<br />\r\n<br />\r\nTrường hợp kh&ocirc;ng đưa đất v&agrave;o sử dụng th&igrave; chủ đầu tư được gia hạn sử dụng 24 th&aacute;ng v&agrave; phải nộp cho Nh&agrave; nước khoản tiền tương ứng với mức tiền sử dụng đất, tiền thu&ecirc; đất đối với thời gian chậm tiến độ thực hiện dự &aacute;n trong thời gian n&agrave;y; hết thời hạn được gia hạn m&agrave; chủ đầu tư vẫn chưa đưa đất v&agrave;o sử dụng th&igrave; Nh&agrave; nước thu hồi đất m&agrave; kh&ocirc;ng bồi thường về đất v&agrave; t&agrave;i sản gắn liền với đất, trừ trường hợp do bất khả kh&aacute;ng.<br />\r\n<br />\r\n2. Việc thu hồi đất do vi phạm ph&aacute;p luật về đất đai phải căn cứ v&agrave;o văn bản, quyết định của cơ quan nh&agrave; nước c&oacute; thẩm quyền x&aacute;c định h&agrave;nh vi vi phạm ph&aacute;p luật về đất đai.<br />\r\n<br />\r\n3. Ch&iacute;nh phủ quy định chi tiết điều n&agrave;y<br />\r\n<br />\r\n<strong>Luật Đất Đai 2013, Điều 6. Nguy&ecirc;n tắc sử dụng đất</strong><br />\r\n<br />\r\nĐ&uacute;ng quy hoạch, kế hoạch sử dụng đất v&agrave; đ&uacute;ng mục đ&iacute;ch sử dụng đất.<br />\r\n<br />\r\nĐất đai thuộc sở hữu to&agrave;n d&acirc;n do Nh&agrave; nước đại diện chủ sở hữu v&agrave; thống nhất quản l&yacute;. Nh&agrave; nước trao quyền sử dụng đất cho người sử dụng đất theo quy định. Đăng k&yacute; biến động được thực hiện đối với trường hợp đ&atilde; được cấp Giấy chứng nhận hoặc đ&atilde; đăng k&yacute; m&agrave; c&oacute; thay đổi về chuyển mục đ&iacute;ch sử dụng đất. Theo quy định tại khoản 1 Điều 57 Luật đất đai năm 2013 th&igrave; c&aacute;c trường hợp chuyển mục đ&iacute;ch sử dụng đất phải được ph&eacute;p của cơ quan nh&agrave; nước c&oacute; thẩm quyền bao gồm:<br />\r\n<br />\r\n- Chuyển đất trồng l&uacute;a sang đất trồng c&acirc;y l&acirc;u năm, đất trồng rừng, đất nu&ocirc;i trồng thủy sản, đất l&agrave;m muối<br />\r\n<br />\r\n- Chuyển đất trồng c&acirc;y h&agrave;ng năm kh&aacute;c sang đất nu&ocirc;i trồng thủy sản nước mặn, đất l&agrave;m muối, đất nu&ocirc;i trồng thủy sản dưới h&igrave;nh thức ao, hồ, đầm<br />\r\n<br />\r\n- Chuyển đất rừng đặc dụng, đất rừng ph&ograve;ng hộ, đất rừng sản xuất sang sử dụng v&agrave;o mục đ&iacute;ch kh&aacute;c trong nh&oacute;m đất n&ocirc;ng nghiệp<br />\r\n<br />\r\n- Chuyển đất n&ocirc;ng nghiệp sang đất phi n&ocirc;ng nghiệp<br />\r\n<br />\r\n- Chuyển đất phi n&ocirc;ng nghiệp được Nh&agrave; nước giao đất kh&ocirc;ng thu tiền sử dụng đất sang đất phi n&ocirc;ng nghiệp được Nh&agrave; nước giao đất c&oacute; thu tiền sử dụng đất hoặc thu&ecirc; đất<br />\r\n<br />\r\n- Chuyển đất phi n&ocirc;ng nghiệp kh&ocirc;ng phải l&agrave; đất ở sang đất ở<br />\r\n<br />\r\n- Chuyển đất x&acirc;y dựng c&ocirc;ng tr&igrave;nh sự nghiệp, đất sử dụng v&agrave;o mục đ&iacute;ch c&ocirc;ng cộng c&oacute; mục đ&iacute;ch kinh doanh, đất sản xuất, kinh doanh phi n&ocirc;ng nghiệp kh&ocirc;ng phải l&agrave; đất thương mại, dịch vụ sang đất thương mại, dịch vụ; chuyển đất thương mại, dịch vụ, đất x&acirc;y dựng c&ocirc;ng tr&igrave;nh sự nghiệp sang đất cơ sở sản xuất phi n&ocirc;ng nghiệp.<br />\r\n<br />\r\nCăn cứ cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất dựa tr&ecirc;n kế hoạch sử dụng đất h&agrave;ng năm của cấp huyện đ&atilde; được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt v&agrave; nhu cầu sử dụng đất thể hiện trong đơn xin chuyển mục đ&iacute;ch sử dụng đất.<br />\r\n<br />\r\nTheo th&ocirc;ng tin bạn cung cấp th&igrave; đất của bạn l&agrave; đất ở n&ocirc;ng th&ocirc;n thuộc nh&oacute;m đất phi n&ocirc;ng nghiệp, mục đ&iacute;ch sử dụng l&agrave; đất ở th&igrave; việc đ&agrave;o ao chuyển mục đ&iacute;ch sử dụng đất sang đất n&ocirc;ng nghiệp cần đ&aacute;p ứng điều kiện về chuyển mục đ&iacute;ch sử dụng đất theo quy định ph&aacute;p luật.&nbsp;', 0, NULL, NULL, 1, 0, 5, '2017-07-09 15:24:31', '2017-07-14 09:32:23', 2, 2);
INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(5, 'Điều kiện để cấp giấy chứng nhận quyền sử dụng đất như thế nào', 'dieu-kien-de-cap-giay-chung-nhan-quyen-su-dung-dat-nhu-the-nao', 'Dieu kien de cap giay chung nhan quyen su dung dat nhu the nao', 'Nếu quý vị có một mảnh đất do cha mẹ để lại nhưng chưa có giấy tờ gì cả. Bây giờ muốn xin sổ đỏ lần đầu tiên nhưng lại không biết điều kiện để cấp giấy chứng nhận quyền sử dụng đất như thế nào thì hãy tham khảo bài viết dưới đây.', '2017/07/09/dieu-kien-de-cap-giay-chung-nhan-quyen-su-dung-dat-nhu-the-nao-1499591082.png', 5, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><img alt=\"Điều kiện để cấp giấy chứng nhận quyền sử dụng đất như thế nào\" src=\"/uploads/images/dieu-kien-de-cap-giay-chung-nhan-quyen-su-dung-dat-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nTheo quy định tại Khoản 1 Điều 100 Luật đất đai về cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n, cộng đồng d&acirc;n cư đang sử dụng đất c&oacute; giấy tờ về quyền sử dụng đất:<br />\r\n&nbsp;<br />\r\nĐiều 100. Cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n, cộng đồng d&acirc;n cư đang sử dụng đất c&oacute; giấy tờ về quyền sử dụng đất<br />\r\n&nbsp;<br />\r\nHộ gia đ&igrave;nh, c&aacute; nh&acirc;n đang sử dụng đất ổn định m&agrave; c&oacute; một trong c&aacute;c loại giấy tờ sau đ&acirc;y th&igrave; được cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất v&agrave; kh&ocirc;ng phải nộp tiền sử dụng đất:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Những giấy tờ về quyền được sử dụng đất trước ng&agrave;y 15 th&aacute;ng 10 năm 1993 do cơ quan c&oacute; thẩm quyền cấp trong qu&aacute; tr&igrave;nh thực hiện ch&iacute;nh s&aacute;ch đất đai của Nh&agrave; nước Việt Nam d&acirc;n chủ Cộng h&ograve;a, Ch&iacute;nh phủ C&aacute;ch mạng l&acirc;m thời Cộng h&ograve;a miền Nam Việt Nam v&agrave; Nh&agrave; nước Cộng h&ograve;a x&atilde; hội chủ nghĩa Việt Nam</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất tạm thời được cơ quan nh&agrave; nước c&oacute; thẩm quyền cấp hoặc c&oacute; t&ecirc;n trong Sổ đăng k&yacute; ruộng đất, Sổ địa ch&iacute;nh trước ng&agrave;y 15 th&aacute;ng 10 năm 1993</li>\r\n	<li>Giấy tờ hợp ph&aacute;p về thừa kế, tặng cho quyền sử dụng đất hoặc t&agrave;i sản gắn liền với đất,&nbsp;giấy tờ giao nh&agrave; t&igrave;nh nghĩa, nh&agrave; t&igrave;nh thương gắn liền với đất</li>\r\n	<li>Giấy tờ chuyển nhượng quyền sử dụng đất, mua b&aacute;n nh&agrave; ở gắn liền với đất ở trước ng&agrave;y 15 th&aacute;ng 10 năm 1993 được Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; x&aacute;c nhận l&agrave; đ&atilde; sử dụng trước ng&agrave;y 15 th&aacute;ng 10 năm 1993</li>\r\n	<li>Giấy tờ thanh l&yacute;, h&oacute;a gi&aacute; nh&agrave; ở gắn liền với đất ở; giấy tờ mua nh&agrave; ở thuộc sở hữu nh&agrave; nước theo quy định của ph&aacute;p luật</li>\r\n	<li>Giấy tờ về quyền sử dụng đất do cơ quan c&oacute; thẩm quyền thuộc chế độ cũ cấp cho người sử dụng đất</li>\r\n	<li>C&aacute;c loại giấy tờ kh&aacute;c được x&aacute;c lập trước ng&agrave;y 15 th&aacute;ng 10 năm 1993 theo quy định của Ch&iacute;nh phủ.</li>\r\n</ul>\r\n&nbsp;<br />\r\nV&agrave; d&ugrave; kh&ocirc;ng c&oacute; giấy tờ về quyền sử dụng đất nhưng nếu đất nh&agrave; bạn đang sử dụng đ&aacute;p ứng được điều kiện quy định tại Điều 101 Luật Đất đai th&igrave; gia đ&igrave;nh bạn vẫn c&oacute; thể được cấp giấy chứng nhận quyền sử dụng đất:<br />\r\n&nbsp;<br />\r\nĐiều 101. Cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n đang sử dụng đất m&agrave; kh&ocirc;ng c&oacute; giấy tờ về quyền sử dụng đất<br />\r\n&nbsp;<br />\r\nHộ gia đ&igrave;nh, c&aacute; nh&acirc;n đang sử dụng đất trước ng&agrave;y Luật n&agrave;y c&oacute; hiệu lực thi h&agrave;nh m&agrave; kh&ocirc;ng c&oacute; c&aacute;c giấy tờ quy định tại Điều 100 của Luật n&agrave;y, c&oacute; hộ khẩu thường tr&uacute; tại địa phương v&agrave; trực tiếp sản xuất n&ocirc;ng nghiệp, l&acirc;m nghiệp, nu&ocirc;i trồng thủy sản, l&agrave;m muối tại v&ugrave;ng c&oacute; điều kiện kinh tế - x&atilde; hội kh&oacute; khăn, v&ugrave;ng c&oacute; điều kiện kinh tế - x&atilde; hội đặc biệt kh&oacute; khăn, nay được Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi c&oacute; đất x&aacute;c nhận l&agrave; người đ&atilde; sử dụng đất ổn định, kh&ocirc;ng c&oacute; tranh chấp th&igrave; được cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất v&agrave; kh&ocirc;ng phải nộp tiền sử dụng đất.<br />\r\n&nbsp;<br />\r\nHộ gia đ&igrave;nh, c&aacute; nh&acirc;n đang sử dụng đất kh&ocirc;ng c&oacute; c&aacute;c giấy tờ quy định tại Điều 100 của Luật n&agrave;y nhưng đất đ&atilde; được sử dụng ổn định từ trước ng&agrave;y 01 th&aacute;ng 7 năm 2004 v&agrave; kh&ocirc;ng vi phạm ph&aacute;p luật về đất đai, nay được Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; x&aacute;c nhận l&agrave; đất kh&ocirc;ng c&oacute; tranh chấp, ph&ugrave; hợp với quy hoạch sử dụng đất, quy hoạch chi tiết x&acirc;y dựng đ&ocirc; thị, quy hoạch x&acirc;y dựng điểm d&acirc;n cư n&ocirc;ng th&ocirc;n đ&atilde; được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt đối với nơi đ&atilde; c&oacute; quy hoạch th&igrave; được cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất.<br />\r\n&nbsp;<br />\r\n<strong>Ch&iacute;nh phủ quy định chi tiết điều n&agrave;y</strong><br />\r\n&nbsp;<br />\r\nNhư vậy, trong trường hợp của gia đ&igrave;nh bạn nếu đ&atilde; ở qu&aacute; l&acirc;u v&agrave; kh&ocirc;ng thể chứng minh về t&iacute;nh hợp ph&aacute;p nhưng đất gia đ&igrave;nh bạn đang ở được sử dụng ổn định l&acirc;u d&agrave;i v&agrave; kh&ocirc;ng c&oacute; vi phạm ph&aacute;p luật đất đai, kh&ocirc;ng c&oacute; tranh chấp v&agrave; ph&ugrave; hợp với quy hoạch của địa phương th&igrave; gia đ&igrave;nh bạn vẫn c&oacute; thể l&agrave;m đơn để y&ecirc;u cầu cấp giấy chứng nhận quyền sử dụng đất.<br />\r\n&nbsp;<br />\r\nĐể được cấp giấy chứng nhận quyền sử dụng đất gia đ&igrave;nh bạn cần ra Ủy ban nh&acirc;n d&acirc;n x&atilde;, phường, thị trấn nơi c&oacute; đất để nộp hồ sơ xin Giấy Chứng nhận:<br />\r\n&nbsp;<br />\r\n<span style=\"font-size:16px\"><strong>► Hồ sơ xin cấp sổ đỏ bao gồm</strong></span><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đơn đề nghị cấp Giấy chứng nhận (theo mẫu)</li>\r\n	<li>Bản photocopy sổ hộ khẩu, giấy chứng minh nh&acirc;n d&acirc;n</li>\r\n	<li>Bản sao chứng thực giấy tờ về quyền sử dụng đất (trong trường hợp gia đ&igrave;nh bạn c&oacute; thể nộp theo 2 tờ phiếu thu để l&agrave;m căn cứ chứng minh nguồn gốc đất)</li>\r\n	<li>Bản sao chứng thực giấy tờ về t&agrave;i sản gắn liền với đất theo quy định tại bản Quy định n&agrave;y (nếu&nbsp;c&oacute; t&agrave;i sản v&agrave; c&oacute; y&ecirc;u cầu chứng nhận quyền sở hữu).</li>\r\n	<li>Sơ đồ nh&agrave; ở, c&ocirc;ng tr&igrave;nh x&acirc;y dựng (trừ trường hợp trong giấy tờ quy định tại điểm d khoản n&agrave;y đ&atilde; c&oacute; sơ đồ nh&agrave; ở, c&ocirc;ng tr&igrave;nh x&acirc;y dựng)</li>\r\n	<li>Văn bản ủy quyền nộp hồ sơ hoặc nhận Giấy chứng nhận (nếu c&oacute;)</li>\r\n	<li>Bản sao c&aacute;c giấy tờ li&ecirc;n quan đến việc thực hiện nghĩa vụ t&agrave;i ch&iacute;nh về đất đai theo quy định của ph&aacute;p luật (nếu c&oacute;)</li>\r\n</ul>\r\n<br />\r\nTr&ecirc;n đ&acirc;y l&agrave; to&agrave;n bộ chia sẻ về việc xin cấp sỏ đỏ lần đầu v&agrave; những điều kiện để được cấp sỏ đỏ cho qu&yacute; vị tham khảo. Nếu mảnh đất của qu&yacute; vị ph&ugrave; hợp với những điều kiện tr&ecirc;n th&igrave; c&oacute; thể xin cấp sổ đỏ được rồi.', 0, NULL, NULL, 1, 0, 6, '2017-07-09 16:05:04', '2017-07-14 09:30:34', 2, 2),
(6, 'Cách chuyển đất trồng lâu năm thành đất thổ cư như thế nào', 'cach-chuyen-dat-trong-lau-nam-thanh-dat-tho-cu-nhu-the-nao', 'Cach chuyen dat trong lau nam thanh dat tho cu nhu the nao', 'Một vị độc giả đã gửi câu hỏi cho chúng tôi để hỏi về vấn đề cách chuyển đất trồng lâu năm thành đất thổ cư như thế nào. Thửa đất đấy được cấp giấy chứng nhận quyền sử dụng đất với mục đích trồng cây lâu năm và có thời hạn đến năm 2060. Để hiểu rõ hơn về vấn đề này thì mời quý vị tham khảo dưới đây.', '2017/07/09/cach-chuyen-dat-trong-lau-nam-thanh-dat-tho-cu-nhu-the-nao-1499592291.png', 5, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><img alt=\"Cách chuyển đất trồng lâu năm thành đất thổ cư như thế nào\" src=\"/uploads/images/cach-chuyen-dat-trong-lau-nam-thanh-dat-tho-cu-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n&diams; Thời hạn sử dụng đất đến năm 2058 l&agrave; thời hạn nh&agrave; nước giao đất cho người sử dụng từ thời điểm giao đến năm 2058 hết thời hạn tr&ecirc;n sẽ c&oacute; c&aacute;c khả năng sau c&oacute; thể xảy ra: Nh&agrave; nước thu hồi lại diện t&iacute;ch đất n&agrave;y để sử dụng hoặc giao cho người sử dụng kh&aacute;c sử dụng; Nh&agrave; nước tiếp tục giao đất cho người đang sử dụng v&agrave; cấp giấy chứng nhận quyền sử dụng đất mới hoặc gia hạn thời hạn sử dụng đất ghi tr&ecirc;n giấy chứng nhận quyền sử dụng đất<br />\r\n<br />\r\n&diams; Về việc chuyển mục đ&iacute;ch sử dụng đất luật đất đai v&agrave; c&aacute;c văn bản hướng dẫn thực hiện kh&ocirc;ng cấm tuy nhi&ecirc;n việc chuyển đổi mục đ&iacute;ch sử dụng đất c&oacute; được hay kh&ocirc;ng th&igrave; phải phụ thuộc v&agrave;o c&aacute;c ch&iacute;nh s&aacute;ch, kế hoạch, quy hoạch của địa phương nơi c&oacute; thửa đất đ&oacute; quy định tại Điều 30, Nghị định181/2004/ND-CP.&nbsp;<br />\r\nDiện t&iacute;ch đ&acirc;t được ph&eacute;p chuyển đổi phải căn cứ v&agrave;o quy định về hạn mức giao đất của địa phương nơi c&oacute; đất<br />\r\n<br />\r\n<strong>C&aacute;c khoản t&agrave;i ch&iacute;nh li&ecirc;n quan đến việc chuyển đổi mục đ&iacute;ch sử dụng đất như sau:</strong>\r\n\r\n<ul>\r\n	<li>100% phần ch&ecirc;nh lệch giữa gi&aacute; trị sử dụng đất từ đất trồng c&acirc;y l&acirc;u năm sang đất thổ cư ( gi&aacute; c&aacute;c loại đất được UBND tỉnh th&agrave;nh phố ban h&agrave;nh h&agrave;ng năm )</li>\r\n	<li>Thuế chuyển quyền sử dụng đất 2%</li>\r\n	<li>Lệ ph&iacute; trước bạ 0.5 gi&aacute; trị quyền sử dụng đất</li>\r\n	<li>Lệ ph&iacute; việc cấp giấy chứng nhận quyền sử dụng đất</li>\r\n</ul>\r\n<br />\r\n&diams; Người sử dụng đất khi muốn chuyển đổi mục đ&iacute;ch sử dụng đất sang đất thổ cư thường l&agrave; c&oacute; &yacute; định x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh ki&ecirc;n cố tr&ecirc;n đất trường hợp chỉ c&oacute; &yacute; định l&agrave;m trang trại hoặc sinh th&aacute;i nh&agrave; vườn th&igrave; kh&ocirc;ng nhất định phải chuyển mục đ&iacute;ch sử dụng đất sang đất thổ cư. Người sử dụng đất c&oacute; thể theo nhu cầu thực tế của m&igrave;nh đồng thời ph&ugrave; hợp với quy định ph&aacute;p luật về quản l&yacute; sử dụng đ&acirc;t đai<br />\r\n<br />\r\n&diams; Cơ quan giải quyết thủ tục n&agrave;y đối với trường hợp người sử dụng đất l&agrave; hộ gia đ&igrave;nh, c&aacute; nh&acirc;n th&igrave; thuộc thẩm quyền của UBND cấp qu&acirc;n huyện (Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất - Ph&ograve;ng t&agrave;i nguy&ecirc;n m&ocirc;i trường), với tổ chức v&agrave; nguời nước ngo&agrave;i th&igrave; thẩm quyền thuộc UBND cấp tỉnh, th&agrave;nh phố trực thuộc trung ương (Sở t&agrave;i nguy&ecirc;n m&ocirc;i trường). Thời hạn để thực hiện c&aacute;c thủ tục n&agrave;y l&agrave; 30 ng&agrave;y l&agrave;m việc kể từ khi cơ quan nh&agrave; nước nhận được hồ sơ hợp lệ (thời gian n&agrave;y kh&ocirc;ng bao gồm thời gian nộp thuế v&agrave; thực hiện c&aacute;c nghĩa vụ t&agrave;i ch&iacute;nh kh&aacute;c)<br />\r\n<br />\r\n&diams; Mục đ&iacute;ch sử dụng đất trồng c&acirc;y l&acirc;u năm, đất vườn v&agrave; đất trang trại....đều l&agrave; c&aacute;c loại đất kh&ocirc;ng được x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh ki&ecirc;n cố v&agrave; chỉ được sử dụng để sản xuất n&ocirc;ng, l&acirc;m nghiệp. Ch&uacute;ng chỉ kh&aacute;c nhau về gi&aacute; trị quyền sử dụng đất được quy định tại quyết định của địa phương nơi c&oacute; đất', 0, NULL, NULL, 1, 0, 7, '2017-07-09 16:24:56', '2017-07-14 09:16:20', 2, 2),
(7, 'Thừa kế thế vị là gì? Quy định thừa kế như thế nào', 'thua-ke-the-vi-la-gi-quy-dinh-thua-ke-nhu-the-nao', 'Thua ke the vi la gi Quy dinh thua ke nhu the nao', 'Nếu quý vị đang thắc mắc không biết thừa kế thế vị là gì? Quy định thừa kế như thế nào thì hãy tham khảo ngay bài viết dưới đây, quý vị sẽ nắm rõ hơn về hồ sơ, quy định thừa kế và những trường hợp được thừa kế.', '2017/07/09/thua-ke-the-vi-la-gi-quy-dinh-thua-ke-nhu-the-nao-1499592834.png', 5, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><img alt=\"Thừa kế thế vị là gì? Quy định thừa kế như thế nào\" src=\"/uploads/images/thua-ke-the-vi-la-gi-quy-dinh-thua-ke-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Thừa kế vị l&agrave; g&igrave;</strong></span><br />\r\n<br />\r\nThừa kế được hiểu l&agrave; việc dịch chuyển t&agrave;i sản của người đ&atilde; chết sang cho người c&ograve;n sống. Thực tế cho thấy c&oacute; những trường hợp người được hưởng thừa kế lại chết trước hoặc chết c&ugrave;ng l&uacute;c với người để lại di sản.<br />\r\n<br />\r\nKhi đ&oacute; ph&aacute;p luật cho ph&eacute;p con của người thừa kế được hưởng phần di sản m&agrave; lẽ ra bố mẹ ch&uacute;ng sẽ được hưởng theo ph&aacute;p luật nếu c&ograve;n sống. Chế định n&agrave;y gọi l&agrave; thừa kế thế vị.<br />\r\n<br />\r\n<strong>Thừa kế thế vị l&agrave; một trường hợp thừa kế đặc biệt chỉ xảy ra khi đủ những điều kiện quy định tại điều 677</strong><br />\r\n<br />\r\nTrong trường hợp con của người để lại di sản chết trước hoặc c&ugrave;ng một thời điểm với người để lại di sản th&igrave; ch&aacute;u được hưởng phần di sản m&agrave; cha hoặc mẹ của ch&aacute;u được hưởng nếu c&ograve;n sống; nếu ch&aacute;u cũng chết trước hoặc c&ugrave;ng một thời điểm với người để lại di sản th&igrave; chắt được hưởng phần di sản m&agrave; cha hoặc mẹ của chắt được hưởng nếu c&ograve;n sống.<br />\r\n<br />\r\nNhư vậy, thừa kế thế vị l&agrave; việc c&aacute;c con (ch&aacute;u, chắt) được thay v&agrave;o vị tr&iacute; của bố hoặc mẹ (&ocirc;ng hoặc b&agrave;) để hưởng di sản của &ocirc;ng, b&agrave; (hoặc cụ) trong trường hợp bố hoặc mẹ (&ocirc;ng hoặc b&agrave;) chết trước hoặc chết c&ugrave;ng &ocirc;ng, b&agrave; (hoặc cụ).<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► C&aacute;c trường hợp thừa kế thế vị</strong></span><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Ch&aacute;u thế vị cha hoặc mẹ để hưởng phần di sản của &ocirc;ng b&agrave;.</li>\r\n	<li>Chắt thế vị cha hoặc mẹ để hưởng phần di sản của cụ.</li>\r\n</ul>\r\n<br />\r\nThừa kế thế vị chỉ ph&aacute;t sinh từ thừa kế theo ph&aacute;p luật m&agrave; kh&ocirc;ng ph&aacute;t sinh từ thừa kế theo di ch&uacute;c. Nếu cha, mẹ chết trước hoặc chết c&ugrave;ng thời điểm với &ocirc;ng, b&agrave; hoặc cụ th&igrave; phần di ch&uacute;c định đoạt t&agrave;i sản cho cha mẹ (nếu c&oacute; di ch&uacute;c) sẽ v&ocirc; hiệu. Phần di sản đ&oacute; được chia theo ph&aacute;p luật v&agrave; l&uacute;c n&agrave;y ch&aacute;u (chắt) mới được hưởng thừa kế thế vị.<br />\r\n<br />\r\nQuan hệ thừa kế thế vị kh&ocirc;ng phải l&agrave; thừa kế theo tr&igrave;nh tự h&agrave;ng nhưng h&agrave;ng thừa kế l&agrave; căn cứ để x&aacute;c định quan hệ thừa kế thế vị. Thừa kế thế vị l&agrave; một chế định của ph&aacute;p luật nhằm bảo về quyền lợi cho những người th&acirc;n thuộc nhất của người để lại di sản, tr&aacute;nh trường hợp di sản của &ocirc;ng m&agrave; m&agrave; ch&aacute;u kh&ocirc;ng được hưởng lại để cho người kh&aacute;c hưởng.<br />\r\n<br />\r\nCũng như những người thừa kế kh&aacute;c, người thừa kế thế vị kh&ocirc;ng được hưởng di sản nếu họ từ chối nhận di sản hoặc bị truất quyền hưởng di sản theo quy định tại điều 642 v&agrave; điều 643 Bộ luật D&acirc;n sự 2005.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Điều kiện hưởng thừa kế thế vị</strong></span><br />\r\n<br />\r\nThừa kế thế vị l&agrave; trường hợp thừa kế đặc biệt. Do vậy, những người thừa kế thế vị cũng rất đặc biệt. Khi x&eacute;t về h&agrave;ng thừa kế th&igrave; họ kh&ocirc;ng được hưởng di sản nhưng họ được nhận thay cho bố (hoặc mẹ) của họ (l&agrave; những người đ&aacute;ng ra được hưởng thừa kế nếu c&ograve;n sống). Theo quy định th&igrave; thừa kế thế vị chỉ đặt ra khi thỏa m&atilde;n những điều kiện sau:<br />\r\n<br />\r\n&diams; Những người thừa kế phải c&oacute; quan hệ thừa kế thứ hai trong h&agrave;ng thừa kế thứ nhất (quan hệ thừa kế giữa cha mẹ, con), trong đ&oacute; người thế vị phải l&agrave; người ở đời sau. Nghĩa l&agrave; chỉ c&oacute; con được thế vị cha mẹ m&agrave; kh&ocirc;ng bao giờ c&oacute; trường hợp cha, mẹ thế vị cho con. Người được hưởng thừa kế thế vị c&oacute; thể l&agrave; con đẻ hoặc con nu&ocirc;i. Điều 678 BLDS 2005 quy định: &ldquo;Con nu&ocirc;i v&agrave; cha nu&ocirc;i, mẹ nu&ocirc;i được thừa kế di sản của nhau v&agrave; c&ograve;n được thừa kế di sản theo quy định tại Điều 676 v&agrave; Điều 677 của bộ luật n&agrave;y.<br />\r\n<br />\r\n&diams; Thừa kế thế vị chỉ được đặt ra khi người thừa kế chết trước hoặc chết c&ugrave;ng thời điểm với người để lại di sản: X&eacute;t theo nguy&ecirc;n tắc th&igrave; kh&ocirc;ng thể c&oacute; trường hợp hai người chết c&ugrave;ng một thời điểm. Nhưng tr&ecirc;n thực tế xảy ra c&oacute; những trường hợp nhiều người chết trong một tai nạn m&agrave; kh&ocirc;ng thể x&aacute;c định được ai chết trước, ai chết sau. V&igrave; vậy, buộc phải suy đo&aacute;n họ chết c&ugrave;ng một thời điểm. Nếu hai người thừa kế t&agrave;i sản của nhau m&agrave; được coi l&agrave; chết c&ugrave;ng một thời điểm, th&igrave; họ sẽ kh&ocirc;ng được thừa kế của nhau, di sản của mỗi người được chia cho những người thừa kế của họ. Ph&aacute;p luật quy định như vậy để việc chia di sản thừa kế được tiến h&agrave;nh b&igrave;nh thường, kh&ocirc;ng ảnh hưởng đến quyền lợi của những người thừa kế kh&aacute;c<br />\r\n<br />\r\n&diams; Ch&aacute;u, chắt của người để lại di sản phải c&ograve;n sống hoặc chưa ra đời nhưng đ&atilde; th&agrave;nh thai v&agrave;o thời điểm &ocirc;ng, b&agrave;, cụ chết mới được &aacute;p dụng chế định thừa kế thế vị: Thừa kế l&agrave; việc dịch chuyển t&agrave;i sản của những người đ&atilde; chết sang cho những người c&ograve;n sống. Do vậy, điều kiện để những người được hưởng thừa kế thế vị cũng l&agrave; họ phải c&ograve;n sống hoặc đ&atilde; th&agrave;nh thai v&agrave;o thời điểm mở thừa kế. Nếu thai nhi sinh ra v&agrave; c&ograve;n sống th&igrave; được hưởng phần di sản đ&oacute;, nếu chết trước khi sinh hoặc ngay sau khi sinh th&igrave; phần di sản được chia cho những người thừa kế kh&aacute;c.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Hồ sơ khai nhận hưởng thừa kế thế vị</strong></span><br />\r\n<br />\r\n&rArr; Hồ sơ khai nhận di sản của người thừa kế\r\n<ul>\r\n	<li>Chứng minh thư nh&acirc;n d&acirc;n hoặc hộ chiếu của từng người</li>\r\n	<li>Hộ khẩu</li>\r\n	<li>Giấy đăng k&yacute; kết h&ocirc;n, giấy x&aacute;c nhận h&ocirc;n nh&acirc;n thực tế hoặc x&aacute;c nhận độc th&acirc;n</li>\r\n	<li>Hợp đồng ủy quyền, giấy ủy quyền (nếu x&aacute;c lập giao dịch th&ocirc;ng qua người đại diện);</li>\r\n	<li>Giấy khai sinh, Giấy x&aacute;c nhận con nu&ocirc;i; bản &aacute;n, sơ yếu l&yacute; lịch, c&aacute;c giấy tờ kh&aacute;c chứng minh quan hệ giữa người để lại di sản v&agrave; người hưởng di sản thừa kế.</li>\r\n	<li>Giấy chứng tử của bố hoặc mẹ</li>\r\n</ul>\r\n<br />\r\n&rArr;&nbsp;Hồ sơ ph&aacute;p l&yacute; của người đề lại di sản thừa kế\r\n<ul>\r\n	<li>Giấy chứng tử của người để lại di sản (hoặc) giấy b&aacute;o tử (hoặc) bản &aacute;n tuy&ecirc;n bố đ&atilde; chết</li>\r\n	<li>Giấy đăng k&yacute; kết h&ocirc;n, giấy x&aacute;c nhận h&ocirc;n nh&acirc;n thực tế hoặc x&aacute;c nhận độc th&acirc;n</li>\r\n	<li>Di ch&uacute;c&nbsp;</li>\r\n</ul>\r\n<strong>Giấy tờ chứng minh quyền sử dụng, quyền sở hữu t&agrave;i sản l&agrave; di sản thừa kế</strong>\r\n\r\n<ul>\r\n	<li>Giấy chứng nhận quyền sử dụng đất, Giấy chứng nhận quyền sở hữu nh&agrave; ở v&agrave; quyền sử dụng đất, do UBND cấp, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh tr&ecirc;n đất, c&aacute;c loại giấy tờ kh&aacute;c về nh&agrave; ở; Giấy ph&eacute;p mua b&aacute;n, chuyển dịch nh&agrave; cửa - hợp thức h&oacute;a do UBND quận/huyện cấp c&oacute; x&aacute;c nhận của Ph&ograve;ng thuế trước bạ v&agrave; thổ trạch; Văn tự b&aacute;n nh&agrave; được UBND chứng nhận (nếu c&oacute;); Bi&ecirc;n lai thu thuế nh&agrave; đất (nếu c&oacute;)</li>\r\n	<li>Giấy ph&eacute;p x&acirc;y dựng (nếu c&oacute;)</li>\r\n	<li>Bi&ecirc;n bản kiểm tra c&ocirc;ng trr&igrave;nh ho&agrave;n th&agrave;nh (nếu c&oacute;)</li>\r\n	<li>Bản vẽ do cơ quan, đơn vị c&oacute; thẩm quyền thực hiện, đ&atilde; được UBND hoặc Ph&ograve;ng T&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường x&aacute;c nhận kiểm tra nội nghiệp, quy hoạch (nếu c&oacute;)</li>\r\n	<li>Giấy tờ về t&agrave;i sản kh&aacute;c (Sổ tiết kiệm, chứng chỉ tiền gửi, giấy x&aacute;c nhận của ng&acirc;n h&agrave;ng mở t&agrave;i khoản, giấy đăng k&yacute; xe &ocirc; t&ocirc;, cổ phiếu&hellip;)</li>\r\n</ul>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Quy định thừa kế vị theo luật ph&aacute;p như thế n&agrave;o</strong></span><br />\r\n<br />\r\nTrong trường hợp con của người để lại di sản chết trước hoặc c&ugrave;ng một thời điểm với người để lại di sản th&igrave; ch&aacute;u được hưởng phần di sản m&agrave; cha hoặc mẹ của ch&aacute;u được hưởng nếu c&ograve;n sống; nếu ch&aacute;u cũng chết trước hoặc c&ugrave;ng một thời điểm với người để lại di sản th&igrave; chắt được hưởng phần di sản m&agrave; cha hoặc mẹ của chắt được hưởng nếu c&ograve;n sống.<br />\r\n<br />\r\n+ Ch&aacute;u đươc thừa kế thế vị hưởng di sản của &ocirc;ng, b&agrave;. Đ&acirc;y l&agrave; trường hợp khi bố chết trước &ocirc;ng nội, b&agrave; nội th&igrave; con thay vị tr&iacute; của người bố để hưởng di sản của &ocirc;ng nội, b&agrave; nội khi &ocirc;ng nội, b&agrave; nội chết. Khi mẹ chết trước &ocirc;ng ngoại, b&agrave; ngoại th&igrave; con thay vị tr&iacute; của ngư&ograve;i mẹ để hưởng di sản của &ocirc;ng ngoại, b&agrave; ngoại khi &ocirc;ng ngoại, b&agrave; ngoại chết.<br />\r\n<br />\r\n+ Trong trường hợp con nu&ocirc;i chết trước cha nu&ocirc;i, mẹ nu&ocirc;i, th&igrave; con đẻ của người con nu&ocirc;i (tức l&agrave; ch&aacute;u của cha nu&ocirc;i, mẹ nu&ocirc;i của người chết trước) được hưởng phần di sản m&agrave; đ&aacute;ng lẽ cha mẹ của ch&aacute;u c&ograve;n sống v&agrave;o thời điểm mở thừa kế được hưởng.<br />\r\n<br />\r\n+ Chắt được thừa kế thế vi hưởng di sản của cụ. Đ&acirc;y l&agrave; trường hợp nếu &ocirc;ng, b&agrave; chết trước cụ, cha hoặc mẹ chết sau &ocirc;ng, b&agrave; nhưng vẫn chết trước cụ (người để lại di sản) th&igrave; con của ngư&ograve;i cha hoặc người mẹ đ&atilde; chết đ&oacute; (tức l&agrave; chắt của người để lại di sản) được hưởng phần di sản m&agrave; đ&aacute;ng lẽ cha hoặc mẹ của chắt (tức l&agrave; ch&aacute;u của người để lại di sản) được hưởng nếu c&ograve;n sống v&agrave;o thời điểm người để lại di sản chết.<br />\r\n<br />\r\nTrong thừa kế thế vị, mối quan hệ giữa người được thừa kế thế vị với người để lại di sản l&agrave; ch&aacute;u đối với &ocirc;ng b&agrave; nội, &ocirc;ng b&agrave; ngoại, chắt đối với c&aacute;c cụ. Phần di sản m&agrave; người ch&aacute;u, chắt thay thế bố, mẹ ch&uacute;ng được hưởng tương ứng với phần di sản m&agrave; người bố, người mẹ của người thừa kế thế vị được hưởng từ di sản của &ocirc;ng, b&agrave; hoặc cụ.<br />\r\n&nbsp;', 0, NULL, NULL, 1, 0, 8, '2017-07-09 16:55:30', '2017-07-14 09:09:08', 2, 2),
(10, 'Những điều cấm kỵ trong phong thủy phòng ngủ', 'nhung-dieu-cam-ky-trong-phong-thuy-phong-ngu', 'Nhung dieu cam ky trong phong thuy phong ngu', 'Nếu quý vị là người quan tâm đến phong thủy nhà ở, thì phong thủy phòng ngủ là cực ky quan trọng và không thể bỏ qua. Sau đây là bài viết chia sẻ về những điều cấm kỵ trong phong thủy phòng ngủ, hy vọng quý vị quan tâm đến những điều bài viết chia sẻ.', '2017/07/10/nhung-dieu-cam-ky-trong-phong-thuy-phong-ngu-1499667827.png', 4, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><img alt=\"những điều cấm kỵ trong phong thủy phòng ngủ\" src=\"/uploads/images/nhung-dieu-cam-ky-trong-phong-thuy-phong-ngu.png\" style=\"height:400px; width:650px\" /></div>\r\n&nbsp;\r\n\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n<strong>Cửa ph&ograve;ng ngủ đối diện cửa ph&ograve;ng tắm/nh&agrave; vệ sinh</strong><br />\r\n<br />\r\nPh&ograve;ng ngủ kiểu n&agrave;y chủ yếu g&acirc;y ra c&aacute;c bệnh về xương khớp, cơ bắp v&agrave; cũng khiến tiền bạc bị thất tho&aacute;t. Ph&ograve;ng tắm/nh&agrave; vệ sinh l&agrave; nơi ẩm ướt, thường lưu lại m&ugrave;i h&ocirc;i kh&oacute; chịu. V&igrave; thế, bạn sẽ hấp thụ phải nguồn năng lượng ti&ecirc;u cực.<br />\r\n<br />\r\n<strong>Cửa ph&ograve;ng tắm/nh&agrave; vệ sinh đối diện giường ngủ</strong><br />\r\n<br />\r\nTrường hợp n&agrave;y c&ograve;n xấu hơn so với trường hợp 1 l&agrave; cửa ph&ograve;ng ngủ đối diện cửa ph&ograve;ng tắm/nh&agrave; vệ sinh. N&oacute; sẽ g&acirc;y ra chứng đau đầu nghi&ecirc;m trọng, l&agrave;m giảm sự tập trung, giảm khả năng suy nghĩ cho chủ nh&acirc;n căn ph&ograve;ng.<br />\r\n<br />\r\nNếu vấn đề rắc rối n&agrave;y kh&ocirc;ng được giải quyết trong một thời gian d&agrave;i, n&oacute; c&oacute; thể dẫn đến bệnh ung thư v&agrave; nhiều loại bệnh tật kh&aacute;c. Do đ&oacute;, bạn cần thay đổi c&aacute;ch bố tr&iacute; nội thất trong ph&ograve;ng ngủ, đặc biệt l&agrave; vị tr&iacute; k&ecirc; giường ngủ c&agrave;ng nhanh c&agrave;ng tốt.<br />\r\nGương soi đối diện giường ngủ<br />\r\nNằm ngủ đối diện với gương sẽ l&agrave;m bạn dễ rơi v&agrave;o trạng th&aacute;i m&ecirc; man, ngủ kh&ocirc;ng s&acirc;u, kh&oacute; tập trung suy nghĩ về mọi chuyện.<br />\r\n<br />\r\n<strong>Thanh x&agrave; nh&agrave; ph&iacute;a tr&ecirc;n giường ngủ</strong><br />\r\n<br />\r\nNằm ngủ ở vị tr&iacute; b&ecirc;n dưới c&aacute;c thanh x&agrave; nh&agrave; lu&ocirc;n l&agrave;m bạn cảm thấy bị &aacute;p lực đ&egrave; nặng. N&oacute; c&oacute; thể g&acirc;y ra chứng mất ngủ, thiếu ngủ v&agrave; đau đầu triển mi&ecirc;n.<br />\r\nGiường ngủ đối diện với g&oacute;c nhọn<br />\r\nT&igrave;nh huống n&agrave;y c&oacute; thể g&acirc;y ch&oacute;ng mặt, đau đầu v&agrave; c&aacute;c triệu chứng bệnh kh&ocirc;ng thể ph&aacute;t hiện.<br />\r\n<br />\r\nGiải ph&aacute;p: Trong trường hợp 1 g&oacute;c tường nh&ocirc; ra, hoặc 1 g&oacute;c nhọn của đồ nội thất kh&ocirc;ng thể di chuyển được &quot;chĩa&quot; thẳng v&agrave;o giường ngủ, bạn cố gắng l&agrave;m mềm c&aacute;c g&oacute;c nhọn v&agrave; năng lượng hung h&atilde;n bằng c&aacute;ch phủ vải b&ecirc;n ngo&agrave;i.<br />\r\n<br />\r\n<strong>Giường ngủ nằm dưới cầu thang</strong><br />\r\n<br />\r\nVị tr&iacute; giường ngủ n&agrave;y cực kỳ xấu, n&oacute; sẽ khiến người nằm tr&ecirc;n giường dễ mơ &aacute;c mộng v&agrave; kh&ocirc;ng may mắn.<br />\r\n<br />\r\nGiải ph&aacute;p: C&aacute;ch tốt nhất v&agrave; dễ d&agrave;ng nhất l&agrave; chuyển giường ngủ đến một vị tr&iacute; mới tốt hơn, thay v&igrave; di chuyển cầu thang.<br />\r\nPhần đầu đối diện bức tường ph&iacute;a sau bồn cầu<br />\r\n<br />\r\nTrường hợp n&agrave;y sẽ l&agrave;m cho bạn phải chịu đựng bệnh đau đầu triền mi&ecirc;n, rối loạn &yacute; thức hệ v&agrave; t&acirc;m tr&iacute; th&igrave; rất kh&oacute; tập trung.<br />\r\n<br />\r\nGiải ph&aacute;p: Di chuyển giường ngủ đến vị tr&iacute; kh&aacute;c tốt hơn trong ph&ograve;ng. Tuy nhi&ecirc;n, bạn phải lu&ocirc;n lưu t&acirc;m đến c&aacute;c lưu &yacute; phong thủy với từng vị tr&iacute; cụ thể.<br />\r\n<br />\r\n<strong>Phần đầu đối diện bức tường ph&iacute;a sau b&agrave;n thờ</strong><br />\r\n<br />\r\nGiải ph&aacute;p: Di chuyển một trong hai vị tr&iacute; của giường ngủ hoặc b&agrave;n thờ để tho&aacute;t khỏi những ảnh hưởng xấu đến sức khỏe.<br />\r\nĐầu giường kh&ocirc;ng c&oacute; điểm tựa vững chắc<br />\r\n<br />\r\nMột số người th&iacute;ch xoay ch&eacute;o giường ngủ để tạo n&ecirc;n bầu kh&ocirc;ng kh&iacute; l&atilde;ng mạn hoặc để tr&aacute;nh c&aacute;c thanh x&agrave; nh&agrave; tr&ecirc;n cao, nhưng lu&ocirc;n lu&ocirc;n nhớ rằng, đầu giường cần phải được tựa v&agrave;o một bức tường bằng phẳng, vững chắc. Nếu kh&ocirc;ng, n&oacute; sẽ ảnh hưởng đến chất lượng của giấc ngủ.<br />\r\n<br />\r\n<strong>Đặt TV, hệ thống &acirc;m thanh, điện thoại ph&iacute;a trước giường ngủ</strong><br />\r\n<br />\r\nPh&ograve;ng ngủ l&agrave; nơi để nghỉ ngơi v&agrave; hồi phục năng lượng cho ng&agrave;y mới. Nếu đặt TV trong ph&ograve;ng ngủ, bạn c&oacute; xu hướng xem c&aacute;c chương tr&igrave;nh từ 1 - 2 tiếng cho đến khi cảm thấy buồn ngủ. Việc n&agrave;y sẽ r&uacute;t ngắn số giờ ngủ xuống v&agrave; ảnh hưởng đến năng lượng d&agrave;nh cho ng&agrave;y h&ocirc;m sau của bạn. Thời gian xem TV c&agrave;ng k&eacute;o d&agrave;i c&agrave;ng ảnh hưởng đến sức khỏe v&agrave; sự nghiệp.<br />\r\n<br />\r\nTrong khi đ&oacute;, điện thoại di động vẫn ph&aacute;t ra bức xạ ngay cả khi để ở chế độ chờ (stand by). V&igrave; thế, bạn kh&ocirc;ng n&ecirc;n đặt điện thoại di động b&ecirc;n cạnh người khi ngủ.<br />\r\n<br />\r\n<strong>Lắp điều h&ograve;a nhiệt độ ngay tr&ecirc;n đầu giường</strong><br />\r\n<br />\r\nNếu điều h&ograve;a nhiệt độ được lắp ngay tr&ecirc;n đầu giường, kh&ocirc;ng kh&iacute; lạnh sẽ thổi trực tiếp l&ecirc;n cơ thể bạn. Đặc biệt trong l&uacute;c ngủ, lỗ ch&acirc;n l&ocirc;ng c&oacute; xu hướng hơi mở ra n&ecirc;n rất dễ bị c&aacute;c bệnh như cảm lạnh, cứng cơ v&agrave; đau đầu.<br />\r\n<br />\r\n<strong>Giường ngủ nằm ph&iacute;a tr&ecirc;n/b&ecirc;n dưới bếp nấu hoặc ph&ograve;ng tắm/nh&agrave; vệ sinh</strong><br />\r\n<br />\r\nNếu giường ngủ của bạn đang nằm ph&iacute;a tr&ecirc;n hoặc b&ecirc;n dưới bếp nấu hoặc ph&ograve;ng tắm/nh&agrave; vệ sinh th&igrave; chức năng của gan sẽ bị ảnh hưởng. Bởi v&igrave; vị tr&iacute; n&agrave;y l&agrave;m tăng yếu tố lửa.<br />\r\n<br />\r\nNgo&agrave;i ra, ph&ograve;ng tắm/nh&agrave; vệ sinh l&agrave; nơi t&iacute;ch tụ v&ocirc; số vi khuẩn, bụi bẩn... n&ecirc;n sẽ g&acirc;y ra rất nhiều vấn đề sức khỏe.<br />\r\n<br />\r\n<strong>Sử dụng vật trang tr&iacute; cầu kỳ tr&ecirc;n trần nh&agrave;</strong><br />\r\n<br />\r\nGiữ cho trần nh&agrave; đơn giản nhất c&oacute; thể lu&ocirc;n l&agrave; lựa chọn ho&agrave;n hảo, thậm ch&iacute; l&agrave; kh&ocirc;ng c&oacute; g&igrave; tốt hơn. Nhiều năm trước, mọi người th&iacute;ch trang tr&iacute; trần nh&agrave; với gương hoặc một v&agrave;i chi tiết nghệ thuật. Thế nhưng, n&oacute; sẽ g&acirc;y kh&oacute; ngủ v&agrave; về l&acirc;u d&agrave;i c&oacute; thể sinh ra nhiều vấn đề sức khỏe.<br />\r\n<br />\r\n<strong>Giường ngủ đối diện cửa ph&ograve;ng</strong><br />\r\n<br />\r\nTrường hợp n&agrave;y sẽ ảnh hưởng xấu đến sức khỏe của nhiều bộ phận tr&ecirc;n cơ thể. T&ugrave;y thuộc c&aacute;nh cửa đối diện với từng phần cơ thể khi nằm ngủ tr&ecirc;n giường sẽ c&oacute; hậu quả kh&aacute;c nhau, cụ thể:<br />\r\n-Nếu cửa ph&ograve;ng đối diện với phần đầu sẽ g&acirc;y ra bệnh đau đầu, đau nửa đầu.<br />\r\n-Cửa ph&ograve;ng đối diện với phần th&acirc;n sẽ g&acirc;y ra bệnh đau dạ d&agrave;y v&agrave; c&aacute;c bệnh li&ecirc;n quan đến khoang bụng.<br />\r\n-Nếu cửa ph&ograve;ng đối diện với phần ch&acirc;n sẽ g&acirc;y ra c&aacute;c bệnh li&ecirc;n quan đến ch&acirc;n.<br />\r\n<br />\r\nCảm ơn qu&yacute; vị đ&atilde; đọc b&agrave;i viết về những điều cấm kỵ trong phong thủy ph&ograve;ng ngủ, để biết th&ecirc;m chi tiết về phong thủy nh&agrave; ở xin mời qu&yacute; vị xem th&ecirc;m tr&ecirc;n Website của ch&uacute;ng t&ocirc;i.<br />\r\n&nbsp;', 0, NULL, NULL, 1, 0, 16, '2017-07-10 13:26:54', '2017-07-14 09:02:15', 3, 2),
(11, 'Những điều kiêng kỵ trong phong thủy nhà ở', 'nhung-dieu-kieng-ky-trong-phong-thuy-nha-o', 'Nhung dieu kieng ky trong phong thuy nha o', 'Phong Thủy nhà ở mọi người có thể tin hoặc không tin, nhưng có rất nhiều trường hợp gia chủ do không tin phong thủy nhà ở đã gặp liên tiếp những điềm xấu và đến khi nhận ra thì mọi chyện cũng đã rồi chỉ còn hối tiếc. Xin mời quý vị cùng tham khảo bài viết những điều kiêng kỵ trong phong thủy nhà ở để rút ra cho mình những bài học.', '2017/07/10/nhung-dieu-kieng-ky-trong-phong-thuy-nha-o-1499669179.png', 4, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"những điều kiêng kỵ trong phong thủy nhà ở\" src=\"/uploads/images/nhung-dieu-kieng-ky-trong-phong-thuy-nha-o%281%29.png\" style=\"height:400px; width:650px\" /></div>\r\n\r\n<div><br />\r\nDựa tr&ecirc;n những nguy&ecirc;n tắc của khoa học phong thủy, sau đ&acirc;y l&agrave; những lỗi cấm kỵ trong phong thủy nh&agrave; ở m&agrave; ch&uacute;ng ta cần tr&aacute;nh<br />\r\n<br />\r\n<strong>Gương đối diện với cửa ch&iacute;nh</strong><br />\r\n<br />\r\nPh&ograve;ng kh&aacute;ch l&agrave; căn ph&ograve;ng quan trọng nhất trong nh&agrave;. V&igrave; vậy, cần phải đặc biệt ch&uacute; &yacute; tới việc b&agrave;i tr&iacute; trong ph&ograve;ng n&agrave;y. Một trong những sai lầm m&agrave; mọi người hay mắc phải ch&iacute;nh l&agrave; để gương đối diện với cửa ch&iacute;nh.<br />\r\nGương đối diện với cửa ch&iacute;nh sẽ sinh ra t&agrave; kh&iacute; rất lớn, kh&ocirc;ng chỉ ảnh hưởng đến t&agrave;i vận của người trong gia đ&igrave;nh, m&agrave; c&ograve;n ảnh hưởng ti&ecirc;u cực đến sức khỏe. Tuy nhi&ecirc;n th&ocirc;ng thường c&aacute;c gia đ&igrave;nh đều đặt gương cố định, rất kh&oacute; di chuyển hoặc thậm ch&iacute; l&agrave; kh&ocirc;ng thể di chuyển, l&uacute;c n&agrave;y chỉ c&oacute; thể đặt v&agrave;o giữa gương v&agrave; cửa ch&iacute;nh một bức b&igrave;nh phong, che gương lại, hoặc l&agrave; treo r&egrave;m ch&acirc;u ở cửa ch&iacute;nh.<br />\r\n<br />\r\n<strong>Từ cửa ch&iacute;nh c&oacute; thể nh&igrave;n xuy&ecirc;n bếp</strong><br />\r\n<br />\r\nNh&agrave; bếp được coi như l&agrave; kho t&agrave;i của ng&ocirc;i nh&agrave;. Nếu vừa mở cửa l&agrave; c&oacute; thể nh&igrave;n thấy bếp, sẽ c&oacute; thể g&acirc;y hao t&agrave;i tốn của, c&oacute; t&agrave;i vận cũng kh&oacute; m&agrave; giữ được.<br />\r\n<br />\r\nNếu gia đ&igrave;nh mắc lỗi phong thủy n&agrave;y, c&oacute; thể treo r&egrave;m ở trước cửa ch&iacute;nh để che bớt đi, đương nhi&ecirc;n, nếu như c&oacute; điều kiện, tốt nhất l&agrave; lắp th&ecirc;m cửa hoặc tấm b&igrave;nh phong ở giữa để h&oacute;a giải.<br />\r\n<br />\r\n<strong>Giường đặt dưới cửa sổ hoặc đối diện cửa ch&iacute;nh</strong><br />\r\n<br />\r\nViệc đặt giường dưới cửa sổ hoặc đối diện cửa ch&iacute;nh sẽ l&agrave;m cho ta c&oacute; cảm gi&aacute;c bồn chồn v&agrave; bất an khi ngủ. Điều n&agrave;y kh&ocirc;ng những ảnh hưởng đến sức khỏe của chủ nh&acirc;n m&agrave; c&ograve;n ảnh hưởng đến t&agrave;i vận.<br />\r\nNếu phạm phải lỗi n&agrave;y th&igrave; l&uacute;c ngủ n&ecirc;n bu&ocirc;ng r&egrave;m hoặc đ&oacute;ng cửa ph&ograve;ng ngủ để giảm ảnh hưởng kh&ocirc;ng tốt.<br />\r\n<br />\r\n<strong>Trong nh&agrave; lộn xộn v&agrave; u &aacute;m</strong><br />\r\n<br />\r\nC&aacute;c vật dụng trong nh&agrave; cần phải giữ g&igrave;n sạch sẽ, gọn g&agrave;ng, tr&aacute;nh chồng chất cao v&agrave; dồn &eacute;p. Điều n&agrave;y ảnh hưởng lớn đến t&acirc;m l&yacute; v&agrave; tinh thần của gia chủ. Nếu nh&agrave; kh&ocirc;ng đủ &aacute;nh s&aacute;ng, tốt nhất l&agrave; lắp đ&egrave;n hoặc l&agrave; c&aacute;c thiết bị chiếu s&aacute;ng kh&aacute;c để bổ sung &aacute;nh s&aacute;ng cho ph&ograve;ng, nhằm tăng cường dương kh&iacute; cho căn ph&ograve;ng.<br />\r\n<br />\r\n<strong>Để c&acirc;y cảnh đ&atilde; chết trong nh&agrave; hoặc ngo&agrave;i s&acirc;n</strong><br />\r\n<br />\r\nĐiều lưu t&acirc;m cơ bản nhất của chủ nh&acirc;n ng&ocirc;i nh&agrave; l&agrave; lu&ocirc;n tạo cho căn nh&agrave; mang vẻ sức sống, tươi mới. Do đ&oacute;, h&atilde;y n&ecirc;n ch&uacute; &yacute; đến c&aacute;c loại c&acirc;y trồng trong nh&agrave; bạn. Chăm s&oacute;c ch&uacute;ng thật tốt, rửa bụi bẩn, thay nước v&agrave; thay hoa đều đặn. H&atilde;y để hoa, c&acirc;y cảnh mang sức sống, sự tươi mới v&agrave; sinh động theo đ&uacute;ng nghĩa của n&oacute;.<br />\r\nNếu để c&acirc;y chết v&agrave; h&eacute;o &uacute;a trong nh&agrave; hoặc ngo&agrave;i s&acirc;n sẽ cho thấy sự cạn kiệt về nguồn năng lượng trong ng&ocirc;i nh&agrave; của bạn.<br />\r\n<br />\r\n<strong>Nh&agrave; tắm v&agrave; ph&ograve;ng bếp liền kề nhau</strong><br />\r\n<br />\r\nNh&agrave; tắm đại diện cho yếu tố Thủy c&ograve;n nh&agrave; bếp lại đại diện cho Hỏa. Trong ngũ h&agrave;nh tương sinh tương khắc th&igrave; thủy khắc hỏa. N&ecirc;n nếu để nh&agrave; tắm v&agrave; ph&ograve;ng bếp liền nhau sẽ tạo ra sự khắc chế trong ng&ocirc;i nh&agrave; ảnh hưởng đến quan hệ trong gia đ&igrave;nh.<br />\r\n<br />\r\nVới điều n&agrave;y th&igrave; việc di dời l&agrave; kh&ocirc;ng thể v&igrave; vậy cần th&ecirc;m yếu tố Mộc để trung h&ograve;a sự tương khắc bằng việc sử dụng một v&agrave;i loại c&acirc;y cảnh.<br />\r\n<br />\r\n<strong>Đặt tivi trong ph&ograve;ng ngủ</strong><br />\r\n<br />\r\nĐ&acirc;y l&agrave; sai lầm m&agrave; nhiều hộ gia đ&igrave;nh hay mắc phải. Việc để một chiếc ti vi trong ph&ograve;ng ngủ l&agrave;m ph&aacute; đi sự y&ecirc;n tĩnh ảnh hưởng đến giấc ngủ v&agrave; cả mối quan hệ vợ chồng.<br />\r\nH&atilde;y nhớ, ph&ograve;ng ngủ l&agrave; kh&ocirc;ng gian ri&ecirc;ng tư, nghỉ ngơi đầy l&atilde;ng mạn của mỗi người, mỗi cặp vợ chồng, v&igrave; thế, h&atilde;y cố gắng để ch&uacute;ng kh&ocirc;ng bị t&aacute;c động bởi c&aacute;c đồ vật kh&aacute;c.<br />\r\n<br />\r\n<strong>Để hệ thống ống nước r&ograve; rỉ</strong><br />\r\n<br />\r\nMặc d&ugrave; rất &iacute;t người coi trọng v&agrave; lưu t&acirc;m, nhưng để hệ thống ống nước nh&agrave; bạn r&ograve; rỉ m&agrave; kh&ocirc;ng sửa chữa ngay, sẽ l&agrave; yếu tố g&acirc;y thất tho&aacute;t cho t&agrave;i lực, năng lượng v&agrave; sức khỏe của gia đ&igrave;nh.<br />\r\nNếu bạn kh&ocirc;ng c&oacute; kinh nghiệm th&igrave; h&atilde;y gọi ngay đội sửa chữa đến c&agrave;ng sớm c&agrave;ng tốt. Tuy n&oacute; l&agrave; một vấn đề nhỏ nhưng để l&acirc;u ng&agrave;y sẽ ảnh hưởng đến chất lượng cuộc sống của gia đ&igrave;nh bạn v&agrave; l&agrave;m ti&ecirc;u tan những nguồn kh&iacute; t&iacute;ch cực của ng&ocirc;i nh&agrave;.<br />\r\n<br />\r\n<strong>Kh&ocirc;ng x&acirc;y nh&agrave; tr&ecirc;n mặt ao</strong><br />\r\n<br />\r\nC&oacute; nhiều người c&oacute; tiền n&ecirc;n th&iacute;ch x&acirc;y nh&agrave; tr&ecirc;n mặt ao, hồ v&igrave; kiểu thiết kế n&agrave;y sẽ rất đẹp, lại thơ mộng, nhưng tr&ecirc;n thực tế th&igrave; kiểu thiết kế n&agrave;y phạm phong thủy, dễ khiến bạn ph&aacute; sản.<br />\r\n<br />\r\n<strong>Kh&ocirc;ng lưu trữ qu&aacute; nhiều nước</strong><br />\r\n<br />\r\nĐ&acirc;y l&agrave; một trong những điều ki&ecirc;ng kỵ trong nh&agrave; m&agrave; ai cũng cần phải biết. Nhiều người quan niệm rằng, khi trong nh&agrave; hết nước cũng đồng nghĩa với việc t&agrave;i lộc sẽ ngừng do vậy họ hay t&iacute;ch trữ nước trong c&aacute;c chai nước lớn ở khắp c&aacute;c ng&otilde; ng&aacute;ch trong nh&agrave;.<br />\r\nTuy nhi&ecirc;n, theo quan niệm phong thủy th&igrave; đ&acirc;y lại l&agrave; việc kh&ocirc;ng n&ecirc;n l&agrave;m v&igrave; quan niệm phong thủy cho rằng, việc để hệ thống ống nước r&ograve; rỉ m&agrave; kh&ocirc;ng sửa chữa ngay, đồng nghĩa với việc năng lực t&agrave;i ch&iacute;nh, năng lượng c&aacute; nh&acirc;n của gia chủ đang c&oacute; vấn đề.<br />\r\nKh&ocirc;ng những thế v&ograve;i nước r&ograve; rỉ cũng đồng nghĩa với việc l&agrave;m ti&ecirc;u tan những nguồn kh&iacute; t&iacute;ch cực của ng&ocirc;i nh&agrave;. Ch&iacute;nh v&igrave; vậy, nếu như trong nh&agrave; xuất hiện t&igrave;nh trạng r&ograve; rỉ nước th&igrave; cần phải sửa chữa ngay.<br />\r\n<br />\r\nQua b&agrave;i viết, hy vọng qu&yacute; vị đ&atilde; tr&aacute;nh những điều kh&ocirc;ng mang lại may mắn. Lựa chọn được điểm đẹp nhất cho căn nh&agrave; gi&uacute;p mang lại cho gia chủ tốt l&agrave;nh về mọi kh&iacute;a cạnh trong cuộc sống.<br />\r\n&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n', 0, NULL, NULL, 1, 0, 17, '2017-07-10 14:03:01', '2017-07-14 09:01:56', 3, 2),
(12, 'Cách khắc phục hướng nhà không tốt', 'cach-khac-phuc-huong-nha-khong-tot', 'Cach khac phuc huong nha khong tot', 'Nếu quý vị là người quan tâm đến phong thủy, thì cách khắc phục hướng nhà không tốt chắc hẵn quý vị không nên bỏ qua. Sau đây là bài viết chia sẻ về cách khắc phục hướng nhà không tốt.', '2017/07/10/cach-khac-phuc-huong-nha-khong-tot-1499670609.png', 4, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><img alt=\"cách khắc phục hướng nhà không tốt\" src=\"/uploads/images/cach-khac-phuc-huong-nha-khong-tot.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nTrong m&ocirc;i trường thi&ecirc;n nhi&ecirc;n, c&aacute;c hướng vốn kh&ocirc;ng đổi. Sở dĩ c&oacute; lẫn lộn về hướng tốt xấu l&agrave; v&igrave; c&oacute; nhiều quan điểm kh&aacute;c nhau v&agrave; thiếu giải th&iacute;ch r&otilde; r&agrave;ng. Thực ra, 4 phương 8 hướng m&agrave; ch&uacute;ng ta biết đều c&oacute; c&aacute;c đặc t&iacute;nh ri&ecirc;ng t&ugrave;y theo c&aacute;ch xem x&eacute;t. C&oacute; bốn ti&ecirc;u ch&iacute; sau để đ&aacute;nh gi&aacute; tốt xấu về hướng khi l&agrave;m nh&agrave;:<br />\r\n<br />\r\n<strong>Tốt xấu theo hướng kh&iacute; hậu</strong><br />\r\n<br />\r\nV&iacute; dụ như hướng Nam v&agrave; l&acirc;n cận Nam (Đ&ocirc;ng Nam v&agrave; T&acirc;y Nam) l&agrave; những hướng tốt đối với điều kiện kh&iacute; hậu Việt Nam, bởi đ&oacute;n được gi&oacute; m&aacute;t v&agrave; nguồn &aacute;nh s&aacute;ng ổn định, kh&ocirc;ng kh&iacute; ấm &aacute;p. Trong khi đ&oacute;, c&aacute;c hướng T&acirc;y, T&acirc;y Bắc th&igrave; gặp nắng gắt v&agrave;o buổi chiều; hướng Đ&ocirc;ng th&igrave; ch&oacute;i v&agrave;o buổi s&aacute;ng v&agrave; chịu th&ecirc;m gi&oacute; lạnh từ hướng Đ&ocirc;ng Bắc. Hướng Bắc nằm giữa 2 hướng T&acirc;y Bắc (nắng chiều) v&agrave; Đ&ocirc;ng Bắc (gi&oacute; lạnh) n&ecirc;n cũng kh&ocirc;ng tốt lắm. Bởi thế, &ldquo;l&agrave;m nh&agrave; hướng Nam&rdquo; l&agrave; một trong những kinh nghiệm x&acirc;y dựng d&acirc;n gian để đ&oacute;n được gi&oacute; m&aacute;t, tr&aacute;nh được gi&oacute; lạnh.<br />\r\n<br />\r\n<strong>Tốt xấu theo hướng phương vị</strong><br />\r\n<br />\r\nL&agrave; hướng của một v&ugrave;ng, một vật (hay người) ta x&eacute;t so với 1 điểm gốc n&agrave;o đ&oacute;. V&iacute; dụ n&oacute;i &ldquo;trước mặt tho&aacute;ng đ&atilde;ng, sau lưng c&oacute; chỗ dựa, tả Thanh Long hữu Bạch Hổ&ldquo; l&agrave; &yacute; n&oacute;i đến hướng xung quanh của 1 chủ thể ta x&eacute;t. Khi chủ thể di chuyển, thay đổi, quay về đ&acirc;u th&igrave; trước sau phải tr&aacute;i thay đổi theo.<br />\r\n<br />\r\n<strong>Tốt xấu theo hướng mệnh trạch</strong><br />\r\n<br />\r\nC&oacute; thể tham khảo c&aacute;c s&aacute;ch về Dịch học v&agrave; văn ho&aacute; truyền thống phương Đ&ocirc;ng để t&igrave;m ra cung mệnh của mỗi người tương ứng với c&aacute;c phương hướng c&aacute;t hung. Dịch học phương Đ&ocirc;ng quy định c&oacute; 8 mệnh cung tương ứng với 8 hướng trong tự nhi&ecirc;n theo 8 quẻ của B&aacute;t Qu&aacute;i, ph&acirc;n th&agrave;nh 2 nh&oacute;m l&agrave; nh&oacute;m Đ&ocirc;ng tứ mệnh v&agrave; T&acirc;y tứ mệnh. Nguy&ecirc;n tắc chung l&agrave; người theo nh&oacute;m Đ&ocirc;ng tứ mệnh th&igrave; ở nh&agrave; Đ&ocirc;ng tứ trạch, người theo nh&oacute;m T&acirc;y tứ mệnh ở nh&agrave; T&acirc;y tứ trạch. Khi đi s&acirc;u v&agrave;o từng hướng cụ thể, sẽ c&oacute; th&ecirc;m c&aacute;c ph&acirc;n t&iacute;ch ngũ h&agrave;nh sinh khắc để x&aacute;c định hướng hợp v&agrave; kh&ocirc;ng hợp.<br />\r\nD&ugrave;ng h&igrave;nh khối lồi thụt, m&aacute;i che, c&acirc;y xanh, mở cửa hạn chế&hellip; l&agrave; những biện ph&aacute;p hiệu quả khắc phục hướng kh&iacute; hậu xấu.<br />\r\nC&ugrave;ng một d&atilde;y nh&agrave; (tức l&agrave; nh&igrave;n ra c&ugrave;ng một hướng) v&agrave; c&ugrave;ng bu&ocirc;n b&aacute;n giống nhau, nhưng c&oacute; nh&agrave; thuận lợi c&oacute; nh&agrave; kh&oacute; khăn l&agrave; v&igrave; mỗi nh&agrave; b&ecirc;n trong v&agrave; b&ecirc;n ngo&agrave;i, trước sau phải tr&aacute;i c&oacute; phương vị kh&aacute;c nhau.<br />\r\n<br />\r\n<strong>Tốt xấu theo hướng giao tiếp</strong><br />\r\n<br />\r\nNgo&agrave;i việc ứng ph&oacute; với m&ocirc;i trường thi&ecirc;n nhi&ecirc;n, con người cũng phải ứng xử với m&ocirc;i trường x&atilde; hội. V&igrave; thế, nh&agrave; cần quay mặt (hoặc cửa, lối v&agrave;o một kh&ocirc;ng gian n&agrave;o đ&oacute;) ra những vị tr&iacute; thuận lợi cho việc giao tiếp. Cha &ocirc;ng ta dạy &ldquo;Nhất cận thị, nhị cận giang, tam cận lộ&rdquo; l&agrave; n&oacute;i l&ecirc;n những điểm thuận lợi khi mua đất cất nh&agrave;, từ xưa đến nay vẫn kh&ocirc;ng kh&aacute;c nhau bao nhi&ecirc;u khi x&eacute;t gi&aacute; trị 1 bất động sản.<br />\r\n<br />\r\nNhư vậy, khi xem x&eacute;t ng&ocirc;i nh&agrave; c&oacute; hợp hướng hay kh&ocirc;ng, phải xem x&eacute;t tr&ecirc;n cơ sở ph&acirc;n t&iacute;ch v&agrave; tổng hợp cả 4 loại hướng. Cần ph&acirc;n t&iacute;ch trước ti&ecirc;n xem 4 loại hướng tr&ecirc;n tốt xấu bao nhi&ecirc;u phần, khả năng khắc phục nhiều hay &iacute;t, c&oacute; ảnh hưởng g&igrave; đến m&ocirc;i trường, con người, kết cấu x&acirc;y dựng hay kh&ocirc;ng. Cảm ơn qu&yacute; vị đ&atilde; xem qua b&agrave;i viết của ch&uacute;ng t&ocirc;i, hy vọng c&oacute; thể gi&uacute;p được qu&yacute; vị rước t&agrave;i lộc của m&igrave;nh về nh&agrave;.<br />\r\n&nbsp;', 0, NULL, NULL, 1, 0, 19, '2017-07-10 14:30:03', '2017-07-14 09:01:38', 3, 2);
INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(13, 'Cách hóa giải hướng nhà xấu', 'cach-hoa-giai-huong-nha-xau', 'Cach hoa giai huong nha xau', 'Có những hướng nhà xấu mà ít ai có thể nhận ra bằng quan sát thường và có thể bị nhầm lẫn to lầm nếu đưa ra quyết định mua nhà đó. Và có lỡ mua nhầm nhà ở có hướng xấu, quý vị cũng đừng quá lo lắng, sau đây sẽ là cách hóa giải hướng nhà xấu dành cho gia chủ.', '2017/07/10/cach-hoa-giai-huong-nha-xau-1499672036.png', 4, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><img alt=\"cách hóa giải hướng nhà xấu\" src=\"/uploads/images/cach-hoa-giai-huong-nha-xau.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n3 c&aacute;ch h&oacute;a giải hướng nh&agrave; xấu theo phong thủy kh&ocirc;ng hợp tuổi gia chủ<br />\r\n<br />\r\n<strong>D&ugrave;ng vật phẩm phong thủy trấn giữ xung quanh cửa ra v&agrave;o</strong><br />\r\n<br />\r\nBạn c&oacute; thể tạo ra năng lượng bảo vệ mạnh mẽ xung quanh cửa ra v&agrave;o của ng&ocirc;i nh&agrave; bằng c&aacute;ch sử dụng c&aacute;c vật phẩm phong thủy, trấn &aacute;p lại những luồng kh&iacute; xấu.<br />\r\n<br />\r\nC&oacute; thể đặt tượng hoặc tranh ảnh của Quan Thế &Acirc;m Bồ T&aacute;t ở gần cửa ra v&agrave;o. Đ&acirc;y l&agrave; vị thần đại diện cho l&ograve;ng bao dung v&agrave; t&igrave;nh thương cao cả, sẽ bảo vệ cho gia chủ tho&aacute;t khỏi tai &aacute;ch. Cũng c&oacute; thể đặt tượng Quan C&ocirc;ng, vị thần chiến tranh gần cửa ra v&agrave;o. Ngo&agrave;i ra, bạn cũng c&oacute; thể chọn biểu tượng của ri&ecirc;ng m&igrave;nh nhưng c&oacute; khả năng mang lại năng lượng của sự bảo vệ v&agrave; l&ograve;ng bao dung, che chở.<br />\r\n<br />\r\nC&oacute; 1 c&aacute;ch nữa m&agrave; nhiều người lựa chọn l&agrave; treo gương b&aacute;t qu&aacute;i trước cửa nh&agrave;. B&aacute;t qu&aacute;i treo b&ecirc;n ngo&agrave;i d&ugrave;ng để trừ t&agrave; ma, chỉnh hướng cửa sai&hellip; C&oacute; gương b&aacute;t qu&aacute;i phẳng, gương b&aacute;t qu&aacute;i lồi v&agrave; gương b&aacute;t qu&aacute;i l&otilde;m. Tuy nhi&ecirc;n, cần thận trọng khi sử dụng c&aacute;ch n&agrave;y, chọn loại gương cho ph&ugrave; hợp bởi mỗi loại gương b&aacute;t qu&aacute;i sẽ c&oacute; t&aacute;c dụng kh&aacute;c nhau, c&oacute; thể đ&aacute;nh tan luồng kh&iacute; xấu, cũng c&oacute; thể xua đi c&aacute;t kh&iacute;.<br />\r\n<br />\r\nBạn c&oacute; thể d&ugrave;ng trụ thạch anh để h&oacute;a giải nh&agrave; hướng xấu. Thạch anh tự nhi&ecirc;n mang nguồn năng lượng vũ trụ cực kỳ mạnh mẽ. Với nh&agrave; hướng xấu kh&ocirc;ng hợp tuổi gia chủ, n&ecirc;n d&ugrave;ng trụ thạch anh loại lớn (tối thiểu từ 0,5kg trở l&ecirc;n), để trấn c&aacute;c năng lượng xấu, t&agrave; kh&iacute; xấu do hướng xấu như Ngũ quỷ, Lục s&aacute;t, Họa hại, Tuyệt mệnh g&acirc;y ra. Năng lượng dương cực mạnh của thạch anh sẽ xua tan, đẩy năng lượng &acirc;m ra khỏi căn nh&agrave; của bạn.<br />\r\n<br />\r\nMột c&aacute;ch tuyệt vời kh&aacute;c để đối ph&oacute; với hướng phong thủy xấu hoặc kh&ocirc;ng may mắn l&agrave; l&agrave;m suy yếu yếu tố phong thủy của n&oacute;. V&iacute; dụ, nếu cửa ra v&agrave;o của bạn quay mặt về hướng Bắc (hướng Bắc l&agrave; hướng kh&ocirc;ng may mắn với bạn), th&igrave; h&atilde;y l&agrave;m suy yếu yếu tố Thủy &ndash; biểu tượng của hướng Bắc bằng c&aacute;ch trang tr&iacute; c&aacute;c đồ vật đại diện cho yếu tố Thổ.<br />\r\n<br />\r\nTuy nhi&ecirc;n, biện ph&aacute;p n&agrave;y phải được thực hiện thật tinh tế, đảm bảo được sự c&acirc;n bằng giữa 5 yếu tố phong thủy cơ bản, đồng thời duy tr&igrave; năng lượng hỗ trợ sự nghiệp của hướng Bắc kết nối h&agrave;i h&ograve;a.<br />\r\n<br />\r\n<strong>Đổi hướng cửa ch&iacute;nh v&agrave;o nh&agrave;</strong><br />\r\n<br />\r\nKhi hướng nh&agrave; kh&ocirc;ng hợp tuổi với gia chủ, bạn c&oacute; thể thử đổi hướng bằng c&aacute;ch sử một c&aacute;nh cửa kh&aacute;c thường xuy&ecirc;n hơn. Đ&acirc;y l&agrave; c&aacute;ch xử l&yacute; đ&uacute;ng với hầu hết c&aacute;c trường hợp v&agrave; kh&ocirc;ng hề phức tạp.<br />\r\n<br />\r\nNg&agrave;y nay, đời sống vật chất l&ecirc;n cao, nhiều gia đ&igrave;nh đ&atilde; sắm cho m&igrave;nh &ocirc; t&ocirc; ri&ecirc;ng. Nhiều ng&ocirc;i nh&agrave; c&oacute; thiết kế đường đi v&agrave;o kh&ocirc;ng gian nội thất th&ocirc;ng qua ga-ra để xe hoặc cửa sau. Do đ&oacute;, cửa ch&iacute;nh chỉ c&ograve;n mang &yacute; nghĩa tượng trưng, bởi gia chủ đ&atilde; v&agrave;o nh&agrave; từ hướng kh&aacute;c.<br />\r\nNếu cửa ra v&agrave;o quay mặt về hướng phong thủy kh&ocirc;ng thuận, kh&ocirc;ng may mắn &iacute;t khi được sử dụng, nguồn năng lượng ti&ecirc;u cực sẽ kh&ocirc;ng c&oacute; cơ hội &ldquo;đi s&acirc;u&rdquo; v&agrave;o ng&ocirc;i nh&agrave; hoặc kh&ocirc;ng được k&iacute;ch hoạt, suy yếu đi.<br />\r\n<br />\r\nNếu diện t&iacute;ch nh&agrave; bạn lớn, bạn cũng c&oacute; thể h&oacute;a giải bằng c&aacute;ch l&agrave;m th&ecirc;m một cửa phụ ph&iacute;a trong ng&ocirc;i nh&agrave;. V&igrave; những nguy&ecirc;n tắc của x&acirc;y dựng m&agrave; cửa ch&iacute;nh phải đặt ở nơi kh&ocirc;ng thể thay đổi được. Tuy nhi&ecirc;n, bạn ho&agrave;n to&agrave;n c&oacute; quyền xử l&yacute; với kh&ocirc;ng gian b&ecirc;n trong nh&agrave;.<br />\r\n<br />\r\nTh&ecirc;m một hệ khung cửa phạm vi trong nh&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; g&igrave; kh&oacute; khăn. Cửa mới được tạo th&ecirc;m ở hướng l&agrave;nh, sẽ gi&uacute;p gia chủ khắc chế được những bất lợi do hướng nh&agrave; xấu đem lại.<br />\r\n<br />\r\n<strong>Khắc chế bằng hướng bếp</strong><br />\r\n<br />\r\nNgo&agrave;i hướng nh&agrave;, phong thủy cũng rất coi trọng hướng bếp. Ch&iacute;nh v&igrave; vậy, khi xem hướng nh&agrave;, nếu cửa ch&iacute;nh bị phạm hướng xấu th&igrave; ho&agrave;n to&agrave;n c&oacute; thể khắc chế bằng hướng bếp để khắc chế, cụ thể như sau :<br />\r\n<br />\r\nHướng cửa ch&iacute;nh phạm Ngũ Quỷ th&igrave; đặt bếp hướng Sinh Kh&iacute;.<br />\r\n<br />\r\nHướng cửa ch&iacute;nh phạm Tuyệt Mệnh th&igrave; đặt bếp hướng Thi&ecirc;n Y.<br />\r\n<br />\r\nHướng cửa ch&iacute;nh phạm Lục S&aacute;t th&igrave; đặt bếp hướng Ph&uacute;c Đức.<br />\r\n<br />\r\nHướng cửa ch&iacute;nh phạm Hoạ Hại th&igrave; đặt bếp hướng Phục Vị.<br />\r\n<br />\r\nNếu nh&agrave; bạn quay về hướng T&acirc;y Nam phạm ngũ quỷ, &nbsp;bạn n&ecirc;n đặt bếp hướng sinh kh&iacute;, tức hướng Bắc. Ch&uacute; &yacute; bếp phải đặt tại cung xấu (T&acirc;y, Đ&ocirc;ng Bắc&hellip;) chỉ cần mặt bếp xoay về hướng Bắc, tức khi đứng nấu lưng bạn quay về hướng Bắc.<br />\r\n<br />\r\nMột ng&ocirc;i nh&agrave; c&oacute; thể gặp một hoặc nhiều trong số những trường hợp xấu kể tr&ecirc;n. Với những c&aacute;ch h&oacute;a giải hướng nh&agrave; xấu theo phong thủy như tr&ecirc;n, gia chủ c&oacute; thể tự chỉnh sửa ng&ocirc;i nh&agrave; của m&igrave;nh sao cho ph&ugrave; hợp.<br />\r\n<br />\r\nTuy nhi&ecirc;n, đối với những trường hợp tương đối phức tạp, ch&uacute;ng ta kh&ocirc;ng n&ecirc;n t&ugrave;y tiện sửa đổi m&agrave; phải đi gặp những chuy&ecirc;n gia c&oacute; kinh nghiệm v&agrave; kiến thức về phong thủy để c&oacute; được những lời tư vấn đ&uacute;ng đắn v&agrave; khoa học nhất cho căn nh&agrave; của m&igrave;nh.<br />\r\n<br />\r\n&nbsp;', 0, NULL, NULL, 1, 0, 21, '2017-07-10 14:46:32', '2017-07-14 09:01:03', 3, 2),
(14, 'Những loại cây không nên trồng trong nhà', 'nhung-loai-cay-khong-nen-trong-trong-nha', 'Nhung loai cay khong nen trong trong nha', 'Phong thủy một căn nhà tốt hay xấu do nhiều yếu tố cấu thành, trong đó cách trang trí nội thất trong nhà ảnh hưởng khá quan trọng. Rất nhiều gia chủ đã chọn  trang trí bằng cây cảnh để mang lại không gian xanh cho căn nhà, nhưng sau đây là những loại cây không nên trồng trong nhà bạn nên biết.', '2017/07/10/nhung-loai-cay-khong-nen-trong-trong-nha-1499673067.png', 4, '<h2>&nbsp;</h2>\r\n&nbsp;\r\n\r\n<div style=\"text-align:center\"><img alt=\"những loại cây không nên trồng trong nhà\" src=\"/uploads/images/nhung-loai-cay-khong-nen-trong-trong-nha.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n<strong>Đỗ Quy&ecirc;n:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Rhododendron occidentale. Tất cả c&aacute;c bộ phận của c&acirc;y đều c&oacute; chất độc Andromedotoxin v&agrave; Arbutin glucoside. Người bị ngộ độc do loại c&acirc;y n&agrave;y thường c&oacute; triệu chứng buồn n&ocirc;n, chảy nước d&atilde;i, &oacute;i mửa, uể oải, ch&oacute;ng mặt, kh&oacute; thở, mất c&acirc;n bằng. Một lượng 100 đến 225 gram l&aacute; Đỗ Quy&ecirc;n đủ để g&acirc;y ngộ độc nặng cho trẻ em 25 kg.<br />\r\n<br />\r\n<strong>Thi&ecirc;n điểu:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Strelitzia reginae. Hoa v&agrave; hạt của c&acirc;y c&oacute; c&aacute;c chất g&acirc;y ngộ độc đường ruột. Tiếp x&uacute;c hoặc ăn hoa, hạt sẽ khiến buồn n&ocirc;n, ti&ecirc;u chảy, ch&oacute;ng mặt.<br />\r\n<br />\r\n<strong>M&ocirc;n kiểng:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Caladium hortulanum. Tất cả c&aacute;c bộ phận của c&acirc;y đều c&oacute; chất độc Calcium oxalate v&agrave; Asparagine Khi ăn phải sẽ dẫn đến nguy cơ bị bỏng, ngứa r&aacute;t v&ugrave;ng miệng, ni&ecirc;m mạc ruột.<br />\r\n<br />\r\n<strong>Hoa loa k&egrave;n Arum, &Yacute; lan:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Zantedeschia aethiopica. L&aacute; v&agrave; củ c&acirc;y đều c&oacute; chất độc đường ruột Calcium oxalate. Khi ăn phải loại thực vật n&agrave;y c&oacute; thể bị &oacute;i mửa, bỏng r&aacute;t bề mặt ni&ecirc;m mạc.<br />\r\n&nbsp;<br />\r\n<strong>Xương rồng b&aacute;t ti&ecirc;n:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Euphorbia milii splendens. Nhựa c&acirc;y g&acirc;y bỏng r&aacute;t da khi tiếp x&uacute;c.<br />\r\n<br />\r\n<strong>C&acirc;y Anh Thảo:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Cyclamen persicum. Củ c&acirc;y c&oacute; chất độc Alkaloids g&acirc;y kh&oacute; ti&ecirc;u, ti&ecirc;u chảy, &oacute;i mửa nếu ăn phải.<br />\r\n<br />\r\n<strong>Chuỗi ngọc:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Sedum morganianum: Tất cả bộ phận c&oacute; chất Glucosides g&acirc;y mệt mỏi, kh&oacute; thở, ti&ecirc;u chảy nếu ăn phải.<br />\r\n<br />\r\n<strong>M&ocirc;n l&aacute; lớn:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Colocasia spp. Tất cả c&aacute;c bộ phận tr&ecirc;n c&acirc;y đều chứa chất Calcium oxalate Asparagine g&acirc;y ngứa v&agrave; bỏng r&aacute;t cổ họng, ti&ecirc;u chảy nếu ăn phải.<br />\r\n<br />\r\n<strong>Hồng m&ocirc;n:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Anthurium spp. Tất cả c&aacute;c bộ phận của c&acirc;y đều c&oacute; độc tố Calcium oxalate v&agrave; Asparagine Việc ăn phải loại thực vật n&agrave;y c&oacute; thể g&acirc;y bỏng r&aacute;t v&ugrave;ng họng, dạ d&agrave;y v&agrave; ruột.<br />\r\n<br />\r\n<strong>C&acirc;y Dạ lan:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Hyacinth orientalis. Củ Dạ Lan c&oacute; độc tố Alkaloid g&acirc;y vọp bẻ, đầy bụng, buồn n&ocirc;n, ti&ecirc;u chảy nếu ăn phải.<br />\r\n<br />\r\n<strong>Cẩm t&uacute; cầu:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Hydrangea macrophylla. L&aacute; v&agrave; củ c&acirc;y c&oacute; chất Hydragin-cyanogenic glycoside g&acirc;y ti&ecirc;u chảy, &oacute;i mửa, thở gấp.<br />\r\n<br />\r\n<strong>Xương rồng kiểng:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Euphorbia trigona. Nhựa c&acirc;y C&oacute; thể g&acirc;y bỏng da v&agrave; mắt nếu tiếp x&uacute;c, g&acirc;y t&ecirc; cứng lưỡi v&agrave; miệng, n&ocirc;n mửa nếu ăn phải.<br />\r\n<br />\r\n<strong>C&acirc;y thủy ti&ecirc;n:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Narcissus spp. Củ của c&acirc;y c&oacute; chất Alkaloids g&acirc;y ch&oacute;ng mặt, buồn n&ocirc;n, ti&ecirc;u chảy, run rẩy to&agrave;n th&acirc;n, h&ocirc;n m&ecirc;, c&oacute; thể dẫn đến tử vong khi ăn phải.<br />\r\n<br />\r\n<strong>Một số loại trầu (Trầu b&agrave;, Trầu &ocirc;ng,&hellip;):</strong><br />\r\n<br />\r\nC&oacute; t&ecirc;n khoa học l&agrave; Philodendron spp. L&aacute; v&agrave; th&acirc;n c&acirc;y c&oacute; chất độc Calcium oxalate g&acirc;y ti&ecirc;u chảy, buồn n&ocirc;n, bỏng r&aacute;t ni&ecirc;m mạc miệng khi ăn phải.<br />\r\n<br />\r\n<strong>Hoa Tulip:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Tulipa spp. Củ c&acirc;y c&oacute; chất Tulipene, ăn phải sẽ g&acirc;y ch&oacute;ng mặt, buồn n&ocirc;n.<br />\r\n<br />\r\n<strong>B&egrave;o Lục b&igrave;nh:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Eichhornia crassipes. Tất cả c&aacute;c bộ phận của c&acirc;y đều c&oacute; độc g&acirc;y chứng ăn kh&ocirc;ng ti&ecirc;u, &oacute;i mửa tr&ecirc;n ch&oacute;, m&egrave;o v&agrave; một số vật nu&ocirc;i kh&aacute;c khi ăn phải.<br />\r\n<br />\r\n<strong>C&acirc;y ngoắt nghẻo</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Gloriosa superba. Củ v&agrave; hạt c&acirc;y c&oacute; chất kịch độc Colchicine v&agrave; một số alkaloid kh&aacute;c m&agrave; nếu ăn v&agrave;o sẽ g&acirc;y t&ecirc; lưỡi, l&agrave;m cho cơ thể mất cảm gi&aacute;c, nặng th&igrave; h&ocirc;n m&ecirc; v&agrave; nếu kh&ocirc;ng xử l&yacute; kịp thời sẽ dẫn đến tử vong.<br />\r\n<br />\r\n<strong>Thơm ổi:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Lantana spp. Quả c&oacute; chất độc Lantanin alkaloid Hoặc lantadene A g&acirc;y bỏng r&aacute;t đường ruột, gi&atilde;n cơ, rối loạn tuần ho&agrave;n m&aacute;u v&agrave; c&oacute; thể dẫn đến tử vong.<br />\r\n<br />\r\n<strong>Tr&uacute;c đ&agrave;o:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Nerium oleander. To&agrave;n th&acirc;n Tr&uacute;c đ&agrave;o đều c&oacute; chất cực độc Oleandrin, Neriin. Người ta c&oacute; thể bị ngộ độc do chạm v&agrave;o c&acirc;y hoặc nuốt phải. Nhẹ th&igrave; g&acirc;y buồn n&ocirc;n, &oacute;i mửa, ti&ecirc;u chảy, rối loạn nhịp tim, nặng th&igrave; c&oacute; thể mất kiểm so&aacute;t cơ thể, h&ocirc;n m&ecirc;. Nếu kh&ocirc;ng xử l&yacute; kịp thời sẽ dẫn đến tử vong.<br />\r\nViệc phơi kh&ocirc; hoặc nấu ch&iacute;n cũng kh&ocirc;ng l&agrave;m mất t&iacute;nh độc của lo&agrave;i thực vật n&agrave;y. Kh&ocirc;ng trồng tr&uacute;c đ&agrave;o ở cạnh nguồn nước (giếng ăn, bể nước&hellip;) v&igrave; l&aacute;, hoa tr&uacute;c đ&agrave;o rụng xuống l&agrave;m nhiễm độc nước. Tr&ecirc;n thế giới đ&atilde; ghi nhận nhiều trường hợp bệnh nhi bị ngộ độc do mủ hoa c&acirc;y tr&uacute;c đ&agrave;o.<br />\r\n<br />\r\n<strong>Huệ Lili:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Hippeastrum puniceum. Củ c&acirc;y c&oacute; chất độc Lycorine g&acirc;y ti&ecirc;u chảy, buồn n&ocirc;n, &oacute;i mửa khi ăn phải. Nhựa c&acirc;y c&oacute; thể g&acirc;y n&ocirc;n mửa nếu ăn phải. Tr&aacute;nh tiếp x&uacute;c trực tiếp với da v&igrave; c&oacute; thể g&acirc;y bỏng r&aacute;t, ngứa&hellip;<br />\r\n<br />\r\n<strong>C&acirc;y ng&ocirc; đồng:</strong><br />\r\n<br />\r\nT&ecirc;n khoa học l&agrave; Jatropha podagrica. To&agrave;n th&acirc;n c&acirc;y, đặc biệt l&agrave; củ v&agrave; hạt c&oacute; chứa chất độc Curcin g&acirc;y ch&oacute;ng mặt v&agrave; buồn n&ocirc;n nếu ăn phải.<br />\r\n<br />\r\n<strong>C&acirc;y ba đậu</strong><br />\r\n<br />\r\nBa đậu l&agrave; một c&acirc;y nhỡ cao 3-6m, c&agrave;nh nhẵn. L&aacute; mọc so le, nguy&ecirc;n, h&igrave;nh trứng, đầu nhọn, m&eacute;p c&oacute; răng cưa nhỏ, d&agrave;i 6-8cm, rộng 4-5cm, cuống nhỏ, d&agrave;i 1-2cm. Tr&ocirc;ng to&agrave;n th&acirc;n c&acirc;y thường thấy một số l&aacute; m&agrave;u đỏ n&acirc;u. Hoa mọc th&agrave;nh ch&ugrave;m d&agrave;i 10-20cm ở đầu c&agrave;nh, hoa c&aacute;i ở ph&iacute;a dưới, hoa đực ở đỉnh, cuống nhỏ d&agrave;i 1-3mm. Quả nang, nhẵn, m&agrave;u v&agrave;ng nhạt, cao 2cm, c&oacute; 3 mảnh vỏ khi ch&iacute;n t&aacute;ch ra. Hạt h&igrave;nh trứng d&agrave;i 10mm, rộng 4-6mm, ngo&agrave;i c&oacute; vỏ cứng, mờ, m&agrave;u n&acirc;u x&aacute;m (kh&aacute;c hạt thầu dầu b&oacute;ng v&agrave; c&oacute; v&acirc;n).<br />\r\nDầu ba đậu l&agrave; một chất g&acirc;y phồng rất mạnh: cho t&aacute;c dụng tr&ecirc;n da, người ta thấy da n&oacute;ng bỏng v&agrave; phồng l&ecirc;n, mọng nước, sau đ&oacute; tạo th&agrave;nh mụn tr&oacute;c da.<br />\r\nUống trong, dầu ba đậu l&agrave; một loại thuốc tẩy rất mạnh, với liều rất nhỏ (1/2 đến 2 giọt) đ&atilde; g&acirc;y t&aacute;c dụng sau 1/2 đến 1 giờ. Đi ngo&agrave;i 5-10 lần, l&uacute;c đầu đặc, sau lỏng, bụng đau nhiều hay &iacute;t, n&oacute;ng ở hậu m&ocirc;n.<br />\r\n<br />\r\nVới liều cao hơn 2 giọt, g&acirc;y vi&ecirc;m ruột v&agrave; c&oacute; triệu chứng ngộ độc: N&ocirc;n mửa, đi ngo&agrave;i nhiều, to&aacute;t mồ h&ocirc;i v&agrave; c&oacute; thể dẫn đến tử vong, 10-20 giọt đủ giết một con ngựa.<br />\r\n<br />\r\n<strong>C&acirc;y m&atilde; tiền</strong><br />\r\n<br />\r\nQuả m&atilde; tiền (Strychnos nux-vomica) c&oacute; h&igrave;nh d&aacute;ng rất giống quả cam, được biết đến như một thứ độc dược cực mạnh. Hạt của ch&uacute;ng chứa nhiều chất alcaloid, nếu ăn nhầm sẽ bị co quắp to&agrave;n th&acirc;n v&agrave; t&ecirc; liệt cơ h&ocirc; hấp g&acirc;y ngạt thở dẫn đến tử vong.<br />\r\n<br />\r\n<strong>C&acirc;y th&ocirc;ng thi&ecirc;n hay huỳnh li&ecirc;n :</strong><br />\r\n<br />\r\nThevetia peruviana, t&ecirc;n kh&aacute;c l&agrave; Cascabela thevetia), l&agrave; lo&agrave;i c&acirc;y thuộc họ Tr&uacute;c đ&agrave;o c&oacute; l&aacute; h&igrave;nh m&aacute;c, mọc so le, th&acirc;n c&acirc;y cao khoảng 3 đến 4 m&eacute;t. Th&ocirc;ng thi&ecirc;n c&oacute; xuất xứ từ ch&acirc;u Mỹ, thường gặp ở một số nơi như Kula, Maui, Waihee, Kihei, Kahana Beach, Hawaii&nbsp;<br />\r\nC&acirc;y th&ocirc;ng li&ecirc;n l&agrave; c&acirc;y th&acirc;n gỗ. To&agrave;n c&acirc;y c&oacute; tiết mũ m&agrave;u trắng. Ở Việt Nam, hoa th&ocirc;ng thi&ecirc;n c&oacute; m&agrave;u v&agrave;ng rực, ở một số nơi kh&aacute;c hoa c&oacute; m&agrave;u v&agrave;ng cam, hoa thường c&oacute; 5 c&aacute;nh. Tr&aacute;i c&oacute; h&igrave;nh thoi m&agrave;u xanh.<br />\r\nC&acirc;y th&ocirc;ng li&ecirc;n c&oacute; chứa nhiều chất độc ở hoa, l&aacute;, quả v&agrave; hạt. C&aacute;c độc tố bao gồm: thevetin, neriin, glucozid &hellip;c&oacute; thể g&acirc;y tử vong ở người.<br />\r\n<br />\r\n<strong>&nbsp;C&acirc;y vạn thi&ecirc;n thanh</strong><br />\r\n<br />\r\nThực chất l&agrave; c&acirc;y minh ti, thuộc họ r&aacute;y c&oacute; nhiều chủng lo&agrave;i lai tạo, h&igrave;nh d&aacute;ng l&aacute; rất đẹp n&ecirc;n được ưa chuộng trồng l&agrave;m c&acirc;y cảnh trong nh&agrave;. C&oacute; rất nhiều chủng minh ti với m&agrave;u l&aacute; kh&aacute;c nhau tr&aacute;nh nhầm với c&aacute;c lo&agrave;i vạn ni&ecirc;n thanh thuộc giống Aglaonema (Aglaonema modestum, l&agrave;m cảnh v&agrave; l&agrave;m thuốc). Tất cả bộ phận của c&acirc;y minh ti đều c&oacute; độc. Do đ&oacute; phải cẩn thận khi va chạm, di chuyển hay chăm s&oacute;c loại c&acirc;y cảnh n&agrave;y.<br />\r\nNhựa c&acirc;y minh ti g&acirc;y ngứa v&agrave; nếu chẳng may d&iacute;nh v&agrave;o mắt th&igrave; rất kh&oacute; chịu, ăn phải th&igrave; bị t&ecirc; m&ocirc;i, đỏ lưỡi, n&oacute;i kh&oacute;, ngứa họng&hellip; C&aacute;c b&eacute; con nếu h&aacute;i l&aacute;, ăn l&aacute;, ra hoa, hải quả ăn sẽ bị ngộ độc.<br />\r\nNếu b&eacute; kh&ocirc;ng may d&iacute;nh nhựa c&acirc;y minh ti bị ngứa th&igrave; kh&ocirc;ng n&ecirc;n g&atilde;i m&agrave; hơ n&oacute;ng (ấm) v&ugrave;ng da bị d&iacute;nh sẽ khỏi. Nếu d&iacute;nh nhựa v&agrave;o miệng, mắt th&igrave; s&uacute;c miệng, rửa mắt bằng nước ấm, rồi d&ugrave;ng m&aacute;y sấy t&oacute;c hơ ấm.<br />\r\n<br />\r\nĐ&acirc;y l&agrave;&nbsp;những loại c&acirc;y kh&ocirc;ng n&ecirc;n trồng trong nh&agrave;, qu&yacute; vị n&ecirc;n ch&uacute; &yacute; để tr&aacute;nh mắc phải những lỗi kh&ocirc;ng mong muốn ảnh hưởng đến phong thủy gia đ&igrave;nh gia chủ.', 0, NULL, NULL, 1, 0, 24, '2017-07-10 15:23:38', '2017-07-14 09:00:46', 3, 2),
(15, 'Thị trường giao dịch căn hộ tại TP.HCM vượt đỉnh', 'thi-truong-giao-dich-can-ho-tai-tphcm-vuot-dinh', 'Thi truong giao dich can ho tai TP.HCM vuot dinh', 'Theo báo cáo quý 2/2017 của Savills cho biết 4 dự án mới và giai đoạn tiếp theo của 7 dự án hiện hữu mở bán, cung cấp hơn 4.700 căn. Tổng nguồn cung sơ cấp căn hộ đạt hơn 37.200 căn, giảm 12% theo quý và 7% theo năm.', '2017/07/10/thi-truong-giao-dich-can-ho-tai-tphcm-vuot-dinh-1499675780.png', 7, 'Mặc d&ugrave; nguồn cung giảm nhưng lượng giao dịch tr&ecirc;n thị trường lại ghi nhận mức tăng đột biến, tăng 33% theo qu&yacute; v&agrave; 67% theo năm. Kết quả với gần 11.600 căn được ti&ecirc;u thụ, lượng giao dịch của Q2/2017 đ&atilde; chạm đỉnh kể từ 2011 tới nay.\r\n<div style=\"text-align:center\"><img alt=\"thị trường bất động sản\" src=\"/uploads/images/thi-truong-giao-dich-can-ho-tai-tphcm-vuot-dinh.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nT&igrave;nh h&igrave;nh hoạt động ấn tượng của hạng B v&agrave; C đ&atilde; khiến cho tỉ lệ hấp thụ của thị trường tăng 10 điểm phần trăm theo qu&yacute; v&agrave; 13 điểm phần trăm theo năm tới khoảng 31%. Hạng C c&oacute; tỉ lệ hấp thụ cao nhất khoảng 37% v&agrave; chiếm 64% tổng lượng giao dịch.<br />\r\n<br />\r\nNhận định về nguồn cung tương lai, Savills cho biết từ Q3/2017 đến 2018, gần 48.000 căn được mong đợi sẽ gia nhập thị trường căn hộ. Hạng C được dự kiến sẽ chiếm lĩnh nguồn cung tương lai với gần 46% thị phần.<br />\r\n<br />\r\nTrước đ&oacute;, b&aacute;o c&aacute;o thị trường BĐS TPHCM qu&yacute; 2 của c&ocirc;ng CBRE cũng nhận định thị trường căn hộ đặc biệt s&ocirc;i động trong qu&yacute; 2/2017 sau khi chủ đầu tư đ&atilde; xem x&eacute;t xong chiến lược mở b&aacute;n v&agrave; đ&aacute;nh gi&aacute; sản phẩm. Theo thống k&ecirc; của CBRE, trong Qu&yacute; 2, thị trường cũng ghi nhận tốc độ ti&ecirc;u thụ khả quan. Tổng số căn b&aacute;n được l&agrave; 9.522 căn, tăng 40% so với qu&yacute; trước v&agrave; tăng 59% so với c&ugrave;ng kỳ năm trước.<br />\r\n<br />\r\nNh&igrave;n về triển vọng thị trường tương lai, CBRE cũng dự b&aacute;o sự cải thiện về cơ sở hạ tầng v&agrave; c&aacute;c yếu tố kinh tế vĩ m&ocirc; tiếp tục hỗ trợ thị trường bất động sản. Trong nửa cuối năm 2017, ph&acirc;n kh&uacute;c cao cấp sẽ s&ocirc;i động hơn. Một số dự &aacute;n tại những vị tr&iacute; đắc địa như D&rsquo;Edge tại Thảo Điền (Q2), ch&agrave;o b&aacute;n giai đoạn 2 của Empire City (Q2) đ&atilde; bắt đầu nhận giữ chỗ từ cuối Qu&yacute; 2/2017.<br />\r\n<br />\r\nThị trường sẽ đ&oacute;n nhận th&ecirc;m c&aacute;c sản phẩm mới cao cấp hơn, diện t&iacute;ch lớn hơn v&agrave; sẽ c&oacute; th&ecirc;m dự &aacute;n si&ecirc;u sang. Với sự gia nhập của d&ograve;ng sản phẩm cao cấp gi&aacute; trung b&igrave;nh thị trường v&agrave; kh&aacute;ch mua người nước ngo&agrave;i được kỳ vọng sẽ tăng trong nửa cuối 2017.', 0, NULL, NULL, 1, 0, 25, '2017-07-10 15:36:54', '2017-07-14 08:43:12', 2, 2),
(16, 'Sóng ngầm bất động sản siêu sang trên đất vàng Hồ Chí Minh', 'song-ngam-bat-dong-san-sieu-sang-tren-dat-vang-ho-chi-minh', 'Song ngam bat dong san sieu sang tren dat vang Ho Chi Minh', 'Dù được đánh giá là phân khúc bất động sản (BĐS) rất kén khách, sản phẩm siêu cao cấp vẫn là “hàng nóng” được âm thầm tung ra mỗi năm tại các vị trí vàng ở TP.HCM. ', '2017/07/10/song-ngam-bat-dong-san-sieu-sang-tren-dat-vang-ho-chi-minh-1499676238.png', 7, 'Biệt thự gi&aacute; 70-80 tỷ đồng/căn, villa 400-500 triệu đồng/m2, căn hộ triệu USD đang &acirc;m thầm xuất hiện theo nhiều c&aacute;ch, g&oacute;p phần thổi lửa v&agrave;o cuộc đua săn kh&aacute;ch nh&agrave; gi&agrave;u nhộn nhịp tại TP.HCM. Tuy nhi&ecirc;n, việc xuất hiện nhiều dự &aacute;n si&ecirc;u sang đang đẩy mặt bằng gi&aacute; b&aacute;n căn hộ l&ecirc;n cao. Chuẩn mực về căn hộ si&ecirc;u sang cũng đang bị x&ocirc; lệch bởi cuộc đua n&agrave;y.<br />\r\n<br />\r\n<strong>Từ ch&agrave;o b&aacute;n &acirc;m thầm đến ch&iacute;nh thức lộ diện</strong><br />\r\n<br />\r\nKh&ocirc;ng c&ograve;n ph&aacute;t triển &acirc;m thầm, mở b&aacute;n hạn chế, c&aacute;c doanh nghiệp địa ốc lớn đang cho thấy sự quyết liệt trong việc đầu tư dự &aacute;n si&ecirc;u sang. Cuộc chiến đất v&agrave;ng v&igrave; thế cũng trở n&ecirc;n gay gắt hơn.<br />\r\n<br />\r\nMới đ&acirc;y một dự &aacute;n si&ecirc;u sang tại v&ugrave;ng l&otilde;i trung t&acirc;m mới của TP.HCM c&ocirc;ng bố đ&atilde; ph&aacute; kỷ lục về mức gi&aacute; ch&agrave;o b&aacute;n ở Việt Nam. Tổng số căn hộ của dự &aacute;n n&agrave;y chưa tới 100 căn.<br />\r\n<br />\r\nMức gi&aacute; b&aacute;n dự kiến của dự &aacute;n căn hộ si&ecirc;u sang n&agrave;y dao động 8.000-10.000 USD/m2, c&aacute;n mốc tr&ecirc;n 200 triệu đồng/m2. Dự &aacute;n c&oacute; thể được c&ocirc;ng bố b&aacute;n trong năm 2017. Đ&acirc;y l&agrave; th&ocirc;ng tin m&agrave; đại diện của CBRE Việt Nam tiết lộ tại buổi c&ocirc;ng bố b&aacute;o c&aacute;o nghi&ecirc;n cứu thị trường BĐS TP.HCM qu&yacute; II mới đ&acirc;y.<br />\r\n<br />\r\nNếu mức gi&aacute; n&agrave;y ch&iacute;nh thức giao dịch sẽ đẩy mặt bằng gi&aacute; căn hộ cao cấp tại TP.HCM l&ecirc;n ngang với loại căn hộ cao cấp (luxury) ở Bangkok, dao động khoảng 7.000-10.000 USD/m2 v&agrave; thấp hơn một nửa so với Singapore (20.000-25.000 USD/m2).<br />\r\n<br />\r\nThực tế, ph&acirc;n kh&uacute;c si&ecirc;u cao cấp vẫn ph&aacute;t triển đều đặn, song mức gi&aacute; thật, th&ocirc;ng tin thị trường rất hiếm được c&ocirc;ng bố. Phần lớn dự &aacute;n n&agrave;y tận dụng c&aacute;c quỹ đất v&agrave;ng ở v&ugrave;ng trung t&acirc;m, v&agrave; gi&aacute; phổ biến khoảng 5.000-8.000 USD/m2.<br />\r\n<br />\r\nTheo b&aacute;o c&aacute;o mới nhất của CBRE Việt Nam, giới si&ecirc;u gi&agrave;u ở Việt Nam được dự b&aacute;o sẽ tăng mạnh trong 10 năm tới, n&ecirc;n những dự &aacute;n si&ecirc;u sang được đầu tư để kỳ vọng đ&oacute;n đầu nguồn cầu n&agrave;y.<br />\r\n<br />\r\nMột v&agrave;i doanh nghiệp BĐS c&oacute; t&ecirc;n tuổi đ&atilde; lựa chọn những khu đất &ldquo;kim cương&rdquo; để ph&aacute;t triển d&ograve;ng sản phẩm đặc biệt, c&oacute; thể kể tới như Sơn Kim Land, Novaland&hellip;<br />\r\n<br />\r\n<strong>R&aacute;o riết cuộc đua quanh đất v&agrave;ng</strong><br />\r\n<br />\r\nNgo&agrave;i si&ecirc;u dự &aacute;n c&oacute; mức gi&aacute; đạt đỉnh dự kiến mở b&aacute;n ngay năm nay, một dự &aacute;n phức hợp kh&aacute;c tọa lạc tại đường L&ecirc; Duẩn (quận 1), với căn hộ sở hữu c&oacute; gi&aacute; dự kiến 5.500 USD/m2 cũng đ&atilde; được c&ocirc;ng bố.<br />\r\n<br />\r\nTr&ecirc;n tuyến đường K&yacute; Con (quận 1), một đại gia trong l&agrave;ng BĐS cũng đang x&acirc;y dựng dự &aacute;n si&ecirc;u sang, gi&aacute; b&aacute;n căn hộ dự kiến l&ecirc;n đến 8.800 USD/m2.\r\n<div style=\"text-align:center\"><img alt=\"bất động sản tphcm\" src=\"/uploads/images/song-ngam-bat-dong-san-sieu-sang-tren-dat-vang-ho-chi-minh.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nNgo&agrave;i ra, t&ograve;a th&aacute;p c&oacute; vị tr&iacute; đắc địa tại phố H&agrave;m Nghi, kết nối với đường T&ocirc;n Đức Thắng v&agrave; Bến Chương Dương (quận 1) đ&igrave;nh trệ nhiều năm nhưng gi&aacute; b&aacute;n dự kiến cũng l&ecirc;n đến 7.800 USD/m2 khi chủ đầu tư mới khởi động lại.<br />\r\n<br />\r\nL&atilde;nh đạo một c&ocirc;ng ty c&oacute; thị phần lớn tại khu Đ&ocirc;ng S&agrave;i G&ograve;n tiết lộ doanh nghiệp đang ch&agrave;o b&aacute;n những &quot;si&ecirc;u biệt thự&quot; c&oacute; gi&aacute; 70-80 tỷ đồng/căn. Kh&aacute;ch h&agrave;ng mục ti&ecirc;u giai đoạn đầu l&agrave; CEO hoặc người điều h&agrave;nh c&aacute;c doanh nghiệp lớn, tổ chức t&agrave;i ch&iacute;nh uy t&iacute;n, c&aacute;c quỹ đầu tư ở Việt Nam. Qu&aacute; tr&igrave;nh ch&agrave;o b&aacute;n đầy b&iacute; ẩn v&agrave; hạn chế, v&agrave; số lượng sản phẩm của dự &aacute;n chỉ c&oacute; v&agrave;i chục căn nằm tr&ecirc;n một c&ugrave; lao y&ecirc;n tĩnh.<br />\r\n<br />\r\nCũng được ch&agrave;o b&aacute;n &acirc;m thầm ở giai đoạn chưa c&ocirc;ng bố ra thị trường, một dự &aacute;n nằm gần &quot;phố wall S&agrave;i G&ograve;n&quot; (Nguyễn C&ocirc;ng Trứ, quận 1) đ&atilde; xuất hiện giao dịch căn hộ trị gi&aacute; h&agrave;ng chục tỷ đồng của người nổi tiếng trong l&agrave;ng giải tr&iacute; Việt. Chủ đầu tư dự &aacute;n n&agrave;y từ chối tiết lộ th&ocirc;ng tin v&igrave; cam kết bảo mật.<br />\r\n<br />\r\nTọa lạc tại khu đất kim cương Ba Son, quận 1, TP HCM, dự &aacute;n Vinhomes Golden River c&ocirc;ng bố năm ngo&aacute;i cũng thu h&uacute;t h&agrave;ng ngh&igrave;n người tham dự, bất chấp gi&aacute; b&aacute;n dự kiến thuộc h&agrave;ng khủng nhất nh&igrave; TP.HCM. C&oacute; sản phẩm villa gi&aacute; 400-500 triệu đồng/m2 v&agrave; g&oacute;i gọn tr&ecirc;n dưới 60 sản phẩm.<br />\r\n<br />\r\nLần đầu tham gia ph&acirc;n kh&uacute;c si&ecirc;u sang, Novaland tr&igrave;nh l&agrave;ng dự &aacute;n Madison tại phường Bến Ngh&eacute;, quận 1. Gi&aacute; b&aacute;n căn hộ n&agrave;y dự kiến ở ngưỡng 5.700-6.800 USD/m2, tương đương 120-140 triệu đồng/m2.<br />\r\n<br />\r\nSi&ecirc;u dự &aacute;n của Sacomreal cũng đ&atilde; lộ diện trước sự săn đ&oacute;n r&aacute;o riết của giới bu&ocirc;n địa ốc. Theo giới thạo tin trong lĩnh vực BĐS, đ&acirc;y l&agrave; d&ograve;ng sản phẩm luxury đầu tay của doanh nghiệp, tọa lạc tại g&oacute;c L&yacute; Tự Trọng, Hai B&agrave; Trưng (quận 1), c&oacute; gi&aacute; b&aacute;n dự kiến 120 triệu đồng/m2.<br />\r\n<br />\r\nTheo đ&aacute;nh gi&aacute; của CBRE, tuy ở mức cao kỷ lục kể từ năm 2004, gi&aacute; ch&agrave;o b&aacute;n căn hộ hạng sang tại TP.HCM vẫn được liệt v&agrave;o dạng cạnh tranh nếu so với c&aacute;c th&agrave;nh phố lớn trong khu vực. Bởi gi&aacute; nh&agrave; luxury tại S&agrave;i G&ograve;n đang l&agrave; 3.500-7.000 USD/m2, ngưỡng n&agrave;y vẫn thấp hơn rất nhiều so với Singapore (20.000-25.000 USD/m2), Bangkok (7.000-10.000 USD/m2).<br />\r\n<br />\r\n<strong>Căn cứ n&agrave;o x&aacute;c định căn hộ si&ecirc;u sang?</strong><br />\r\n<br />\r\nTheo nhận định của giới chuy&ecirc;n m&ocirc;n, việc xuất hiện dự &aacute;n căn hộ hạng sang c&oacute; gi&aacute; 10.000 USD/m2 cho thấy bước tiến mới của BĐS Việt Nam. Thị trường đang hướng đến những ti&ecirc;u chuẩn cao hơn, mặt bằng gi&aacute; cũng cao gấp đ&ocirc;i mức trung b&igrave;nh hiện tại.<br />\r\n<br />\r\nTuy nhi&ecirc;n, chuy&ecirc;n gia CBRE cho rằng việc xuất hiện c&aacute;c dự &aacute;n với gi&aacute; đạt đỉnh c&oacute; thể t&aacute;c động tới mức gi&aacute; b&igrave;nh qu&acirc;n của to&agrave;n thị trường căn hộ hạng sang theo chiều hướng tăng l&ecirc;n. V&agrave; gi&aacute; n&agrave;y c&oacute; thể trở th&agrave;nh một chỉ số mới để so s&aacute;nh với c&aacute;c thị trường kh&aacute;c trong khu vực.<br />\r\n<br />\r\n&Ocirc;ng Stephen Wyatt, chuy&ecirc;n gia của Jones Lang LaShalle Việt Nam, nhận định đ&acirc;y l&agrave; bước tiến lớn của thị trường. Tuy nhi&ecirc;n, một dự &aacute;n si&ecirc;u sang kh&ocirc;ng phải chỉ căn cứ v&agrave;o gi&aacute; m&agrave; c&ograve;n phải đ&aacute;p ứng rất nhiều ti&ecirc;u chuẩn khắt khe kh&aacute;c.<br />\r\n<br />\r\nC&aacute;c ti&ecirc;u chuẩn kh&ocirc;ng thể thiếu trong bảng đ&aacute;nh gi&aacute; l&agrave; vị tr&iacute; đắc địa, đẳng cấp của chủ đầu tư, thương hiệu nh&agrave; thầu, số lượng sản phẩm &iacute;t, thiết kế, kh&acirc;u dịch vụ đi k&egrave;m. Bởi đ&acirc;y l&agrave; d&ograve;ng sản phẩm đặc biệt, v&agrave; l&agrave; ph&acirc;n kh&uacute;c rất k&eacute;n kh&aacute;ch.<br />\r\n<br />\r\nV&agrave; d&ugrave; c&aacute;ch tiếp cận kh&aacute;ch VIP c&oacute; doanh nghiệp c&ocirc;ng khai, người b&iacute; mật, c&aacute;c bất động sản si&ecirc;u sang đều c&oacute; điểm chung l&agrave; cực kỳ k&eacute;n chọn nh&agrave; đầu tư. Thanh khoản của những t&agrave;i sản n&agrave;y vẫn c&ograve;n l&agrave; dấu hỏi lớn.<br />\r\n<br />\r\n&quot;BĐS hạng sang l&agrave; h&agrave;ng h&oacute;a đặc biệt nhưng kh&ocirc;ng đi ngược lại quy luật cung cầu của thị trường. Chỉ v&agrave;i phần trăm d&acirc;n số đủ khả năng chi trả cho những si&ecirc;u biệt thự v&agrave; si&ecirc;u căn hộ, v&igrave; vậy, phải chờ xem phản ứng của thị trường&quot;, một chuy&ecirc;n gia BĐS thận trọng nhận x&eacute;t.', 0, NULL, NULL, 1, 0, 26, '2017-07-10 15:44:00', '2017-07-14 08:43:37', 2, 2),
(17, 'Bất động sản quận 8 đang dần trở thành điểm sáng nhờ hạ tầng', 'bat-dong-san-quan-8-dang-dan-tro-thanh-diem-sang-nho-ha-tang', 'Bat dong san quan 8 dang dan tro thanh diem sang nho ha tang', 'Quận 8 là một khu vực bị “bỏ quên”, nhờ chính sách chỉnh trang đô thị và cải tạo kênh rạch, khu vực quận 8 đang dần trở thành điểm sáng trên bản đồ bất động sản TP.HCM. Nhưng hiện tại, đã đi lên nhờ chính sách tốt.', '2017/07/10/bat-dong-san-quan-8-dang-dan-tro-thanh-diem-sang-nho-ha-tang-1499676894.png', 7, 'Nếu như trước đ&acirc;y, nhắc đến quận 8 người ta sẽ nghĩ ngay đến một khu vực bị chia cắt bởi hệ thống k&ecirc;nh rạch chằng chịt, th&igrave; nay ch&iacute;nh điểm n&agrave;y biến quận 8 trở th&agrave;nh v&ugrave;ng đất được nhiều nh&agrave; đầu tư săn đ&oacute;n.<br />\r\n<br />\r\nTheo chương tr&igrave;nh &ldquo;Chỉnh trang v&agrave; ph&aacute;t triển đ&ocirc; thị từ nay tới năm 2025&rdquo;, TP.HCM sẽ di dời v&agrave; giải tỏa trắng gần 20.000 hộ tr&ecirc;n v&agrave; ven k&ecirc;nh rạch, tổ chức lại cuộc sống d&acirc;n cư tốt hơn, gắn với chỉnh trang đ&ocirc; thị dọc hai b&ecirc;n bờ k&ecirc;nh rạch, tạo bộ mặt mới cho TP.HCM hội nhập. Kế hoạch di dời sẽ được tiến h&agrave;nh qua 2 giai đoạn.<br />\r\n<br />\r\n<img alt=\"\" src=\"/uploads/images/bat-dong-san-quan-8-dang-dan-tro-thanh-diem-sang-nho-ha-tang.png\" style=\"height:400px; width:650px\" /><br />\r\n<br />\r\nGiai đoạn đầu từ năm 2015 - 2020, tập trung giải tỏa di dời 9.805 căn nh&agrave; ven k&ecirc;nh rạch. Giai đoạn 2 từ năm 2020 - 2025, ho&agrave;n th&agrave;nh mục ti&ecirc;u di dời 19.524 căn nh&agrave; v&agrave; tổ chức lại cuộc sống của người d&acirc;n đang sống tr&ecirc;n v&agrave; ven k&ecirc;nh rạch.<br />\r\n<br />\r\nB&ecirc;n cạnh sự thay đổi nhanh ch&oacute;ng của hạ tầng giao th&ocirc;ng l&agrave; sự thay da đổi thịt của hạ tầng x&atilde; hội với hệ thống bệnh viện, trường học, si&ecirc;u thị, đ&aacute;p ứng mọi nhu cầu từ học tập, l&agrave;m việc, thư gi&atilde;n.<br />\r\n<br />\r\nGiới đầu tư cho rằng, sự ph&aacute;t triển mạnh mẽ của hạ tầng khu vực, cộng với ch&iacute;nh s&aacute;ch cải tạo nh&agrave; ven k&ecirc;nh rạch đang được quan t&acirc;m đầu tư, khu vực quận 8 đang trở th&agrave;nh t&acirc;m điểm của c&aacute;c nh&agrave; đầu tư bất động sản cũng như người mua nh&agrave;. Nhất l&agrave; hiện nay, những dự &aacute;n nh&agrave; ở c&oacute; mặt view s&ocirc;ng, hướng s&ocirc;ng đang l&agrave; xu thế được kh&aacute;ch h&agrave;ng ưa chọn n&ecirc;n những khu vực n&agrave;y được c&aacute;c doanh nghiệp địa ốc săn đ&oacute;n.<br />\r\n<br />\r\n<strong>Nhận diện &ldquo;điểm n&oacute;ng&rdquo; tại quận 8</strong><br />\r\n<br />\r\nVới sự ph&aacute;t triển ngoạn mục, kh&ocirc;ng ngạc nhi&ecirc;n khi quận 8 gần như trở th&agrave;nh điểm nhấn mới tr&ecirc;n thị trường bất động sản Th&agrave;nh phố. Đ&atilde; c&oacute; h&agrave;ng chục dự &aacute;n được đầu tư x&acirc;y dựng ở khu vực n&agrave;y từ những doanh nghiệp địa ốc lớn như Novaland, Ph&uacute;c Khang, Đức Long Gia Lai, C&ocirc;ng ty Th&aacute;i Bảo, C&ocirc;ng ty Phương Việt hay C&ocirc;ng ty May Song Ngọc với c&aacute;c dự &aacute;n Harbor City, Diamond Lotus, Đức Long New Land, Pega Suite, Avila... đặc biệt l&agrave; Tara Residence.<br />\r\n<br />\r\nTara Residence sở hữu vị tr&iacute; đắc địa ngay trung t&acirc;m quận 8, một trong những khu vực đang được th&agrave;nh phố đầu tư v&agrave; ph&aacute;t triển mạnh về hạ tầng giao th&ocirc;ng, c&aacute;c c&ocirc;ng tr&igrave;nh tiện &iacute;ch x&atilde; hội như trường học, bệnh viện, khu vui chơi giải tr&iacute;&hellip;<br />\r\n<br />\r\nĐược thừa hưởng được tất cả những lợi thế của khu vực quận 8 n&ecirc;n Tara Residence được xem l&agrave; lựa chọn ưu ti&ecirc;n cho những nh&agrave; đầu tư v&agrave; cũng l&agrave; nơi an cư l&yacute; tưởng cho cư d&acirc;n đang c&oacute; nhu cầu nh&agrave; ở tại khu vực n&agrave;y.<br />\r\n<br />\r\nTọa lạc tại số 1A Tạ Quang Bửu, Phường 6, Quận 8, TP.HCM, từ Tara Residence, cư d&acirc;n chỉ cần 5 ph&uacute;t để đến Quận 5, chợ An Đ&ocirc;ng qua tuyến đường Phạm H&ugrave;ng, Nguyễn Tri Phương, mất 15 ph&uacute;t để đến quận 1 Chợ Bến Th&agrave;nh qua cầu chữ Y v&agrave; cũng với 5 ph&uacute;t l&agrave; c&oacute; thể đển ngay trung t&acirc;m Quận 7, Trung Sơn, Him Lam...<br />\r\n<br />\r\nĐược x&acirc;y dựng tr&ecirc;n khu đất c&oacute; tổng diện t&iacute;ch 15.432m2 với mật độ x&acirc;y dựng 40%, Tara Residence gồm 4 block chung cư cao 20 tầng, 1 tầng hầm với tổng cộng 989 căn hộ c&oacute; gi&aacute; b&aacute;n hợp l&yacute;.<br />\r\n<br />\r\nKh&ocirc;ng chỉ c&oacute; gi&aacute; b&aacute;n hợp l&yacute;, Tara Residence c&ograve;n mang đạm dấu ấn ri&ecirc;ng với cảm hứng về một S&agrave;i G&ograve;n xưa lu&ocirc;n hiện hữu trong dự &aacute;n, giữa l&ograve;ng th&agrave;nh phố trẻ ồn &agrave;o, dự &aacute;n mang đậm n&eacute;t xưa cũ nhưng kh&ocirc;ng k&eacute;m phần hiện đại của S&agrave;i G&ograve;n 50 năm trước. Những căn hộ thiết kế theo phong c&aacute;ch retro kh&ocirc;ng chỉ gợi nhắc nhiều kỷ niệm m&agrave; c&ograve;n cho cư d&acirc;n kh&ocirc;ng gian thỏa sức sắp đặt v&agrave; s&aacute;ng tạo nội thất.<br />\r\n<br />\r\nC&aacute;c căn hộ tại đ&acirc;y được bố tr&iacute; từ 1- 3 ph&ograve;ng ngủ, được thiết kế hợp l&yacute;, đảm bảo độ th&ocirc;ng tho&aacute;ng. Đặc biệt, dự &aacute;n nằm trọn trong một quần thể c&ocirc;ng vi&ecirc;n c&acirc;y xanh với hệ thống tiện &iacute;ch c&ocirc;ng cộng ho&agrave;n chỉnh như cửa h&agrave;ng, hồ bơi tr&agrave;n, khu c&ocirc;ng vi&ecirc;n rộng, khu vui chơi cho trẻ nhỏ, cafe ngo&agrave;i trời, quảng trường nội khu&hellip;<br />\r\n<br />\r\nGiới kinh doanh bất động sản cho rằng, với vị tr&iacute; đắc địa, hệ thống hạ tầng đang được đầu tư ho&agrave;n chỉnh, d&acirc;n cư đ&ocirc;ng đ&uacute;c v&agrave; gi&aacute; b&aacute;n c&ograve;n kh&aacute; mềm so với c&aacute;c khu vực kh&aacute;c, khi ho&agrave;n th&agrave;nh chương tr&igrave;nh chỉnh trang đ&ocirc; thị, nh&agrave; đất quận 8 c&oacute; nhiều cơ hội tăng gi&aacute; trong tương lai.<br />\r\n<br />\r\nNhận diện được tiềm năng n&agrave;y, quận 8 đang trở th&agrave;nh điểm ngắm của những người c&oacute; nhu cầu về nh&agrave; ở, giới đầu tư, nhất l&agrave; trong bối cảnh gi&aacute; bất động sản ở c&aacute;c khu d&acirc;n cư tại c&aacute;c tuyến đường ch&iacute;nh như Hưng Ph&uacute;, Phạm H&ugrave;ng, Nguyễn Duy... gần đ&acirc;y tăng kh&aacute; mạnh.', 0, NULL, NULL, 1, 0, 27, '2017-07-10 15:55:26', '2017-07-14 08:43:54', 2, 2),
(19, 'Nghề môi giới bất động sản không hề dễ giàu', 'nghe-moi-gioi-bat-dong-san-khong-he-de-giau', 'Nghe moi gioi bat dong san khong he de giau', 'Việc hoa hồng lớn khiến môi giới bất động sản trở thành nghề hot, thu hút hàng chục nghìn người tham gia, đặc biệt trong bối cảnh thị trường đang sôi động như hiện nay. Tuy nhiên, làm giàu từ nghề môi giới bất động sản không dễ dàng như nhiều người tưởng', '2017/07/10/nghe-bat-dong-san-1499678280.png', 7, 'D&ugrave; nh&acirc;n vi&ecirc;n m&ocirc;i giới kh&ocirc;ng c&oacute; lương cứng, hoặc chỉ nhận mức trợ cấp xăng xe, nhưng nhiều người vẫn rỉ tai nhau &ldquo;muốn l&agrave;m gi&agrave;u nhanh m&agrave; kh&ocirc;ng cần vốn, h&atilde;y l&agrave;m m&ocirc;i giới bất động sản&rdquo;. Thu nhập ch&iacute;nh của nh&acirc;n vi&ecirc;n m&ocirc;i giới đến từ hoa hồng được trả cho mỗi thương vụ th&agrave;nh c&ocirc;ng. T&ugrave;y v&agrave;o ch&iacute;nh s&aacute;ch mỗi c&ocirc;ng ty, thường khoản ph&iacute; hoa hồng n&agrave;y dao động từ 2 - 5% tr&ecirc;n gi&aacute; trị bất động sản.<br />\r\n&nbsp;\r\n<div style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/nghe-bat-dong-san.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nChỉ cần l&agrave;m ph&eacute;p t&iacute;nh nho nhỏ, mỗi th&aacute;ng, nếu nh&acirc;n vi&ecirc;n m&ocirc;i giới bất động sản b&aacute;n th&agrave;nh c&ocirc;ng 1 căn nh&agrave; 1 tỷ đồng, với mức hoa hồng 2 - 5%, đ&atilde; c&oacute; thu nhập từ 30 - 50 triệu đồng. Đ&acirc;y l&agrave; con số mơ ước với nhiều người, nhất l&agrave; với những sinh vi&ecirc;n mới ra trường.<br />\r\n<br />\r\nĂn mặc đẹp, d&ugrave;ng đồ xịn, m&ocirc;i giới bất động sản trở th&agrave;nh nghề nghiệp đầy mơ ước của c&aacute;c bạn trẻ. Thế nhưng, đ&oacute; chỉ l&agrave; phần nổi, thực tế nghề m&ocirc;i giới bất động sản kh&ocirc;ng dễ.<br />\r\n<br />\r\nNgo&agrave;i một số m&ocirc;i giới c&oacute; nghề, c&oacute; tập kh&aacute;ch h&agrave;ng tốt, th&igrave; đa số những nh&acirc;n vi&ecirc;n m&ocirc;i giới kh&aacute;c, nhất l&agrave; nh&acirc;n vi&ecirc;n mới v&agrave;o nghề rất chật vật, d&ugrave; thời điểm thị trường s&ocirc;i động.<br />\r\n<br />\r\nĐể c&oacute; thể b&aacute;n được sản phẩm, đủ định mức được giao, c&aacute;c nh&acirc;n vi&ecirc;n m&ocirc;i giới phải tỏa đi t&igrave;m khắp nơi, d&ugrave;ng đủ mọi phương ph&aacute;p để tiếp cận những kh&aacute;ch h&agrave;ng mục ti&ecirc;u. Tuy nhi&ecirc;n, việc kh&ocirc;ng được đ&agrave;o tạo b&agrave;i bản về c&aacute;c nghiệp vụ, dự &aacute;n khiến nhiều nh&acirc;n vi&ecirc;n m&ocirc;i giới bị động khi tiếp cận những kh&aacute;ch h&agrave;ng mục ti&ecirc;u.<br />\r\n<br />\r\nTheo &ocirc;ng Nguyễn Văn Đ&iacute;nh, Ph&oacute; chủ tịch ki&ecirc;m Tổng thư k&yacute; Hội m&ocirc;i giới bất động sản Việt Nam, ở c&aacute;c quốc gia kh&aacute;c, mấu chốt th&agrave;nh c&ocirc;ng của nghề m&ocirc;i giới bất động sản ch&iacute;nh từ sản phẩm. Hầu hết m&ocirc;i giới vi&ecirc;n d&agrave;nh rất nhiều thời gian để nghi&ecirc;n cứu, đ&aacute;nh gi&aacute; sản phẩm v&agrave; khảo s&aacute;t kh&aacute;ch h&agrave;ng mục ti&ecirc;u trước khi tiếp cận đối tượng để đ&agrave;m ph&aacute;n ch&agrave;o b&aacute;n dự &aacute;n.<br />\r\n<br />\r\nTrong khi đ&oacute;, ở Việt Nam, nhiều nh&acirc;n vi&ecirc;n m&ocirc;i giới mới v&agrave;o nghề đ&atilde; phải đi ch&agrave;o b&aacute;n dự &aacute;n m&agrave; kh&ocirc;ng hề được đ&agrave;o tạo b&agrave;i bản về dự &aacute;n, chỉ nắm th&ocirc;ng tin qua m&ocirc;i giới kh&aacute;c. Ngo&agrave;i ra, nhiều nh&acirc;n vi&ecirc;n chủ yếu thời gian để đi ph&aacute;t tờ rơi, hay ngồi nh&agrave; gọi điện thoại ch&agrave;o mời tới những kh&aacute;ch h&agrave;ng trong danh s&aacute;ch sẵn của C&ocirc;ng ty.<br />\r\n<br />\r\nPh&oacute;ng vi&ecirc;n B&aacute;o Đầu tư Bất động sản từng nhận kh&ocirc;ng &iacute;t cuộc gọi từ c&aacute;c nh&acirc;n vi&ecirc;n m&ocirc;i giới kiểu n&agrave;y để ch&agrave;o b&aacute;n căn hộ dự &aacute;n với những lời lẽ rất dập khu&ocirc;n, như gi&aacute; cả hợp l&yacute;, tiện &iacute;ch ph&ugrave; hợp. Tuy nhi&ecirc;n, khi ph&oacute;ng vi&ecirc;n hỏi lại l&agrave; tiện &iacute;ch n&agrave;o v&agrave; ra sao, th&igrave; &quot;tậm tịt&quot;. Ngay cả tuyến đường v&agrave;o dự &aacute;n đi như thế n&agrave;o, c&oacute; những tuyến đường n&agrave;o kh&aacute;c v&agrave;o dự &aacute;n hay kh&ocirc;ng, c&aacute;c nh&acirc;n vi&ecirc;n n&agrave;y cũng gần như kh&ocirc;ng biết, m&agrave; chỉ n&oacute;i rằng chủ đầu tư đang xin thủ tục mở rộng đường v&agrave;o dự &aacute;n, hay sau n&agrave;y khi ho&agrave;n thiện sẽ c&oacute; tuyến đường nối từ trục ch&iacute;nh v&agrave;o dự &aacute;n&hellip;<br />\r\n<br />\r\nCh&iacute;nh những nh&acirc;n vi&ecirc;n m&ocirc;i giới thiếu chuy&ecirc;n nghiệp n&agrave;y đ&atilde; l&agrave;m ảnh hưởng tới h&igrave;nh ảnh của nghề m&ocirc;i giới n&oacute;i chung, vốn l&agrave; nghề quan trọng trong ng&agrave;nh bất động sản.<br />\r\n<br />\r\nNgo&agrave;i những nh&acirc;n vi&ecirc;n thiếu chuy&ecirc;n nghiệp, hoạt động như những &ldquo;c&ograve;&rdquo; bất động sản tự do, th&igrave; t&igrave;nh trạng nh&acirc;n vi&ecirc;n m&ocirc;i giới bất động sản, thậm ch&iacute; l&agrave; cả s&agrave;n m&ocirc;i giới &ldquo;k&ecirc; gi&aacute; ăn ch&ecirc;nh lệch&rdquo; tiền từ kh&aacute;ch h&agrave;ng cũng ảnh hưởng xấu tới h&igrave;nh ảnh của nghề.<br />\r\n<br />\r\nBản chất của nghề m&ocirc;i giới l&agrave; nghề &ldquo;phục vụ&rdquo; v&agrave; được hưởng th&ugrave; lao từ ch&iacute;nh tiền hoa hồng trong hợp đồng m&ocirc;i giới theo luật định với người b&aacute;n. Tuy nhi&ecirc;n, do ham muốn lợi nhuận cao, một số m&ocirc;i giới cố t&iacute;nh k&ecirc; gi&aacute;, đẩy gi&aacute; sản phẩm l&ecirc;n cao hơn so với gi&aacute; b&aacute;n của chủ đầu tư để kiếm lời.<br />\r\n<br />\r\nViệc l&agrave;m n&agrave;y kh&ocirc;ng chỉ ảnh hưởng tới h&igrave;nh ảnh của nghề m&ocirc;i giới, m&agrave; c&ograve;n ảnh hưởng chung tới sự ph&aacute;t triển bền vững của thị trường bất động sản, bởi n&oacute; l&agrave;m mất l&ograve;ng tin của kh&aacute;ch h&agrave;ng.<br />\r\n<br />\r\n&Ocirc;ng Phạm Thanh Hưng, Ph&oacute; chủ tịch CENGroup cho biết, hiện nay, một số chuy&ecirc;n vi&ecirc;n m&ocirc;i giới y&ecirc;u nghề, cầu thị thật sự mới &yacute; thức được gi&aacute; trị của sự th&agrave;nh c&ocirc;ng trong nghề kh&ocirc;ng hề dễ d&agrave;ng. Họ lu&ocirc;n cố gắng ho&agrave;n thiện c&aacute;c kỹ năng chuy&ecirc;n m&ocirc;n v&agrave; kh&ocirc;ng ngừng học hỏi để ph&aacute;t triển.<br />\r\n<br />\r\nTuy nhi&ecirc;n, kh&ocirc;ng phải tất cả mọi người đều &yacute; thức được điều đ&oacute;. C&ograve;n rất nhiều người c&oacute; nhận thức sai về ng&agrave;nh nghề n&agrave;y, khiến tr&igrave;nh độ của m&ocirc;i giới bất động sản Việt Nam c&ograve;n một khoảng c&aacute;ch kh&aacute; xa so với đồng nghiệp ở c&aacute;c nước trong khu vực.', 0, NULL, NULL, 1, 0, 29, '2017-07-10 16:18:24', '2017-07-14 08:44:21', 2, 2);
INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(20, 'Đua nhau đặt mục tiêu lãi nghìn tỷ của các đại gia bất động sản', 'dua-nhau-dat-muc-tieu-lai-nghin-ty-cua-cac-dai-gia-bat-dong-san', 'Dua nhau dat muc tieu lai nghin ty cua cac dai gia bat dong san', 'Nhiều công ty địa ốc bất ngờ công bố lợi nhuận đầy tham vọng, cán mốc hàng nghìn tỷ đồng trong năm 2017 và một vài năm tới.  Vingroup (mã CK: VIC) là một trong số ít tập đoàn địa ốc không gây bất ngờ với mục tiêu lãi khủng. Doanh nghiệp lên kế hoạch doanh thu thuần 80.000 tỷ đồng, lợi nhuận sau thuế 3.000 tỷ trong năm nay.', '2017/07/10/bat-dong-san-dua-nhau-dat-muc-tieu-lai-nghin-ty-1499678792.png', 7, '&Iacute;t yếu tố bất ngờ bởi lẽ, quy m&ocirc; đa ng&agrave;nh với hệ sinh th&aacute;i bất động sản của tập đo&agrave;n n&agrave;y kh&aacute; d&agrave;y đặc. Năm 2017, VIC hướng đến sự bứt ph&aacute; tr&ecirc;n nhiều lĩnh vực: bất động sản, du lịch v&agrave; vui chơi giải tr&iacute;, b&aacute;n lẻ, y tế, gi&aacute;o dục v&agrave; n&ocirc;ng nghiệp. Ngo&agrave;i ra, VIC c&ograve;n ph&aacute;t triển d&ograve;ng bất động sản đại ch&uacute;ng mới mang thương hiệu VinCity.&nbsp;\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"\" src=\"/uploads/images/bat-dong-san-dua-nhau-dat-muc-tieu-lai-nghin-ty.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nNgoại trừ đại gia VIC, phần c&ograve;n lại của thị trường địa ốc c&oacute; kh&aacute; nhiều doanh nghiệp g&acirc;y sốc với kế hoạch lợi nhuận chưa từng c&oacute; từ trước tới nay. Điển h&igrave;nh l&agrave; C&ocirc;ng ty cổ phần Quốc Cường Gia Lai (m&atilde; CK: QCG) c&ocirc;ng bố mức lợi nhuận kỳ vọng trong 3 năm tới (2018-2020) l&ecirc;n đến 6.000 tỷ đồng. Kế hoạch kinh doanh n&agrave;y được giới đầu tư đ&aacute;nh gi&aacute; nếu đạt được sẽ l&agrave; những kỷ lục mở ra chu kỳ mới của c&ocirc;ng ty v&igrave; non nửa thập ni&ecirc;n qua, QCG chỉ lẹt đẹt b&aacute;o l&atilde;i từ dăm bảy tỷ đồng đến v&agrave;i chục tỷ đồng mỗi năm.<br />\r\n<br />\r\nTại Đại hội cổ đ&ocirc;ng thường ni&ecirc;n QCG năm 2017, Chủ tịch HĐQT Nguyễn Thị Như Loan cho biết trừ năm 2017 doanh thu của c&ocirc;ng ty ước đạt 2.500 tỷ đồng, lợi nhuận trước thuế dự kiến 720 tỷ. C&aacute;c năm tiếp theo mức l&atilde;i sẽ cực kỳ ho&agrave;nh tr&aacute;ng. B&agrave; Loan hồ hởi chia sẻ với cổ đ&ocirc;ng năm 2018, lợi nhuận trước thuế của c&ocirc;ng ty c&oacute; thể c&aacute;n mốc 2.000 tỷ đồng. Giai đoạn 2019-2020 QCG cũng đặt mục ti&ecirc;u 2.000 tỷ mỗi năm. &ldquo;T&ocirc;i chắc chắn sẽ l&agrave;m được nếu thị trường giữ nhịp độ như hiện nay, kh&ocirc;ng rơi v&agrave;o khủng hoảng&quot;, b&agrave; Loan n&oacute;i.<br />\r\n<br />\r\nThậm ch&iacute;, sau kỳ đại hội, trả lời chất vấn của b&aacute;o ch&iacute; liệu kế hoạch kinh doanh c&oacute; qu&aacute; tham vọng, Ph&oacute; tổng gi&aacute;m đốc Quốc Cường Gia Lai, Nguyễn Quốc Cường c&ograve;n khẳng định: &ldquo;Mức lợi nhuận trước thuế 6.000 tỷ đồng cho 3 năm tới đ&atilde; l&agrave; mục ti&ecirc;u khi&ecirc;m tốn nhất&rdquo;. Theo &ocirc;ng Cường, QCG từ năm 2017 đ&atilde; đạt được sự c&acirc;n bằng t&agrave;i ch&iacute;nh nhờ trả xong khoản nợ v&agrave; l&atilde;i hơn 1.300 tỷ đồng cho BIDV v&agrave; sẵn s&agrave;ng cho những c&uacute; &quot;đại nhảy vọt&quot; sắp tới.<br />\r\n<br />\r\nMột đại gia địc ốc mới ni&ecirc;m yết tại s&agrave;n HOSE trong năm nay l&agrave; Novaland (m&atilde; CK: NVL) cũng g&acirc;y ấn tượng mạnh cho giới đầu tư v&igrave; kế hoạch kinh doanh cực kỳ s&aacute;n lạn. Cụ thể, năm 2017, theo kế hoạch ban đầu NVL dự kiến doanh thu đạt khoảng 17.528 tỷ đồng v&agrave; lợi nhuận sau thuế ước đạt 3.144 tỷ đồng. Cơ sở để đơn vị n&agrave;y mạnh dạn nhắm đến doanh số khủng v&igrave; kh&aacute; nhiều dự &aacute;n b&agrave;n giao trong năm 2017 v&agrave; bắt đầu ghi nhận doanh thu.<br />\r\n<br />\r\nMột số doanh nghiệp địa ốc kh&aacute;c tuy mục ti&ecirc;u c&oacute; thấp hơn đ&ocirc;i ch&uacute;t nhưng cũng tạo c&uacute; h&iacute;ch lớn cho giới đầu tư bằng bảng kế hoạch lợi nhuận đầy lạc quan cho một v&agrave;i năm tới. Đ&oacute; l&agrave; trường hợp của C&ocirc;ng ty Ph&aacute;t triển Đ&ocirc; thị Kinh Bắc (m&atilde; CK: KBC), mục ti&ecirc;u doanh thu v&agrave; lợi nhuận sau thuế năm 2017 đạt lần lượt 2.800 tỷ đồng v&agrave; 850 tỷ đồng, c&aacute;ch ngưỡng l&atilde;i ngh&igrave;n tỷ kh&ocirc;ng xa. C&aacute;c mục ti&ecirc;u n&agrave;y được ban l&atilde;nh đạo đề ra chủ yếu tr&ecirc;n cơ sở b&aacute;n đất tại khu c&ocirc;ng nghiệp Quang Ch&acirc;u, Nam Sơn Hạp Linh v&agrave; Tr&agrave;ng Duệ 2. Ngo&agrave;i ra nguồn thu c&ograve;n đến từ việc b&aacute;n nh&agrave; xưởng tại khu c&ocirc;ng nghiệp Quế V&otilde; v&agrave; b&aacute;n đất tại dự &aacute;n Ph&uacute;c Ninh (6,27ha).<br />\r\n<br />\r\nRi&ecirc;ng Tập đo&agrave;n Đất Xanh (m&atilde; CK: DXG) c&ograve;n c&oacute; những kế hoạch tăng trưởng doanh thu v&agrave; lợi nhuận xếp v&agrave;o h&agrave;ng &ldquo;như mơ&rdquo;. Năm nay doanh nghiệp th&ocirc;ng qua kế hoạch doanh thu thuần 3.300 tỷ đồng v&agrave; l&atilde;i r&ograve;ng 700 tỷ. Kh&ocirc;ng dừng lại ở đ&oacute;, DXG hướng đến mục ti&ecirc;u lợi nhuận ngh&igrave;n tỷ từ năm 2018 trở đi. Cụ thể, năm 2018, kế hoạch l&atilde;i 1.000 tỷ đồng v&agrave; năm 2022 phấn đấu nộp thuế 1.000 tỷ, tức l&atilde;i khoảng 3.000 tỷ đồng. Dự kiến đến đầu năm 2019, vốn h&oacute;a DXG đạt một tỷ USD.<br />\r\n<br />\r\nThứ nhất, kể từ năm 2014 đến nay, tổng cộng gần 4 năm thị trường bất động sản hồi phục sau khủng hoảng. Thanh khoản của thị trường li&ecirc;n tục s&ocirc;i động, gi&aacute; b&aacute;n tăng cao nhưng sức mua cũng lớn. Căn cứ v&agrave;o đ&agrave; tăng trưởng li&ecirc;n tục những năm qua, kh&aacute; nhiều doanh nghiệp đ&atilde; bước v&agrave;o giai đoạn b&agrave;n giao nh&agrave;, đủ điều kiện ghi nhận doanh thu v&agrave; lợi nhuận. Đ&acirc;y l&agrave; một trong những cơ sở ch&iacute;nh, x&aacute;c đ&aacute;ng dẫn đến c&aacute;c mục ti&ecirc;u lợi nhuận ngh&igrave;n tỷ của doanh nghiệp địa ốc.<br />\r\n<br />\r\nThứ hai, thị trường bất động sản ph&aacute;t triển dựa tr&ecirc;n kỳ vọng tương lai kh&aacute; lớn: hạ tầng h&igrave;nh th&agrave;nh theo quy hoạch d&agrave;i hạn, nh&agrave; ở h&igrave;nh th&agrave;nh trong tương lai. Kh&ocirc;ng &iacute;t doanh nghiệp vẫn đặt niềm tin thị trường đang trong giai đoạn tăng trưởng, chưa c&oacute; dấu hiệu bong b&oacute;ng hay khủng hoảng. Do đ&oacute;, c&aacute;c kết quả doanh thu, lợi nhuận ngh&igrave;n tỷ/năm được x&acirc;y dựng tr&ecirc;n cơ sở lạc quan, t&iacute;ch cực khi thị trường vẫn tiếp tục ph&aacute;t triển tốt. Tuy nhi&ecirc;n, cơ sở n&agrave;y mang t&iacute;nh chủ quan, cảm t&iacute;nh nhiều hơn.<br />\r\n<br />\r\nThứ ba, mục ti&ecirc;u kinh doanh khủng c&oacute; thể l&agrave; &ldquo;b&aacute;nh vẽ&rdquo; để giảm &aacute;p lực với cổ đ&ocirc;ng, l&agrave;m h&agrave;i l&ograve;ng nh&agrave; đầu tư tạm thời trong kỳ đại hội. C&aacute;c doanh nghiệp c&ograve;n c&oacute; thể d&ugrave;ng những kế hoạch lợi nhuận ấn tượng như một thủ thuật k&iacute;ch đ&ograve;n bẩy t&agrave;i ch&iacute;nh, lấy đ&oacute; l&agrave;m cơ sở huy động vốn qua k&ecirc;nh ph&aacute;t h&agrave;nh tr&aacute;i phiếu, cổ phiếu tr&ecirc;n s&agrave;n chứng kho&aacute;n. Kế hoạch kinh doanh c&agrave;ng đẹp chừng n&agrave;o th&igrave; cơ hội huy động vốn c&agrave;ng khả thi chừng nấy.', 0, NULL, NULL, 1, 0, 30, '2017-07-10 16:26:52', '2017-07-14 08:45:01', 2, 2),
(21, 'Bất động sản quận 8 phát triển mạnh nhờ quy hoạch', 'bat-dong-san-quan-8-phat-trien-manh-nho-quy-hoach', 'Bat dong san quan 8 phat trien manh nho quy hoach', 'Bất động sản quận 8 sẽ hưởng lợi trong thời gian tới khi các dự án cải tạo, nâng cấp đô thị được triển khai.\r\nĐại diện Samland cho biết, theo định hướng quy hoạch của thành phố, thời gian tới các kênh rạch trên địa bàn quận 8 sẽ được giải tỏa để xây dựng công viên, mở thêm đường và nhiều cây cầu kết nối với quận 4, quận 5, quận 6', '2017/07/10/bat-dong-san-dua-nhau-dat-muc-tieu-lai-nghin-ty-1499679382.png', 7, 'V&igrave; thế, nhiều tập đo&agrave;n lớn đ&atilde; &quot;nhắm&quot; đến quận 8 v&agrave; chuẩn bị sẵn quỹ đất để ph&aacute;t triển dự &aacute;n căn hộ cao cấp.&nbsp;\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n\r\n<div style=\"text-align:center\"><img alt=\"\" src=\"/uploads/images/bat-dong-san-dua-nhau-dat-muc-tieu-lai-nghin-ty%281%29.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nTrong khi đ&oacute;, nhiều kh&aacute;ch h&agrave;ng c&oacute; nhu cầu nh&agrave; ở cũng quan t&acirc;m đến c&aacute;c dự &aacute;n tr&ecirc;n địa b&agrave;n quận 8 do gi&aacute; b&aacute;n c&ograve;n thấp so với c&aacute;c khu vực kh&aacute;c nhưng tiềm năng ph&aacute;t triển lớn.<br />\r\n<br />\r\nTheo khảo s&aacute;t của Samland, những dự &aacute;n đ&atilde; b&agrave;n giao nh&agrave; v&agrave; c&oacute; sổ hồng được đ&ocirc;ng đảo kh&aacute;ch h&agrave;ng săn l&ugrave;ng. Gi&aacute; căn hộ tại quận 8 đang xoay quanh mức 20-27 triệu đồng một m2 trong khi quận 5 chỉ c&aacute;ch một con k&ecirc;nh nhưng gi&aacute; c&oacute; thể l&ecirc;n đến 50-60 triệu đồng một m2. Tại quận 7, d&ugrave; giao th&ocirc;ng v&agrave;o trung t&acirc;m th&agrave;nh phố c&ograve;n nhiều kh&oacute; khăn nhưng gi&aacute; căn hộ cũng từ 40 đến 70 triệu đồng một m2.<br />\r\n<br />\r\n&quot;Yếu tố n&agrave;y c&agrave;ng củng cố niềm tin của giới đầu tư rằng bất động sản quận 8 sẽ bứt ph&aacute; mạnh khi c&aacute;c dự &aacute;n cải tạo, n&acirc;ng cấp đ&ocirc; thị được triển khai. Nhiều chuy&ecirc;n gia cũng dự b&aacute;o sắp tới thị trường sẽ chứng kiến một cuộc chạy đua đầu tư v&agrave;o quận 8 giữa c&aacute;c doanh nghiệp bất động sản&quot;, &ocirc;ng Phạm Ngọc T&ugrave;ng, Tổng gi&aacute;m đốc Samland cho biết.<br />\r\n<br />\r\nKhảo s&aacute;t gần đ&acirc;y của Samland cũng cho thấy, nhiều kh&aacute;ch h&agrave;ng đang c&oacute; xu hướng lựa chọn căn hộ diện t&iacute;ch lớn thay v&igrave; mua nh&agrave; phố. Một nghi&ecirc;n cứu của CBRE Việt Nam cho biết, nửa đầu năm 2017 c&oacute; đến 44% số kh&aacute;ch h&agrave;ng mua những căn hộ c&oacute; diện t&iacute;ch lớn 3-4 ph&ograve;ng ngủ.<br />\r\n<br />\r\nSamland l&yacute; giải, &nbsp;nh&agrave; phố c&oacute; diện t&iacute;ch nhỏ, nằm trong c&aacute;c con hẻm s&acirc;u, thiếu tiện &iacute;ch v&agrave; kh&ocirc;ng đảm bảo an ninh. Nhưng nếu mua căn hộ với số tiền tương đương, ngo&agrave;i kh&ocirc;ng gian sống hiện đại, rộng tho&aacute;ng, kh&aacute;ch h&agrave;ng c&oacute; thể an t&acirc;m về an ninh khi được bảo vệ 24/24h v&agrave; sử dụng nhiều tiện nghi hiện đại ngay trong khu ở, từ trường học, c&ocirc;ng vi&ecirc;n, hồ bơi đến nh&agrave; h&agrave;ng, qu&aacute;n c&agrave; ph&ecirc;, si&ecirc;u thị, ph&ograve;ng kh&aacute;m y tế...<br />\r\n<br />\r\nDự &aacute;n Samland Giai Việt của Samland đ&aacute;p ứng nhu cầu của kh&aacute;ch h&agrave;ng nhờ tiện &iacute;ch đầy đủ, giao th&ocirc;ng thuận tiện, diện t&iacute;ch căn hộ lớn. Nằm ngay giao lộ Tạ Quang Bửu - Phạm H&ugrave;ng (trung t&acirc;m quận 8), dự &aacute;n thu h&uacute;t nhiều kh&aacute;ch h&agrave;ng quan t&acirc;m.<br />\r\n<br />\r\nSamland Giai Việt gồm 222 căn hộ cao cấp bố tr&iacute; 2-4 ph&ograve;ng ngủ, hệ thống tiện &iacute;ch kh&eacute;p k&iacute;n v&agrave; đ&atilde; c&oacute; sổ hồng ri&ecirc;ng từng căn. Đợt cuối c&ugrave;ng chủ đầu tư ch&agrave;o b&aacute;n 30 căn với gi&aacute; từ 20 triệu đồng một m2 k&egrave;m theo ch&iacute;nh s&aacute;ch chiết khấu thanh to&aacute;n nhanh từ 5 đến 6,5%. Kh&aacute;ch h&agrave;ng c&ograve;n nhận c&aacute;c ưu đ&atilde;i như chuyến du lịch ch&acirc;u &Acirc;u, lộc v&agrave;ng l&ecirc;n đến 8 chỉ v&agrave;ng v&agrave; r&uacute;t thăm tr&uacute;ng thưởng với nhiều m&oacute;n qu&agrave; gi&aacute; trị', 0, NULL, NULL, 1, 0, 31, '2017-07-10 16:36:32', '2017-07-14 08:45:28', 2, 2),
(22, 'Những điều cấm kỵ khi xây nhà', 'nhung-dieu-cam-ky-khi-xay-nha', 'Nhung dieu cam ky khi xay nha', 'Trong Phong Thủy có rất những điều cấm kỵ khi xây nhà, nên lúc xây nhà bạn cần quan tâm đặc biệt vấn đề này, để tránh xảy ra những điều không mong muốn. Dưới đây là bài viết chia sẻ về vấn đề này, mời quý bị và các bạn cùng tham khảo.', '2017/07/10/nhung-dieu-cam-ky-khi-xay-nha-1499679274.png', 4, '<h2>&nbsp;</h2>\r\n<br />\r\n<img alt=\"những điều cấm kỵ khi xây nhà\" src=\"/uploads/images/nhung-dieu-cam-ky-khi-xay-nha.png\" style=\"height:400px; width:650px\" /><br />\r\n<br />\r\n<strong>Ki&ecirc;ng kỵ x&acirc;y nh&agrave; tr&ecirc;n mảnh đất lồi l&otilde;m, h&igrave;nh tam gi&aacute;c</strong><br />\r\n<br />\r\nX&acirc;y nh&agrave; tr&ecirc;n mảnh đất h&igrave;nh tam gi&aacute;c, theo quan niệm phong thủy đ&oacute; l&agrave; đại hung do thế đất bị cắt, gia đ&igrave;nh thường dễ bị bất h&ograve;a. Hơn nữa, sẽ rất tốn diện t&iacute;ch, tốn về kinh tế, kh&oacute; thiết kế cho đẹp khi x&acirc;y một căn nh&agrave; tr&ecirc;n một mảnh đất h&igrave;nh tam gi&aacute;c.<br />\r\n<br />\r\nN&ecirc;n x&acirc;y nh&agrave; tr&ecirc;n một khu đất bằng phẳng, c&oacute; địa h&igrave;nh, địa thế cao, đủ rộng r&atilde;i để c&oacute; thể thiết kế c&aacute;c ph&ograve;ng chức năng xinh đẹp, thuận lợi hơn.<br />\r\n<br />\r\n<strong>Ki&ecirc;ng kỵ x&acirc;y nh&agrave; ở ch&acirc;n n&uacute;i hay đầu hẻm n&uacute;i</strong><br />\r\n<br />\r\nKh&ocirc;ng n&ecirc;n chọn vị tr&iacute; dưới n&uacute;i đ&aacute;, nơi ch&acirc;n n&uacute;i nối liền với mặt đất, hay giữa hai đầu hẻm n&uacute;i l&agrave;m địa điểm x&acirc;y nh&agrave; v&igrave; nguy cơ n&uacute;i lở hoặc nước lũ l&agrave; rất lớn. Những nơi n&agrave;y c&oacute; phong cảnh kh&aacute; l&agrave; đẹp, nhưng do hai ngọn n&uacute;i h&igrave;nh th&agrave;nh hẻm n&uacute;i h&igrave;nh dẻ quạt, qua nhiều năm mưa gi&oacute;, đ&aacute;y s&ocirc;ng lắng đọng nhiều c&aacute;t, khu vực nền m&oacute;ng yếu v&agrave; tiềm ẩn nguy cơ sạt lở, ngập lụt cao.<br />\r\n<br />\r\n<br />\r\n<strong>Ki&ecirc;ng kỵ x&acirc;y nh&agrave; ở cuối đường hay qu&aacute; gần đường c&aacute;i</strong><br />\r\n<br />\r\nTheo quan niệm phong thủy, kh&ocirc;ng n&ecirc;n x&acirc;y nh&agrave; ở cuối đường v&igrave; ở địa phận n&agrave;y, khả năng xảy ra trộm cắp l&agrave; kh&aacute; lớn. X&acirc;y nh&agrave; ở cuối ng&otilde; cũng kh&ocirc;ng n&ecirc;n v&igrave; địa điểm n&agrave;y kh&ocirc;ng thuận tiện, khi xảy ra sự cố kh&ocirc;ng c&oacute; lối tho&aacute;t, rất nguy hiểm.<br />\r\n<br />\r\nC&ograve;n trong quan niệm của người xưa, x&acirc;y nh&agrave; ở ng&atilde; tư đường hay gần đường c&aacute;i lớn cũng kh&ocirc;ng n&ecirc;n v&igrave; dễ gặp họa s&aacute;t thương. Hiện nay, nhiều người vẫn tin v&agrave;o quan niệm n&agrave;y v&igrave; nh&agrave; ở vị tr&iacute; n&agrave;y được cho l&agrave; &iacute;t an to&agrave;n, dễ gặp tai nạn giao th&ocirc;ng, ảnh hưởng đến sự an b&igrave;nh của những người trong nh&agrave;.<br />\r\n<br />\r\n<strong>Ki&ecirc;ng kỵ trồng c&acirc;y to hay cột điện ngay trước nh&agrave;</strong><br />\r\n<br />\r\n&Ocirc;ng b&agrave; ta thường n&oacute;i nếu trước cửa c&oacute; c&acirc;y to hay cột điện l&agrave; điềm dữ. Bởi lẽ c&aacute;c cụ cho rằng những c&acirc;y cổ thụ sẽ g&acirc;y kh&oacute; khăn trong việc đi lại, th&ecirc;m nữa sẽ chắn kh&iacute; dương v&agrave;o nh&agrave;, để &acirc;m kh&iacute; t&iacute;ch tụ kh&oacute; tho&aacute;t ra.<br />\r\n<br />\r\nNgo&agrave;i ra, đ&acirc;y cũng l&agrave; cơ hội để kẻ xấu lợi dụng sự che chắn của những c&agrave;nh l&aacute; to để tr&egrave;o v&agrave;o nh&agrave;, g&acirc;y phiền to&aacute;i v&agrave; lo lắng cho gia đ&igrave;nh. Cho n&ecirc;n, bạn n&ecirc;n ki&ecirc;ng trồng những c&acirc;y qu&aacute; to trong s&acirc;n hay trước nh&agrave;. M&agrave; thay v&agrave;o đ&oacute; h&atilde;y trồng c&acirc;y c&oacute; d&aacute;ng mảnh mai, d&aacute;ng cao, l&aacute; c&acirc;y thưa v&agrave; s&aacute;ng như c&acirc;y cau, dừa cảnh, b&agrave;ng, c&acirc;y sung&hellip;<br />\r\n<br />\r\n<strong>Ki&ecirc;ng kỵ trong việc x&acirc;y nh&agrave; qu&aacute; cao</strong><br />\r\n<br />\r\nTheo phong thủy, việc x&acirc;y nh&agrave; qu&aacute; cao khi bốn ph&iacute;a đều kh&ocirc;ng được che chắn hợp l&yacute;, thiếu k&iacute;n đ&aacute;o sẽ tạo ra một t&acirc;m l&yacute; bất ổn cho người trong nh&agrave;. Kh&ocirc;ng chỉ vậy, điều đ&oacute; c&ograve;n tạo sự c&aacute;ch biệt với những h&agrave;ng x&oacute;m xung quanh.<br />\r\n<br />\r\nNhững ng&ocirc;i nh&agrave; qu&aacute; cao cũng sẽ kh&ocirc;ng thể che nắng, che mưa. Như c&aacute;c cụ thường n&oacute;i điều đ&oacute; sẽ g&acirc;y ra &quot;dương thịnh &acirc;m suy&quot;, &acirc;m dương m&agrave; kh&ocirc;ng được điều h&ograve;a sẽ ảnh hưởng rất lớn đến sức khỏe những người trong gia đ&igrave;nh. V&igrave; vậy, bạn cần c&acirc;n nhắc kỹ để thiết kế cho căn nh&agrave; vừa tầm, h&agrave;i h&ograve;a, khang trang v&agrave; xinh đẹp.<br />\r\n<br />\r\n<strong>C&aacute;c kiểu nh&agrave; cần tr&aacute;nh</strong><br />\r\n<br />\r\nC&aacute;c bạn ch&uacute; &yacute;, khi x&acirc;y nh&agrave; tr&aacute;nh kiểu chữa b&aacute;t c&oacute; thể ngh&egrave;o đ&oacute;i, bệnh tật, c&ocirc; đơn; chữ hỏa: bế kinh; h&igrave;nh c&aacute;i quạt cuộc sống bếp b&ecirc;nh, vất vả, kh&oacute; khăn; qu&aacute; giang c&oacute; cột k&iacute;ch thước kh&ocirc;ng bằng nhau th&igrave; lu&ocirc;n bị kẻ kh&aacute;c g&acirc;y phiền to&aacute;i; hai thượng lương chồng l&ecirc;n nhau nếu kh&ocirc;ng c&oacute; m&aacute;i nh&agrave; sẽ bị đổ hay mắc bệnh &ocirc;n dịch.<br />\r\n<br />\r\nKhi bạn đ&atilde; ho&agrave;n tất qu&aacute; tr&igrave;nh x&acirc;y dựng, nh&agrave; kh&ocirc;ng được tạo h&igrave;nh chữ sơn hay chữ đột c&oacute; thể g&acirc;y mất an to&agrave;n. Đ&acirc;y l&agrave; một kiểu nh&agrave; kh&ocirc;ng chỉ xấu về h&igrave;nh thức m&agrave; c&ograve;n ảnh hưởng đến cả &yacute; niệm t&acirc;m linh.<br />\r\n<br />\r\nV&igrave; vậy, khi mua nh&agrave; hay x&acirc;y nh&agrave;, c&aacute;c bạn n&ecirc;n tr&aacute;nh xa những kiểu nh&agrave; n&agrave;y để tr&aacute;nh rước họa cho cả gia đ&igrave;nh.<br />\r\n<br />\r\n<strong>D&aacute;ng n&agrave;o nh&agrave; n&agrave;o cần tr&aacute;nh</strong><br />\r\n<br />\r\nKhi x&acirc;y nh&agrave; đẹp, bạn cần xem x&eacute;t kỹ từng yếu tố nhỏ như ch&acirc;n tường yếu: gia thế suy vong v&agrave; gặp tai họa; chiều rộng mặt tiền lớn hơn chiều d&agrave;i nh&agrave;: kh&ocirc;ng may mắn; tuyệt đối kh&ocirc;ng được ở những ng&ocirc;i nh&agrave; c&oacute; khuyết ở bốn g&oacute;c mặt bằng; mất hết của cải khi diện t&iacute;ch mặt bằng h&igrave;nh tam gi&aacute;c hay mặt trước lớn hơn ph&iacute;a sau. Ngược lại, nếu bạn x&acirc;y nh&agrave;, mua nh&agrave;, thậm ch&iacute; thu&ecirc; nh&agrave; c&oacute; mặt bằng trước hẹp hơn ph&iacute;a sau đại c&aacute;t đại lợi, ph&uacute; qu&yacute; đầy đủ.<br />\r\n<br />\r\nB&ecirc;n cạnh đ&oacute;, bạn cần nhớ x&acirc;y nh&agrave; đẹp sao cho g&oacute;c tường r&agrave;o nh&agrave; kh&aacute;c kh&ocirc;ng đ&acirc;m thẳng v&agrave;o nh&agrave; m&igrave;nh. Nếu chĩa v&agrave;o g&oacute;c tr&aacute;i th&igrave; nh&agrave; gặp nhiều trắc trở, c&ograve;n b&ecirc;n phải phụ nữ trong gia đ&igrave;nh sẽ gặp xui xẻo.<br />\r\n<br />\r\nTrường hợp gh&eacute;p hai nh&agrave; l&agrave;m nh&agrave; một, tr&aacute;nh để m&aacute;i hi&ecirc;n liền nhau. Khi l&agrave;m cho con c&aacute;i th&igrave; kh&ocirc;ng n&ecirc;n l&agrave;m nh&agrave; trong diện t&iacute;ch s&acirc;n của nh&agrave; bố mẹ.<br />\r\nSố ph&ograve;ng v&agrave; số bậc thang trong nh&agrave;<br />\r\n<br />\r\nKhi x&acirc;y nh&agrave;, cần ch&uacute; &yacute; số bằng cầu thang kh&ocirc;ng được kết th&uacute;c bằng chữ &ldquo;tử&rdquo;. Về số ph&ograve;ng v&agrave; số bậc thang kh&ocirc;ng chỉ quan trọng về thiết kế m&agrave; c&ograve;n l&agrave; chuyện l&agrave;nh dữ của cả gia đ&igrave;nh.<br />\r\n<br />\r\nVậy n&ecirc;n cần tu&acirc;n theo những số may mắn để được ph&uacute;c lộc. Theo phong thủy, khi x&acirc;y nh&agrave; n&ecirc;n tr&aacute;nh số chắn v&agrave; lấy số lẻ. Số cầu thang theo &ldquo;sinh l&atilde;o bệnh tử&rdquo;, nếu bậc cuối c&ugrave;ng l&agrave; sinh th&igrave; tốt, xấu khi l&agrave; bệnh hay tử.<br />\r\n<br />\r\n<strong>Một số ki&ecirc;ng kỵ kh&aacute;c</strong><br />\r\n<br />\r\nGỗ sử dụng trong nh&agrave; kh&ocirc;ng d&ugrave;ng c&aacute;c loại gỗ c&oacute; t&iacute;nh ấm như h&ograve;e, lật, nam&hellip; n&ecirc;n d&ugrave;ng &nbsp;gỗ c&oacute; t&iacute;nh dương như lật, nam, h&ograve;e m&agrave; chỉ n&ecirc;n d&ugrave;ng gỗ c&oacute; t&iacute;nh san, mai, t&ugrave;ng.<br />\r\n<br />\r\nC&aacute;c ph&ograve;ng phụ kh&ocirc;ng bao giờ x&acirc;y cao v&agrave; rộng hơn ph&ograve;ng ch&iacute;nh d&ugrave; c&ugrave;ng nằm trong một mặt bằng. &Acirc;m kh&iacute; sẽ nặng nề nếu x&acirc;y nh&agrave; trước một ng&ocirc;i nh&agrave; bỏ hoang, ng&ocirc;i miếu hay nghĩa trang.<br />\r\n<br />\r\nHy vọng với một số điều cấm kỵ khi x&acirc;y nh&agrave; tr&ecirc;n, gia chủ sẽ dễ d&agrave;ng sở hữu một ng&ocirc;i nh&agrave; c&oacute; đẹp m&agrave; kh&ocirc;ng vướng phải c&aacute;c yếu tố xấu trong phong thủy. Cảm ơn qu&yacute; vị đ&atilde; tham khảo b&agrave;i viết của ch&uacute;ng t&ocirc;i.', 0, NULL, NULL, 1, 0, 32, '2017-07-10 16:53:18', '2017-07-14 09:00:27', 3, 2),
(24, 'Cách khai quang gương bát quái', 'cach-khai-quang-guong-bat-quai', 'Cach khai quang guong bat quai', 'Hiện nay có rất nhiều gia đình sử dụng gương bát quái nhưng không phải ai cũng biết cách đặt đúng để tránh sát khí cũng như hoá giải hướng xấu cho căn hộ. Cùng nhau tìm hiểu cách khai quang gương bát quái và công dụng của loại gương này nhé.', '2017/07/10/cach-khai-quang-guong-bat-quai-1499680812.png', 4, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><img alt=\"cách khai quang gương bát quái\" src=\"/uploads/images/nhung-dieu-cam-ky-khi-xay-nha%281%29.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n<strong>Gương B&aacute;t Qu&aacute;i l&agrave; g&igrave;?</strong><br />\r\n<br />\r\nGương b&aacute;t qu&aacute;i l&agrave; một trong những c&ocirc;ng cụ phổ biến được sử dụng trong phong thủy để bảo vệ, khắc phục, hoặc h&oacute;a giải cho c&aacute;c ng&ocirc;i nh&agrave; c&oacute; hướng xấu. Ở c&aacute;c th&agrave;nh phố lớn hiện nay việc mua phải ng&ocirc;i nh&agrave; c&oacute; hướng kh&ocirc;ng hợp tuổi với gia chủ l&agrave; chuyện rất dễ gặp v&agrave; tất cả mọi người sẽ t&igrave;m đến gương b&aacute;t qu&aacute;i để xua tan đi nỗi lo sai hướng.<br />\r\n<br />\r\n<strong>Chọn gương b&aacute;t qu&aacute;i kh&ocirc;ng ph&ugrave; hợp</strong><br />\r\n<br />\r\nC&oacute; ba mẫu thiết kế gương b&aacute;t qu&aacute;i ch&iacute;nh l&agrave; mặt l&otilde;m, mặt lồi v&agrave; mặt phẳng.<br />\r\nGương l&otilde;m c&oacute; h&igrave;nh dạng cong v&agrave;o trong để t&iacute;ch tụ c&aacute;c luồng năng lượng. Nếu phương vị phong thủy c&oacute; hiện tượng t&aacute;n kh&iacute; hoạc những nguồn năng lượng tốt kh&ocirc;ng thể trực tiếp v&agrave;o nh&agrave; th&igrave; n&ecirc;n treo gương b&aacute;t qu&aacute;i mặt l&otilde;m để thu h&uacute;t.<br />\r\n<br />\r\nGương b&aacute;t qu&aacute;i lồi c&oacute; một đường cong hướng ra ngo&agrave;i để phản lại v&agrave; l&agrave;m ph&acirc;n t&aacute;n những năng lượng ti&ecirc;u cực v&agrave; c&oacute; hại. Khi nh&agrave; ở, cửa ch&iacute;nh hay cửa sổ bị c&aacute;c vật thể mang s&aacute;t kh&iacute; xung chiếu, gương lồi sẽ c&oacute; t&aacute;c dụng rất hữu hiệu.&nbsp;<br />\r\n<br />\r\nSử dụng loại gương b&aacute;t qu&aacute;i cần ch&uacute; &yacute; đặc biệt v&igrave; nếu kh&ocirc;ng b&agrave;i tr&iacute; đ&uacute;ng c&aacute;ch th&igrave; c&oacute; thể g&acirc;y tổn hại cho h&agrave;ng x&oacute;m. Do vậy, gương phẳng trung t&iacute;nh được sử dụng nhiều hơn cả.<br />\r\n<br />\r\n<strong>Treo sai vị tr&iacute;</strong><br />\r\n<br />\r\nKhi treo gương b&aacute;t qu&aacute;i cần l&agrave;m theo chỉ dẫn của người c&oacute; chuy&ecirc;n m&ocirc;n, hiểu biết, gia chủ kh&ocirc;ng n&ecirc;n t&ugrave;y tiện treo. Việc một gia đ&igrave;nh treo 4-5 gương b&aacute;t qu&aacute;i trong nh&agrave;, cửa to n&agrave;o cũng treo v&agrave; ph&ograve;ng n&agrave;o cũng treo kh&ocirc;ng phải l&agrave; c&aacute;ch hay.<br />\r\nGương b&aacute;t qu&aacute;i, khi mua về n&ecirc;n được bọc k&iacute;n, trước khi treo phải l&agrave;m lễ, xin ph&eacute;p thần linh trong ng&ocirc;i nh&agrave;. Tức l&agrave; gương được đặt l&ecirc;n ban thờ, sau đ&oacute; người c&oacute; chuy&ecirc;n m&ocirc;n sẽ đọc tr&igrave; ch&uacute; để khai quang gương rồi mới tiến h&agrave;nh treo gương. Gương n&ecirc;n được treo v&agrave;o ng&agrave;y rằm hoặc m&ugrave;ng một.<br />\r\n<br />\r\nC&oacute; một số những lưu &yacute; căn bản để c&aacute;c gia đ&igrave;nh c&oacute; thể t&igrave;m ra những vị tr&iacute; tốt nhất cho những chiếc gương b&aacute;t qu&aacute;i của m&igrave;nh như: kh&ocirc;ng n&ecirc;n treo một chiếc gương ở đối diện cửa ra v&agrave;o, đặc biệt l&agrave; ph&iacute;a sau bếp nấu&hellip;<br />\r\n<br />\r\n<strong>Treo gương b&aacute;t qu&aacute;i b&ecirc;n ngo&agrave;i nh&agrave;</strong><br />\r\n<br />\r\nN&oacute;i chung, hầu hết gương b&aacute;t qu&aacute;i được đặt tr&ecirc;n hoặc gần cửa ra v&agrave;o để ph&acirc;n t&aacute;n hay t&iacute;ch tụ năng lượng t&ugrave;y v&agrave;o mục đ&iacute;ch sử dụng của gia đ&igrave;nh. Ngo&agrave;i ra, trong nh&agrave; kh&ocirc;ng bao giờ n&ecirc;n treo nhiều hơn hai chiếc gương b&aacute;t qu&aacute;i - một chiếc ở mặt trước, một chiếc ở mặt sau l&agrave; đủ.<br />\r\n<br />\r\n<strong>L&agrave;m lễ xin ph&eacute;p thổ địa ng&ocirc;i nh&agrave;</strong><br />\r\n<br />\r\nGương b&aacute;t qu&aacute;i, khi mua về n&ecirc;n được bọc k&iacute;n trong vải đỏ. Trước khi treo gương l&ecirc;n tr&ecirc;n trước cửa phải l&agrave;m lễ, xin ph&eacute;p thần linh, thổ địa cai quản ng&ocirc;i nh&agrave;. Tức l&agrave; gương được đặt l&ecirc;n ban thờ, sau đ&oacute; người c&oacute; chuy&ecirc;n m&ocirc;n sẽ đọc tr&igrave; ch&uacute; để khai quang rồi mới tiến h&agrave;nh treo gương. Th&ocirc;ng thường, gương sẽ được khai quang v&agrave;o ng&agrave;y rằm hoặc m&ugrave;ng một.<br />\r\n<br />\r\n<strong>Kh&ocirc;ng sử dụng c&aacute;c loại gương c&oacute; h&igrave;nh thần tướng cưỡi hổ</strong><br />\r\n<br />\r\nNếu gương b&aacute;t qu&aacute;i c&oacute; khắc h&igrave;nh thần tướng cưỡi hổ, tay cầm binh kh&iacute; hoặc hổ ph&ugrave; ngậm binh kh&iacute; sẽ tạo ra s&aacute;t kh&iacute; mạnh. Nếu gia đ&igrave;nh treo gương b&ecirc;n ngo&agrave;i sẽ g&acirc;y ảnh hưởng đến nh&agrave; đối diện. Ngo&agrave;i ra, hung kh&iacute; ở khắp nơi cũng k&eacute;o đến nh&agrave; bạn với sức mạnh &aacute;p đảo khiến gương kh&ocirc;ng chống trọi được. Đ&acirc;y l&agrave; một trong những sai lầm lớn v&igrave; nhiều người cho rằng gương b&aacute;t qu&aacute;i th&ecirc;m thần hổ phụ trợ th&igrave; c&agrave;ng th&ecirc;m mạnh.<br />\r\n<br />\r\n<strong>B&acirc;y giờ ta bắt đầu khai nh&atilde;n thần bằng một trong 3 c&aacute;ch sau :</strong><br />\r\n<br />\r\nTr&igrave; ch&uacute; đại bi bẩy biến rồi d&ugrave;ng hương thư chữ yểm v&agrave;o gương l&agrave; xong&nbsp;<br />\r\n<br />\r\nTr&igrave; ch&uacute; ti&ecirc;u tai c&aacute;t tường thần ch&uacute; v&agrave;o gương v&agrave; bắt ấn c&aacute;t tường thư v&agrave;o gương v&agrave; b&uacute;ng ấn v&agrave;o gương.<br />\r\nNa ma, sa măn ta- b&uacute;t đa nam, a pơ ra ti &nbsp;ha tha, s&agrave; sa na &nbsp;nam,ta đi da tha: &ocirc;m &nbsp;kha, kha, kh&agrave; &nbsp;hi, kh&agrave; hi, h&ugrave;m, h&ugrave;m, di va la, di va la, pơ ra di va la, pơ ra di va la , ti si &nbsp;tha, sờ &nbsp;tri, &nbsp;sờ &nbsp;tri,s&agrave; &nbsp;pha &nbsp;ta, s&agrave; &nbsp;pha &nbsp;ta, săn &nbsp;ti &nbsp;ka, &nbsp;sờ &nbsp;ri &nbsp;d&ecirc;, s&oacute;a ha.<br />\r\n<br />\r\nĐọc b&agrave;i ch&uacute; luyện b&aacute;t qu&aacute;i của thầy Ti&ecirc;u Di&ecirc;u Tử :<br />\r\nNhất khảm Qu&aacute;n &acirc;m mẫu độ sinh_ Nhị Kh&ocirc;n thi&ecirc;n tướng gi&aacute;ng hung tinh_Tam chấn l&ocirc;i đ&igrave;nh uy dũng m&atilde;nh_Tứ tốn phong vũ tất đan thanh_ Ngũ trung th&aacute;nh thần lai hộ thuật_Lục c&agrave;n binh tướng gi&aacute;ng ph&ugrave; linh_Thất đo&agrave;i l&ocirc;i c&ocirc;ng lai s&aacute;t quỷ_B&aacute;t cấn bổn tướng trảm t&agrave; tinh_Cửu ly ti&ecirc;n th&aacute;nh gi&aacute;ng hội đồng_Nam m&ocirc; b&aacute;t qu&aacute;i minh sinh _Cửu long thi&ecirc;n tử_Long thần hổ tướng _B&aacute;t qu&aacute;i kim cang_Ngũ h&agrave;nh binh tướng_cấp như sắc lệnh _cấp cấp như luật lệnh sắc.<br />\r\n&nbsp;V&agrave; b&uacute;ng ấn c&aacute;t tường v&agrave;o gương .<br />\r\n<br />\r\nCảm ơn qu&yacute; vị đ&atilde; tham khảo b&agrave;i viết của ch&uacute;ng t&ocirc;i để biết th&ecirc;m nhiều kh&iacute;a cạnh của mặt phong thủy nh&agrave; đất, xin mời qu&yacute; vị t&igrave;m đọc tr&ecirc;n Website của ch&uacute;ng t&ocirc;i.<br />\r\n&nbsp;<br />\r\n&nbsp;', 0, NULL, NULL, 1, 0, 34, '2017-07-10 17:10:53', '2017-07-14 09:00:07', 3, 2),
(34, 'Nghĩa vụ tài chính khi chuyển mục đích sử dụng đất như thế nào', 'nghia-vu-tai-chinh-khi-chuyen-muc-dich-su-dung-dat-nhu-the-nao', 'Nghia vu tai chinh khi chuyen muc dich su dung dat nhu the nao', 'Nếu quý vị có một mảnh đất nông nghiệp muốn xây nhà trên đó thì phải chuyển mục đích sử dụng đất. Vậy nghĩa vụ tài chính khi chuyển mục đích sử dụng đất như thế nào thì mời quý vị tham khảo bài viết dưới đây.', '2017/07/13/nghia-vu-tai-chinh-khi-chuyen-muc-dich-su-dung-dat-nhu-the-nao-1499926473.png', 5, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><img alt=\"Nghĩa vụ tài chính khi chuyển mục đích sử dụng đất như thế nào\" src=\"/uploads/images/nghia-vu-tai-chinh-khi-chuyen-muc-dich-su-dung-dat-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nHiện nay theo quy định của ph&aacute;p luật nếu qu&yacute; vị muốn chuyển mục đ&iacute;ch sử dụng đất từ đất n&ocirc;ng nghiệp sang đất ở bạn phải l&agrave;m hồ sơ xin chuyển mục đ&iacute;ch sử dụng đất bao gồm:\r\n<ul>\r\n	<li>Đơn xin chuyển mục đ&iacute;ch sử dụng đất</li>\r\n	<li>Giấy chứng nhận QSDĐ</li>\r\n</ul>\r\n<br />\r\nBi&ecirc;n bản x&aacute;c minh thực địa\r\n<ul>\r\n	<li>Văn bản thẩm định nhu cầu sử dụng đất, thẩm định điều kiện cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất</li>\r\n	<li>Tr&iacute;ch lục bản đồ địa ch&iacute;nh thửa</li>\r\n</ul>\r\n<br />\r\nNộp hồ sơ tại UBND cấp huyện<br />\r\n<br />\r\nCụ thể tại Điều 69 Nghị định 43/2014/NĐ-CP quy định:&nbsp;Điều 69. Tr&igrave;nh tự, thủ tục cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất&nbsp;<br />\r\n<br />\r\n1. Người sử dụng đất nộp đơn xin ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất k&egrave;m theo Giấy chứng nhận đến cơ quan t&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường.&nbsp;<br />\r\n<br />\r\n2. Cơ quan t&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường c&oacute; tr&aacute;ch nhiệm thẩm tra hồ sơ; x&aacute;c minh thực địa, thẩm định nhu cầu chuyển mục đ&iacute;ch sử dụng đất; hướng dẫn người sử dụng đất thực hiện nghĩa vụ t&agrave;i ch&iacute;nh theo quy định của ph&aacute;p luật; tr&igrave;nh Ủy ban nh&acirc;n d&acirc;n cấp c&oacute; thẩm quyền quyết định cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất; chỉ đạo cập nhật, chỉnh l&yacute; cơ sở dữ liệu đất đai, hồ sơ địa ch&iacute;nh. Đối với trường hợp nh&agrave; đầu tư nhận chuyển nhượng quyền sử dụng đất của người đang sử dụng đất để thực hiện dự &aacute;n đầu tư th&igrave; thực hiện thủ tục chuyển nhượng quyền sử dụng đất đồng thời với thủ tục cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất.<br />\r\n<br />\r\n3. Người sử dụng đất thực hiện nghĩa vụ t&agrave;i ch&iacute;nh theo quy định<br />\r\n<br />\r\nNhư vậy khi được nh&agrave; nước cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất bạn phải nộp tiền sử dụng đất theo quy định tại Điều 1 Nghị định 45/2014/NĐ-CP:<br />\r\n<br />\r\nĐiều 1. Phạm vi điều chỉnh nghị định n&agrave;y quy định về thu tiền sử dụng đất trong trường hợp:\r\n<ul>\r\n	<li>Nh&agrave; nước giao đất c&oacute; thu tiền sử dụng đất.</li>\r\n	<li>Nh&agrave; nước cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất từ đất n&ocirc;ng nghiệp, đất phi n&ocirc;ng nghiệp kh&ocirc;ng phải l&agrave; đất ở sang đất ở hoặc đất nghĩa trang, nghĩa địa c&oacute; mục đ&iacute;ch kinh doanh thuộc trường hợp phải nộp tiền sử dụng đất.</li>\r\n	<li>Nh&agrave; nước c&ocirc;ng nhận quyền sử dụng đất cho c&aacute;c đối tượng đang sử dụng đất thuộc trường hợp phải nộp tiền sử dụng đất.</li>\r\n	<li>Điểm b khoản 2 Điều 5 Nghị định 45/2014 quy định: Chuyển từ đất n&ocirc;ng nghiệp được Nh&agrave; nước giao kh&ocirc;ng thu tiền sử dụng đất sang đất ở th&igrave; thu tiền sử dụng đất bằng mức ch&ecirc;nh lệch giữa tiền sử dụng đất t&iacute;nh theo gi&aacute; đất ở với tiền sử dụng đất t&iacute;nh theo gi&aacute; đất n&ocirc;ng nghiệp tại thời điểm c&oacute; quyết định chuyển mục đ&iacute;ch của cơ quan nh&agrave; nước c&oacute; thẩm quyền.</li>\r\n</ul>\r\n', 0, NULL, NULL, 1, 0, 73, '2017-07-13 13:15:06', '2017-07-14 09:08:05', 2, 2),
(35, 'Người nước ngoài có được nhận hoặc tặng nhà tại Việt Nam hay không', 'nguoi-nuoc-ngoai-co-duoc-nhan-hoac-tang-nha-tai-viet-nam-hay-khong', 'Nguoi nuoc ngoai co duoc nhan hoac tang nha tai Viet Nam hay khong', 'Nếu quý vị là người nước ngoài hoặc có người thân là người nước ngoài hiện đang sinh sống và làm việc tại Việt Nam. Hiện đang sở hữu một mảnh đất hoặc căn hộ tại Việt Nam nay muốn tặng lại cho người thân đang sinh sống tại nước ngoài.', '2017/07/13/nguoi-nuoc-ngoai-co-duoc-nhan-hoac-tang-nha-tai-viet-nam-hay-khong-1499928916.png', 5, '<h2>Vậy người nước ngo&agrave;i c&oacute; được nhận hoặc tặng nh&agrave; tại Việt Nam hay kh&ocirc;ng th&igrave; mời qu&yacute; vị tham khảo dưới đ&acirc;y.</h2>\r\n\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n\r\n<div style=\"text-align:center\"><img alt=\"Người nước ngoài có được nhận hoặc tặng nhà tại Việt Nam hay không\" src=\"/uploads/images/nguoi-nuoc-ngoai-co-duoc-nhan-hoac-tang-nha-tai-viet-nam-hay-khong.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nCăn cứ điều 5 Luật Đất đai 2013, người sử dụng đất kh&ocirc;ng bao gồm người nước ngo&agrave;i. Như vậy ph&aacute;p luật Việt Nam kh&ocirc;ng cho ph&eacute;p người nước ngo&agrave;i c&oacute; quyền sử dụng đất tại Việt Nam. Người nước ngo&agrave;i chỉ được sở hữu căn hộ hoặc nh&agrave; ở ri&ecirc;ng lẻ trong c&aacute;c dự &aacute;n x&acirc;y dựng nh&agrave; ở m&agrave; kh&ocirc;ng được mua nh&agrave; ở ngo&agrave;i dự &aacute;n. Như vậy, bạn kh&ocirc;ng thể nhận chuyển nhượng quyền sử dụng đất tại VIệt Nam m&agrave; chỉ c&oacute; thể mua nh&agrave; ở thương mại &nbsp;trong c&aacute;c dự &aacute;n x&acirc;y dựng nh&agrave; ở. Cụ thể ph&aacute;p luật về nh&agrave; ở c&oacute; một số quy định về quyền sở hữu nh&agrave; ở của người nước ngo&agrave;i như sau:<br />\r\n<br />\r\n<strong>Về quyền sở hữu nh&agrave; ở tại Việt Nam của người nước ngo&agrave;i</strong><br />\r\n&nbsp;<br />\r\nLuật Nh&agrave; ở 2014 quy định: &ldquo;Điều 159. Đối tượng được sở hữu nh&agrave; ở v&agrave; h&igrave;nh thức sở hữu nh&agrave; ở tại Việt Nam của tổ chức, c&aacute; nh&acirc;n nước ngo&agrave;i\r\n<ul>\r\n	<li>Đối tượng tổ chức, c&aacute; nh&acirc;n nước ngo&agrave;i được sở hữu nh&agrave; ở tại Việt Nam bao gồm:</li>\r\n	<li>C&aacute; nh&acirc;n nước ngo&agrave;i được ph&eacute;p nhập cảnh v&agrave;o Việt Nam.</li>\r\n	<li>Tổ chức, c&aacute; nh&acirc;n nước ngo&agrave;i được sở hữu nh&agrave; ở tại Việt Nam th&ocirc;ng qua c&aacute;c h&igrave;nh thức sau đ&acirc;y:</li>\r\n	<li>Đầu tư x&acirc;y dựng nh&agrave; ở theo dự &aacute;n tại Việt Nam theo quy định của Luật n&agrave;y v&agrave; ph&aacute;p luật c&oacute; li&ecirc;n quan;</li>\r\n	<li>Mua, thu&ecirc; mua, nhận tặng cho, nhận thừa kế nh&agrave; ở thương mại bao gồm căn hộ chung cư v&agrave; nh&agrave; ở ri&ecirc;ng lẻ trong dự &aacute;n đầu tư x&acirc;y dựng nh&agrave; ở, trừ khu vực bảo đảm quốc ph&ograve;ng, an ninh theo quy định của Ch&iacute;nh phủ.</li>\r\n</ul>\r\n<br />\r\nĐiều 160. Điều kiện tổ chức, c&aacute; nh&acirc;n nước ngo&agrave;i được sở hữu nh&agrave; ở tại Việt Nam<br />\r\n<br />\r\nĐối với c&aacute; nh&acirc;n nước ngo&agrave;i quy định tại điểm c khoản 1 Điều 159 của Luật n&agrave;y th&igrave; phải được ph&eacute;p nhập cảnh v&agrave;o Việt Nam v&agrave; kh&ocirc;ng thuộc diện được hưởng quyền ưu đ&atilde;i, miễn trừ ngoại giao, l&atilde;nh sự theo quy định của ph&aacute;p luật.<br />\r\n<br />\r\nNhư vậy, người nước ngo&agrave;i chỉ được sở hữu nh&agrave; ở thương mại trong dự &aacute;n đầu tư x&acirc;y dựng nh&agrave; ở th&ocirc;ng qua việc mua, thu&ecirc; mua, nhận tặng cho, nhận thừa kế. Người nước ngo&agrave;i phải được ph&eacute;p nhập cảnh v&agrave;o Việt Nam v&agrave; kh&ocirc;ng thuộc diện được hưởng quyền ưu đ&atilde;i, miễn trừ ngoại giao, l&atilde;nh sự theo quy định của ph&aacute;p luật th&igrave; mới được sở hữu nh&agrave; ở v&agrave; căn hộ chung cơ tại Việt Nam.<br />\r\n<br />\r\nĐể chứng minh được ph&eacute;p nhập cảnh v&agrave;o Việt Nam th&igrave; phải c&oacute; hộ chiếu c&ograve;n gi&aacute; trị c&oacute; đ&oacute;ng dấu kiểm chứng nhập cảnh của cơ quan quản l&yacute; xuất, nhập cảnh Việt Nam (Nghị định 99/2015/NĐ-CP quy định chi tiết v&agrave; hướng dẫn thi h&agrave;nh một số điều của Luật Nh&agrave; ở). Do đ&oacute;, bạn muốn mua một căn hộ tại Việt Nam th&igrave; phải đ&aacute;p ứng được những điều kiện như tr&ecirc;n. Th&ocirc;ng thường, người nước ngo&agrave;i được sở hữu nh&agrave; tại Việt Nam kh&ocirc;ng qu&aacute; 50 năm kể từ ng&agrave;y được cấp giấy chứng nhận (c&oacute; thể gia hạn một lần kh&ocirc;ng qu&aacute; 50 năm).<br />\r\n&nbsp;<br />\r\nTrường hợp bạn muốn tặng cho căn hộ cho người kh&aacute;c cũng l&agrave; người nước ngo&agrave;i th&igrave; người đ&oacute; cũng phải đ&aacute;p ứng được những điều kiện n&ecirc;u tr&ecirc;n mới c&oacute; thể được c&ocirc;ng nhận quyền sở hữu đối với căn hộ. Ngo&agrave;i ra khi bạn tặng cho nh&agrave; ở cho người kh&aacute;c th&igrave; c&ograve;n phải tu&acirc;n thủ quy định sau của Nghị định 99/2015/NĐ-CP về thời hạn sở hữu nh&agrave; ở:<br />\r\n&nbsp;<br />\r\n<strong>Điều 7. Thời han sở hữu nh&agrave; ở</strong><br />\r\n<br />\r\nTrường hợp trước khi hết hạn sở hữu nh&agrave; ở m&agrave; tổ chức, c&aacute; nh&acirc;n nước ngo&agrave;i b&aacute;n hoặc tặng cho nh&agrave; ở th&igrave; người mua, người được tặng cho được sở hữu nh&agrave; ở theo quy định sau đ&acirc;y:\r\n<ul>\r\n	<li>Trường hợp b&aacute;n hoặc tặng cho nh&agrave; ở cho tổ chức, c&aacute; nh&acirc;n nước ngo&agrave;i thuộc diện được sở hữu nh&agrave; ở tại Việt Nam th&igrave; b&ecirc;n mua, b&ecirc;n nhận tặng cho chỉ được sở hữu nh&agrave; ở trong thời hạn c&ograve;n lại; khi hết thời hạn sở hữu c&ograve;n lại m&agrave; chủ sở hữu c&oacute; nhu cầu gia hạn th&ecirc;m th&igrave; được Nh&agrave; nước xem x&eacute;t, gia hạn th&ecirc;m theo quy định tại Điều 77 của Nghị định n&agrave;y</li>\r\n	<li>B&ecirc;n b&aacute;n, b&ecirc;n tặng cho nh&agrave; ở phải nộp thuế v&agrave; c&aacute;c nghĩa vụ t&agrave;i ch&iacute;nh cho Nh&agrave; nước Việt Nam theo quy định của ph&aacute;p luật Việt Nam.</li>\r\n</ul>\r\n<br />\r\n<strong>Về Thủ Tục</strong><br />\r\n&nbsp;<br />\r\nTheo quy định tại Điều 9 Luật Nh&agrave; ở 2014, tr&igrave;nh tự, thủ tục cấp Giấy chứng nhận cho chủ sở hữu nh&agrave; ở được thực hiện theo quy định của ph&aacute;p luật về đất đai.<br />\r\n<br />\r\nĐể tặng cho nh&agrave; ở m&agrave; bạn sở hữu cho người th&acirc;n th&igrave; bạn phải lập hợp đồng tặng cho t&agrave;i sản. Sau đ&oacute; l&agrave;m thủ tục xin cấp Giấy chứng nhận quyền sở hữu nh&agrave; ở. Người nhận tặng cho thực hiện thủ tục như sau:', 0, NULL, NULL, 1, 0, 74, '2017-07-13 13:55:55', '2017-07-14 09:06:57', 2, 2),
(36, 'Tài sản đất chưa có sổ đỏ lập di chúc như thế nào', 'tai-san-dat-chua-co-so-do-lap-di-chuc-nhu-the-nao', 'Tai san dat chua co so do lap di chuc nhu the nao', 'Nếu quý vị hiện đang sinh sống trên mảnh đất và xây nhà trên đó nhưng chưa được cấp sổ đỏ. Nhưng bây giờ lại muốn lập di chúc để phân chia tải sản, nhưng lại không biết tài sản đất chưa có sổ đỏ lập di chúc như thế nào. Thì mời quý vị tham khảo bài viết dưới đây để năm rõ hơn.', '2017/07/13/tai-san-dat-chua-co-so-do-lap-di-chuc-nhu-the-nao-1499934626.png', 5, '<h2>&nbsp;</h2>\r\n\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"\" src=\"/uploads/images/tai-san-dat-chua-co-so-do-lap-di-chuc-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nTheo quy định tại Điều 631 BLDS Về quyền thừa kế của c&aacute; nh&acirc;n th&igrave;: C&aacute; nh&acirc;n c&oacute; quyền lập di ch&uacute;c để định đoạt t&agrave;i sản của m&igrave;nh; để lại t&agrave;i sản của m&igrave;nh cho người thừa kế theo ph&aacute;p luật; hưởng di sản theo di ch&uacute;c hoặc theo ph&aacute;p lu&acirc;t.<br />\r\n&nbsp;<br />\r\nCăn cứ Khoản 1 Điều 106 luật đất đai quy định về quyền chuyển đổi, chuyển nhượng, cho thu&ecirc;, cho thu&ecirc; lại, thừa kế, tặng cho quyền sử dụng đất; quyền thế chấp, bảo l&atilde;nh, g&oacute;p vốn bằng quyền sử đụng dất; quyền được bồi thường khi Nh&agrave; nước thu hồi đất th&igrave;<br />\r\n&nbsp;<br />\r\nNgười sử dụng đất được thực hiện c&aacute;c quyền chuyển đổi, chuyển nhượng, cho thu&ecirc;, cho thu&ecirc; lại, thừa kế, tặng cho quyền sử dụng đất; thế chấp, bảo l&atilde;nh, g&oacute;p vốn bằng quyền sử dụng đất theo quy định tại khoản<br />\r\n<br />\r\nĐiều 110: khoản 2 v&agrave; khoản 3 Điều 112; c&aacute;c khoản 2, 3, 4, 5, 6, 7 v&agrave; 8 Điều 113; khoản 2 Điều 115; điểm b khoản 1, c&aacute;c điểm b, c, d, đ v&agrave; e khoản 3 Điều 119; điểm b khoản 1, điểm b v&agrave; điểm c khoản 2 Điều 120 của Luật n&agrave;y khi c&oacute; c&aacute;c điều kiện sau đ&acirc;y:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>C&oacute; giấy chứng nhận quyền sử dụng đất&nbsp;</li>\r\n	<li>Đất kh&ocirc;ng c&oacute; tranh chấp&nbsp;</li>\r\n	<li>Quyền sử dụng đất kh&ocirc;ng bị k&ecirc; bi&ecirc;n để bảo đảm thi h&agrave;nh &aacute;n</li>\r\n	<li>Trong thời hạn sử dụng đất.</li>\r\n</ul>\r\n&nbsp;<br />\r\nTuy nhi&ecirc;n, căn cứ v&agrave;o Điều 66 Nghị định 84/2007 về thời hạn thống nhất thực hiện c&aacute;c giao dịch về quyền sử dụng đất bằng Giấy chứng nhận th&igrave;:<br />\r\n&nbsp;<br />\r\nKể từ ng&agrave;y 01 th&aacute;ng 01 năm 2008, người sử dụng đất phải c&oacute; Giấy chứng nhận mới được thực hiện c&aacute;c quyền chuyển đổi, chuyển nhượng, cho thu&ecirc;, cho thu&ecirc; lại, tặng cho quyền sử dụng đất, thế chấp, g&oacute;p vốn bằng quyền sử dụng đất, trừ trường hợp quy định tại khoản 2 Điều n&agrave;y.<br />\r\n&nbsp;<br />\r\nTrường hợp trước ng&agrave;y 01 th&aacute;ng 11 năm 2007 người sử dụng đất đ&atilde; nộp hồ sơ xin cấp Giấy chứng nhận theo đ&uacute;ng quy định của ph&aacute;p luật m&agrave; chưa được cơ quan nh&agrave; nước c&oacute; thẩm quyền cấp Giấy chứng nhận v&agrave; người sử dụng đất c&oacute; một trong c&aacute;c loại giấy tờ về quyền sử dụng đất quy định tại c&aacute;c khoản 1, 2 v&agrave; 5 Điều 50 của Luật Đất đai th&igrave; vẫn được thực hiện c&aacute;c quyền chuyển đổi, chuyển nhượng, cho thu&ecirc;, cho thu&ecirc; lại, tặng cho quyền sử dụng đất, thế chấp, g&oacute;p vốn bằng quyền sử dụng đất&quot;.<br />\r\n&nbsp;<br />\r\nNhư vậy, kể từ ng&agrave;y 01/01/2008 th&igrave; người sử dụng đất phải c&oacute; Giấy chứng nhận mới được thực hiện c&aacute;c quyền chuyển đổi, chuyển nhượng, cho thu&ecirc;, cho thu&ecirc; lại, tặng cho quyền sử dụng đất, thế chấp, g&oacute;p vốn bằng quyền sử dụng đất. Do đ&oacute;, việc lập di ch&uacute;c kh&ocirc;ng bắt buộc người lập di ch&uacute;c phải c&oacute; Giấy chứng nhận quyền sử dụng đất mới được lập di ch&uacute;c m&agrave; chỉ cần c&oacute; c&aacute;c giấy tờ chứng minh quyền sử dụng đất, c&aacute;c giấy tờ l&agrave;m căn cứ để xin cấp Giấy chứng nhận quyền sử dụng đất được ghi nhận tại Điều 50 Luật đất đai năm 2003.<br />\r\n&nbsp;<br />\r\nVề việc lập di ch&uacute;c, di ch&uacute;c l&agrave; sự thể hiện &yacute; ch&iacute; của c&aacute; nh&acirc;n người để lại t&agrave;i sản cho người kh&aacute;c trước khi chết. Do đ&oacute;, bố bạn c&oacute; thể chỉ định người thừa kế, ph&acirc;n định t&agrave;i sản của m&igrave;nh theo &yacute; ch&iacute;, nguyện vọng của m&igrave;nh.<br />\r\n&nbsp;<br />\r\nVề h&igrave;nh thức của di ch&uacute;c th&igrave; di ch&uacute;c phải được lập th&agrave;nh văn bản, nếu kh&ocirc;ng thể lập được di ch&uacute;c bằng văn bản th&igrave; c&oacute; thể di ch&uacute;c miệng.<br />\r\n&nbsp;<br />\r\n- C&aacute;ch 1: Bố bạn mang theo giấy tờ nh&acirc;n th&acirc;n, giấy tờ chứng minh quyền sử dụng, sở hữu t&agrave;i sản của người lập di ch&uacute;c đến UBND x&atilde;/phường, Văn ph&ograve;ng c&ocirc;ng chứng hoặc ph&ograve;ng c&ocirc;ng chứng để l&agrave;m văn bản lập di ch&uacute;c.<br />\r\n&nbsp;<br />\r\n- C&aacute;ch 2: Bố bạn c&oacute; thể lập di ch&uacute;c bằng văn bản (viết tay hoặc đ&aacute;nh m&aacute;y) nhưng phải c&oacute; &iacute;t nhất 2 người l&agrave;m chứng. Người lập di ch&uacute;c phải k&yacute; t&ecirc;n hoặc điểm chỉ v&agrave;o bản di ch&uacute;c trước mặt những người l&agrave;m chứng; những người l&agrave;m chứng x&aacute;c nhận chữ k&yacute;, điểm chỉ của người lập di ch&uacute;c v&agrave; k&yacute; v&agrave;o bản di ch&uacute;c.<br />\r\n&nbsp;<br />\r\n- C&aacute;ch 3: Bố bạn tự viết di ch&uacute;c bằng tay v&agrave; k&yacute; v&agrave;o bản di ch&uacute;c (kh&ocirc;ng cần người l&agrave;m chứng).', 0, NULL, NULL, 1, 0, 75, '2017-07-13 15:30:57', '2017-07-14 09:06:27', 2, 2);
INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(37, 'Thủ tục chuyển nhượng đất liền kề như thế nào', 'thu-tuc-chuyen-nhuong-dat-lien-ke-nhu-the-nao', 'Thu tuc chuyen nhuong dat lien ke nhu the nao', 'Nếu quý vị muốn chuyển nhuộng đất liền kề trước hết phải biết được đất liền kề là gì và thủ tục chuyển nhượng đất liền kề như thế nào. Để biết rõ hơn những thông tin này, mời quý vị tham khảo bài viết dưới đây.', '2017/07/13/thu-tuc-chuyen-nhuong-dat-lien-ke-nhu-the-nao-1499938468.png', 5, '<h2>&nbsp;</h2>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"font-size:18px\"><strong><img alt=\"Thủ tục chuyển nhượng đất liền kề như thế nào\" src=\"/uploads/images/thu-tuc-chuyen-nhuong-dat-lien-ke-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></strong></span></h3>\r\n\r\n<h3><br />\r\n<span style=\"font-size:18px\"><strong>Thế n&agrave;o l&agrave; đất liền kề</strong></span></h3>\r\nĐất liền kề l&agrave; Khu đất liền nhau, tiếp nối nhau với khu đất đ&atilde; được x&aacute;c định.<br />\r\n&nbsp;\r\n<h3><span style=\"font-size:18px\"><strong>Thủ tục chuyển nhượng đất liền kề</strong></span></h3>\r\n<br />\r\nTr&igrave;nh tự, thủ tục t&aacute;ch thửa hoặc hợp thửa được thực hiện theo quy định tại điều 145 Nghị định 181 ng&agrave;y 29.10.2004 như sau:<br />\r\n<br />\r\n1 bộ hồ sơ gồm đơn xin t&aacute;ch thửa, hợp thửa v&agrave; giấy chứng nhận quyền sử dụng đất hoặc một trong c&aacute;c loại giấy tờ về quyền sử dụng đất quy định tại c&aacute;c khoản 1,2 v&agrave; 5 điều 50 của Luật đất đai, nộp tại Ph&ograve;ng T&agrave;i nguy&ecirc;n &amp; M&ocirc;i trường (đối với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n).<br />\r\n&nbsp;\r\n<h3><span style=\"font-size:18px\"><strong>Thủ tục mua th&ecirc;m đất liền kề</strong></span></h3>\r\n<br />\r\nNghị định số 198/2004/NĐ-CP ng&agrave;y 3/12/2004 của Ch&iacute;nh phủ về thu tiền sử dụng đất, khi được UBND quận, huyện, thị x&atilde; quyết định cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất th&igrave; hộ gia đ&igrave;nh, c&aacute; nh&acirc;n phải thực hiện nghĩa vụ t&agrave;i ch&iacute;nh như sau:<br />\r\n<br />\r\nChuyển từ đất vườn, ao trong c&ugrave;ng thửa đất c&oacute; nh&agrave; ở thuộc khu d&acirc;n cư kh&ocirc;ng được c&ocirc;ng nhận l&agrave; đất ở sang l&agrave;m đất ở th&igrave; thu tiền sử dụng đất bằng 50% ch&ecirc;nh lệch giữa thu tiền sử dụng đất t&iacute;nh theo gi&aacute; đất ở với thu tiền sử dụng đất t&iacute;nh theo gi&aacute; đất n&ocirc;ng nghiệp<br />\r\n<br />\r\nChuyển từ đất n&ocirc;ng nghiệp được Nh&agrave; nước giao kh&ocirc;ng thu tiền sử dụng đất sang đất ở th&igrave; thu tiền sử dụng đất bằng mức ch&ecirc;nh lệch giữa thu tiền sử dụng đất t&iacute;nh theo gi&aacute; đất ở với thu tiền sử dụng đất t&iacute;nh theo gi&aacute; đất n&ocirc;ng nghiệp;<br />\r\n<br />\r\nChuyển mục đ&iacute;ch sử dụng đất c&oacute; nguồn gốc từ nhận chuyển nhượng của người sử dụng đất hợp ph&aacute;p<br />\r\nsang đất ở th&igrave; thu tiền sử dụng đất như sau:<br />\r\n<br />\r\nĐất nhận chuyển nhượng c&oacute; nguồn gốc l&agrave; đất n&ocirc;ng nghiệp th&igrave; thu tiền sử dụng đất bằng mức ch&ecirc;nh lệch giữa thu tiền sử dụng đất t&iacute;nh theo gi&aacute; đất ở với thu tiền sử dụng đất t&iacute;nh theo gi&aacute; đất n&ocirc;ng nghiệp<br />\r\n<br />\r\nĐất nhận chuyển nhượng c&oacute; nguồn gốc l&agrave; đất phi n&ocirc;ng nghiệp (kh&ocirc;ng phải l&agrave; đất ở) th&igrave; thu tiền sử dụng đất bằng mức ch&ecirc;nh lệch giữa thu tiền sử dụng đất t&iacute;nh theo gi&aacute; đất ở với thu tiền sử dụng đất t&iacute;nh theo gi&aacute; đất phi n&ocirc;ng nghiệp<br />\r\n<br />\r\nGi&aacute; đất t&iacute;nh thu tiền sử dụng đất l&agrave; gi&aacute; đất theo mục đ&iacute;ch sử dụng của đất sau khi chuyển mục đ&iacute;ch sử dụng tại thời điểm ra quyết định cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất.<br />\r\n<br />\r\nGi&aacute; đất t&iacute;nh thu tiền sử dụng đất được x&aacute;c định theo quy định hiện h&agrave;nh của Ch&iacute;nh phủ, Bộ T&agrave;i ch&iacute;nh v&agrave; UBND cấp tỉnh c&ocirc;ng bố c&ocirc;ng khai v&agrave;o ng&agrave;y 1/1 h&agrave;ng năm\r\n<h3><span style=\"font-size:18px\"><strong>Thủ tục mua b&aacute;n đất vườn liền kề</strong></span></h3>\r\nTổ chức kinh tế, hộ gia đ&igrave;nh, c&aacute; nh&acirc;n được nhận chuyển nhượng, nhận g&oacute;p vốn, thu&ecirc; quyền sử dụng đất n&ocirc;ng nghiệp để thực hiện dự &aacute;n đầu tư sản xuất, kinh doanh phi n&ocirc;ng nghiệp khi c&oacute; đủ c&aacute;c điều kiện sau đ&acirc;y<br />\r\n<br />\r\nC&oacute; văn bản chấp thuận của cơ quan nh&agrave; nước c&oacute; thẩm quyền đối với tổ chức kinh tế nhận chuyển nhượng, nhận g&oacute;p vốn, thu&ecirc; quyền sử dụng đất n&ocirc;ng nghiệp để thực hiện dự &aacute;n<br />\r\n<br />\r\nMục đ&iacute;ch sử dụng đối với diện t&iacute;ch đất nhận chuyển nhượng, nhận g&oacute;p vốn, thu&ecirc; quyền sử dụng đất phải ph&ugrave; hợp với quy hoạch, kế hoạch sử dụng đất đ&atilde; được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt<br />\r\n<br />\r\nĐối với đất chuy&ecirc;n trồng l&uacute;a nước th&igrave; phải thực hiện theo quy định tại khoản 3 Điều 134 của Luật n&agrave;y<br />\r\n&nbsp;', 0, NULL, NULL, 1, 0, 76, '2017-07-13 16:35:01', '2017-07-14 09:05:34', 2, 2),
(38, 'Chuyển đất từ sổ đỏ sang sổ hồng như thế nào', 'chuyen-dat-tu-so-do-sang-so-hong-nhu-the-nao', 'Chuyen dat tu so do sang so hong nhu the nao', 'Nếu quý vị đang quan tâm đến việc chuyển đất từ sổ đỏ sang sổ hồng như thế nào. Hãy tham khảo ngay bài viết dưới đây, quý vị sẽ biết rõ hơn về các chuyển cần những thủ tục gì, chi phi bao nhiêu.', '2017/07/14/chuyen-dat-tu-so-do-sang-so-hong-nhu-the-nao-1500001348.png', 5, 'Qu&yacute; vị c&oacute; một mảnh đất hiện đang ở sổ đỏ v&agrave; b&acirc;y giờ muốn chuyển l&ecirc;n sổ hồng, trước hết qu&yacute; vị cần phải định h&igrave;nh hai việc l&agrave; phần thủ tục để chuyển v&agrave; chi ph&iacute;. Thanh Ph&uacute; Thịnh Land sẽ chia sẻ 2 vấn đề n&agrave;y r&otilde; hơn cho qu&yacute; vị tham khảo.<br />\r\n&nbsp;\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n\r\n<div style=\"text-align:center\"><img alt=\"Chuyển đất từ sổ đỏ sang sổ hồng như thế nào\" src=\"/uploads/images/chuyen-dat-tu-so-do-sang-so-hong-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Điều kiện chuyển từ sổ đổ l&ecirc;n sổ hồng</strong></span><br />\r\n<br />\r\nTheo quy định tại Điều 76 Nghị định 43/2013/NĐ-CP như sau:<br />\r\n<br />\r\nĐiều 76. Cấp đổi Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng. Việc cấp đổi Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; cấp được thực hiện trong c&aacute;c trường hợp sau:\r\n<ul>\r\n	<li>Người sử dụng đất c&oacute; nhu cầu đổi Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng hoặc c&aacute;c loại Giấy chứng nhận đ&atilde; cấp trước ng&agrave;y 10 th&aacute;ng 12 năm 2009 sang loại Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất</li>\r\n	<li>Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; cấp bị ố, nh&ograve;e, r&aacute;ch, hư hỏng</li>\r\n	<li>Do thực hiện dồn điền, đổi thửa, đo đạc x&aacute;c định lại diện t&iacute;ch, k&iacute;ch thước thửa đất</li>\r\n	<li>Trường hợp quyền sử dụng đất, quyền sở hữu t&agrave;i sản gắn liền với đất l&agrave; t&agrave;i sản chung của vợ v&agrave; chồng m&agrave; Giấy chứng nhận đ&atilde; cấp chỉ ghi họ, t&ecirc;n của vợ hoặc của chồng, nay c&oacute; y&ecirc;u cầu cấp đổi Giấy chứng nhận để ghi cả họ, t&ecirc;n vợ v&agrave; họ, t&ecirc;n chồng</li>\r\n</ul>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Thủ tục cấp đổi như thế n&agrave;o</strong></span><br />\r\n<br />\r\nTheo Th&ocirc;ng tư 24/2014/TT/BTNMT, anh cần phải nộp 1 bộ hồ sơ đề nghị cấp đổi giấy chứng nhận l&ecirc;n Văn ph&ograve;ng đăng k&yacute; đất đai.<br />\r\n<br />\r\nHồ sơ bao gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đơn đề nghị cấp đổi Giấy chứng nhận theo Mẫu số 10/ĐK</li>\r\n	<li>Bản gốc Giấy chứng nhận đ&atilde; cấp</li>\r\n</ul>\r\n<br />\r\nCơ quan c&oacute; thẩm quyền nhận hồ sơ v&agrave; trả kết quả<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Văn ph&ograve;ng đăng k&iacute; đất đai</li>\r\n	<li>Trường hợp anh c&oacute; nhu cầu nộp hồ sơ tại Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; th&igrave; Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi c&oacute; đất tiếp nhận hồ sơ v&agrave; trả kết quả. Trường hợp cấp đổi Giấy chứng nhận th&igrave; trong thời hạn 3 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y nhận đủ hồ sơ, Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; phải chuyển hồ sơ đến Văn ph&ograve;ng đăng k&yacute; đất đai.</li>\r\n</ul>\r\n<br />\r\nThời gian thực hiện: Kh&ocirc;ng qu&aacute; 07 ng&agrave;y.<br />\r\n<br />\r\n<strong>Thứ hai về việc chuyển mục đ&iacute;ch sử dụng đất từ đất n&ocirc;ng nghiệp sang đất ở</strong><br />\r\n<br />\r\n<strong>► Quy định về chuyển mục đ&iacute;ch sử dụng đất</strong><br />\r\n<br />\r\nĐiều 57 Luật đất đai năm 2013 quy định về Chuyển mục đ&iacute;ch sử dụng đất như sau:<br />\r\n<br />\r\n1. C&aacute;c trường hợp chuyển mục đ&iacute;ch sử dụng đất phải được ph&eacute;p của cơ quan nh&agrave; nước c&oacute; thẩm quyền bao gồm:\r\n<ul>\r\n	<li>Chuyển đất trồng l&uacute;a sang đất trồng c&acirc;y l&acirc;u năm, đất trồng rừng, đất nu&ocirc;i trồng thủy sản, đất l&agrave;m muối</li>\r\n	<li>Chuyển đất trồng c&acirc;y h&agrave;ng năm kh&aacute;c sang đất nu&ocirc;i trồng thủy sản nước mặn, đất l&agrave;m muối, đất nu&ocirc;i trồng thủy sản dưới h&igrave;nh thức ao, hồ, đầm</li>\r\n	<li>Chuyển đất rừng đặc dụng, đất rừng ph&ograve;ng hộ, đất rừng sản xuất sang sử dụng v&agrave;o mục đ&iacute;ch kh&aacute;c trong nh&oacute;m đất n&ocirc;ng nghiệp</li>\r\n	<li>Chuyển đất n&ocirc;ng nghiệp sang đất phi n&ocirc;ng nghiệp</li>\r\n	<li>Chuyển đất phi n&ocirc;ng nghiệp được Nh&agrave; nước giao đất kh&ocirc;ng thu tiền sử dụng đất sang đất phi n&ocirc;ng nghiệp được Nh&agrave; nước giao đất c&oacute; thu tiền sử dụng đất hoặc thu&ecirc; đất</li>\r\n	<li>Chuyển đất phi n&ocirc;ng nghiệp kh&ocirc;ng phải l&agrave; đất ở sang đất ở</li>\r\n	<li>Chuyển đất x&acirc;y dựng c&ocirc;ng tr&igrave;nh sự nghiệp, đất sử dụng v&agrave;o mục đ&iacute;ch c&ocirc;ng cộng c&oacute; mục đ&iacute;ch kinh doanh, đất sản xuất, kinh doanh phi n&ocirc;ng nghiệp kh&ocirc;ng phải l&agrave; đất thương mại, dịch vụ sang đất thương mại, dịch vụ; chuyển đất thương mại, dịch vụ, đất x&acirc;y dựng c&ocirc;ng tr&igrave;nh sự nghiệp sang đất cơ sở sản xuất phi n&ocirc;ng nghiệp.</li>\r\n</ul>\r\n<br />\r\n2. Khi chuyển mục đ&iacute;ch sử dụng đất theo quy định tại khoản 1 Điều n&agrave;y th&igrave; người sử dụng đất phải thực hiện nghĩa vụ t&agrave;i ch&iacute;nh theo quy định của ph&aacute;p luật; chế độ sử dụng đất, quyền v&agrave; nghĩa vụ của người sử dụng đất được &aacute;p dụng theo loại đất sau khi được chuyển mục đ&iacute;ch sử dụng.<br />\r\n<br />\r\n<strong>► Thủ tục chuyển mục đ&iacute;ch sử dụng đất</strong><br />\r\n<br />\r\nHồ sơ bao gồm hồ sơ xin chuyển mục đ&iacute;ch sử dụng đất. Hồ sơ (Theo Th&ocirc;ng tư 30/2014/TT-BTNMT) bao gồm:\r\n<ul>\r\n	<li>Đơn xin chuyển mục đ&iacute;ch sử dụng đất (Mẫu số 01)</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất hoặc Giấy chứng nhận quyền sở hữu nh&agrave; ở v&agrave; quyền sử dụng đất ở hoặc Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất.</li>\r\n</ul>\r\n<br />\r\nb, Cơ quan tiếp nhận hồ sơ: Ủy ban nh&acirc;n d&acirc;n x&atilde; (UBND x&atilde; chuyển l&ecirc;n cho văn ph&ograve;ng đăng k&yacute; đất đai) hoặc nộp tại văn ph&ograve;ng đăng k&yacute; đất đai<br />\r\n<br />\r\nTr&igrave;nh tự giải quyết như sao<br />\r\n<br />\r\nVăn ph&ograve;ng đăng k&yacute; đất đai c&oacute; tr&aacute;ch nhiệm thực hiện c&aacute;c c&ocirc;ng việc sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Kiểm tra hồ sơ, x&aacute;c nhận v&agrave;o đơn đề nghị về l&yacute; do cấp đổi Giấy chứng nhận</li>\r\n	<li>Lập hồ sơ tr&igrave;nh cơ quan c&oacute; thẩm quyền cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất</li>\r\n	<li>Cập nhật, chỉnh l&yacute; hồ sơ địa ch&iacute;nh, cơ sở dữ liệu đất đai; trao Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho người được cấp hoặc gửi Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; để trao đối với trường hợp nộp hồ sơ tại cấp x&atilde;.</li>\r\n</ul>\r\n<br />\r\nThời gian giải quyết: kh&ocirc;ng qu&aacute; 15 ng&agrave;y.<br />\r\n<br />\r\n<strong>Về nghĩa vụ t&agrave;i ch&iacute;nh</strong><br />\r\n<br />\r\nTheo quy định của ph&aacute;p luật, trường hợp chuyển mục đ&iacute;ch sử dụng đất của anh phải thực hiện nghĩa vụ t&agrave;i ch&iacute;nh.<br />\r\n<br />\r\nChuyển mục đ&iacute;ch sử dụng đất c&oacute; nguồn gốc từ nhận chuyển nhượng quyền sử dụng đất hợp ph&aacute;p theo ph&aacute;p luật đất đai của người sử dụng đất sang đất ở th&igrave; căn cứ v&agrave;o nguồn gốc đất nhận chuyển nhượng để thu tiền sử dụng đất theo mức quy định tại Điểm a, Điểm b, Điểm c Khoản 2 Điều 5 Nghị định 45/2014/NĐ-CP.<br />\r\n<br />\r\n<strong>Về thủ tục cấp giấy chứng nhận, Khoản 1 Điều 8 Th&ocirc;ng tư 24/ 2014/TT-BTNMT quy định</strong><br />\r\n<br />\r\n<strong>Điều 8. Hồ sơ nộp khi thực hiện thủ tục đăng k&yacute;, cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất lần đầu</strong><br />\r\n<br />\r\nHồ sơ nộp khi thực hiện thủ tục đăng k&yacute;, cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất lần đầu gồm c&oacute;<br />\r\n<br />\r\nĐơn đăng k&yacute;, cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất theo Mẫu số 04/ĐK<br />\r\n<br />\r\nMột trong c&aacute;c loại giấy tờ quy định tại Điều 100 của Luật Đất đai v&agrave; Điều 18 của Nghị định số 43/2014/NĐ-CP ng&agrave;y 15 th&aacute;ng 5 năm 2014 của Ch&iacute;nh phủ quy định chi tiết thi h&agrave;nh một số điều của Luật Đất đai (sau đ&acirc;y gọi l&agrave; Nghị định số 43/2014/NĐ-CP) đối với trường hợp đăng k&yacute; về quyền sử dụng đất<br />\r\n<br />\r\nMột trong c&aacute;c giấy tờ quy định tại c&aacute;c Điều 31, 32, 33 v&agrave; 34 của Nghị định số 43/2014/NĐ-CP đối với trường hợp đăng k&yacute; về quyền sở hữu t&agrave;i sản gắn liền với đất<br />\r\n<br />\r\nTrường hợp đăng k&yacute; về quyền sở hữu nh&agrave; ở hoặc c&ocirc;ng tr&igrave;nh x&acirc;y dựng th&igrave; phải c&oacute; sơ đồ nh&agrave; ở, c&ocirc;ng tr&igrave;nh x&acirc;y dựng (trừ trường hợp trong giấy tờ về quyền sở hữu nh&agrave; ở, c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; c&oacute; sơ đồ ph&ugrave; hợp với hiện trạng nh&agrave; ở, c&ocirc;ng tr&igrave;nh đ&atilde; x&acirc;y dựng)<br />\r\n<br />\r\nB&aacute;o c&aacute;o kết quả r&agrave; so&aacute;t hiện trạng sử dụng đất đối với trường hợp tổ chức trong nước, cơ sở t&ocirc;n gi&aacute;o đang sử dụng đất từ trước ng&agrave;y 01 th&aacute;ng 7 năm 2004 theo Mẫu số 08/ĐK<br />\r\n<br />\r\nChứng từ thực hiện nghĩa vụ t&agrave;i ch&iacute;nh; giấy tờ li&ecirc;n quan đến việc miễn, giảm nghĩa vụ t&agrave;i ch&iacute;nh về đất đai, t&agrave;i sản gắn liền với đất (nếu c&oacute;)<br />\r\n<br />\r\nĐối với đơn vị lực lượng vũ trang nh&acirc;n d&acirc;n sử dụng đất v&agrave;o mục đ&iacute;ch quốc ph&ograve;ng, an ninh th&igrave; ngo&agrave;i giấy tờ quy định tại c&aacute;c Điểm a, b v&agrave; d Khoản n&agrave;y phải c&oacute; quyết định của Bộ trưởng Bộ Quốc ph&ograve;ng, Bộ trưởng Bộ C&ocirc;ng an về vị tr&iacute; đ&oacute;ng qu&acirc;n hoặc địa điểm c&ocirc;ng tr&igrave;nh; bản sao quyết định của Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt quy hoạch sử dụng đất v&agrave;o mục đ&iacute;ch quốc ph&ograve;ng, an ninh tr&ecirc;n địa b&agrave;n c&aacute;c qu&acirc;n khu, tr&ecirc;n địa b&agrave;n c&aacute;c đơn vị thuộc Bộ Tư lệnh Bộ đội Bi&ecirc;n ph&ograve;ng, tr&ecirc;n địa b&agrave;n tỉnh, th&agrave;nh phố trực thuộc Trung ương m&agrave; c&oacute; t&ecirc;n đơn vị đề nghị cấp Giấy chứng nhận<br />\r\n<br />\r\nTrường hợp c&oacute; đăng k&yacute; quyền sử dụng hạn chế đối với thửa đất liền kề phải c&oacute; hợp đồng hoặc văn bản thỏa thuận hoặc quyết định của T&ograve;a &aacute;n nh&acirc;n d&acirc;n về việc x&aacute;c lập quyền sử dụng hạn chế thửa đất liền kề, k&egrave;m theo sơ đồ thể hiện vị tr&iacute;, k&iacute;ch thước phần diện t&iacute;ch thửa đất m&agrave; người sử dụng thửa đất liền kề được quyền sử dụng hạn chế<br />\r\n<br />\r\n<strong>Điều 86. Thủ tục đ&iacute;nh ch&iacute;nh Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; cấp&nbsp;</strong><br />\r\n<br />\r\nNgười sử dụng đất, chủ sở hữu t&agrave;i sản gắn liền với đất nộp Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; cấp c&oacute; sai s&oacute;t cho Văn ph&ograve;ng đăng k&yacute; đất đai để đ&iacute;nh ch&iacute;nh. Trường hợp sai s&oacute;t do lỗi của người sử dụng đất, chủ sở hữu t&agrave;i sản gắn liền với đất th&igrave; người sử dụng đất, chủ sở hữu t&agrave;i sản gắn liền với đất phải c&oacute; đơn đề nghị để được đ&iacute;nh ch&iacute;nh.&nbsp;<br />\r\n<br />\r\nTrường hợp Văn ph&ograve;ng đăng k&yacute; đất đai ph&aacute;t hiện Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; cấp c&oacute; sai s&oacute;t th&igrave; th&ocirc;ng b&aacute;o cho người sử dụng đất biết v&agrave; y&ecirc;u cầu người sử dụng đất, chủ sở hữu t&agrave;i sản gắn liền với đất nộp Giấy chứng nhận đ&atilde; cấp để thực hiện đ&iacute;nh ch&iacute;nh.&nbsp;<br />\r\n<br />\r\nVăn ph&ograve;ng đăng k&yacute; đất đai c&oacute; tr&aacute;ch nhiệm kiểm tra; lập bi&ecirc;n bản kết luận về nội dung v&agrave; nguy&ecirc;n nh&acirc;n sai s&oacute;t; lập hồ sơ tr&igrave;nh cơ quan c&oacute; thẩm quyền thực hiện đ&iacute;nh ch&iacute;nh v&agrave;o Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; cấp c&oacute; sai s&oacute;t; đồng thời chỉnh l&yacute; nội dung sai s&oacute;t v&agrave;o hồ sơ địa ch&iacute;nh, cơ sở dữ liệu đất đai.&nbsp;<br />\r\n<br />\r\nTrường hợp đ&iacute;nh ch&iacute;nh m&agrave; người được cấp Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng c&oacute; y&ecirc;u cầu cấp đổi sang Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất th&igrave; Văn ph&ograve;ng đăng k&yacute; đất đai tr&igrave;nh cơ quan c&oacute; thẩm quyền để cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất.<br />\r\n<br />\r\nThời gian thực hiện việc đ&iacute;nh ch&iacute;nh l&agrave; tối đa 30 ng&agrave;y kể từ ng&agrave;y cơ quan quản l&yacute; đất đai nhận đủ hồ sơ hợp lệ.<br />\r\n<br />\r\nVề thủ tục cấp đổi Giấy chứng nhận, Điều 76 Nghị định 43/2014/NĐ-CP quy định:<br />\r\n<br />\r\n<strong>Điều 76. Cấp đổi Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng&nbsp;</strong><br />\r\n<br />\r\nViệc cấp đổi Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; cấp được thực hiện trong c&aacute;c trường hợp sau:&nbsp;\r\n<ul>\r\n	<li>Người sử dụng đất c&oacute; nhu cầu đổi Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng hoặc c&aacute;c loại Giấy chứng nhận đ&atilde; cấp trước ng&agrave;y 10 th&aacute;ng 12 năm 2009 sang loại Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất;&nbsp;</li>\r\n	<li>Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; cấp bị ố, nh&ograve;e, r&aacute;ch, hư hỏng;&nbsp;</li>\r\n	<li>Do thực hiện dồn điền, đổi thửa, đo đạc x&aacute;c định lại diện t&iacute;ch, k&iacute;ch thước thửa đất;&nbsp;</li>\r\n	<li>Trường hợp quyền sử dụng đất, quyền sở hữu t&agrave;i sản gắn liền với đất l&agrave; t&agrave;i sản chung của vợ v&agrave; chồng m&agrave; Giấy chứng nhận đ&atilde; cấp chỉ ghi họ, t&ecirc;n của vợ hoặc của chồng, nay c&oacute; y&ecirc;u cầu cấp đổi Giấy chứng nhận để ghi cả họ, t&ecirc;n vợ v&agrave; họ, t&ecirc;n chồng.&nbsp;</li>\r\n</ul>\r\n<br />\r\nNgười sử dụng đất nộp 01 bộ hồ sơ đề nghị cấp đổi giấy chứng nhận. &nbsp;Văn ph&ograve;ng đăng k&yacute; đất đai c&oacute; tr&aacute;ch nhiệm thực hiện c&aacute;c c&ocirc;ng việc sau:&nbsp;\r\n<ul>\r\n	<li>Kiểm tra hồ sơ, x&aacute;c nhận v&agrave;o đơn đề nghị về l&yacute; do cấp đổi Giấy chứng nhận;</li>\r\n	<li>Lập hồ sơ tr&igrave;nh cơ quan c&oacute; thẩm quyền cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất;</li>\r\n	<li>Cập nhật, chỉnh l&yacute; hồ sơ địa ch&iacute;nh, cơ sở dữ liệu đất đai; trao Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho người được cấp hoặc gửi Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; để trao đối với trường hợp nộp hồ sơ tại cấp x&atilde;.&nbsp;</li>\r\n</ul>\r\n<br />\r\nTrường hợp cấp đổi Giấy chứng nhận sau khi dồn điền đổi thửa, đo đạc lập bản đồ địa ch&iacute;nh m&agrave; Giấy chứng nhận đ&atilde; cấp đang thế chấp tại tổ chức t&iacute;n dụng th&igrave; người sử dụng đất nộp bản sao hợp đồng thế chấp quyền sử dụng đất, t&agrave;i sản gắn liền với đất thay cho Giấy chứng nhận đ&atilde; cấp để l&agrave;m thủ tục cấp đổi giấy mới.&nbsp;<br />\r\n<br />\r\nVăn ph&ograve;ng đăng k&yacute; đất đai c&oacute; tr&aacute;ch nhiệm th&ocirc;ng b&aacute;o danh s&aacute;ch c&aacute;c trường hợp l&agrave;m thủ tục cấp đổi Giấy chứng nhận cho tổ chức t&iacute;n dụng nơi nhận thế chấp quyền sử dụng đất, t&agrave;i sản gắn liền với đất; x&aacute;c nhận việc đăng k&yacute; thế chấp v&agrave;o Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất sau khi được cơ quan c&oacute; thẩm quyền k&yacute; cấp đổi.&nbsp;<br />\r\n<br />\r\nViệc trao Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất mới cấp đổi quy định tại Khoản 4 Điều n&agrave;y được thực hiện đồng thời giữa ba b&ecirc;n gồm Văn ph&ograve;ng đăng k&yacute; đất đai, người sử dụng đất v&agrave; tổ chức t&iacute;n dụng theo quy định như sau:&nbsp;\r\n<ul>\r\n	<li>Người sử dụng đất k&yacute;, nhận Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất mới từ Văn ph&ograve;ng đăng k&yacute; đất đai để trao cho tổ chức t&iacute;n dụng nơi đang nhận thế chấp;&nbsp;</li>\r\n	<li>Tổ chức t&iacute;n dụng c&oacute; tr&aacute;ch nhiệm trao Giấy chứng nhận cũ đang thế chấp cho Văn ph&ograve;ng đăng k&yacute; đất đai để quản l&yacute;.&nbsp;</li>\r\n</ul>\r\n<br />\r\n<strong>Điều 10. Hồ sơ nộp khi thực hiện thủ tục cấp đổi, cấp lại, đ&iacute;nh ch&iacute;nh, thu hồi giấy chứng nhận&nbsp;</strong><br />\r\n<br />\r\nHồ sơ nộp khi thực hiện thủ tục cấp đổi Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng gồm c&oacute;:\r\n<ul>\r\n	<li>Đơn đề nghị cấp đổi Giấy chứng nhận theo Mẫu số 10/ĐK</li>\r\n	<li>Bản gốc Giấy chứng nhận đ&atilde; cấp</li>\r\n	<li>Bản sao hợp đồng thế chấp quyền sử dụng đất, t&agrave;i sản gắn liền với đất thay cho bản gốc Giấy chứng nhận đ&atilde; cấp đối với trường hợp cấp đổi Giấy chứng nhận sau khi dồn điền đổi thửa, đo đạc lập bản đồ địa ch&iacute;nh m&agrave; Giấy chứng nhận đ&atilde; cấp đang thế chấp tại tổ chức t&iacute;n dụng.</li>\r\n</ul>\r\n', 0, NULL, NULL, 1, 0, 77, '2017-07-14 10:03:16', '2017-07-14 10:03:16', 2, 2),
(39, 'Thủ tục chuyển nhượng nhà chung cư chưa có sổ đỏ như thế nào', 'thu-tuc-chuyen-nhuong-nha-chung-cu-chua-co-so-do-nhu-the-nao', 'Thu tuc chuyen nhuong nha chung cu chua co so do nhu the nao', 'Quý vị đang sở hữu một căn hộ chưng cư và đang muốn chuyển nhượng lại cho người khác. Nhưng lại không biết thủ tục chuyển nhượng nhà chung cư chưa có sổ đỏ như thế nào thì mời tham khảo bài viết dưới đây.', '2017/07/14/thu-tuc-chuyen-nhuong-nha-chung-cu-chua-co-so-do-nhu-the-nao-1500002494.png', 5, 'Trong trường hợp căn hộ chưa được cấp sổ đỏ nhưng đ&atilde; c&oacute; bi&ecirc;n bản b&agrave;n giao căn hộ v&agrave; c&oacute; hợp đồng mua b&aacute;n với chủ đầu tư th&igrave; b&ecirc;n mua căn hộ c&oacute; thể chuyển nhượng nh&agrave; chung cư chưa c&oacute; sổ đỏ cho qu&yacute; vị dưới h&igrave;nh thức chuyển nhượng hợp đồng mua b&aacute;n căn hộ theo quy định tại khoản 2 Điều 123 Luật nh&agrave; ở 2014<br />\r\n<br />\r\nTrường hợp b&ecirc;n mua nh&agrave; ở thương mại của chủ đầu tư nếu thuộc diện chưa nộp hồ sơ đề nghị cơ quan nh&agrave; nước c&oacute; thẩm quyền cấp Giấy chứng nhận đối với nh&agrave; ở đ&oacute; v&agrave; c&oacute; nhu cầu th&igrave; được chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở; b&ecirc;n nhận chuyển nhượng hợp đồng c&oacute; tr&aacute;ch nhiệm thực hiện đầy đủ nghĩa vụ theo hợp đồng mua b&aacute;n nh&agrave; ở đ&atilde; k&yacute; với chủ đầu tư.\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"Thủ tục chuyển nhượng nhà chung cư chưa có sổ đỏ như thế nào\" src=\"/uploads/images/thu-tuc-chuyen-nhuong-nha-chung-cu-chua-co-so-do-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nVề điều kiện chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; chung cư được quy định tại điều 32 th&ocirc;ng tư 19/2016/TT-BXD, cụ thể Khoản 1 quy định như sau.<br />\r\n<br />\r\nTổ chức, c&aacute; nh&acirc;n mua nh&agrave; ở của chủ đầu tư dự &aacute;n x&acirc;y dựng nh&agrave; ở thương mại chưa nhận b&agrave;n giao nh&agrave; ở hoặc đ&atilde; nhận b&agrave;n giao nh&agrave; ở c&oacute; quyền chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở cho tổ chức, c&aacute; nh&acirc;n kh&aacute;c khi hồ sơ đề nghị cấp Giấy chứng nhận chưa nộp cho cơ quan nh&agrave; nước c&oacute; thẩm quyền<br />\r\n<br />\r\nDo đ&oacute;, trong trường hợp n&agrave;y, nếu căn hộ đ&oacute; chưa được chủ đầu tư nộp hồ sơ đề nghị cơ quan c&oacute; thẩm quyền cấp sổ đỏ th&igrave; b&ecirc;n mua ho&agrave;n to&agrave;n c&oacute; thể chuyển nhượng lại căn hộ cho qu&yacute; vị.<br />\r\n<br />\r\n<strong>Tr&igrave;nh tự, thủ tục chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; chung cư được quy định tại Điều 33 Th&ocirc;ng tư 19/2016/TT-BXD, cụ thể gồm c&aacute;c bước sau:</strong><br />\r\n<br />\r\n&nbsp;Bước 1: Bạn v&agrave; b&ecirc;n chuyển nhượng thống nhất lập văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở theo quy định của ph&aacute;p luật.<br />\r\n<br />\r\n&nbsp;Trong trường hợp n&agrave;y, nếu b&ecirc;n b&aacute;n kh&ocirc;ng phải l&agrave; tổ chức c&oacute; chứng năng chuy&ecirc;n về kinh doanh bất động sản th&igrave; văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở phải được c&ocirc;ng chứng hoặc chứng thực theo quy định của ph&aacute;p luật.<br />\r\n<br />\r\n<strong>Hồ sơ đề nghị c&ocirc;ng chứng, chứng thực: được quy định tại điểm a khoản 2 điều 33 bao gồm:</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>07 bản ch&iacute;nh văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở</li>\r\n	<li>Bản ch&iacute;nh hợp đồng mua b&aacute;n nh&agrave; ở đ&atilde; k&yacute; với chủ đầu tư dự &aacute;n x&acirc;y dựng nh&agrave; ở thương mại</li>\r\n	<li>Bản sao c&oacute; chứng thực hoặc bản sao v&agrave; phải xuất tr&igrave;nh bản ch&iacute;nh để đối chiếu của c&aacute;c giấy tờ: Chứng minh nh&acirc;n d&acirc;n hoặc thẻ căn cước c&ocirc;ng d&acirc;n hoặc hộ chiếu c&ograve;n gi&aacute; trị</li>\r\n	<li>C&aacute;c giấy tờ kh&aacute;c theo quy định của ph&aacute;p luật về c&ocirc;ng chứng, chứng thực. Tổ chức h&agrave;nh nghề c&ocirc;ng chứng, Cơ quan chứng thực c&oacute; tr&aacute;ch nhiệm c&ocirc;ng chứng, chứng thực v&agrave;o văn bản chuyển nhượng hợp đồng theo thời hạn quy định của ph&aacute;p luật về c&ocirc;ng chứng, chứng thực.</li>\r\n</ul>\r\nBước 2: Sau khi thực hiện c&aacute;c nghĩa vụ nộp thuế, ph&iacute;, lệ ph&iacute; cho việc chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở, bạn nộp 01 bộ hồ sơ đề nghị chủ đầu tư x&aacute;c nhận v&agrave;o văn bản chuyển nhượng hợp đồng.<br />\r\n<br />\r\n<strong>Hồ sơ đề nghị chủ đầu tư x&aacute;c nhận, được quy định tại Điểm a Khoản 3 Điều 19 bao gồm:</strong>\r\n\r\n<ul>\r\n	<li>05 bản ch&iacute;nh văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở trong đ&oacute; c&oacute; 01 bản của b&ecirc;n chuyển nhượng</li>\r\n	<li>Bản ch&iacute;nh hợp đồng mua b&aacute;n nh&agrave; ở đ&atilde; k&yacute; với chủ đầu tư dự &aacute;n x&acirc;y dựng nh&agrave; ở thương mại</li>\r\n	<li>Bi&ecirc;n lai nộp thuế cho việc chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở hoặc giấy tờ chứng minh về việc được miễn thuế theo quy định ph&aacute;p luật về thuế</li>\r\n	<li>Bản sao c&oacute; chứng thực hoặc bản sao chứng minh nh&acirc;n d&acirc;n hoặc thẻ căn cước c&ocirc;ng d&acirc;n hoặc hộ chiếu</li>\r\n</ul>\r\n<br />\r\n<strong>Chủ đầu tư x&aacute;c nhận v&agrave; văn bản chuyển nhượng hợp đồng mua b&aacute;n trong thời hạn 05 ng&agrave;y l&agrave;m việc v&agrave; giao lại cho b&ecirc;n nộp hồ sơ c&aacute;c giấy tờ sau:</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>02 văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở đ&atilde; c&oacute; x&aacute;c nhận của chủ đầu tư, trong đ&oacute; c&oacute; 01 bản của b&ecirc;n chuyển nhượng v&agrave; 01 bản của b&ecirc;n nhận chuyển nhượng;</li>\r\n	<li>Bản ch&iacute;nh hợp đồng mua b&aacute;n nh&agrave; ở đ&atilde; k&yacute; với chủ đầu tư dự &aacute;n x&acirc;y dựng nh&agrave; ở thương mại; bản ch&iacute;nh văn bản chuyển nhượng hợp đồng của lần chuyển nhượng liền kề trước đ&oacute;</li>\r\n	<li>Bi&ecirc;n lai nộp thuế cho việc chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở hoặc giấy tờ chứng minh về việc được miễn thuế theo quy định ph&aacute;p luật về thuế.</li>\r\n</ul>\r\n<br />\r\nBước 3: B&ecirc;n nhận chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở cuối c&ugrave;ng được cơ quan nh&agrave; nước cấp c&oacute; thẩm quyền cấp Giấy chứng nhận theo quy định của ph&aacute;p luật về đất đai.', 0, NULL, NULL, 1, 0, 78, '2017-07-14 10:22:33', '2017-07-14 10:22:33', 2, 2),
(40, 'Quy định về thủ tục sang tên quyền sử dụng đất như thế nào', 'quy-dinh-ve-thu-tuc-sang-ten-quyen-su-dung-dat-nhu-the-nao', 'Quy dinh ve thu tuc sang ten quyen su dung dat nhu the nao', 'Quý vị đang muốn chuyển nhượng lại quyền sử dụng đất cho một ai đó. Nhưng lại gặp khó khăn không biết quy định về thủ tục sang tên quyền sử dụng đất như thế nào. Hãy tham khảo ngay bài viết dưới đây để biết rõ hơn về quy định.', '2017/07/14/quy-dinh-ve-thu-tuc-sang-ten-quyen-su-dung-dat-nhu-the-nao-1500005542.png', 5, '<div style=\"text-align:center\"><img alt=\"Quy định về thủ tục sang tên quyền sử dụng đất như thế nào\" src=\"/uploads/images/quy-dinh-ve-thu-tuc-sang-ten-quyen-su-dung-dat-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nQu&yacute; vị n&ecirc;n biết những cơ sở ph&aacute;p l&yacute; n&agrave;o để quy định về việc l&agrave;m thủ tục chuyển quyền sử dụng đất cho người kh&aacute;c. Tham khảo những cơ sở ph&aacute;p l&yacute; dưới đ&acirc;y.\r\n<ul>\r\n	<li>Luật đất đai năm 2013</li>\r\n	<li>Nghị định 43/2014/NĐ-CP hướng dẫn thi h&agrave;nh Luật đất đai</li>\r\n	<li>Nghị định 45/2014/NĐ-CP Quy định về tiền sử dụng đất</li>\r\n	<li>Luật Cư tr&uacute; số 81/2006/QH11 của Quốc hội</li>\r\n	<li>Nghị định số 31/2014/NĐ-CP của Ch&iacute;nh phủ : Quy định chi tiết một số điều v&agrave; biện ph&aacute;p thi h&agrave;nh Luật cư tr&uacute;</li>\r\n	<li>Bộ luật d&acirc;n sự 2015</li>\r\n	<li>Luật c&ocirc;ng chứng số 53/2014/QH13 của Quốc hội</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 1: C&aacute;c b&ecirc;n đến cơ quan c&ocirc;ng chứng lập hợp đồng chuyển nhượng, thừa kế, cho tặng</strong><br />\r\n<br />\r\n(Ch&uacute; &yacute;: Trong thời hạn 10 &ndash; 30 ng&agrave;y kể từ ng&agrave;y lập hợp đồng, c&aacute;c b&ecirc;n phải k&ecirc; khai lệ ph&iacute; trước bạ, thuế thu nhập c&aacute; nh&acirc;n, nếu qu&aacute; thời hạn tr&ecirc;n sẽ bị phạt theo quy định của nh&agrave; nước)<br />\r\n<br />\r\n<strong>Bước 2: K&ecirc; khai nghĩa vụ t&agrave;i ch&iacute;nh tại UBND quận/huyện nơi c&oacute; nh&agrave;, đất</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Tờ khai lệ ph&iacute; trước bạ (02 bản do b&ecirc;n mua k&yacute;)</li>\r\n	<li>Tờ khai thuế thu nhập c&aacute; nh&acirc;n (02 bản do b&ecirc;n b&aacute;n k&yacute;. Ri&ecirc;ng trường hợp cho tặng 04 bản).</li>\r\n	<li>Hợp đồng c&ocirc;ng chứng đ&atilde; lập (01 bản ch&iacute;nh)</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản gắn liền với đất (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền)</li>\r\n	<li>CMND + Sổ hộ khẩu của cả b&ecirc;n mua v&agrave; b&ecirc;n b&aacute;n (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền)</li>\r\n	<li>Đối với trường hợp cho tặng, thừa kế phải c&oacute; giấy tờ chứng minh quan hệ nh&acirc;n th&acirc;n của người cho v&agrave; người nhận để được miễn thuế thu nhập c&aacute; nh&acirc;n.</li>\r\n</ul>\r\n<br />\r\nThời hạn c&oacute; th&ocirc;ng b&aacute;o nộp thuế: 15 ng&agrave;y<br />\r\n<br />\r\n<strong>Bước 3: K&ecirc; khai sang t&ecirc;n (tại UBND quận/huyện nơi c&oacute; nh&agrave;, đất)</strong>\r\n\r\n<ul>\r\n	<li>Đơn đề nghị đăng k&yacute; biến động (01 bản do b&ecirc;n b&aacute;n k&yacute;); Trong trường hợp c&oacute; thoả thuận trong hợp đồng về việc b&ecirc;n mua thực hiện thủ tục h&agrave;nh ch&iacute;nh th&igrave; b&ecirc;n mua c&oacute; thể k&yacute; thay.</li>\r\n	<li>Hợp đồng chuyển nhượng; hợp đồng tặng cho; hoặc văn bản thỏa thuận ph&acirc;n chia di sản; văn bản khai nhận di sản;</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; v&agrave; t&agrave;i sản gắn liền với đất (bản gốc)</li>\r\n	<li>Giấy nộp tiền v&agrave;o nh&acirc;n s&aacute;ch nh&agrave; nước (bản gốc)</li>\r\n	<li>Bản sao CMND + Sổ hộ khẩu của b&ecirc;n nhận chuyển nhượng, thừa kế, cho tặng.</li>\r\n</ul>\r\n<br />\r\n&nbsp;Thời hạn sang t&ecirc;n: 15 &ndash; 30 ng&agrave;y<br />\r\n<br />\r\n<strong>Bước 4: Nộp đủ lệ ph&iacute; theo quy định v&agrave; nhận sổ đỏ</strong><br />\r\n<br />\r\nNộp ph&iacute; v&agrave; lệ ph&iacute; sang t&ecirc;n sổ đỏ sẽ c&oacute; h&oacute;a đơn chứng từ r&otilde; r&agrave;ng nhưng đ&ocirc;i khi cơ quan thuế sẽ t&iacute;nh thuế sai, chồng ch&eacute;o 2 lần&hellip;. Bạn n&ecirc;n nhờ người c&oacute; hiểu biết về ph&aacute;p luật tư vấn để tr&aacute;nh đ&oacute;ng thừa tiền. Từng quận, huyện sẽ c&oacute; thời gian l&agrave;m thủ tục sang t&ecirc;n v&agrave; phiếu hẹn thời gian trả hồ sơ h&agrave;nh ch&iacute;nh kh&aacute;c nhau l&ecirc;n c&oacute; đ&ocirc;i ch&uacute;t sai lệch về thời gian mong c&aacute;c bạn th&ocirc;ng cảm.<br />\r\n<br />\r\nT&oacute;m lại thời gian sang t&ecirc;n sổ đỏ khoảng 30 &ndash; 45 ng&agrave;y kể từ ng&agrave;y nộp hồ sơ đối với hồ sơ b&igrave;nh thường. Trường hợp cần sang t&ecirc;n sổ đỏ nhanh, sang t&ecirc;n sổ đỏ với hồ sơ kh&oacute; c&aacute;c bạn sẽ mất th&ecirc;m chi ph&iacute; dịch vụ. Với khoảng thời gian n&agrave;y, hai chồng bạn c&oacute; thể sắp xếp thời gian về nước để sang t&ecirc;n sổ đỏ cho hợp l&yacute;.<br />\r\n<br />\r\n<strong>Lệ ph&iacute; sang t&ecirc;n sổ đỏ gồm</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Lệ ph&iacute; địa ch&iacute;nh: 15.000 đồng/trường hợp</li>\r\n	<li>Lệ ph&iacute; thẩm định: Mức thu t&iacute;nh bằng 0,15% gi&aacute; trị (sang t&ecirc;n) chuyển nhượng (Tối thiểu 100.000 đồng đến tối đa kh&ocirc;ng qu&aacute; 5.000.000 đồng/trường hợp)</li>\r\n</ul>\r\n<br />\r\n<strong>THAM KHẢO TH&Ecirc;M MỐT SỐ TH&Ocirc;NG TIN KH&Aacute;C</strong><br />\r\n<br />\r\nThủ tục chuyển nhượng, tặng cho quyền sử dụng đất v&agrave; t&agrave;i sản gắn liền với đất:<br />\r\n<br />\r\n- Trước hết, lập hợp đồng chuyển nhượng, tặng cho quyền sử dụng đất theo quy định tại Điểm a Khoản 3 Điều 167 Luật Đất đai 2013:<br />\r\n<br />\r\nHợp đồng chuyển nhượng, tặng cho, thế chấp, g&oacute;p vốn bằng quyền sử dụng đất, quyền sử dụng đất v&agrave; t&agrave;i sản gắn liền với đất phải được c&ocirc;ng chứng hoặc chứng thực, trừ trường hợp kinh doanh bất động sản quy định tại điểm b khoản n&agrave;y<br />\r\n<br />\r\n- Tiến h&agrave;nh khai thuế v&agrave; lệ ph&iacute; trước bạ.<br />\r\n<br />\r\n- Đăng k&yacute; biến động quyền sử dụng đất theo quy định tại Điểm a, b Khoản 2 Điều 9 Th&ocirc;ng tư 24/2014/TT-BTNMT.<br />\r\n<br />\r\n2. Tr&igrave;nh tự thực hiện:<br />\r\n<br />\r\nBước 1. Người nhận chuyển nhượng, thừa kế, tặng cho quyền sử dụng đất nộp hồ sơ tại Bộ phận tiếp nhận v&agrave; trả kết quả của UBND huyện. C&aacute;n bộ Bộ phận tiếp nhận v&agrave; trả kết quả kiểm tra, xem x&eacute;t hồ sơ; nếu hồ sơ chưa đầy đủ th&igrave; hướng dẫn cụ thể một lần đầy đủ để người nhận quyền sử dụng đất bổ sung, ho&agrave;n chỉnh hồ sơ; nếu hồ sơ đ&atilde; đầy đủ th&igrave; viết giấy nhận hồ sơ, hẹn ng&agrave;y trả kết quả v&agrave; chuyển hồ sơ đến Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất.<br />\r\n<br />\r\nBước 2. Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất thẩm tra hồ sơ, tr&iacute;ch sao hồ sơ địa ch&iacute;nh, gửi th&ocirc;ng tin địa ch&iacute;nh đến cơ quan thuế để x&aacute;c định nghĩa vụ t&agrave;i ch&iacute;nh đối với trường hợp phải thực hiện nghĩa vụ t&agrave;i ch&iacute;nh.<br />\r\n<br />\r\nBước 3. Cơ quan thuế x&aacute;c định nghĩa vụ t&agrave;i ch&iacute;nh v&agrave; gửi th&ocirc;ng b&aacute;o nghĩa vụ t&agrave;i ch&iacute;nh cho người chuyển nhượng, nhận chuyển nhượng, thừa kế, tặng cho quyền sử dụng đất về thời gian, địa điểm nộp tiền v&agrave; gửi th&ocirc;ng b&aacute;o cho Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất lưu hồ sơ.<br />\r\n<br />\r\nBước 4. Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất viết giấy chứng nhận quyền sử dụng đất mới cho b&ecirc;n nhận chuyển nhượng, thừa kế, tặng cho quyền sử dụng đất. Chuyển hồ sơ cấp mới giấy chứng nhận cho ph&ograve;ng T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường.<br />\r\n<br />\r\nBước 5. Ph&ograve;ng T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường kiểm tra hồ sơ tr&igrave;nh Chủ tịch UBND huyện.<br />\r\n<br />\r\nBước 6. Chủ tịch UBND huyện k&yacute; giấy chứng nhận quyền sử dụng đất, chuyển kết quả cho Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất.<br />\r\n<br />\r\nBước 7. Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất v&agrave;o sổ cấp giấy chứng nhận quyền sử dụng đất v&agrave; chỉnh l&yacute; hồ sơ địa ch&iacute;nh theo quy định; chuyển hồ sơ cho Bộ phận tiếp nhận v&agrave; trả kết quả nơi nhận hồ sơ.<br />\r\n<br />\r\nBước 8. Bộ phận tiếp nhận v&agrave; trả kết quả v&agrave;o sổ, thu ph&iacute;, lệ ph&iacute; (nếu c&oacute;) v&agrave; trả kết quả cho người nhận quyền sử dụng đất.<br />\r\n<br />\r\n3. Hồ sơ bao gồm:<br />\r\n<br />\r\n- Hợp đồng chuyển nhượng, thừa kế, tặng cho quyền sử dụng đất, quyền sở hữu t&agrave;i sản gắn liền với đất<br />\r\n<br />\r\nTrường hợp người thừa kế quyền sử dụng đất, quyền sở hữu t&agrave;i sản gắn liền với đất l&agrave; người duy nhất th&igrave; phải c&oacute; đơn đề nghị được đăng k&yacute; thừa kế quyền sử dụng đất, quyền sở hữu t&agrave;i sản gắn liền với đất của người thừa kế.<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Bản gốc Giấy chứng nhận đ&atilde; cấp</li>\r\n	<li>Tờ khai lệ ph&iacute; trước bạ nh&agrave;, đất</li>\r\n	<li>Tờ khai thuế thu nhập c&aacute; nh&acirc;n</li>\r\n</ul>\r\n<br />\r\n4. Cơ quan thực hiện thủ tục h&agrave;nh ch&iacute;nh<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Cơ quan có th&acirc;̉m quy&ecirc;̀n quy&ecirc;́t định: UBND cấp huyện.</li>\r\n	<li>Cơ quan trực ti&ecirc;́p thực hiện TTHC: Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất, Ph&ograve;ng T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường huyện.</li>\r\n	<li>Cơ quan phối hợp: UBND x&atilde;.</li>\r\n</ul>\r\n<br />\r\n<strong>Lệ ph&iacute; như thế n&agrave;o</strong><br />\r\n<br />\r\nKhi tặng cho quyền sử dụng đất sẽ th&igrave; b&ecirc;n tặng cho sẽ phải k&ecirc; khai nộp thuế thu nhập c&aacute; nh&acirc;n c&ograve;n b&ecirc;n nhận tặng cho sẽ phải k&ecirc; khai nộp lệ ph&iacute; trước bạ.\r\n<ul>\r\n	<li>Thuế thu nhập c&aacute; nh&acirc;n: 2% gi&aacute; trị chuyển nhượng.</li>\r\n	<li>Lệ ph&iacute; trước bạ: 0,5% x diện t&iacute;ch x gi&aacute; đất ( gi&aacute; đất do Ủy ban nh&acirc;n d&acirc;n tỉnh ban h&agrave;nh)</li>\r\n	<li>Lệ ph&iacute; c&ocirc;ng chứng:</li>\r\n</ul>\r\n', 0, NULL, NULL, 1, 0, 79, '2017-07-14 11:13:05', '2017-07-14 11:13:05', 2, 2),
(41, 'Thủ tục sang tên đổi chủ nhà đất như thế nào', 'thu-tuc-sang-ten-doi-chu-nha-dat-nhu-the-nao', 'Thu tuc sang ten doi chu nha dat nhu the nao', 'Nếu quý vị đang dự định hay mong muốn sang tên nhà đất lại cho ai đó, nhưng lại không biết thủ tục sang tên đổi chủ nhà đất như thế nào. Hãy tham khảo bài viết dưới đây, quý vị sẽ nắm được rõ hơn về các thủ tục cần có.', '2017/07/14/thu-tuc-sang-ten-doi-chu-nha-dat-nhu-the-nao-1500006051.png', 5, '<div style=\"text-align:center\">&nbsp;</div>\r\n\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n\r\n<div style=\"text-align:center\"><img alt=\"Thủ tục sang tên đổi chủ nhà đất như thế nào\" src=\"/uploads/images/thu-tuc-sang-ten-doi-chu-nha-dat-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nViệc sang t&ecirc;n đổi chủ nh&agrave; đất kh&ocirc;ng hẳn chỉ cần thủ tục giấy tờ l&agrave; xong. Qu&yacute; vị cần n&ecirc;n biết l&agrave; phải trải qua<br />\r\nnhững quy tr&igrave;nh, qua nhiều kh&acirc;u xử l&yacute; của c&aacute;c cơ quan v&agrave; hai b&ecirc;n. Cho n&ecirc;n để hiểu r&otilde; hơn, ch&uacute;ng t&ocirc;i sẽ chia sẻ tất tần tật những quy tr&igrave;nh cụ thể.<br />\r\n<br />\r\n<strong>Lập hợp đồng chuyển nhượng</strong><br />\r\n<br />\r\nNgười mua v&agrave; người b&aacute;n c&ugrave;ng đến cơ quan c&ocirc;ng chứng gần đ&oacute; để lập hợp đồng chuyển nhượng. Sau 10 ng&agrave;y, kể từ ng&agrave;y đến l&agrave;m hợp đồng, hai b&ecirc;n k&ecirc; khai phải thực hiện đầy đủ nghĩa vụ k&ecirc; khai lệ ph&iacute; trước bạ, thuế thu nhập c&aacute; nh&acirc;n. Nếu để qu&aacute; thời hạn sẽ bị phạt theo quy định của nh&agrave; nước hiện h&agrave;nh.<br />\r\n<br />\r\n<strong>Thực hiện nghĩa vụ t&agrave;i ch&iacute;nh với cơ quan nh&agrave; nước li&ecirc;n quan</strong><br />\r\n<br />\r\nTrước ti&ecirc;n, k&ecirc; khai nghĩa vụ t&agrave;i ch&iacute;nh tại UBND cấp quận/ huyện nơi c&oacute; đất cần mua v&agrave; khi đi cần chuẩn bị một bộ hồ sơ, bao gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Tờ khai lệ ph&iacute; trước bạ (02 bản do b&ecirc;n mua k&yacute;).</li>\r\n	<li>Tờ khai thuế thu nhập c&aacute; nh&acirc;n (02 bản do b&ecirc;n b&aacute;n k&yacute;. Ri&ecirc;ng trường hợp cho tặng 04 bản).</li>\r\n	<li>Hợp đồng c&ocirc;ng chứng đ&atilde; lập (01 bản ch&iacute;nh).</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất (sổ đỏ), quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản gắn liền với đất (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền).</li>\r\n	<li>Cả b&ecirc;n mua v&agrave; b&ecirc;n b&aacute;n nh&agrave; đất đều phải nộp CMND + Sổ hộ khẩu (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền).</li>\r\n	<li>&nbsp;Để được miễn thuế thu nhập c&aacute; nh&acirc;n với trường hợp cho, tặng hay thừa kế th&igrave; phải giấy tờ chứng minh quan hệ nh&acirc;n th&acirc;n.</li>\r\n</ul>\r\n<br />\r\nNộp đầy đủ giấy tờ cần thiết th&igrave; sau 10 ng&agrave;y sẽ c&oacute; th&ocirc;ng b&aacute;o nộp thuế của cơ quan nh&agrave; nước c&oacute; thẩm quyền li&ecirc;n quan với thuế sang t&ecirc;n sổ đỏ gồm thuế thu nhập c&aacute; nh&acirc;n 2 % (người b&aacute;n nộp) v&agrave; thuế trước bạ 0.5 % (người mua nộp). Nếu người b&aacute;n với người mua đất c&oacute; sự thoả thuận ri&ecirc;ng th&igrave; c&oacute; thể một trong hai người sẽ đ&oacute;ng hết ph&iacute;. Trường hợp được miễn thuế thu nhập c&aacute; nh&acirc;n khi người b&aacute;n chỉ sở hữu duy nhất một mảnh đất.<br />\r\n<br />\r\n<strong>K&ecirc; khai hồ sơ tại cơ quan nh&agrave; nước c&oacute; thẩm quyền</strong><br />\r\n<br />\r\nThủ tục sang t&ecirc;n đổi chủ khi mua đất tiếp theo bạn cần nhớ l&agrave; k&ecirc; khai hồ sơ tại cơ quan quận/huyện ở địa phương gồm c&aacute;c giấy tờ:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>B&ecirc;n b&aacute;n đất tr&igrave;nh đơn đề nghị đăng k&yacute; biến động. Nếu c&oacute; thoả thuận th&igrave; b&ecirc;n mua đất c&oacute; thể k&yacute; thay.</li>\r\n	<li>Hợp đồng chuyển nhượng, hợp đồng tặng cho hay văn bản thỏa thuận ph&acirc;n chia t&agrave;i sản.</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất (sổ đỏ).</li>\r\n	<li>Bi&ecirc;n lai nộp tiền v&agrave;o ng&acirc;n s&aacute;ch nh&agrave; nước (bản gốc).</li>\r\n	<li>B&ecirc;n nhận chuyển nhượng bất động sản phải cung cấp bản sao CMND + Sổ hộ khẩu</li>\r\n</ul>\r\n<br />\r\nSau đ&oacute;, bạn phải nộp lệ ph&iacute; địa ch&iacute;nh 15.000 đồng/trường hợp, lệ ph&iacute; thẩm định t&iacute;nh bằng 0.15% gi&aacute; trị chuyển nhượng (Tối thiểu 100.000 đồng v&agrave; tối đa kh&ocirc;ng qu&aacute; 5.000.000 đồng/trường hợp). Khi đ&atilde; ho&agrave;n tất đ&oacute;ng c&aacute;c khoản lệ ph&iacute; theo quy định th&igrave; bạn được lấy sổ đỏ.', 0, NULL, NULL, 1, 0, 80, '2017-07-14 11:21:32', '2017-07-14 11:21:32', 2, 2);
INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(42, 'Thủ tục sang tên quyền sử dụng đất từ cha mẹ cho con như thế nào', 'thu-tuc-sang-ten-quyen-su-dung-dat-tu-cha-me-cho-con-nhu-the-nao', 'Thu tuc sang ten quyen su dung dat tu cha me cho con nhu the nao', 'Nếu quý vị muốn sang tên giấy chứng nhận hoặc sổ đỏ quyền sử dụng đất cho con cái của mình, nhưng lại không biết thủ tục sang tên quyền sử dụng đất từ cha mẹ cho con như thế nào. Hãy tham khảo ngay bài viết dưới đây để biết rõ hơn.', '2017/07/14/thu-tuc-sang-ten-quyen-su-dung-dat-tu-cha-me-cho-con-nhu-the-nao-1500016254.png', 5, 'Khi chuyển nhượng quyền sử dụng đất từ người n&agrave;y sang người kh&aacute;c, qu&yacute; vị cần tiến h&agrave;nh thủ tục đăng k&yacute; quyền sử dụng đất theo quy định của ph&aacute;p luật để bảo vệ quyền v&agrave; lợi &iacute;ch hợp ph&aacute;p của m&igrave;nh.&nbsp;Thẩm quyền giải quyết cho qu&yacute; vị đ&oacute; l&agrave; UBND cấp huyện nơi c&oacute; nh&agrave; đất.\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"Thủ tục sang tên quyền sử dụng đất từ cha mẹ cho con như thế nào\" src=\"/uploads/images/thu-tuc-sang-ten-quyen-su-dung-dat-tu-cha-me-cho-con-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nBước 1: &nbsp;Bố mẹ bạn v&agrave; bạn n&ecirc;n l&agrave;m giấy tờ tặng cho t&agrave;i sản tại Văn ph&ograve;ng c&ocirc;ng chứng (hoặc tại Uỷ ban nh&acirc;n d&acirc;n x&atilde;). Nếu kh&ocirc;ng bạn c&oacute; thể đến Văn ph&ograve;ng c&ocirc;ng chứng, c&ocirc;ng chứng vi&ecirc;n sẽ gi&uacute;p bạn soạn hợp đồng cho tặng t&agrave;i sản. C&aacute;c giấy tờ cần mang theo bao gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Chứng minh nh&acirc;n d&acirc;n, Hộ khẩu thường tr&uacute; của bố mẹ v&agrave; bạn</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất v&agrave; sở hữu nh&agrave; ở</li>\r\n	<li>Giấy khai sinh của bạn (để l&agrave;m cơ sở miễn thuế thu nhập c&aacute; nh&acirc;n)</li>\r\n</ul>\r\n\r\n<p>&nbsp;<br />\r\nTrong thời hạn 10 ng&agrave;y kể từ ng&agrave;y lập hợp đồng, c&aacute;c gia đ&igrave;nh bạn phải k&ecirc; khai lệ ph&iacute; trước bạ, thuế thu nhập c&aacute; nh&acirc;n, nếu qu&aacute; thời hạn tr&ecirc;n sẽ bị phạt theo quy định của nh&agrave; nước.<br />\r\n<br />\r\n<strong>Bước 2: &nbsp;K&ecirc; khai nghĩa vụ t&agrave;i ch&iacute;nh (tại UBND cấp huyện nơi c&oacute; nh&agrave;, đất)</strong><br />\r\n&nbsp;<br />\r\nBạn tiến h&agrave;nh nộp hồ sơ v&agrave;o gồm c&aacute;c giấy tờ đ&atilde; n&ecirc;u ở tr&ecirc;n đến UBND cấp huyện nơi c&oacute; nh&agrave; đất<br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li>&nbsp;Đơn đề nghị chuyển quyền sở hữu nh&agrave; ở v&agrave; quyền sử dụng đất</li>\r\n	<li>&nbsp;Tờ khai lệ ph&iacute; trước bạ (02 bản do b&ecirc;n mua k&yacute;)</li>\r\n	<li>&nbsp;Tờ khai thuế thu nhập c&aacute; nh&acirc;n (02 bản do b&ecirc;n b&aacute;n k&yacute;. Ri&ecirc;ng trường hợp cho tặng 04 bản)</li>\r\n	<li>&nbsp;Hợp đồng tặng cho c&ocirc;ng chứng đ&atilde; lập (01 bản ch&iacute;nh)</li>\r\n	<li>&nbsp;Giấy chứng nhận quyền sử dụng đất (sổ đỏ), quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản gắn liền với đất (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền)</li>\r\n	<li>&nbsp;CMND + Sổ hộ khẩu của cả b&ecirc;n mua v&agrave; b&ecirc;n b&aacute;n (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền)</li>\r\n	<li>Đối với trường hợp cho tặng, thừa kế phải c&oacute; giấy tờ chứng minh quan hệ nh&acirc;n th&acirc;n của người cho v&agrave; người nhận để được miễn thuế thu nhập c&aacute; nh&acirc;n. (giấy khai sinh của bạn)</li>\r\n</ul>\r\n\r\n<p>&nbsp;<br />\r\nC&aacute;c loại thuế phải đ&oacute;ng khi chuyển nhượng, tặng cho quyền sử dụng đất l&agrave;: Thuế thu nhập c&aacute; nh&acirc;n, Lệ ph&iacute; trước bạ. Quy định tại &nbsp;khoản 1 v&agrave; 4 Điều 4 Luật Thuế thu nhập c&aacute; nh&acirc;n 2007 quy định thu nhập được miễn thuế thu nhập c&aacute; nh&acirc;n như sau:<br />\r\n<br />\r\nThu nhập từ chuyển nhượng bất động sản giữa vợ với chồng; cha đẻ, mẹ đẻ với con đẻ; cha nu&ocirc;i, mẹ nu&ocirc;i với con nu&ocirc;i; cha chồng, mẹ chồng với con d&acirc;u; cha vợ, mẹ vợ với con rể; &ocirc;ng nội, b&agrave; nội với ch&aacute;u nội; &ocirc;ng ngoại, b&agrave; ngoại với ch&aacute;u ngoại; anh, chị, em ruột với nhau.<br />\r\n<br />\r\nThu nhập từ nhận thừa kế, qu&agrave; tặng l&agrave; bất động sản giữa vợ với chồng; cha đẻ, mẹ đẻ với con đẻ; cha nu&ocirc;i, mẹ nu&ocirc;i với con nu&ocirc;i; cha chồng, mẹ chồng với con d&acirc;u; cha vợ, mẹ vợ với con rể; &ocirc;ng nội, b&agrave; nội với ch&aacute;u nội; &ocirc;ng ngoại, b&agrave; ngoại với ch&aacute;u ngoại; anh, chị, em ruột với nhau.<br />\r\n<br />\r\n<strong>Trường hợp của bạn thuộc khoản 1 điều 4 n&ecirc;n được miễn thuế thu nhập c&aacute; nh&acirc;n.</strong><br />\r\n<br />\r\nKhoản 10 Điều 4 Nghị định 45/2011/NĐ-CP ng&agrave;y 17/6/2011 về lệ ph&iacute; trước bạ quy định: &ldquo;Nh&agrave;, đất nhận thừa kế hoặc l&agrave; qu&agrave; tặng giữa: vợ với chồng; cha đẻ, mẹ đẻ với con đẻ; cha nu&ocirc;i, mẹ nu&ocirc;i với con nu&ocirc;i; cha chồng, mẹ chồng với con d&acirc;u; cha vợ, mẹ vợ với con rể; &ocirc;ng nội, b&agrave; nội với ch&aacute;u nội; &ocirc;ng ngoại, b&agrave; ngoại với ch&aacute;u ngoại; anh, chị, em ruột với nhau nay được cơ quan nh&agrave; nước c&oacute; thẩm quyền cấp giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất lần đầu&rdquo; l&agrave; một trong c&aacute;c trường hợp kh&ocirc;ng phải đ&oacute;ng lệ ph&iacute; trước bạ.<br />\r\n<br />\r\nTheo đ&oacute;, &nbsp;lệ ph&iacute; trước bạ về tặng cho cha mẹ, con c&aacute;i&hellip;chỉ được miễn lệ ph&iacute; trước bạ khi được Cơ quan nh&agrave; nước cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất lần đầu, trong trường hợp t&agrave;i sản tặng cho kh&ocirc;ng phải lần đầu, lệ ph&iacute; trước bạ được t&iacute;nh theo tỷ lệ l&agrave; 0.5%.<br />\r\n&nbsp;<br />\r\nBước 3: K&ecirc; khai sang t&ecirc;n (tại UBND quận/huyện nơi c&oacute; nh&agrave;, đất)<br />\r\n<br />\r\n<strong>Th&agrave;nh phần hồ sơ gồm c&oacute;:</strong></p>\r\n\r\n<ul>\r\n	<li>Đơn đề nghị đăng k&yacute; biến động (do b&ecirc;n b&aacute;n k&yacute;)</li>\r\n	<li>Hợp đồng tặng cho t&agrave;i sản c&oacute; c&ocirc;ng chứng</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất (sổ đỏ), quyền sở hữu nh&agrave; v&agrave; t&agrave;i sản gắn liền với đất (bản gốc)</li>\r\n	<li>Giấy nộp tiền v&agrave;o ng&acirc;n s&aacute;ch nh&agrave; nước (bản gốc)</li>\r\n	<li>Bản sao CMND + Sổ hộ khẩu của b&ecirc;n nhận chuyển nhượng, thừa kế, cho tặng</li>\r\n	<li>Thời hạn sang t&ecirc;n: 15 ng&agrave;y</li>\r\n</ul>\r\n<br />\r\n<strong>Lệ ph&iacute; sang t&ecirc;n như thế n&agrave;o</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Lệ ph&iacute; địa ch&iacute;nh: tr&iacute;ch lục bản đồ địa ch&iacute;nh, văn bản, số liệu hồ sơ địa ch&iacute;nh 15.000 đồng/trường hợp</li>\r\n	<li>Lệ ph&iacute; thẩm định: Mức thu t&iacute;nh bằng 0,15% gi&aacute; trị (sang t&ecirc;n) chuyển nhượng (Tối thiểu 100.000 đồng đến tối đa kh&ocirc;ng qu&aacute; 5.000.000 đồng/trường hợp)</li>\r\n</ul>\r\n&nbsp;<br />\r\nBước 4: &nbsp;Sau khi nhận hồ sơ hợp lệ, cơ quan c&oacute; thẩm quyền kiểm tra c&oacute; tr&aacute;ch nhiệm thẩm tra hồ sơ, chuyển hồ sơ sang cơ quan thuế để t&iacute;nh thuế thu nhập c&aacute; nh&acirc;n.<br />\r\n<br />\r\nSau khi bạn ho&agrave;n th&agrave;nh lệ ph&iacute;, nghĩa vụ nộp thuế hoặc c&oacute; tờ khai miễn thuế (trong trường hợp được miễn thuế), cơ quan nh&agrave; đất sẽ l&agrave;m thủ tục đăng k&yacute; sang t&ecirc;n bạn tr&ecirc;n Giấy chứng nhận quyền sử dụng đất v&agrave; sở hữu nh&agrave; ở', 0, NULL, NULL, 1, 0, 81, '2017-07-14 14:11:26', '2017-07-14 14:11:26', 2, 2),
(43, 'Thủ tục sang tên sổ đỏ đất thổ cư như thế nào', 'thu-tuc-sang-ten-so-do-dat-tho-cu-nhu-the-nao', 'Thu tuc sang ten so do dat tho cu nhu the nao', 'Quý vị có mảnh đất thổ cư và đang muốn sang tên lại cho con, người khác. Hoặc nhận từ bố mẹ hay người khác sang tên cho quý vị thì thủ tục sang tên sổ đỏ đất thổ cư như thế nào. Thì mời tham khảo ngay bài viết dưới đây, để biết rõ hơn về các thủ tục cần chuẩn bị cũng như chi phí', '2017/07/14/thu-tuc-sang-ten-so-do-dat-tho-cu-nhu-the-nao-1500017057.png', 5, 'Trước ti&ecirc;n phải l&agrave;m thủ tục tặng cho QSDĐ tại (Văn) Ph&ograve;ng C&ocirc;ng chứng tại địa phương. C&oacute; chứng cứ chứng minh quan hệ mẹ con th&igrave; người tặng cho kh&ocirc;ng phải đ&oacute;ng thuế TNCN. Sau đ&oacute; qu&yacute; vị l&agrave;m thủ tục trước bạ sang t&ecirc;n v&agrave; đăng bộ.\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n\r\n<div style=\"text-align:center\"><br />\r\n<strong><img alt=\"Thủ tục sang tên sổ đỏ đất thổ cư như thế nào\" src=\"/uploads/images/thu-tuc-sang-ten-so-do-dat-tho-cu-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></strong></div>\r\n<br />\r\n<strong>► Việc nộp hồ sơ chuyển nhượng quyền, tặng cho quyền sử dụng đất được quy định như sau</strong><br />\r\n<br />\r\nHồ sơ chuyển nhượng quyền sử dụng đất nộp tại Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất; trường hợp hộ gia đ&igrave;nh, c&aacute; nh&acirc;n sử dụng đất tại n&ocirc;ng th&ocirc;n th&igrave; nộp tại Uỷ ban nh&acirc;n d&acirc;n x&atilde; nơi c&oacute; đất để chuyển cho Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất<br />\r\n<br />\r\nHồ sơ chuyển nhượng quyền sử dụng đất bao gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Giấy chứng nhận quyền sử dụng đất ( 01 bản gốc + 03 bản photo c&ocirc;ng chứng)</li>\r\n	<li>Hợp đồng chuyển nhượng quyền sử dụng đất</li>\r\n	<li>Hợp đồng chuyển nhượng quyền sử dụng đất phải c&oacute; chứng nhận của (văn) Ph&ograve;ng C&ocirc;ng chứng</li>\r\n	<li>Sơ đồ kỹ thuật thửa đất (nếu c&oacute; y&ecirc;u cầu)</li>\r\n	<li>Chứng minh thư, hộ khẩu của hai b&ecirc;n chuyển nhượng, tặng cho (03 bản photo c&ocirc;ng chứng)</li>\r\n	<li>C&aacute;c giấy tờ kh&aacute;c: (giấy khai sinh của người con v&agrave; giấy tờ kh&aacute;c nếu c&oacute; y&ecirc;u cầu)</li>\r\n</ul>\r\n<br />\r\nTrong thời hạn kh&ocirc;ng qu&aacute; mười lăm ng&agrave;y l&agrave;m việc, kể từ ng&agrave;y nhận đủ hồ sơ hợp lệ, Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất c&oacute; tr&aacute;ch nhiệm thẩm tra hồ sơ, gửi hồ sơ cho cơ quan quản l&yacute; đất đai thuộc Uỷ ban nh&acirc;n d&acirc;n cấp c&oacute; thẩm quyền cấp giấy chứng nhận quyền sử dụng đất để l&agrave;m thủ tục cấp Giấy chứng nhận quyền sử dụng đất.<br />\r\n<br />\r\nTrường hợp c&aacute;c b&ecirc;n chuyển nhượng phải thực hiện nghĩa vụ t&agrave;i ch&iacute;nh m&agrave; nghĩa vụ t&agrave;i ch&iacute;nh đ&oacute; được x&aacute;c định theo số liệu địa ch&iacute;nh th&igrave; Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất gửi số liệu địa ch&iacute;nh đến cơ quan thuế để x&aacute;c định nghĩa vụ t&agrave;i ch&iacute;nh theo quy định của ph&aacute;p luật; Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất th&ocirc;ng b&aacute;o cho c&aacute;c b&ecirc;n chuyển nhượng thực hiện nghĩa vụ t&agrave;i ch&iacute;nh.<br />\r\n<br />\r\nTrong thời hạn kh&ocirc;ng qu&aacute; năm ng&agrave;y l&agrave;m việc, kể từ ng&agrave;y thực hiện xong nghĩa vụ t&agrave;i ch&iacute;nh, c&aacute;c b&ecirc;n tham gia chuyển nhượng quyền sử dụng đất nhận giấy chứng nhận quyền sử dụng đất tại nơi đ&atilde; nộp hồ sơ.<br />\r\n<br />\r\n<strong>Nơi nộp hồ sơ</strong><br />\r\n<br />\r\n&ndash; Hồ sơ ban đầu nộp tại ph&ograve;ng c&ocirc;ng chứng th&agrave;nh phố hoặc UBND quận, huyện nơi c&oacute; đất chuyển nhượng. Trong thời hạn 3 ng&agrave;y kể từ ng&agrave;y nhận được hồ sơ, cơ quan n&ecirc;u tr&ecirc;n phải ho&agrave;n tất thủ tục c&ocirc;ng chứng, chứng thực.<br />\r\n<br />\r\n&ndash; Thuế chuyển quyền sử dụng đất ở: B&ecirc;n chuyển quyền v&agrave; b&ecirc;n nhận quyền đến cơ quan thuế nộp thuế chuyển quyền sử dụng đất theo quy định. Cơ quan thuế căn cứ v&agrave;o hồ sơ đ&atilde; được c&ocirc;ng chứng, x&aacute;c định c&aacute;c khoản thuế, lệ ph&iacute; trong thời hạn 3 ng&agrave;y v&agrave; tổ chức thu theo quy định<br />\r\n<br />\r\n&ndash; Lệ ph&iacute; trước bạ nh&agrave; ở, đất ở: B&ecirc;n nhận quyền sở hữu đất nộp to&agrave;n bộ hồ sơ, chứng từ đ&atilde; được thực hiện theo c&aacute;c bước n&oacute;i tr&ecirc;n tại sở địa ch&iacute;nh nh&agrave; đất (nếu giấy chứng nhận quyền sử dụng đất ban đầu do UBND th&agrave;nh phố cấp), hoặc tại UBND quận, huyện (nếu giấy chứng nhận quyền sử dụng đất do UBND quận, huyện cấp) để l&agrave;m thủ tục đăng k&yacute; trước bạ, sang t&ecirc;n v&agrave;o giấy chứng nhận quyền sử dụng đất.', 0, NULL, NULL, 1, 0, 82, '2017-07-14 14:24:48', '2017-07-14 14:24:48', 2, 2),
(44, 'Thủ tục sang tên sổ đỏ nhà chung cư như thế nào', 'thu-tuc-sang-ten-so-do-nha-chung-cu-nhu-the-nao', 'Thu tuc sang ten so do nha chung cu nhu the nao', 'Nếu quý vị có một căn hộ chung cư muốn bán nhưng lại không biết biết thủ tục sang tên sổ đỏ nhà chung cư như thế nào. Hãy tham khảo bài viết dưới đây, quý vị sẽ nắm được những thủ tục cần thiết và chi phí thuế cần nọp như thế nào.', '2017/07/14/thu-tuc-sang-ten-so-do-nha-chung-cu-nhu-the-nao-1500018668.png', 5, 'Khi chuyển nhượng quyền sử dụng đất từ người n&agrave;y sang người kh&aacute;c ch&uacute;ng ta cần tiến h&agrave;nh thủ tục đăng k&yacute; quyền sử dụng đất theo quy định của ph&aacute;p luật để bảo vệ quyền v&agrave; lợi &iacute;ch hợp ph&aacute;p của m&igrave;nh nhằm tr&aacute;nh những tranh chấp hoặc c&aacute;c rủi ro kh&ocirc;ng đ&aacute;ng c&oacute; li&ecirc;n quan tới nh&agrave; đất đ&atilde; chuyển nhượng, mua b&aacute;n.\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"Thủ tục sang tên sổ đỏ nhà chung cư như thế nào\" src=\"/uploads/images/thu-tuc-sang-ten-so-do-nha-chung-cu-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nBước 1: C&aacute;c b&ecirc;n đến cơ quan c&ocirc;ng chứng lập hợp đồng chuyển nhượng, thừa kế, cho tặng. Trong thời hạn 10 ng&agrave;y kể từ ng&agrave;y lập hợp đồng, c&aacute;c b&ecirc;n phải k&ecirc; khai lệ ph&iacute; trước bạ, thuế thu nhập c&aacute; nh&acirc;n, nếu qu&aacute; thời hạn tr&ecirc;n sẽ bị phạt theo quy định của nh&agrave; nước.<br />\r\n<br />\r\nBước 2: K&ecirc; khai nghĩa vụ t&agrave;i ch&iacute;nh (tại UBND cấp huyện nơi c&oacute; nh&agrave;, đất)<br />\r\n<br />\r\n<strong>Hồ sơ thực hiện việc sang t&ecirc;n sổ đỏ gồm</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Tờ khai lệ ph&iacute; trước bạ (02 bản do b&ecirc;n mua k&yacute;)</li>\r\n	<li>Tờ khai thuế thu nhập c&aacute; nh&acirc;n (02 bản do b&ecirc;n b&aacute;n k&yacute;. Ri&ecirc;ng trường hợp cho tặng 04 bản).</li>\r\n	<li>Hợp đồng c&ocirc;ng chứng đ&atilde; lập (01 bản ch&iacute;nh)</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất (sổ đỏ), quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản gắn liền với đất (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền)</li>\r\n	<li>CMND + Sổ hộ khẩu của cả b&ecirc;n mua v&agrave; b&ecirc;n b&aacute;n (01 bản sao c&oacute; chứng thực của cơ quan c&oacute; thẩm quyền)</li>\r\n	<li>Đối với trường hợp cho tặng, thừa kế phải c&oacute; giấy tờ chứng minh quan hệ nh&acirc;n th&acirc;n của người cho v&agrave; người nhận để được miễn thuế thu nhập c&aacute; nh&acirc;n.</li>\r\n	<li>Thời hạn c&oacute; th&ocirc;ng b&aacute;o nộp thuế: 10 ng&agrave;y Sau khi c&oacute; th&ocirc;ng b&aacute;o th&igrave; người nộp thuế nộp tiền v&agrave;o ng&acirc;n s&aacute;ch nh&agrave; nước</li>\r\n</ul>\r\n<br />\r\n<strong>Thuế sang t&ecirc;n sổ đỏ</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Thuế thu nhập c&aacute; nh&acirc;n: 2 %</li>\r\n	<li>Lệ ph&iacute; trước bạ: 0,5 %</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 3: K&ecirc; khai hồ sơ sang t&ecirc;n (tại UBND quận/huyện nơi c&oacute; nh&agrave;, đất)</strong><br />\r\n<br />\r\nTh&agrave;nh phần hồ sơ gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đơn đề nghị đăng k&yacute; biến động (do b&ecirc;n b&aacute;n k&yacute;); Trong trường hợp c&oacute; thoả thuận trong hợp đồng về việc b&ecirc;n mua thực hiện thủ tục h&agrave;nh ch&iacute;nh th&igrave; b&ecirc;n mua c&oacute; thể k&yacute; thay.</li>\r\n	<li>Hợp đồng chuyển nhượng; hợp đồng tặng cho; hoặc văn bản thỏa thuận ph&acirc;n chia di sản; văn bản khai nhận di sản;</li>\r\n	<li>Giấy chứng nhận quyền sử dụng đất (sổ đỏ), quyền sở hữu nh&agrave; v&agrave; t&agrave;i sản gắn liền với đất (bản gốc)</li>\r\n	<li>Giấy nộp tiền v&agrave;o ng&acirc;n s&aacute;ch nh&agrave; nước (bản gốc)</li>\r\n	<li>Bản sao CMND + Sổ hộ khẩu của b&ecirc;n nhận chuyển nhượng, thừa kế, cho tặng</li>\r\n	<li>Thời hạn sang t&ecirc;n: Theo quy định của ph&aacute;p luật</li>\r\n</ul>\r\n<br />\r\n<strong>Lệ ph&iacute; sang t&ecirc;n sổ đỏ gồm</strong>\r\n\r\n<ul>\r\n	<li>Lệ ph&iacute; địa ch&iacute;nh: 15.000 đồng/trường hợp</li>\r\n	<li>Lệ ph&iacute; thẩm định: Mức thu t&iacute;nh bằng 0,15% gi&aacute; trị (sang t&ecirc;n) chuyển nhượng (Tối thiểu 100.000 đồng đến tối đa kh&ocirc;ng qu&aacute; 5.000.000 đồng/trường hợp)</li>\r\n	<li>Lệ ph&iacute; cấp sổ đỏ t&ugrave;y theo từng trường hợp cụ thể.</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 4: Nộp đủ lệ ph&iacute; theo quy định v&agrave; nhận sổ đỏ</strong><br />\r\n<br />\r\nĐể nhận được sự hỗ trợ, tư vấn của c&aacute;c chuy&ecirc;n vi&ecirc;n về chi ph&iacute;, thủ tục sang t&ecirc;n sổ đỏ , tư vấn về mua b&aacute;n đất đai cũng như c&aacute;c thủ tục về c&ocirc;ng chứng, c&aacute;c bạn h&atilde;y li&ecirc;n hệ với ch&uacute;ng t&ocirc;i để đạt được hiệu quả nhanh nhất v&agrave; tốt nhất<br />\r\n<br />\r\n<strong>Những khoản thuế phải nộp khi sang t&ecirc;n sổ đỏ nh&agrave; chung cư</strong><br />\r\n<br />\r\nNghĩa vụ t&agrave;i ch&iacute;nh trong qu&aacute; tr&igrave;nh l&agrave;m thủ tục sang t&ecirc;n sổ đỏ nh&agrave; chung cư sẽ bao gồm nghĩa vụ nộp lệ ph&iacute; trước bạ, lệ ph&iacute; c&ocirc;ng chứng, lệ ph&iacute; cấp giấy chứng nhận quyền sở hữu nh&agrave; ở, thuế thu nhập c&aacute; nh&acirc;n. Nếu kh&ocirc;ng c&oacute; thỏa thuận g&igrave; kh&aacute;c, b&ecirc;n mua c&oacute; nghĩa vụ nộp lệ ph&iacute; c&ocirc;ng chứng, lệ ph&iacute; trước bạ v&agrave; lệ ph&iacute; cấp giấy chứng nhận quyền sở hữu nh&agrave; ở, b&ecirc;n b&aacute;n sẽ chịu tr&aacute;ch nhiệm nộp thuế thu nhập c&aacute; nh&acirc;n.<br />\r\n<br />\r\nTheo điều 6 nghị định 80/2008/NĐ-CP, lệ ph&iacute; trước bạ nh&agrave; đất sẽ l&agrave; 0,5% gi&aacute; chuyển nhượng ghi trong hợp đồng. Lệ ph&iacute; cấp giấy chứng nhận quyền sở hữu nh&agrave; ở sẽ được thu dựa tr&ecirc;n điều 49 nghị định 90/2006/NĐ-CP, theo đ&oacute; mức lệ ph&iacute; sẽ kh&ocirc;ng qu&aacute; 100.000 đồng đối với c&aacute; nh&acirc;n khi đề nghị cấp giấy chứng nhận quyền sở hữu nh&agrave; ở lần đầu v&agrave; kh&ocirc;ng qu&aacute; 50.000 đồng đối với trường hợp thay đổi giấy chứng nhận quyền sở hữu nh&agrave; ở.<br />\r\n<br />\r\nKhoản thuế thu nhập c&aacute; nh&acirc;n của b&ecirc;n b&aacute;n sẽ được t&iacute;nh dựa tr&ecirc;n mức thuế suất l&agrave; 2% tr&ecirc;n gi&aacute; chuyển nhượng hoặc 25% tr&ecirc;n thu nhập chịu thuế. Thu nhập chịu thuế được x&aacute;c định bằng ch&ecirc;nh lệch giữa gi&aacute; mua v&agrave; gi&aacute; b&aacute;n trừ đi c&aacute;c chi ph&iacute; li&ecirc;n quan kh&aacute;c ( điều 22 nghị định 100/2008/NĐ-CP).<br />\r\n<br />\r\nTheo th&ocirc;ng tin bạn cung cấp, khi k&yacute; hợp đồng ủy quyền cho bạn l&agrave;m thủ tục sang sổ đỏ, b&ecirc;n b&aacute;n đang độc th&acirc;n v&agrave; hiện tại họ đ&atilde; c&oacute; gia đ&igrave;nh. Trong trường hợp n&agrave;y, theo điều 27 Luật h&ocirc;n nh&acirc;n v&agrave; gia đ&igrave;nh, căn hộ n&oacute;i tr&ecirc;n c&oacute; trước thời kỳ h&ocirc;n nh&acirc;n sẽ kh&ocirc;ng l&agrave; t&agrave;i sản chung của vợ chồng nếu như họ kh&ocirc;ng c&oacute; thỏa thuận đ&oacute; l&agrave; t&agrave;i sản chung.<br />\r\n<br />\r\nPh&aacute;p luật quy định như thế nhưng c&ocirc;ng chứng vi&ecirc;n kh&ocirc;ng thể biết tại thời điểm chuyển nhượng, giữa vợ chồng của b&ecirc;n b&aacute;n c&oacute; thỏa thuận nhập v&agrave;o t&agrave;i sản chung hay kh&ocirc;ng. Do vậy, c&aacute;c c&ocirc;ng chứng vi&ecirc;n lu&ocirc;n đề nghị c&oacute; giấy cam đoan l&agrave; t&agrave;i sản ri&ecirc;ng (c&oacute; x&aacute;c nhận chữ k&yacute; của UBND phường, x&atilde;) của người vợ hoặc chồng kh&ocirc;ng c&oacute; t&ecirc;n trong giấy chứng nhận quyền sở hữu nh&agrave;.<br />\r\n<br />\r\nViệc cho thu&ecirc; căn hộ chỉ được thực hiện khi bạn c&oacute; giấy chứng nhận quyền sở hữu nh&agrave; ở. Hay n&oacute;i c&aacute;ch kh&aacute;c, khi chưa được Nh&agrave; nước cấp giấy chứng nhận quyền sở hữu nh&agrave; ở th&igrave; bạn sẽ kh&ocirc;ng được thực hiện quyền cho thu&ecirc; nh&agrave; ở ( điều 91 Luật nh&agrave; ở).', 0, NULL, NULL, 1, 0, 83, '2017-07-14 14:51:49', '2017-07-14 14:53:11', 2, 2),
(45, 'Thủ tục chuyển nhượng nhà chung cư chưa nhận bàn giao như thế nào', 'thu-tuc-chuyen-nhuong-nha-chung-cu-chua-nhan-ban-giao-nhu-the-nao', 'Thu tuc chuyen nhuong nha chung cu chua nhan ban giao nhu the nao', 'Quý vị đang muốn chuyển nhượng căn hộ chung cư cho người khác nhưng chủ đầu tư chưa bàn giao nhà. Quý vị đang gặp khó khăn không biết thủ tục chuyển nhượng nhà chung cư chưa nhận bàn giao như thế nào. Hãy tham khảo bài viết dưới đây để biết rõ hơn các giấy tờ cần chuẩn bị.', '2017/07/14/thu-tuc-chuyen-nhuong-nha-chung-cu-chua-nhan-ban-giao-nhu-the-nao-1500020547.png', 5, '<div style=\"text-align:center\"><img alt=\"Thủ tục chuyển nhượng nhà chung cư chưa nhận bàn giao như thế nào\" src=\"/uploads/images/thu-tuc-chuyen-nhuong-nha-chung-cu-chua-nhan-ban-giao-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nĐối với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n hoặc tổ chức kh&ocirc;ng c&oacute; chức năng kinh doanh bất động sản m&agrave; b&aacute;n lại nh&agrave; ở đ&atilde; mua cho tổ chức, c&aacute; nh&acirc;n kh&aacute;c th&igrave; c&ograve;n phải tu&acirc;n thủ c&aacute;c quy định sau đ&acirc;y.\r\n<ol>\r\n	<li>Nếu đ&atilde; nhận b&agrave;n giao nh&agrave; ở từ chủ đầu tư th&igrave; phải thực hiện việc mua b&aacute;n nh&agrave; ở theo đ&uacute;ng thủ tục quy định của Luật Nh&agrave; ở v&agrave; ph&aacute;p luật về d&acirc;n sự (b&ecirc;n b&aacute;n phải c&oacute; giấy chứng nhận quyền sở hữu về nh&agrave; ở do cơ quan c&oacute; thẩm quyền cấp, c&aacute;c b&ecirc;n phải lập hợp đồng mua b&aacute;n nh&agrave; ở v&agrave; nộp thuế cho Nh&agrave; nước theo quy định)</li>\r\n	<li>Nếu chưa nhận b&agrave;n giao nh&agrave; ở từ chủ đầu tư th&igrave; được ph&eacute;p chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở theo quy định tại khoản 1 Điều 20 của Th&ocirc;ng tư n&agrave;y m&agrave; kh&ocirc;ng bắt buộc phải th&ocirc;ng qua s&agrave;n giao dịch bất động sản</li>\r\n</ol>\r\n<strong>Theo khoản 1 Điều 20 Th&ocirc;ng tư 16/2010/TT-BXD:</strong><br />\r\n<br />\r\nC&aacute;c b&ecirc;n phải lập văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở để cơ quan c&ocirc;ng chứng chứng nhận theo mẫu quy định tại phụ lục số 14 ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y. Khi đề nghị c&ocirc;ng chứng, c&aacute;c b&ecirc;n phải xuất tr&igrave;nh hợp đồng mua b&aacute;n nh&agrave; ở đ&atilde; k&yacute; với chủ đầu tư; nếu chuyển nhượng từ lần thứ hai trở đi th&igrave; phải xuất tr&igrave;nh văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở lần trước<br />\r\n<br />\r\nTr&ecirc;n cơ sở văn bản chuyển nhượng hợp đồng được lập theo quy định tại điểm a khoản n&agrave;y, một trong hai b&ecirc;n theo thỏa thuận nộp bản sao c&aacute;c giấy tờ (gồm văn bản chuyển nhượng hợp đồng; bản sao bi&ecirc;n lai nộp tiền g&oacute;p vốn, tiền mua nh&agrave; ở cho chủ đầu tư v&agrave; bản sao hợp đồng mua b&aacute;n nh&agrave; ở k&yacute; với chủ đầu tư) cho cơ quan thuế để l&agrave;m thủ tục thu thuế thu nhập theo quy định của ph&aacute;p luật. Nếu việc chuyển nhượng hợp đồng thuộc diện được miễn thuế thu nhập th&igrave; phải c&oacute; giấy tờ x&aacute;c nhận về việc miễn thuế thu nhập của cơ quan thuế<br />\r\n<br />\r\nSau khi đ&atilde; nộp thuế theo quy định, b&ecirc;n nhận chuyển nhượng hợp đồng nộp 01 bộ hồ sơ đề nghị chuyển nhượng hợp đồng (gồm: bản sao bi&ecirc;n lai thuế thu nhập hoặc giấy tờ chứng minh về việc miễn thuế thu nhập của cơ quan thuế; bản sao hợp đồng mua b&aacute;n nh&agrave; ở k&yacute; với chủ đầu tư v&agrave; bản gốc văn bản chuyển nhượng hợp đồng đ&atilde; c&oacute; chứng nhận của c&ocirc;ng chứng) để chủ đầu tư x&aacute;c nhận v&agrave;o văn bản chuyển nhượng hợp đồng n&agrave;y. Trong thời hạn tối đa l&agrave; 05 ng&agrave;y l&agrave;m việc, kể từ ng&agrave;y nhận được hồ sơ của b&ecirc;n nhận chuyển nhượng, chủ đầu tư c&oacute; tr&aacute;ch nhiệm x&aacute;c nhận v&agrave;o văn bản chuyển nhượng hợp đồng v&agrave; giao lại cho b&ecirc;n nhận chuyển nhượng, chủ đầu tư kh&ocirc;ng được thu bất kỳ một khoản ph&iacute; n&agrave;o từ việc chuyển nhượng hợp đồng n&agrave;y.<br />\r\n<br />\r\nKể từ ng&agrave;y chủ đầu tư x&aacute;c nhận v&agrave;o văn bản chuyển nhượng hợp đồng th&igrave; chủ đầu tư chấm dứt giao dịch với b&ecirc;n chuyển nhượng hợp đồng v&agrave; trực tiếp giao dịch với b&ecirc;n nhận chuyển nhượng hợp đồng; b&ecirc;n nhận chuyển nhượng hợp đồng được tiếp tục thực hiện c&aacute;c quyền, nghĩa vụ của b&ecirc;n chuyển nhượng theo hợp đồng mua b&aacute;n nh&agrave; ở đ&atilde; k&yacute; với chủ đầu tư v&agrave; được coi l&agrave; b&ecirc;n mua nh&agrave; ở kể từ ng&agrave;y chủ đầu tư x&aacute;c nhận v&agrave;o văn bản chuyển nhượng hợp đồng.<br />\r\n<br />\r\n<strong>► Hướng dẫn mua nh&agrave; chung cư chưa c&oacute; sổ hồng</strong><br />\r\n<br />\r\nHiện nay luật đang &aacute;p dụng l&agrave; Luật nh&agrave; ở 2014 hiện tại đ&atilde; c&oacute; hiệu lực từ ng&agrave;y 01/07/2015 quy định r&otilde; ở điều 120 như sau:<br />\r\n<br />\r\n<strong>Điều 120. Tr&igrave;nh tự, thủ tục thực hiện giao dịch về nh&agrave; ở</strong><br />\r\n<br />\r\nC&aacute;c b&ecirc;n tham gia giao dịch nh&agrave; ở thỏa thuận lập hợp đồng mua b&aacute;n, cho thu&ecirc;, thu&ecirc; mua, tặng cho, đổi, thế chấp, g&oacute;p vốn, cho mượn, cho ở nhờ, ủy quyền quản l&yacute; nh&agrave; ở hoặc văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; ở thương mại (sau đ&acirc;y gọi chung l&agrave; hợp đồng về nh&agrave; ở) c&oacute; c&aacute;c nội dung quy định tại Điều 121 của Luật n&agrave;y; trường hợp tổ chức tặng cho nh&agrave; t&igrave;nh nghĩa, nh&agrave; t&igrave;nh thương th&igrave; chỉ cần lập văn bản tặng cho.<br />\r\n<br />\r\nC&aacute;c b&ecirc;n thỏa thuận để một b&ecirc;n thực hiện nộp hồ sơ đề nghị cơ quan nh&agrave; nước c&oacute; thẩm quyền cấp Giấy chứng nhận đối với nh&agrave; ở đ&oacute;; trường hợp mua, thu&ecirc; mua nh&agrave; ở của chủ đầu tư dự &aacute;n th&igrave; chủ đầu tư c&oacute; tr&aacute;ch nhiệm l&agrave;m thủ tục để cơ quan nh&agrave; nước c&oacute; thẩm quyền cấp Giấy chứng nhận cho b&ecirc;n mua, b&ecirc;n thu&ecirc; mua, trừ trường hợp b&ecirc;n mua, b&ecirc;n thu&ecirc; mua tự nguyện l&agrave;m thủ tục đề nghị cấp Giấy chứng nhận.<br />\r\n<br />\r\nCơ quan nh&agrave; nước c&oacute; thẩm quyền khi thực hiện cấp Giấy chứng nhận cho b&ecirc;n mua, b&ecirc;n thu&ecirc; mua, b&ecirc;n nhận tặng cho, nhận đổi, nhận g&oacute;p vốn, nhận thừa kế nh&agrave; ở c&ugrave;ng với nhận chuyển quyền sử dụng đất ở hợp ph&aacute;p c&oacute; nh&agrave; ở đ&oacute; th&igrave; đồng thời c&ocirc;ng nhận quyền sở hữu nh&agrave; ở v&agrave; quyền sử dụng đất ở cho b&ecirc;n nhận quyền sở hữu nh&agrave; ở.<br />\r\n<br />\r\nHai b&ecirc;n mua b&aacute;n c&oacute; thể lập văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; chung cư &nbsp;tại văn ph&ograve;ng c&ocirc;ng chứng để đảm bảo gi&aacute; trị ph&aacute;p l&yacute; của giao dịch v&agrave; an to&agrave;n cho người mua.<br />\r\n<br />\r\nTuy nhi&ecirc;n trước khi thực hiện lập văn bản n&agrave;y chủ nh&agrave; phải xin x&aacute;c nhận đồng &yacute; cho chuyển nhượng của chủ đầu tư, trong một số trường hợp nh&agrave; chung cư sẽ kh&ocirc;ng thể thực hiện được thủ tục n&agrave;y nếu chủ đầu tư kh&ocirc;ng đồng &yacute;.<br />\r\n<br />\r\nNhư vậy về việc x&aacute;c lập văn bản chuyển nhượng hợp đồng mua b&aacute;n nh&agrave; chung cư hiện nay kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c so với trước đ&acirc;y. V&igrave; vậy kh&aacute;ch h&agrave;ng c&oacute; thể y&ecirc;u cầu b&ecirc;n b&aacute;n hợp t&aacute;c để thực hiện thủ tục.', 0, NULL, NULL, 1, 0, 84, '2017-07-14 15:22:54', '2017-07-14 15:22:54', 2, 2),
(48, 'Thủ tục cấp lại giấy chứng nhận quyền sử dụng đất như thế nào', 'thu-tuc-cap-lai-giay-chung-nhan-quyen-su-dung-dat-nhu-the-nao', 'Thu tuc cap lai giay chung nhan quyen su dung dat nhu the nao', 'Quý vị cần nên biết giấy chứng nhận quyền sử dụng đất là gì và quy trình cũng như các thủ tục cần có để được cấp lại. Nếu quý vị chưa nắm rõ hãy tham khảo ngay bài viết dưới đây.', '2017/07/15/thu-tuc-cap-lai-giay-chung-nhan-quyen-su-dung-dat-nhu-the-nao-1500092695.png', 5, 'Giấy chứng nhận quyền sử dụng đất l&agrave; một giấy tờ rất quan trọng đối với c&aacute;c c&aacute; nh&acirc;n, hộ gia đ&igrave;nh c&oacute; quyền sử dụng đất v&agrave; sở hữu nh&agrave; ở. Giấy chứng nhận quyền sử dụng đất kh&ocirc;ng chỉ c&oacute; &yacute; nghĩa ghi nhận quyền sử dụng v&agrave; quyền sở hữu của c&aacute; nh&acirc;n, hộ gia đ&igrave;nh m&agrave; c&ograve;n c&oacute; thể được sử dụng trong c&aacute;c giao dịch trong cuộc sống.\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"Thủ tục cấp lại giấy chứng nhận quyền sử dụng đất như thế nào\" src=\"/uploads/images/thu-tuc-cap-lai-giay-chung-nhan-quyen-su-dung-dat-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nTrường hợp Giấy chứng nhận quyền sử dụng đất bị mất th&igrave; sẽ g&acirc;y ảnh hưởng kh&ocirc;ng nhỏ tới quyền lợi hợp ph&aacute;p của c&aacute; nh&acirc;n, hộ gia đ&igrave;nh. V&igrave; l&iacute; do n&agrave;y, Nh&agrave; nước ta tạo điều kiện cho c&aacute;c c&aacute;c nh&acirc;n, hộ gia đ&igrave;nh trong trường hợp n&agrave;y c&oacute; thể xin cấp lại Giấy chứng nhận quyền sử dụng đất. Vậy muốn được cấp lại Giấy chứng nhận quyền sử dụng đất th&igrave; cần l&agrave;m những thủ tục g&igrave;, quy tr&igrave;nh thực hiện như thế n&agrave;o?<br />\r\n<br />\r\n<strong>► Cơ quan thực hiện</strong><br />\r\n<br />\r\nUBND x&atilde; nơi người xin cấp lại Giấy chứng nhận quyền sử dụng đất cư tr&uacute;. Sở T&agrave;i nguyền v&agrave; M&ocirc;i trường.<br />\r\n<br />\r\n<strong>► Cơ sở ph&aacute;p l&yacute;</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Th&ocirc;ng tư số 24/2014/TT-BTNMT của Bộ T&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường quy định về hồ sơ địa ch&iacute;nh;</li>\r\n	<li>Quyết định số 37/2015/QĐ-UBND của UBND th&agrave;nh phố H&agrave; Nội;</li>\r\n	<li>Nghị định số 43/2014/NĐ-CP của Ch&iacute;nh Phủ quy định chi tiết thi h&agrave;nh một số điều của luật đất đai;</li>\r\n	<li>Quyết định số 60/2014/QĐ-UBND của UBND th&agrave;nh phố H&agrave; Nội về việc thu lệ ph&iacute; địa ch&iacute;nh tr&ecirc;n địa b&agrave;n th&agrave;nh phố H&agrave; Nội.</li>\r\n</ul>\r\n<br />\r\n<strong>► Th&agrave;nh phần hồ sơ cần c&oacute;</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đơn đề nghị cấp lại Giấy chứng nhận theo Mẫu số 10/ĐK</li>\r\n	<li>Giấy x&aacute;c nhận của UBND x&atilde; về việc đ&atilde; ni&ecirc;m yết th&ocirc;ng b&aacute;o mất giấy trong thời hạn 15 ng&agrave;y (đối với c&aacute; nh&acirc;n, hộ gia đ&igrave;nh)</li>\r\n	<li>Giấy tờ chứng minh đ&atilde; đăng tin 03 lần tr&ecirc;n phương tiện th&ocirc;ng tin đại ch&uacute;ng ở H&agrave; Nội về việc mất GCN (đối với tổ chức, cơ sở t&ocirc;n gi&aacute;o, c&aacute; nh&acirc;n nước ngo&agrave;i, tổ chức nước ngo&agrave;i c&oacute; chức năng ngoại giao, doanh nghiệp c&oacute; vốn đầu tư nước ngo&agrave;i v&agrave; người Việt Nam định cư ở nước ngo&agrave;i thực hiện dự &aacute;n đầu tư)</li>\r\n	<li>Giấy x&aacute;c nhận của UBND x&atilde; về việc thi&ecirc;n tai, hỏa hoạn (trường hợp mất GCN do thi&ecirc;n tai, hỏa hoạn)</li>\r\n</ul>\r\n<br />\r\nSố lượng: 1 bộ<br />\r\n<br />\r\n<strong>► C&aacute;c bước quy tr&igrave;nh thực hiện</strong><br />\r\n<br />\r\nBước 1: Th&ocirc;ng b&aacute;o về việc bị mất Giấy chứng nhận quyền sử dung đất<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Hộ gia đ&igrave;nh v&agrave; c&aacute; nh&acirc;n, cộng đồng d&acirc;n cư c&oacute; tr&aacute;ch nhiệm khai b&aacute;o với UBND x&atilde; nơi c&oacute; đất về việc bị mất Giấy chứng nhận quyền sử dung đất</li>\r\n	<li>UBND x&atilde; c&oacute; tr&aacute;ch nhiệm ni&ecirc;m yết th&ocirc;ng b&aacute;o mất Giấy chứng nhận quyền sử dung đất tại trụ sở UBND x&atilde;</li>\r\n	<li>Tổ chức trong nước, cơ sở t&ocirc;n gi&aacute;o, tổ chức nước ngo&agrave;i, c&aacute; nh&acirc;n nước ngo&agrave;i, người Việt Nam định cư ở nước ngo&agrave;i phải đăng tin mất Giấy chứng nhận, Giấy chứng nhận quyền sở hữu nh&agrave; ở, Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng tr&ecirc;n phương tiện th&ocirc;ng tin đại ch&uacute;ng ở H&agrave; Nội</li>\r\n</ul>\r\n<br />\r\nBước 2: Nộp hồ sơ đề nghị cấp lại Giấy chứng nhận quyền sử dung đất<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Sau 30 ng&agrave;y kể từ ng&agrave;y ni&ecirc;m yết th&ocirc;ng b&aacute;o mất Giấy chứng nhận quyền sử dung đất tại trụ sở UBND x&atilde; /ng&agrave;y đăng tin lần đầu tr&ecirc;n phương ti&ecirc;n th&ocirc;ng tin đại ch&uacute;ng ở H&agrave; Nội, người bị mất Giấy chứng nhận quyền sử dung đất nộp 01 bộ hồ sơ tại Bộ phận tiếp nhận v&agrave; trả kết quả thủ tục h&agrave;nh ch&iacute;nh của Chi nh&aacute;nh văn ph&ograve;ng đăng k&yacute; đất đai H&agrave; Nội.</li>\r\n	<li>Sau đ&oacute; lấy phiếu nhận v&agrave; hẹn trả kết quả giải quyết thủ tục h&agrave;nh ch&iacute;nh &nbsp;đề nghị cấp lại Giấy chứng nhận quyền sử dung đất (trường hợp hồ sơ thiếu th&igrave; người bị mất GCN nộp bổ sung hồ sơ đến khi hồ sơ đủ v&agrave; hợp lệ)</li>\r\n	<li>Nộp lệ ph&iacute; cấp lại GCN</li>\r\n</ul>\r\n<br />\r\nBước 3: Cơ quan c&oacute; thẩm quyền xử l&yacute; hồ sơ<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Chi nh&aacute;nh Văn ph&ograve;ng đăng k&yacute; đất đai H&agrave; Nội c&oacute; tiến h&agrave;nh kiểm tra hồ sơ, tr&iacute;ch lục bản đồ địa ch&iacute;nh hoặc tr&iacute;ch đo địa ch&iacute;nh thửa đất đối với trường hợp chưa c&oacute; bản đồ địa ch&iacute;nh v&agrave; chưa tr&iacute;ch đo địa ch&iacute;nh thửa đất</li>\r\n	<li>Lập hồ sơ tr&igrave;nh Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường k&yacute; quyết định hủy Giấy chứng nhận quyền sử dung đất bị mất</li>\r\n	<li>K&yacute; cấp lại Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất; ch&iacute;nh l&yacute;, cập nhập biến động v&agrave;o hồ sơ địa ch&iacute;nh, cơ sở dữ liệu đất đai</li>\r\n	<li>Trao Giấy chứng nhận quyền sử dung đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho người được cấp.</li>\r\n</ul>\r\n<br />\r\nBước 4: Cấp lại Giấy chứng nhận quyền sử dung đất<br />\r\n<br />\r\nSở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường k&yacute; Quyết đinh hủy Giấy chứng nhận quyền sử dụng đất bị mất, đồng thời cấp lại GCN<br />\r\n<br />\r\nBước 5: Nhận Giấy chứng nhận quyền sử dung đất mới<br />\r\n<br />\r\nBộ phận tiếp nhận hồ sơ v&agrave; trả kết quả của Chi nh&aacute;nh văn ph&ograve;ng đăng k&yacute; đất đai H&agrave; Nội giao Giấy chứng nhận quyền sử dung đất mới cho người bị mất GCN.<br />\r\n<br />\r\n<strong>► Lệ ph&iacute; xin cấp lại</strong><br />\r\n<br />\r\n+Trường hợp GCN chỉ c&oacute; quyền sử dụng đất: 20.000 đồng (c&aacute;c phường thuộc quận, thị x&atilde;); 10.000 đồng (khu vực kh&aacute;c); 50.000 đồng (đối với tổ chức.<br />\r\n<br />\r\n+Trường hợp GCN bao gồm quyền sử dụng đất, quyền sở hữu nh&agrave; ở, t&agrave;i sản găn liền với đất: 50.000 đồng (phường thuộc quận, thị x&atilde;); 25.000 đồng (khu vực kh&aacute;c); 50.000 đồng (tổ chức)', 0, NULL, NULL, 1, 0, 87, '2017-07-15 11:25:16', '2017-07-15 11:47:15', 2, 2),
(50, 'Những rủi ro khi bán nhà đất bằng giấy tờ viết tay cần biết', 'nhung-rui-ro-khi-ban-nha-dat-bang-giay-to-viet-tay-can-biet', 'Nhung rui ro khi ban nha dat bang giay to viet tay can biet', 'Nếu quý vị đang mua bán đất thổ cư ,có viết giấy tờ mua viết tay có người xác nhận. Nhưng quý vị có biết những rủi ro khi bán nhà đất bằng giấy tờ viết tay cần biết có thể xảy ra hay không. Để biết rõ hơn hãy xem bài viết dưới đây.', '2017/07/15/nhung-rui-ro-khi-ban-nha-dat-bang-giay-to-viet-tay-can-biet-1500102432.png', 5, '<div style=\"text-align:center\"><img alt=\"Những rủi ro khi bán nhà đất bằng giấy tờ viết tay cần biết\" src=\"/uploads/images/nhung-rui-ro-khi-ban-nha-dat-bang-giay-to-viet-tay-can-biet.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nCăn cứ Điều 2 Mục II Nghị quyết 02/2004/NQ-HĐTP quy định. Việc giải quyết tranh chấp hợp đồng chuyển nhượng quyền sử dụng đất được x&aacute;c lập từ sau ng&agrave;y 15/10/1993.<br />\r\n<br />\r\n<strong>Điều kiện để c&ocirc;ng nhận hợp đồng chuyển nhượng quyền sử dụng đất.</strong><br />\r\n&nbsp;<br />\r\nTheo qui định tại Điều 131, c&aacute;c điều từ Điều 705 đến Điều 707 v&agrave; Điều 711 của Bộ luật D&acirc;n sự, khoản 2 Điều 3, Điều 30, Điều 31 của Luật Đất đai năm 1993 v&agrave; khoản 1 Điều 106, điểm b khoản 1 Điều 127, khoản 1 Điều 146 của Luật Đất đai năm 2003, th&igrave; To&agrave; &aacute;n chỉ c&ocirc;ng nhận hợp đồng chuyển nhượng quyền sử dụng đất khi hợp đồng đ&oacute; c&oacute; đầy đủ c&aacute;c điều kiện sau đ&acirc;y.<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Người tham gia giao kết hợp đồng chuyển nhượng quyền sử dụng đất c&oacute; năng lực h&agrave;nh vi d&acirc;n sự</li>\r\n	<li>Người tham gia giao kết hợp đồng chuyển nhượng quyền sử dụng đất ho&agrave;n to&agrave;n tự nguyện</li>\r\n	<li>Mục đ&iacute;ch v&agrave; nội dung của hợp đồng chuyển nhượng quyền sử dụng đất kh&ocirc;ng tr&aacute;i ph&aacute;p luật, đạo đức x&atilde; hội</li>\r\n	<li>Đất chuyển nhượng đ&atilde; được cơ quan c&oacute; thẩm quyền cấp giấy chứng nhận quyền sử dụng đất theo Luật Đất đai năm 1987, Luật Đất đai năm 1993, Luật Đất đai năm 2003</li>\r\n	<li>B&ecirc;n chuyển nhượng quyền sử dụng đất phải c&oacute; đủ điều kiện chuyển nhượng v&agrave; b&ecirc;n nhận chuyển nhượng quyền sử dụng đất phải c&oacute; đủ điều kiện về nhận chuyển nhượng theo qui định của ph&aacute;p luật</li>\r\n	<li>Hợp đồng chuyển nhượng quyền sử dụng đất được lập th&agrave;nh văn bản c&oacute; chứng nhận của C&ocirc;ng chứng nh&agrave; nước hoặc chứng thực của Uỷ ban nh&acirc;n d&acirc;n cấp c&oacute; thẩm quyền</li>\r\n</ul>\r\n<br />\r\n<strong>X&aacute;c định hợp đồng chuyển nhượng quyền sử dụng đất kh&ocirc;ng c&oacute; đầy đủ c&aacute;c điều kiện được hướng dẫn tại điểm a tiểu mục 2.3 mục 2 n&agrave;y</strong><br />\r\n<br />\r\nĐối với hợp đồng được giao kết trước ng&agrave;y 01/7/2004 vi phạm điều kiện được hướng dẫn tại điểm a.4 tiểu mục 2.3 mục 2 n&agrave;y, nhưng đ&atilde; c&oacute; một trong c&aacute;c loại giấy tờ về quyền sử dụng đất được quy định tại c&aacute;c khoản 1, 2 v&agrave; 5 Điều 50 của Luật Đất đai năm 2003, m&agrave; c&oacute; ph&aacute;t sinh tranh chấp, nếu từ ng&agrave;y 01/7/2004 mới c&oacute; y&ecirc;u cầu To&agrave; &aacute;n giải quyết, th&igrave; kh&ocirc;ng coi l&agrave; hợp đồng v&ocirc; hiệu do vi phạm điều kiện n&agrave;y.<br />\r\n&nbsp;<br />\r\nĐối với hợp đồng chuyển nhượng quyền sử dụng đất m&agrave; tại thời điểm giao kết vi phạm c&aacute;c điều kiện được hướng dẫn tại điểm a.4 v&agrave; điểm a.6 tiểu mục 2, 3 mục 2 n&agrave;y, nhưng sau đ&oacute; đ&atilde; được Uỷ ban nh&acirc;n d&acirc;n cấp c&oacute; thẩm quyền cấp giấy chứng nhận quyền sử dụng đất hoặc một trong c&aacute;c loại giấy tờ quy định tại c&aacute;c khoản 1, 2 v&agrave; 5 Điều 50 của Luật Đất đai năm 2003 m&agrave; c&oacute; ph&aacute;t sinh tranh chấp v&agrave; từ ng&agrave;y 01/7/2004 mới c&oacute; y&ecirc;u cầu To&agrave; &aacute;n giải quyết, th&igrave; kh&ocirc;ng coi l&agrave; hợp đồng v&ocirc; hiệu do vi phạm điều kiện n&agrave;y.<br />\r\n&nbsp;<br />\r\nĐối với hợp đồng chuyển nhượng quyền sử dụng đất vi phạm điều kiện được hướng dẫn tại điểm a.4 v&agrave; điểm a.6 tiểu mục 2.3 mục 2 n&agrave;y, nếu sau khi thực hiện hợp đồng chuyển nhượng, b&ecirc;n nhận chuyển nhượng đ&atilde; trồng c&acirc;y l&acirc;u năm, đ&atilde; l&agrave;m nh&agrave; ki&ecirc;n cố... v&agrave; b&ecirc;n chuyển nhượng kh&ocirc;ng phản đối v&agrave; cũng kh&ocirc;ng bị cơ quan nh&agrave; nước c&oacute; thẩm quyền xử l&yacute; vi phạm h&agrave;nh ch&iacute;nh theo c&aacute;c quy định của Nh&agrave; nước về xử l&yacute; vi phạm h&agrave;nh ch&iacute;nh trong lĩnh vực đất đai, th&igrave; To&agrave; &aacute;n c&ocirc;ng nhận hợp đồng.<br />\r\n<br />\r\nNếu b&ecirc;n nhận chuyển nhượng chỉ l&agrave;m nh&agrave; tr&ecirc;n một phần đất, th&igrave; To&agrave; &aacute;n c&ocirc;ng nhận phần hợp đồng chuyển nhượng quyền sử dụng phần đất c&oacute; nh&agrave; ở v&agrave; huỷ phần hợp đồng đối với diện t&iacute;ch đất c&ograve;n lại, buộc b&ecirc;n nhận chuyển nhượng giao trả phần đất đ&oacute; cho b&ecirc;n chuyển nhượng, trừ trường hợp việc giao trả kh&ocirc;ng bảo đảm mục đ&iacute;ch sử dụng cho cả hai b&ecirc;n giao kết hợp đồng, đồng thời buộc c&aacute;c b&ecirc;n thanh to&aacute;n cho nhau phần ch&ecirc;nh lệch.<br />\r\n&nbsp;<br />\r\nTheo như bạn tr&igrave;nh b&agrave;y th&igrave; hợp đồng chuyển nhượng được viết tay, nếu sau khi thực hiện hợp đồng, b&ecirc;n nhận chuyển nhượng đ&atilde; trồng c&acirc;y l&acirc;u năm, đ&atilde; l&agrave;m nh&agrave; ki&ecirc;n cố v&agrave; b&ecirc;n chuyển nhượng kh&ocirc;ng phản đối v&agrave; cũng kh&ocirc;ng bị cơ quan nh&agrave; nước c&oacute; thẩm quyền xử l&yacute; vi phạm h&agrave;nh ch&iacute;nh theo c&aacute;c quy định của Nh&agrave; nước về xử l&yacute; vi phạm h&agrave;nh ch&iacute;nh trong lĩnh vực đất đai, th&igrave; To&agrave; &aacute;n c&ocirc;ng nhận hợp đồng.<br />\r\n&nbsp;<br />\r\nNếu xảy ra tranh chấp c&aacute;c b&ecirc;n tranh chấp đất đai tự h&ograve;a giải hoặc giải quyết tranh chấp đất đai th&ocirc;ng qua h&ograve;a giải ở cơ sở. Tranh chấp đất đai m&agrave; c&aacute;c b&ecirc;n tranh chấp kh&ocirc;ng h&ograve;a giải được th&igrave; gửi đơn đến UBND cấp x&atilde; nơi c&oacute; đất tranh chấp để h&ograve;a giải. Tranh chấp đất đai đ&atilde; được h&ograve;a giải tại UBND x&atilde; m&agrave; kh&ocirc;ng th&agrave;nh th&igrave; đối với tranh chấp đất đai m&agrave; đương sự kh&ocirc;ng c&oacute; Giấy chứng nhận hoặc kh&ocirc;ng c&oacute; một trong c&aacute;c loại giấy tờ quy định tại Điều 100 của Luật n&agrave;y th&igrave; đương sự chỉ được lựa chọn một trong hai h&igrave;nh thức giải quyết tranh chấp đất đai theo quy định sau đ&acirc;y:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Nộp đơn y&ecirc;u cầu giải quyết tranh chấp tại UBND cấp c&oacute; thẩm quyền theo quy định tại khoản 3 điều n&agrave;y</li>\r\n	<li>Khởi kiện tại T&ograve;a &aacute;n nh&acirc;n d&acirc;n c&oacute; thẩm quyền theo quy định của ph&aacute;p luật về tố tụng d&acirc;n sự</li>\r\n</ul>\r\n&nbsp;', 0, NULL, NULL, 1, 0, 90, '2017-07-15 14:07:29', '2017-07-15 14:07:29', 2, 2);
INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(56, 'Thủ tục cấp giấy phép xây dựng như thế nào', 'thu-tuc-cap-giay-phep-xay-dung-nhu-the-nao', 'Thu tuc cap giay phep xay dung nhu the nao', 'Giấy phép xây dựng như tấm vé thông hành đối với các chủ đầu tư, là tiền đề để họ có thể thực hiện việc xây dựng. Nắm bắt các quy định pháp luật hiện hành về giấy phép xây dựng là rất cần thiết, giúp các chủ đầu tư tránh được những sai phạm đáng tiếc. Vậy thủ tục cấp giấy phép xây dựng như thế nào thì mời quý vị tham khảo dưới đây', '2017/07/17/thu-tuc-cap-giay-phep-xay-dung-nhu-the-nao-1500267894.png', 5, '&nbsp;\r\n<div style=\"text-align:center\"><img alt=\"Thủ tục cấp giấy phép xây dựng như thế nào\" src=\"/uploads/images/thu-tuc-cap-giay-phep-xay-dung-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n<strong>► Những cơ quan c&oacute; thể nộp hồ sơ xin cấp giấy ph&eacute;p x&acirc;y dựng</strong><br />\r\n<br />\r\nUỷ ban nh&acirc;n d&acirc;n cấp huyện cấp Giấy ph&eacute;p x&acirc;y dựng đối với c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng trong đ&ocirc; thị, c&aacute;c trung t&acirc;m cụm x&atilde; thuộc địa giới quản l&yacute; h&agrave;nh ch&iacute;nh của quận, huyện m&igrave;nh, trừ c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng thuộc thẩm quyền cấp ph&eacute;p của Uỷ ban nh&acirc;n d&acirc;n tỉnh.<br />\r\n<br />\r\nUỷ ban nh&acirc;n d&acirc;n cấp x&atilde; cấp Giấy xin ph&eacute;p x&acirc;y dựng nh&agrave; ở ri&ecirc;ng lẻ ở những điểm d&acirc;n cư n&ocirc;ng th&ocirc;n đ&atilde; c&oacute; quy hoạch x&acirc;y dựng được duyệt, những điểm d&acirc;n cư theo quy định của Uỷ ban nh&acirc;n d&acirc;n cấp huyện phải cấp Giấy ph&eacute;p x&acirc;y dựng thuộc địa giới h&agrave;nh ch&iacute;nh m&igrave;nh quản l&yacute;.<br />\r\n<br />\r\nKhi tiếp nhận hồ sơ xin ph&eacute;p x&acirc;y dựng, cơ quan cấp Giấy ph&eacute;p c&oacute; tr&aacute;ch nhiệm kiểm tra hồ sơ, nếu hồ sơ xin cấp Giấy ph&eacute;p x&acirc;y dựng chưa đầy đủ th&igrave; cơ quan cấp Giấy ph&eacute;p x&acirc;y dựng phải giải th&iacute;ch, hướng dẫn cho người xin cấp Giấy ph&eacute;p x&acirc;y dựng bổ sung hồ sơ theo đ&uacute;ng quy định.<br />\r\n<br />\r\nThời gian ho&agrave;n chỉnh hồ sơ kh&ocirc;ng t&iacute;nh v&agrave;o thời hạn cấp Giấy ph&eacute;p x&acirc;y dựng. Trường hợp hồ sơ c&oacute; đủ giấy tờ hợp lệ, cơ quan cấp Giấy ph&eacute;p x&acirc;y dựng sẽ trao giấy bi&ecirc;n nhận trong đ&oacute; hẹn ng&agrave;y nhận kết quả. Giấy bi&ecirc;n nhận được lập th&agrave;nh 02 bản, một bản giao cho người xin cấp Giấy ph&eacute;p x&acirc;y dựng v&agrave; một bản lưu tại cơ quan cấp Giấy ph&eacute;p x&acirc;y dựng. Đối với nh&agrave; ở ri&ecirc;ng lẻ th&igrave; thời hạn cấp Giấy ph&eacute;p x&acirc;y dựng kh&ocirc;ng qu&aacute; 15 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y nhận đủ hồ sơ hợp lệ.<br />\r\n<br />\r\n<strong>► Tr&igrave;nh tự, thủ tục xin cấp giấy ph&eacute;p x&acirc;y dựng bao gồm</strong><br />\r\n<br />\r\nChuẩn bị hồ sơ đề nghị cấp giấy ph&eacute;p x&acirc;y dựng<br />\r\n<br />\r\n&nbsp;C&ocirc;ng việc đầu ti&ecirc;n cần phải thực hiện khi muốn xin cấp ph&eacute;p x&acirc;y dựng l&agrave; chuẩn bị hồ sơ đề nghị cấp giấy ph&eacute;p x&acirc;y dựng. Dưới đ&acirc;y l&agrave; những t&agrave;i liệu cần c&oacute; trong hồ sơ đề nghị cấp giấy ph&eacute;p x&acirc;y dựng:<br />\r\n<br />\r\n- Đơn đề nghị cấp giấy ph&eacute;p x&acirc;y dựng (theo mẫu đối với từng trường hợp, từng loại c&ocirc;ng tr&igrave;nh, nội dung n&agrave;y được hướng dẫn cụ thể tại Điều 1 Th&ocirc;ng tư số &nbsp;10/2012/TT-BXD).<br />\r\n<br />\r\n- Bản sao c&oacute; chứng thực một trong những giấy tờ về quyền sử dụng đất theo quy định của ph&aacute;p luật về đất đai; giấy tờ về quyền sở hữu c&ocirc;ng tr&igrave;nh, nh&agrave; ở, đối với trường hợp sửa chữa, cải tạo; giấy ủy quyền, đối với trường hợp được chủ sở hữu c&ocirc;ng tr&igrave;nh ủy quyền thực hiện sửa chữa, cải tạo; quyết định cho ph&eacute;p đầu tư x&acirc;y dựng của cấp c&oacute; thẩm quyền đối với c&ocirc;ng tr&igrave;nh ngầm đ&ocirc; thị; quyết định ph&ecirc; duyệt b&aacute;o c&aacute;o đ&aacute;nh gi&aacute; t&aacute;c động m&ocirc;i trường hoặc cam kết bảo vệ m&ocirc;i trường theo quy định của ph&aacute;p luật về bảo vệ m&ocirc;i trường.<br />\r\n<br />\r\n- Hai bộ bản vẽ thiết kế do tổ chức, c&aacute; nh&acirc;n c&oacute; đủ điều kiện năng lực thực hiện v&agrave; đ&atilde; được chủ đầu tư tổ chức thẩm định, ph&ecirc; duyệt theo quy định (nội dung n&agrave;y được hướng dẫn cụ thể tại Th&ocirc;ng tư số &nbsp;10/2012/TT-BXD).<br />\r\n<br />\r\n- Đối với trường hợp lắp đặt thiết bị hoặc kết cấu kh&aacute;c v&agrave;o c&ocirc;ng tr&igrave;nh đ&atilde; x&acirc;y dựng, nhưng kh&ocirc;ng thuộc sở hữu của chủ đầu tư th&igrave; phải c&oacute; bản sao c&oacute; chứng thực Hợp đồng với chủ sở hữu c&ocirc;ng tr&igrave;nh.<br />\r\n- Quyết định ph&ecirc; duyệt dự &aacute;n k&egrave;m theo văn bản chấp thuận đầu tư của cơ quan c&oacute; thẩm quyền, &yacute; kiến bằng văn bản về thiết kế cơ sở (nếu c&oacute;) của cơ quan quản l&yacute; nh&agrave; nước theo quy định.<br />\r\n<br />\r\n<strong>Thủ tục xin cấp giấy ph&eacute;p x&acirc;y dựng</strong><br />\r\n<br />\r\n- Chủ đầu tư nộp trực tiếp 1 bộ hồ sơ cho cơ quan cấp giấy ph&eacute;p x&acirc;y dựng.<br />\r\n<br />\r\n- Cơ quan cấp giấy ph&eacute;p x&acirc;y dựng tiếp nhận hồ sơ đề nghị cấp giấy ph&eacute;p x&acirc;y dựng; kiểm tra hồ sơ; ghi giấy bi&ecirc;n nhận đối với trường hợp hồ sơ đ&aacute;p ứng theo quy định hoặc hướng dẫn để chủ đầu tư ho&agrave;n thiện hồ sơ đối với trường hợp hồ sơ kh&ocirc;ng đ&aacute;p ứng theo quy định.<br />\r\n<br />\r\n- Trong thời hạn 10 ng&agrave;y l&agrave;m việc, kể từ ng&agrave;y nhận được hồ sơ, đơn vị được giao thẩm định hồ sơ xem x&eacute;t hồ sơ, kiểm tra thực địa (nếu cần thiết). Cơ quan cấp giấy ph&eacute;p x&acirc;y dựng x&aacute;c định c&aacute;c t&agrave;i liệu c&ograve;n thiếu, c&aacute;c t&agrave;i liệu kh&ocirc;ng đ&uacute;ng theo quy định hoặc kh&ocirc;ng đ&uacute;ng với thực tế để th&ocirc;ng b&aacute;o một lần bằng văn bản cho chủ đầu tư bổ sung, ho&agrave;n chỉnh hồ sơ.<br />\r\n<br />\r\nNếu hồ sơ bổ sung chưa đ&aacute;p ứng được y&ecirc;u cầu th&igrave; cơ quan cấp ph&eacute;p th&ocirc;ng b&aacute;o bằng văn bản hướng dẫn cho chủ đầu tư tiếp tục ho&agrave;n thiện hồ sơ. Sau hai lần bổ sung, hồ sơ vẫn kh&ocirc;ng đ&aacute;p ứng được c&aacute;c điều kiện theo quy định th&igrave; cơ quan cấp ph&eacute;p c&oacute; quyền kh&ocirc;ng xem x&eacute;t việc cấp giấy ph&eacute;p v&agrave; th&ocirc;ng b&aacute;o cho chủ đầu tư biết.<br />\r\n<br />\r\n- Căn cứ quy m&ocirc;, t&iacute;nh chất, loại c&ocirc;ng tr&igrave;nh v&agrave; địa điểm x&acirc;y dựng c&ocirc;ng tr&igrave;nh c&oacute; trong hồ sơ đề nghị cấp giấy ph&eacute;p x&acirc;y dựng, cơ quan cấp ph&eacute;p x&acirc;y dựng đối chiếu với c&aacute;c điều kiện được cấp giấy ph&eacute;p x&acirc;y dựng để gửi văn bản lấy &yacute; kiến của c&aacute;c cơ quan quản l&yacute; nh&agrave; nước về những lĩnh vực li&ecirc;n quan đến c&ocirc;ng tr&igrave;nh x&acirc;y dựng.<br />\r\n<br />\r\nCơ quan cấp giấy ph&eacute;p x&acirc;y dựng căn cứ c&aacute;c quy định hiện h&agrave;nh v&agrave; c&aacute;c điều kiện được cấp giấy ph&eacute;p x&acirc;y dựng để quyết định việc cấp giấy ph&eacute;p x&acirc;y dựng.<br />\r\n<br />\r\n- Thời gian cấp giấy ph&eacute;p x&acirc;y dựng: Đối với trường hợp cấp giấy ph&eacute;p x&acirc;y dựng mới, bao gồm cả giấy ph&eacute;p x&acirc;y dựng tạm, giấy ph&eacute;p x&acirc;y dựng điều chỉnh, giấy ph&eacute;p di dời, thời gian kh&ocirc;ng qu&aacute; 20 ng&agrave;y l&agrave;m việc đối với c&ocirc;ng tr&igrave;nh; 15 ng&agrave;y l&agrave;m việc đối với nh&agrave; ở ri&ecirc;ng lẻ tại đ&ocirc; thị; 10 ng&agrave;y l&agrave;m việc đối với nh&agrave; ở n&ocirc;ng th&ocirc;n, kể từ ng&agrave;y nhận đủ hồ sơ hợp lệ. Đối với trường hợp cấp lại hoặc gia hạn giấy ph&eacute;p x&acirc;y dựng: Kh&ocirc;ng qu&aacute; 10 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y nhận đủ hồ sơ hợp lệ.<br />\r\n<br />\r\nTrường hợp đến hạn n&oacute;i tr&ecirc;n, nhưng cần phải xem x&eacute;t th&ecirc;m th&igrave; cơ quan cấp giấy ph&eacute;p x&acirc;y dựng phải th&ocirc;ng b&aacute;o bằng văn bản cho chủ đầu tư biết l&yacute; do, đồng thời b&aacute;o c&aacute;o cấp c&oacute; thẩm quyền quản l&yacute; trực tiếp xem x&eacute;t v&agrave; chỉ đạo thực hiện, nhưng kh&ocirc;ng được qu&aacute; 10 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y hết hạn.<br />\r\n<br />\r\n- Nhận kết quả v&agrave; nộp lệ ph&iacute;: Chủ đầu tư tới nơi tiếp nhận hồ sơ theo thời gian ghi trong giấy bi&ecirc;n nhận để nhận kết quả v&agrave; nộp lệ ph&iacute; theo quy định khi nộp hồ sơ. Chủ đầu tư nhận giấy ph&eacute;p x&acirc;y dựng k&egrave;m theo hồ sơ thiết kế c&oacute; đ&oacute;ng dấu của cơ quan cấp giấy ph&eacute;p x&acirc;y dựng hoặc văn bản trả lời (đối với trường hợp kh&ocirc;ng đủ điều kiện để cấp giấy ph&eacute;p x&acirc;y dựng).<br />\r\n<br />\r\nNếu qu&aacute; thời hạn ghi trong giấy bi&ecirc;n nhận m&agrave; cơ quan cấp ph&eacute;p kh&ocirc;ng trả lời th&igrave; chủ đầu tư được ph&eacute;p x&acirc;y dựng c&ocirc;ng tr&igrave;nh theo hồ sơ thiết kế đ&atilde; được thẩm định, ph&ecirc; duyệt theo quy định c&oacute; trong hồ sơ đề nghị cấp giấy ph&eacute;p x&acirc;y dựng.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Những quy định mới về thủ tục xin cấp giấy ph&eacute;p x&acirc;y dựng</strong></span><br />\r\n<br />\r\nViệc cấp giấy ph&eacute;p x&acirc;y dựng được coi l&agrave; cơ sở đảm bảo quản l&yacute; nh&agrave; nước về x&acirc;y dựng, đặc biệt l&agrave; việc x&acirc;y dựng theo quy hoạch v&agrave; tu&acirc;n thủ c&aacute;c quy định ph&aacute;p luật c&oacute; li&ecirc;n quan; bảo vệ quyền v&agrave; lợi &iacute;ch hợp ph&aacute;p cho c&aacute;c c&aacute; nh&acirc;n, tổ chức, hộ gia đ&igrave;nh trong x&acirc;y dựng c&ocirc;ng tr&igrave;nh; đồng thời tạo điều kiện kiểm tra, gi&aacute;m s&aacute;t, xử l&iacute; c&aacute;c vi phạm về trật tự x&acirc;y dựng.<br />\r\n<br />\r\n<strong>Những c&ocirc;ng tr&igrave;nh x&acirc;y dựng n&agrave;o phải xin giấy ph&eacute;p x&acirc;y dựng</strong><br />\r\n<br />\r\nChủ đầu tư v&agrave; nh&agrave; thầu thi c&ocirc;ng chỉ được khởi c&ocirc;ng x&acirc;y dựng c&ocirc;ng tr&igrave;nh khi đ&atilde; c&oacute; đủ c&aacute;c điều kiện khởi c&ocirc;ng theo quy định tại Điều 72 Luật X&acirc;y dựng năm 2003, bao gồm: C&oacute; mặt bằng x&acirc;y dựng; c&oacute; Giấy ph&eacute;p x&acirc;y dựng đối với c&aacute;c c&ocirc;ng tr&igrave;nh phải c&oacute; Giấy ph&eacute;p x&acirc;y dựng; c&oacute; thiết kế bản vẽ thi c&ocirc;ng đ&atilde; được ph&ecirc; duyệt; c&oacute; biện ph&aacute;p bảo đảm an to&agrave;n, vệ sinh m&ocirc;i trường; c&oacute; hợp đồng x&acirc;y dựng; c&oacute; đủ nguồn vốn để thực hiện theo tiến độ.<br />\r\n<br />\r\nV&igrave; vậy, trước khi khởi c&ocirc;ng x&acirc;y dựng c&ocirc;ng tr&igrave;nh, chủ đầu tư phải c&oacute; Giấy ph&eacute;p x&acirc;y dựng, trừ trường hợp x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh kh&ocirc;ng phải xin cấp ph&eacute;p x&acirc;y dựng được quy định tại Khoản 1 Điều 3 Nghị định 64/2012/NĐ-CP ng&agrave;y 04/09/2012 &ldquo;Về cấp giấy ph&eacute;p x&acirc;y dựng&rdquo;, bao gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>C&ocirc;ng tr&igrave;nh b&iacute; mật nh&agrave; nước, c&ocirc;ng tr&igrave;nh theo lệnh khẩn cấp, c&ocirc;ng tr&igrave;nh tạm phục vụ thi c&ocirc;ng x&acirc;y dựng c&ocirc;ng tr&igrave;nh ch&iacute;nh v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh kh&aacute;c theo quy định của Ch&iacute;nh phủ được miễn giấy ph&eacute;p x&acirc;y dựng.</li>\r\n	<li>C&ocirc;ng tr&igrave;nh x&acirc;y dựng theo tuyến kh&ocirc;ng đi qua đ&ocirc; thị nhưng ph&ugrave; hợp với quy hoạch x&acirc;y dựng đ&atilde; được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt.</li>\r\n	<li>C&ocirc;ng tr&igrave;nh thuộc dự &aacute;n đầu tư x&acirc;y dựng được Thủ tướng Ch&iacute;nh phủ, Bộ trưởng, Thủ trưởng cơ quan ngang Bộ, Chủ tịch Ủy ban nh&acirc;n d&acirc;n c&aacute;c cấp quyết định đầu tư.</li>\r\n	<li>C&ocirc;ng tr&igrave;nh sửa chữa, cải tạo, lắp đặt thiết bị b&ecirc;n trong c&ocirc;ng tr&igrave;nh kh&ocirc;ng l&agrave;m thay đổi kiến tr&uacute;c c&aacute;c mặt ngo&agrave;i, kết cấu chịu lực, c&ocirc;ng năng sử dụng v&agrave; an to&agrave;n c&ocirc;ng tr&igrave;nh.</li>\r\n	<li>C&ocirc;ng tr&igrave;nh hạ tầng kỹ thuật chỉ y&ecirc;u cầu phải lập b&aacute;o c&aacute;o kinh tế - kỹ thuật v&agrave; nh&agrave; ở ri&ecirc;ng lẻ tại v&ugrave;ng s&acirc;u, v&ugrave;ng xa thuộc khu vực chưa c&oacute; quy hoạch điểm d&acirc;n cư n&ocirc;ng th&ocirc;n (quy hoạch x&acirc;y dựng x&atilde; n&ocirc;ng th&ocirc;n mới) được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt.</li>\r\n</ul>\r\n<br />\r\nCần lưu &yacute; một điểm mới trong quy định n&agrave;y, đ&oacute; l&agrave; theo điểm c Khoản 1 Điều 19 Nghị định số 12/2009/NĐ-CP ng&agrave;y 12-2-2009 của ch&iacute;nh phủ &ldquo;Về quản l&yacute; dự &aacute;n đầu tư x&acirc;y dựng c&ocirc;ng tr&igrave;nh&rdquo; (văn bản đ&atilde; hết hiệu lực), c&ocirc;ng tr&igrave;nh x&acirc;y dựng thuộc dự &aacute;n khu đ&ocirc; thị, khu c&ocirc;ng nghiệp, khu chế xuất, khu c&ocirc;ng nghệ cao, khu nh&agrave; ở c&oacute; quy hoạch chi tiết x&acirc;y dựng tỷ lệ 1/500 đ&atilde; được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt thuộc diện kh&ocirc;ng phải xin giấy ph&eacute;p x&acirc;y dựng.<br />\r\n<br />\r\nTuy nhi&ecirc;n, theo quy định hiện h&agrave;nh tại Nghị định 64/2012/NĐ-CP, trường hợp n&agrave;y kh&ocirc;ng c&ograve;n thuộc diện được miễn giấy ph&eacute;p x&acirc;y dựng nữa. Đối với những c&ocirc;ng tr&igrave;nh, theo quy định trước khi Nghị định số 64/2012/NĐ-CP c&oacute; hiệu lực m&agrave; thuộc đối tượng kh&ocirc;ng phải xin giấy ph&eacute;p x&acirc;y dựng, nhưng theo quy định của nghị định n&agrave;y thuộc đối tượng phải c&oacute; giấy ph&eacute;p x&acirc;y dựng, nếu đ&atilde; khởi c&ocirc;ng x&acirc;y dựng th&igrave; kh&ocirc;ng phải đề nghị cấp giấy ph&eacute;p, nhưng nếu chưa khởi c&ocirc;ng x&acirc;y dựng th&igrave; phải nộp hồ sơ đề nghị để được xem x&eacute;t cấp ph&eacute;p x&acirc;y dựng trước khi khởi c&ocirc;ng x&acirc;y dựng.<br />\r\n<br />\r\n<strong>Thẩm quyền cấp giấy ph&eacute;p x&acirc;y dựng</strong><br />\r\n<br />\r\nHiểu r&otilde; thẩm quyền cấp giấy ph&eacute;p x&acirc;y dựng gi&uacute;p c&aacute;c chủ đầu tư x&aacute;c định đ&uacute;ng nơi m&igrave;nh phải nộp hồ sơ đề nghị cấp giấy ph&eacute;p x&acirc;y dựng. Thẩm quyền cấp giấy ph&eacute;p x&acirc;y dựng được ph&aacute;p luật hiện h&agrave;nh quy định kh&aacute; cụ thể, r&otilde; r&agrave;ng t&ugrave;y thuộc v&agrave;o đối tượng xin cấp ph&eacute;p x&acirc;y dựng, cụ thể như sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Ủy ban nh&acirc;n d&acirc;n cấp tỉnh ủy quyền cho Sở X&acirc;y dựng cấp giấy ph&eacute;p x&acirc;y dựng đối với c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng cấp đặc biệt, cấp I, cấp II (x&aacute;c định theo hướng dẫn ph&acirc;n cấp c&aacute;c loại c&ocirc;ng tr&igrave;nh x&acirc;y dựng do Bộ x&acirc;y dựng ban h&agrave;nh); c&ocirc;ng tr&igrave;nh t&ocirc;n gi&aacute;o; c&ocirc;ng tr&igrave;nh di t&iacute;ch lịch sử - văn ho&aacute;; c&ocirc;ng tr&igrave;nh tượng đ&agrave;i, tranh ho&agrave;nh tr&aacute;ng thuộc địa giới h&agrave;nh ch&iacute;nh do m&igrave;nh quản l&yacute;; những c&ocirc;ng tr&igrave;nh tr&ecirc;n c&aacute;c tuyến, trục đường phố ch&iacute;nh trong đ&ocirc; thị theo quy định của UBND cấp tỉnh; c&ocirc;ng tr&igrave;nh thuộc dự &aacute;n c&oacute; vốn đầu tư trực tiếp nước ngo&agrave;i; c&ocirc;ng tr&igrave;nh thuộc dự &aacute;n v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh kh&aacute;c do Ủy ban nh&acirc;n d&acirc;n cấp tỉnh ủy quyền</li>\r\n	<li>Ủy ban nh&acirc;n d&acirc;n cấp huyện cấp giấy ph&eacute;p x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh c&ograve;n lại v&agrave; nh&agrave; ở ri&ecirc;ng lẻ ở đ&ocirc; thị thuộc địa giới h&agrave;nh ch&iacute;nh do m&igrave;nh quản l&yacute;, trừ c&aacute;c đối tượng quy định thuộc thẩm quyền của Ủy ban nh&acirc;n d&acirc;n cấp tỉnh</li>\r\n	<li>Ủy ban nh&acirc;n d&acirc;n x&atilde; cấp giấy ph&eacute;p x&acirc;y dựng nh&agrave; ở ri&ecirc;ng lẻ tại những điểm d&acirc;n cư n&ocirc;ng th&ocirc;n đ&atilde; c&oacute; quy hoạch x&acirc;y dựng được duyệt v&agrave; tại những khu vực theo quy định của UBND cấp huyện khi x&acirc;y dựng phải c&oacute; giấy ph&eacute;p x&acirc;y dựng thuộc địa giới h&agrave;nh ch&iacute;nh do m&igrave;nh quản l&yacute;</li>\r\n</ul>\r\n<br />\r\nLưu &yacute;: C&ocirc;ng tr&igrave;nh do cơ quan n&agrave;o cấp giấy ph&eacute;p x&acirc;y dựng th&igrave; cơ quan đ&oacute; c&oacute; quyền điều chỉnh, gia hạn, cấp lại v&agrave; thu hồi giấy ph&eacute;p x&acirc;y dựng do m&igrave;nh cấp. Ủy ban nh&acirc;n d&acirc;n cấp tỉnh quyết định thu hồi giấy ph&eacute;p x&acirc;y dựng do cấp dưới cấp kh&ocirc;ng đ&uacute;ng quy định.', 0, NULL, NULL, 1, 0, 96, '2017-07-17 12:06:07', '2017-07-17 12:06:49', 2, 2),
(57, 'Những thủ tục cần thiết khi xây nhà mới', 'nhung-thu-tuc-can-thiet-khi-xay-nha-moi', 'Nhung thu tuc can thiet khi xay nha moi', 'Nếu quý vị chuẩn bị xây nhà nhưng lại chưa có giấy phép vào vị trí không được tự do xây dựng, để xin được giấy phép thì những thủ tục cần thiết khi xây nhà mới dưới đây, quý vị cần phải nắm rõ để xin giấy phép xây nhà.', '2017/07/17/nhung-thu-tuc-can-thiet-khi-xay-nha-moi-1500275501.png', 5, '&nbsp;\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n\r\n<div style=\"text-align:center\"><span style=\"font-size:18px\"><strong><img alt=\"Những thủ tục cần thiết khi xây nhà mới\" src=\"/uploads/images/nhung-thu-tuc-can-thiet-khi-xay-nha-moi.png\" style=\"height:400px; width:650px\" /></strong></span></div>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Những loại ph&aacute;p l&yacute; cần thiết khi x&acirc;y nh&agrave;</strong></span><br />\r\n<br />\r\n<strong>Thứ nhất:</strong> Giấy chứng nhận quyền sử dụng đất<br />\r\n<br />\r\n&ndash; Chỉ khi l&agrave; chủ của l&ocirc; đất bạn mới c&oacute; thể xin giấy ph&eacute;p x&acirc;y dựng một c&aacute;ch hợp ph&aacute;p với c&aacute;c cơ quan nh&agrave; nước c&oacute; thẩm quyền<br />\r\n<br />\r\n<strong>Thứ hai: </strong>Giấy ph&eacute;p x&acirc;y dựng&nbsp;<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Thực tế m&agrave; n&oacute;i th&igrave; việc xin cấp giấy ph&eacute;p x&acirc;y dựng kh&aacute; rắc rối , đ&ograve;i hỏi bạn phải c&oacute; một bộ hồ sơ xin giấy ph&eacute;p x&acirc;y dựng của một đơn vị c&oacute; tư c&aacute;ch ph&aacute;p nh&acirc;n v&agrave; được cấp giấy chứng chỉ h&agrave;nh nghề tư vấn thiết kế c&ocirc;ng tr&igrave;nh đang trong thời hạn hoạt động ( mọi giấy tờ chứng chỉ h&agrave;nh nghề bị cơ quan nh&agrave; nước đ&igrave;nh chỉ xem như KH&Ocirc;NG T&Iacute;NH ) .</li>\r\n	<li>V&igrave; vậy chủ nh&agrave; sẽ kh&ocirc;ng thể c&oacute; được bộ hồ sơ ở tr&ecirc;n m&agrave; cần phải nhờ đến nh&agrave; tư vấn thiết kế x&acirc;y dựng hợp ph&aacute;p chuẩn bị gi&ugrave;m m&igrave;nh.</li>\r\n	<li>Bộ hồ sơ xin giấy cấp ph&eacute;p x&acirc;y dựng l&agrave; tập hợp c&aacute;c th&ocirc;ng tin , dữ liệu thăm d&ograve; từ địa h&igrave;nh , c&aacute;c lớp đất , nền m&oacute;ng được khảo s&aacute;t th&ocirc;ng qua qu&aacute; tr&igrave;nh khoan thăm d&ograve; được thực hiện bởi dụng cụ chuy&ecirc;n dụng của ngh&agrave;nh.</li>\r\n	<li>Bộ hồ sơ n&agrave;y l&agrave; cơ sở quan trọng để c&aacute;c đơn vị tư vấn thiết kế x&acirc;y dựng t&iacute;nh to&aacute;n ch&iacute;nh x&aacute;c cho m&oacute;ng v&agrave; hệ khung kết cấu của căn nh&agrave;.</li>\r\n</ul>\r\n<br />\r\n<strong>Thứ Ba:</strong> Phụ thuộc v&agrave;o từng địa phương m&agrave; sẽ c&oacute; bộ thủ tục cần thiết kh&aacute;c nhau<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Quy tr&igrave;nh xin ph&eacute;p x&acirc;y dựng sửa chữa nh&agrave; ở</strong></span><br />\r\n<br />\r\nBước 1: Chuẩn bị đầy đủ hồ sơ theo quy định.<br />\r\n<br />\r\nBước 2: Nộp hồ sơ tại Bộ phận tiếp nhận v&agrave; trả hồ sơ Sở X&acirc;y dựng của Quận nơi bạn sinh sống.<br />\r\n<br />\r\nKhi nhận hồ sơ xin cấp giấy ph&eacute;p x&acirc;y dựng, Sở X&acirc;y dựng sẽ kiểm tra đầy đủ th&agrave;nh phần hồ sơ theo quy định:<br />\r\n<br />\r\nTrường hợp th&agrave;nh phần hồ sơ đ&atilde; đầy đủ v&agrave; hợp lệ th&igrave; viết bi&ecirc;n nhận, ghi r&otilde; ng&agrave;y hẹn trả kết quả cho người nộp.<br />\r\n<br />\r\nTrường hợp th&agrave;nh phần hồ sơ chưa đầy đủ hoặc chưa hợp lệ th&igrave; hướng dẫn cho người nộp ho&agrave;n thiện hồ sơ theo quy định.<br />\r\n<br />\r\nBước 3: Thụ l&yacute;, giải quyết hồ sơ, cấp giấy ph&eacute;p x&acirc;y dựng (thời hạn giải quyết tối đa 20 ng&agrave;y l&agrave;m việc).<br />\r\n<br />\r\nTrường hợp hồ sơ chưa đủ điều kiện để cấp giấy ph&eacute;p x&acirc;y dựng theo quy định th&igrave; phải c&oacute; văn bản hướng dẫn đầy đủ, một lần cho chủ đầu tư biết để thực hiện.<br />\r\n<br />\r\nBước 4: Nhận kết quả (Giấy ph&eacute;p x&acirc;y dựng hoặc văn bản hướng dẫn) tại bộ phận Tiếp nhận v&agrave; trả kết quả của Sở X&acirc;y dựng.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Một số mức phạt đối với h&agrave;nh vi kh&ocirc;ng xin ph&eacute;p x&acirc;y dựng</strong></span><br />\r\n<br />\r\nTại Khoản 1 Điều 12 v&agrave; Khoản 3 Điều 13 Nghị định 121/2013/NĐ - CP c&oacute; quy định:<br />\r\n<br />\r\n1. Phạt cảnh c&aacute;o hoặc phạt tiền từ 500.000 đồng đến 1.000.000 đồng đối với một trong c&aacute;c h&agrave;nh vi sau đ&acirc;y:<br />\r\n<br />\r\na) Kh&ocirc;ng gửi văn bản th&ocirc;ng b&aacute;o ng&agrave;y khởi c&ocirc;ng cho Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi x&acirc;y dựng c&ocirc;ng tr&igrave;nh v&agrave; cơ quan cấp giấy ph&eacute;p x&acirc;y dựng theo quy định;<br />\r\n<br />\r\nb) Kh&ocirc;ng gửi văn bản ph&ecirc; duyệt biện ph&aacute;p tổ chức thi c&ocirc;ng bảo đảm an to&agrave;n cho c&ocirc;ng tr&igrave;nh x&acirc;y dựng v&agrave; c&ocirc;ng tr&igrave;nh l&acirc;n cận đến Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi x&acirc;y dựng c&ocirc;ng tr&igrave;nh trước khi khởi c&ocirc;ng x&acirc;y dựng c&ocirc;ng tr&igrave;nh;<br />\r\n<br />\r\nc) Kh&ocirc;ng ph&ecirc; duyệt biện ph&aacute;p tổ chức thi c&ocirc;ng theo quy định.<br />\r\n<br />\r\n3. Xử phạt đối với h&agrave;nh vi tổ chức thi c&ocirc;ng x&acirc;y dựng c&ocirc;ng tr&igrave;nh sai giấy ph&eacute;p x&acirc;y dựng được cấp đối với trường hợp cấp ph&eacute;p sửa chữa, cải tạo như sau:<br />\r\n<br />\r\na) Phạt cảnh c&aacute;o hoặc phạt tiền từ 500.000 đồng đến 1.000.000 đồng đối với x&acirc;y dựng nh&agrave; ở ri&ecirc;ng lẻ ở n&ocirc;ng th&ocirc;n hoặc c&ocirc;ng tr&igrave;nh x&acirc;y dựng kh&aacute;c kh&ocirc;ng thuộc trường hợp quy định tại Điểm b, Điểm c Khoản n&agrave;y;<br />\r\n<br />\r\nb) Phạt tiền từ 3.000.000 đồng đến 5.000.000 đồng đối với x&acirc;y dựng nh&agrave; ở ri&ecirc;ng lẻ ở đ&ocirc; thị;<br />\r\n<br />\r\nc) Phạt tiền từ 20.000.000 đồng đến 30.000.000 đồng đối với x&acirc;y dựng c&ocirc;ng tr&igrave;nh thuộc trường hợp phải lập b&aacute;o c&aacute;o kinh tế - kỹ thuật x&acirc;y dựng c&ocirc;ng tr&igrave;nh hoặc lập dự &aacute;n đầu tư x&acirc;y dựng c&ocirc;ng tr&igrave;nh.<br />\r\n<br />\r\nTheo quy định tại khoản 1 Điều 89 Luật X&acirc;y dựng 2014, trước khi khởi c&ocirc;ng x&acirc;y dựng c&ocirc;ng tr&igrave;nh, chủ đầu tư phải c&oacute; giấy ph&eacute;p x&acirc;y dựng do cơ quan nh&agrave; nước c&oacute; thẩm quyền cấp theo quy định (trừ một số trường hợp được miễn giấy ph&eacute;p x&acirc;y dựng như: nh&agrave; ở ri&ecirc;ng lẻ ở n&ocirc;ng th&ocirc;n (kh&ocirc;ng nằm trong khu bảo tồn, khu di t&iacute;ch lịch sử - văn h&oacute;a), nh&agrave; ở thuộc dự &aacute;n ph&aacute;t triển đ&ocirc; thị,&hellip;). Do đ&oacute;, để x&acirc;y dựng nh&agrave; ở mới th&igrave; chủ nh&agrave; cần phải l&agrave;m thủ tục cấp giấy ph&eacute;p x&acirc;y dựng (trừ trường hợp được miễn).<br />\r\n<br />\r\nTheo khoản 3 Điều 103 Luật X&acirc;y dựng, Ủy ban nh&acirc;n d&acirc;n cấp huyện l&agrave; cơ quan c&oacute; thẩm quyền cấp giấy ph&eacute;p x&acirc;y dựng đối với c&aacute;c c&ocirc;ng tr&igrave;nh, nh&agrave; ở ri&ecirc;ng lẻ x&acirc;y dựng trong đ&ocirc; thị.<br />\r\n<br />\r\nTheo quy định tại 1 Điều 93 Luật X&acirc;y dựng, điều kiện chung cấp giấy ph&eacute;p x&acirc;y dựng đối với nh&agrave; ở ri&ecirc;ng lẻ tại đ&ocirc; thị l&agrave;: phải ph&ugrave; hợp với mục đ&iacute;ch sử dụng đất theo quy hoạch sử dụng đất được ph&ecirc; duyệt; bảo đảm an to&agrave;n cho c&ocirc;ng tr&igrave;nh, c&ocirc;ng tr&igrave;nh l&acirc;n cận v&agrave; y&ecirc;u cầu về bảo vệ m&ocirc;i trường, ph&ograve;ng, chống ch&aacute;y, nổ; bảo đảm an to&agrave;n hạ tầng kỹ thuật, h&agrave;nh lang bảo vệ c&ocirc;ng tr&igrave;nh thủy lợi, đ&ecirc; điều, thiết kế x&acirc;y dựng nh&agrave; ở ri&ecirc;ng lẻ phải tu&acirc;n thủ theo quy định...<br />\r\n<br />\r\nĐối với nh&agrave; ở ri&ecirc;ng lẻ tại đ&ocirc; thị phải ph&ugrave; hợp với quy hoạch chi tiết x&acirc;y dựng; đối với nh&agrave; ở ri&ecirc;ng lẻ thuộc khu vực, tuyến phố trong đ&ocirc; thị đ&atilde; ổn định nhưng chưa c&oacute; quy hoạch chi tiết x&acirc;y dựng th&igrave; phải ph&ugrave; hợp với quy chế quản l&yacute; quy hoạch, kiến tr&uacute;c đ&ocirc; thị hoặc thiết kế đ&ocirc; thị được cơ quan nh&agrave; nước c&oacute; thẩm quyền ban h&agrave;nh. Đối với nh&agrave; ở ri&ecirc;ng lẻ tại n&ocirc;ng th&ocirc;n, khi x&acirc;y dựng phải ph&ugrave; hợp với quy hoạch chi tiết x&acirc;y dựng điểm d&acirc;n cư n&ocirc;ng th&ocirc;n.<br />\r\n<br />\r\nTheo khoản 1 Điều 95 Luật X&acirc;y dựng, hồ sơ đề nghị cấp giấy ph&eacute;p x&acirc;y dựng mới đối với nh&agrave; ở ri&ecirc;ng lẻ gồm:<br />\r\n<br />\r\n- Đơn đề nghị cấp giấy ph&eacute;p x&acirc;y dựng;<br />\r\n<br />\r\n- Bản sao một trong những giấy tờ chứng minh quyền sử dụng đất theo quy định của ph&aacute;p luật về đất đai;<br />\r\n<br />\r\n- Bản vẽ thiết kế x&acirc;y dựng;<br />\r\n<br />\r\n- Đối với c&ocirc;ng tr&igrave;nh x&acirc;y dựng c&oacute; c&ocirc;ng tr&igrave;nh liền kề phải c&oacute; bản cam kết bảo đảm an to&agrave;n đối với c&ocirc;ng tr&igrave;nh liền kề.<br />\r\n<br />\r\nT&ugrave;y theo y&ecirc;u cầu v&agrave; thực tế ở từng địa phương, bản cam kết n&agrave;y c&oacute; thể l&agrave; chủ nh&agrave; cam kết đơn phương (c&oacute; x&aacute;c nhận của ch&iacute;nh quyền địa phương) hoặc phải c&oacute; bản cam kết c&oacute; chữ k&yacute; của chủ nh&agrave; với chủ nh&agrave; h&agrave;ng x&oacute;m, c&ocirc;ng tr&igrave;nh liền kề.<br />\r\n<br />\r\nTheo khoản 1 Điều 8 Th&ocirc;ng tư số 05/2015/TT-BXD ng&agrave;y 30/10/2015 của Bộ X&acirc;y dựng quy định về quản l&yacute; chất lượng x&acirc;y dựng v&agrave; bảo tr&igrave; nh&agrave; ở ri&ecirc;ng lẻ, trước khi thi c&ocirc;ng x&acirc;y dựng, chủ nh&agrave; chủ động li&ecirc;n hệ với c&aacute;c chủ sở hữu hoặc người quản l&yacute;, sử dụng c&ocirc;ng tr&igrave;nh liền kề, l&acirc;n cận để kiểm tra hiện trạng v&agrave; ghi nhận c&aacute;c khuyết tật của c&ocirc;ng tr&igrave;nh liền kề, l&acirc;n cận (nếu c&oacute;). Chủ sở hữu hoặc người quản l&yacute;, sử dụng c&ocirc;ng tr&igrave;nh liền kề, l&acirc;n cận c&oacute; tr&aacute;ch nhiệm phối hợp với chủ nh&agrave; để kiểm tra, ghi nhận hiện trạng c&ocirc;ng tr&igrave;nh l&agrave;m căn cứ giải quyết c&aacute;c tranh chấp (nếu c&oacute;).<br />\r\n<br />\r\nVề thiết kế x&acirc;y dựng nh&agrave; ở, đối với nh&agrave; ở c&oacute; tổng diện t&iacute;ch s&agrave;n x&acirc;y dựng nhỏ hơn 250m2 hoặc dưới 3 tầng hoặc c&oacute; chiều cao dưới 12m, chủ nh&agrave; được tự thiết kế. Thiết kế phải ph&ugrave; hợp với quy hoạch x&acirc;y dựng được duyệt v&agrave; chịu tr&aacute;ch nhiệm trước ph&aacute;p luật về chất lượng thiết kế, t&aacute;c động của c&ocirc;ng tr&igrave;nh x&acirc;y dựng đến m&ocirc;i trường v&agrave; an to&agrave;n của c&aacute;c c&ocirc;ng tr&igrave;nh l&acirc;n cận.<br />\r\n<br />\r\nĐối với nh&agrave; ở với quy m&ocirc; như vừa n&ecirc;u, chủ nh&agrave; cũng được tự tổ chức thi c&ocirc;ng x&acirc;y dựng v&agrave; chịu tr&aacute;ch nhiệm về an to&agrave;n x&acirc;y dựng v&agrave; c&aacute;c ảnh hưởng của việc x&acirc;y dựng nh&agrave; ở đến c&aacute;c c&ocirc;ng tr&igrave;nh liền kề, l&acirc;n cận.<br />\r\n<br />\r\nNhư vậy, nếu x&acirc;y nh&agrave; ở mới (kh&ocirc;ng phải l&agrave; nh&agrave; ở ri&ecirc;ng lẻ ở n&ocirc;ng th&ocirc;n v&agrave; c&aacute;c trường hợp kh&aacute;c được miễn giấy ph&eacute;p x&acirc;y dựng) th&igrave; chủ nh&agrave; cần phải thực hiện thủ tục cấp giấy ph&eacute;p x&acirc;y dựng tại cơ quan c&oacute; thẩm quyền, trong đ&oacute; phải c&oacute; bản cam kết đảm bảo an to&agrave;n đối với c&ocirc;ng tr&igrave;nh liền kề. Ngo&agrave;i ra, chủ nh&agrave; c&ograve;n phải tu&acirc;n thủ c&aacute;c quy định về khảo s&aacute;t x&acirc;y dựng, thiết kế x&acirc;y dựng, tổ chức thi c&ocirc;ng x&acirc;y dựng.<br />\r\n<br />\r\nNgo&agrave;i ra, qu&yacute; vị n&ecirc;n tham khảo những bước cần chuẩn bị khi x&acirc;y nh&agrave; dưới đ&acirc;y, để chuẩn bị tốt hơn cho việc x&acirc;y dựng nh&agrave; kh&ocirc;ng gặp phải những vấn đề g&igrave;.<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Bước 1: Chọn mua đất.</li>\r\n	<li>Bước 2: Chuẩn bị c&aacute;c thủ tục ph&aacute;p l&yacute;.</li>\r\n	<li>Bước 3: Chọn nh&agrave; tư vấn thiết kế x&acirc;y dựng.</li>\r\n	<li>Bước 4: Ho&agrave;n thiện hồ sơ x&acirc;y dựng, lựa chọn nh&agrave; thầu x&acirc;y dựng.</li>\r\n	<li>Bước 5: C&aacute;c thủ tục c&uacute;ng lễ chuẩn bị khởi c&ocirc;ng.</li>\r\n	<li>Bước 6: Chuẩn bị mặt bằng.</li>\r\n	<li>Bước 7: X&acirc;y dựng phần th&ocirc;.</li>\r\n	<li>Bước 8: Giai đoạn ho&agrave;n thiện.</li>\r\n	<li>Bước 9: Sản xuất, lắp đặt nội thất.</li>\r\n	<li>Bước 10: Sử dụng v&agrave; c&aacute;c vấn đề bảo dưỡng trong qu&aacute; tr&igrave;nh sử dụng.</li>\r\n</ul>\r\nĐể nắm r&otilde; hơn về c&aacute;c bước, xin mời qu&yacute; vị tham khảo chi tiết hơn về ph&acirc;n t&iacute;ch c&aacute;c bước khi chuẩn bị x&acirc;y nh&agrave; cần n&ecirc;n nắm r&otilde;.<br />\r\n<br />\r\n<strong>Bước 1: Chọn mua đất</strong><br />\r\n<br />\r\nMột mảnh đất đẹp phải hội tụ được c&aacute;c điều kiện:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>M&ocirc;i trường sống tốt: d&acirc;n tr&iacute; cao, hạ tầng điện nước, đường s&aacute; đầy đủ, kh&ocirc;ng gian tho&aacute;ng m&aacute;t, khu vực địa chất tốt, an ninh tốt, kh&ocirc;ng qu&aacute; xa nơi c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh đi học hay đi l&agrave;m,...</li>\r\n	<li>Đất c&oacute; gi&aacute; trị ph&aacute;p l&yacute;: sổ hồng (sổ đỏ) hoặc c&aacute;c giấy tờ kh&aacute;c c&oacute; gi&aacute; trị ph&aacute;p l&yacute; tương đương.</li>\r\n	<li>Hợp về mặt phong thuỷ.</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 2: Chuẩn bị c&aacute;c thủ tục ph&aacute;p l&yacute;</strong><br />\r\n<br />\r\nĐể được ph&eacute;p x&acirc;y dựng, phải đảm bảo đủ c&aacute;c điều kiện:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Khu đất phải được c&ocirc;ng nhận về mặt ph&aacute;p l&yacute; (GCNQSD đất hoặc GCNQSD đất v&agrave; QSH t&agrave;i sản gắn liền đất do CQNN c&oacute; thẩm quyền cấp) v&agrave; được cấp ph&eacute;p x&acirc;y dựng.</li>\r\n	<li>Trường hợp đất dự &aacute;n th&igrave; phải được BDA cấp ph&eacute;p x&acirc;y dựng dựa tr&ecirc;n quy hoạch chi tiết 1/500 của to&agrave;n dự &aacute;n đ&atilde; được CQNN c&oacute; thẩm quyền ph&ecirc; duyệt.</li>\r\n	<li>Phải c&oacute; hồ sơ thiết kế XPXD của đơn vị c&oacute; tư c&aacute;ch ph&aacute;p nh&acirc;n &amp; c&oacute; chứng chỉ h&agrave;nh nghề tư vấn thiết kế c&ocirc;ng tr&igrave;nh</li>\r\n	<li>Đối với nh&agrave; ở kh&ocirc;ng thuộc đất dự &aacute;n, sau khi c&oacute; giấy ph&eacute;p x&acirc;y dựng th&igrave; phải lập điểm b&aacute;o x&acirc;y dựng v&agrave; b&aacute;o c&aacute;o UBND phường sở tại kế hoạch x&acirc;y dựng.</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 3: Chọn nh&agrave; tư vấn thiết kế</strong><br />\r\n<br />\r\nChọn nh&agrave; tư vấn thiết kế c&oacute; đầy đủ năng lực, c&oacute; uy t&iacute;n. Vai tr&ograve; nh&agrave; tư vấn:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Tư vấn c&aacute;c c&ocirc;ng việc chuy&ecirc;n m&ocirc;n trong c&ocirc;ng tr&igrave;nh ph&ugrave; hợp điều kiện thực tế, y&ecirc;u cầu sử dụng của mỗi th&agrave;nh vi&ecirc;n, khả năng t&agrave;i ch&iacute;nh của gia đ&igrave;nh.</li>\r\n	<li>Lập hồ sơ thiết kế thi c&ocirc;ng, dự to&aacute;n chi ph&iacute; v&agrave; tiến độ thực hiện cho c&ocirc;ng tr&igrave;nh.</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 4: Ho&agrave;n thiện hồ sơ x&acirc;y dựng, lựa chọn nh&agrave; thầu x&acirc;y dựng</strong><br />\r\n<br />\r\nChọn nh&agrave; thầu c&oacute; đầy đủ năng lực, c&oacute; uy t&iacute;n, nh&acirc;n lực ph&ugrave; hợp, c&oacute; kinh nghiệm.<br />\r\nVai tr&ograve; của nh&agrave; thầu x&acirc;y dựng: l&agrave;m chất lượng tốt, gi&aacute; cả hợp l&yacute;, thời gian thi c&ocirc;ng nhanh, thực hiện tốt an to&agrave;n lao động.<br />\r\n<br />\r\nHiện nay, để thuận tiện cho chủ nh&agrave;, THAIDUONG.ECC c&oacute; thực hiện bộ tư vấn x&acirc;y dựng trọn g&oacute;i &quot;ch&igrave;a kho&aacute; trao tay&quot;: tiết kiệm thời gian cho chủ nh&agrave;, thiết kế v&agrave; XD mang t&iacute;nh đồng nhất.<br />\r\n<br />\r\nTrước v&agrave; trong khi tiến h&agrave;nh thi c&ocirc;ng, chủ nh&agrave; cần thương thảo với h&agrave;ng x&oacute;m về những ảnh hưởng li&ecirc;n quan trong qu&aacute; tr&igrave;nh x&acirc;y dựng, tu&acirc;n thủ c&aacute;c quy định của địa phương về an ninh, trật tự l&ograve;ng lề đường, vệ sinh c&ocirc;ng cộng.<br />\r\n<br />\r\n<strong>Bước 5: C&aacute;c thủ tục c&uacute;ng lễ chuẩn bị khởi c&ocirc;ng</strong><br />\r\n<br />\r\n&Ocirc;ng b&agrave; ta c&oacute; n&oacute;i &quot; C&oacute; ki&ecirc;ng c&oacute; l&agrave;nh&quot;: trước khi khởi c&ocirc;ng XD cần chọn ng&agrave;y giờ tốt, giờ ho&agrave;ng đạo, hợp tuổi gia chủ, hợp với x&acirc;y cất để l&agrave;m lễ động thổ xin ph&eacute;p l&agrave;m nh&agrave; may mắn tốt l&agrave;nh.<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Tr&igrave;nh tự v&agrave; phẩm vật c&uacute;ng tuỳ theo tập tục địa phương, tốt nhất gia chủ n&ecirc;n tham khảo người am hiểu về phong thuỷ, địa l&yacute; v&agrave; c&aacute;c bậc cao ni&ecirc;n.</li>\r\n	<li>Sau khi l&agrave;m lễ, gia chủ (hoặc người hợp tuổi) l&agrave; người cầm cuốc bổ những nh&aacute;t đầu ti&ecirc;n, tr&igrave;nh với Thổ thần xin được động thổ, tiếp sau đ&oacute; mới cho thợ đ&agrave;o.</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 6: Chuẩn bị mặt bằng</strong>\r\n\r\n<ul>\r\n	<li>Việc chuẩn bị mặt bằng bao gồm: l&agrave;m sạch, ph&aacute;t quang mặt đất; giải toả nh&agrave; &amp; kết cấu XD cũ, vận chuyển phế thải đổ đi.</li>\r\n	<li>Thu&ecirc; v&agrave; dọn nh&agrave; sang chỗ ở tạm nếu x&acirc;y dựng tr&ecirc;n nền nh&agrave; cũ.</li>\r\n	<li>Tập kết vật liệu, l&aacute;n trại cho c&ocirc;ng nh&acirc;n, h&agrave;ng r&agrave;o che chắn c&ocirc;ng tr&igrave;nh.</li>\r\n	<li>Nguồn điện, nước phục vụ việc x&acirc;y dựng.</li>\r\n	<li>C&ocirc;ng t&aacute;c chuẩn bị mặt bằng thường kh&ocirc;ng thể hiện trong hồ sơ thiết kế, v&igrave; vậy c&ocirc;ng t&aacute;c n&agrave;y phải được thoả thuận thống nhất do b&ecirc;n n&agrave;o thực hiện.</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 7: X&acirc;y dựng phần th&ocirc;</strong><br />\r\n<br />\r\nX&acirc;y dựng phần th&ocirc; gồm c&aacute;c c&ocirc;ng việc x&acirc;y dựng phần ch&acirc;n đế v&agrave; bộ khung ch&iacute;nh c&ocirc;ng tr&igrave;nh:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>C&ocirc;ng t&aacute;c l&agrave;m nền m&oacute;ng bao ồm: đ&agrave;o đất, h&uacute;t nước ngầm, đổ đất thừa, be th&agrave;nh đất, gia cố nền (đ&oacute;ng cọc BTCT hoặc cừ tr&agrave;m), gia c&ocirc;ng th&eacute;p m&oacute;ng v&agrave; đổ b&ecirc; t&ocirc;ng m&oacute;ng. M&oacute;ng nh&agrave; d&acirc;n dụng thường l&agrave;: m&oacute;ng đơn (m&oacute;ng c&oacute;c), m&oacute;ng băng, m&oacute;ng b&egrave; (thường cho c&ocirc;ng tr&igrave;nh lớn hoặc nh&agrave; c&oacute; tầng hầm).</li>\r\n	<li>Phần khung nh&agrave; gồm 5 th&agrave;nh phần ch&iacute;nh: cột nh&agrave;, dầm nh&agrave;, bản s&agrave;n, tường nh&agrave; &amp; cầu thang.</li>\r\n	<li>Đan th&eacute;p, gh&eacute;p cốp pha phải theo đ&uacute;ng y&ecirc;u cầu bản vẽ kết cấu &amp; quy chuẩn XD.</li>\r\n	<li>R&uacute;t cốp pha cần lưu &yacute; thời gian ngưng kết của b&ecirc;t&ocirc;ng phải đủ tuổi.</li>\r\n	<li>X&acirc;y tường thẳng, đều, vữa đủ độ kết d&iacute;nh &amp; chống thấm ướt.</li>\r\n	<li>Lắp đặt c&aacute;c cấu kiện ngầm v&agrave; c&aacute;c ống bảo hộ ngầm.</li>\r\n	<li>Mac b&ecirc; tong v&agrave; Mac vữa phải theo hồ sơ thiết kế v&agrave; tu&acirc;n theo TCXD Việt Nam.</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 8: Giai đoạn ho&agrave;n thiện.</strong><br />\r\n<br />\r\nGiai đoạn ho&agrave;n thiện gồm: tr&aacute;t tường, l&aacute;ng s&agrave;n, ốp l&aacute;t, sơn bả tường, lắp đặt trần, cầu thang, lắp đặt HTKT điện, cấp tho&aacute;t nước, điện thoại, chống s&eacute;t,... Một số y&ecirc;u cầu cơ bản khi thi c&ocirc;ng:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>C&ocirc;ng t&aacute;c tr&aacute;t tường, l&aacute;ng s&agrave;n: trộn vữa theo đ&uacute;ng tỷ lệ quy định trong hồ sơ TKKT.</li>\r\n	<li>Việc ốp l&aacute;t gạch theo đ&uacute;ng ti&ecirc;u chuẩn quy định của nh&agrave; SX. Mạch gạch cần đều, c&aacute;c vi&ecirc;n gạch thẳng nhau, kh&ocirc;ng được x&ocirc; lệch, nghi&ecirc;ng ngả.</li>\r\n	<li>Chọn sơn cần chọn loại ph&ugrave; hợp với mỗi bề mặt, chất liệu &amp; vị tr&iacute; cần sơn.</li>\r\n	<li>L&agrave;m mộc: cửa,cầu thang, bếp&hellip;</li>\r\n	<li>C&ocirc;ng t&aacute;c lắp đặt điện, nước &amp; c&aacute;c thiết bị KT: tu&acirc;n thủ theo đ&uacute;ng bản vẽ kỹ thuật &amp; hệ thống ti&ecirc;u chuẩn XDVN.</li>\r\n</ul>\r\n<br />\r\n<strong>Bước 9: Sản xuất, lắp đặt nội thất</strong><br />\r\n<br />\r\nSau khi ho&agrave;n thiện v&agrave; tiến h&agrave;nh kiểm tra chất lượng c&ocirc;ng tr&igrave;nh, tiếp theo l&agrave; giai đoạn ho&agrave;n thiện lắp đặt nội thất v&agrave; trang thiết bị.<br />\r\n<br />\r\n- Vật dụng nội thất v&agrave; trang thiết bị được lắp đặt theo hồ sơ thiết kế.<br />\r\n- Đồ nội thất thể hiện phong th&aacute;i của căn nh&agrave;. Mỗi ph&ograve;ng cần c&oacute; t&ocirc;ng m&agrave;u nội thất kh&aacute;c nhau, ph&ugrave; hợp với mục đ&iacute;ch sử dụng của ph&ograve;ng. Chất liệu của đồ nội thất cũng cần được chọn lựa kĩ lưỡng.<br />\r\n<br />\r\nQua b&agrave;i viết n&agrave;y, ch&uacute;ng t&ocirc;i hi vọng qu&yacute; vị sẽ biết được những <a href=\"http://thanhphuthinhland.vn/tin-tuc/thu-tuc-cap-giay-phep-xay-dung-nhu-the-nao-p56.html\">thủ tục cấp giấy ph&eacute;p x&acirc;y dựng như thế n&agrave;o</a> v&agrave; c&aacute;c bước để x&acirc;y dựng nh&agrave;.', 0, NULL, NULL, 1, 0, 97, '2017-07-17 14:12:37', '2017-07-17 14:12:37', 2, 2);
INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(58, 'Những đối tượng được cấp giấy phép chứng nhận quyền sử dụng đất', 'nhung-doi-tuong-duoc-cap-giay-phep-chung-nhan-quyen-su-dung-dat', 'Nhung doi tuong duoc cap giay phep chung nhan quyen su dung dat', 'Nếu quý vị đang thắc mắc về vấn đề những đối tượng được cấp giấy phép chứng nhận quyền sử dụng đất. Hãy tham khảo ngay bài viết dưới đây để biết rõ hơn về các đối tượng được cấp giấy phép, những đối tượng được miễn giảm phí', '2017/07/17/nhung-doi-tuong-duoc-cap-giay-phep-chung-nhan-quyen-su-dung-dat-1500279290.png', 5, '&nbsp;\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n\r\n<div style=\"text-align:center\"><img alt=\"Những đối tượng được cấp giấy phép chứng nhận quyền sử dụng đất\" src=\"/uploads/images/nhung-doi-tuong-duoc-cap-giay-phep-chung-nhan-quyen-su-dung-dat.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nCăn cứ Điều 99 Luật Đất đai 2013 quy định Trường hợp sử dụng đất được cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất như sau:<br />\r\n<br />\r\n<strong>Nh&agrave; nước cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho những trường hợp sau đ&acirc;y:</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Người đang sử dụng đất c&oacute; đủ điều kiện cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất theo quy định tại c&aacute;c điều 100, 101 v&agrave; 102 của luật n&agrave;y</li>\r\n	<li>Người được Nh&agrave; nước giao đất, cho thu&ecirc; đất từ sau ng&agrave;y Luật n&agrave;y c&oacute; hiệu lực thi h&agrave;nh</li>\r\n	<li>Người được chuyển đổi, nhận chuyển nhượng, được thừa kế, nhận tặng cho quyền sử dụng đất, nhận g&oacute;p vốn bằng quyền sử dụng đất; người nhận quyền sử dụng đất khi xử l&yacute; hợp đồng thế chấp bằng quyền sử dụng đất để thu hồi nợ.</li>\r\n	<li>Người được sử dụng đất theo kết quả h&ograve;a giải th&agrave;nh đối với tranh chấp đất đai; theo bản &aacute;n hoặc quyết định của T&ograve;a &aacute;n nh&acirc;n d&acirc;n, quyết định thi h&agrave;nh &aacute;n của cơ quan thi h&agrave;nh &aacute;n hoặc quyết định giải quyết tranh chấp, khiếu nại, tố c&aacute;o về đất đai của cơ quan nh&agrave; nước c&oacute; thẩm quyền đ&atilde; được thi h&agrave;nh</li>\r\n	<li>Người tr&uacute;ng đấu gi&aacute; quyền sử dụng đất</li>\r\n	<li>Người sử dụng đất trong khu c&ocirc;ng nghiệp, cụm c&ocirc;ng nghiệp, khu chế xuất, khu c&ocirc;ng nghệ cao, khu kinh tế</li>\r\n	<li>Người mua nh&agrave; ở, t&agrave;i sản kh&aacute;c gắn liền với đất</li>\r\n	<li>Người được Nh&agrave; nước thanh l&yacute;, h&oacute;a gi&aacute; nh&agrave; ở gắn liền với đất ở; người mua nh&agrave; ở thuộc sở hữu nh&agrave; nước</li>\r\n	<li>Người sử dụng đất t&aacute;ch thửa, hợp thửa; nh&oacute;m người sử dụng đất hoặc c&aacute;c th&agrave;nh vi&ecirc;n hộ gia đ&igrave;nh, hai vợ chồng, tổ chức sử dụng đất chia t&aacute;ch, hợp nhất quyền sử dụng đất hiện c&oacute;</li>\r\n	<li>Người sử dụng đất đề nghị cấp đổi hoặc cấp lại Giấy chứng nhận bị mất</li>\r\n</ul>\r\n<br />\r\n<strong>Ch&iacute;nh phủ quy định chi tiết điều n&agrave;y</strong><br />\r\n<br />\r\nTheo quy định tr&ecirc;n th&igrave; một trong những trường hợp được cấp giấy chứng nhận quyền sử dụng đất l&agrave; được chuyển đổi, nhận chuyển nhượng, tặng cho quyền sử dụng đất.<br />\r\n<br />\r\nTheo như bạn tr&igrave;nh b&agrave;y, ng&agrave;y xưa bố bạn c&oacute; mua cho b&agrave; nội bạn mảnh đất, b&agrave; nội bạn được Nh&agrave; nước tặng cho nh&agrave; t&igrave;nh nghĩa nhưng do đường x&aacute; kh&oacute; khăn kh&ocirc;ng v&agrave;o x&acirc;y dựng được n&ecirc;n Ủy ban x&atilde; đ&atilde; hướng dẫn b&agrave; nội bạn đổi sang mảnh đất kh&aacute;c để Nh&agrave; nước x&acirc;y nh&agrave; t&igrave;nh nghĩa.<br />\r\n<br />\r\nNhư vậy, đối tượng được Nh&agrave; nước trao nh&agrave; t&igrave;nh nghĩa l&agrave; b&agrave; nội bạn th&igrave; về nguy&ecirc;n tắc khi k&ecirc; khai th&ocirc;ng tin để cấp giấy chứng nhận quyền sử dụng đất phải l&agrave; b&agrave; nội bạn. Do đ&oacute;, gia đ&igrave;nh bạn c&oacute; thể l&agrave;m đơn tường tr&igrave;nh gửi tới ủy ban nh&acirc;n d&acirc;n cấp x&atilde; n&ecirc;u r&otilde; về nguồn gốc sử dụng đất để x&aacute;c định ch&iacute;nh x&aacute;c người đứng t&ecirc;n tr&ecirc;n giấy chứng nhận quyền sử dụng đất.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Những đối tượng miễn giảm ph&iacute; khi cấp giấy chứng nhận quyền sử dụng đất</strong></span><br />\r\n<br />\r\nKhi bạn l&agrave;m thủ tục cấp giấy chứng nhận quyền sử dụng đất, bạn phải đ&oacute;ng lệ ph&iacute; trước bạ theo quy định tại Nghị định 45/2011/NĐ-CP, đ&oacute;ng tiền sử dụng đất theo quy định tại Nghị định 45/2014/NĐ-CP.<br />\r\n<br />\r\nC&aacute;c trường hợp kh&ocirc;ng phải nộp lệ ph&iacute; trước bạ theo quy định tại Điều 4 Nghị định 45/2011/NĐ-CP gồm c&aacute;c trường hợp sau đ&acirc;y:<br />\r\n<br />\r\n- Đất được Nh&agrave; nước giao cho c&aacute;c tổ chức, c&aacute; nh&acirc;n sử dụng v&agrave;o c&aacute;c mục đ&iacute;ch sau đ&acirc;y:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Sử dụng v&agrave;o mục đ&iacute;ch c&ocirc;ng cộng theo quy định của ph&aacute;p luật về đất đai</li>\r\n	<li>Thăm d&ograve;, khai th&aacute;c kho&aacute;ng sản; nghi&ecirc;n cứu khoa học theo giấy ph&eacute;p hoặc x&aacute;c nhận của cơ quan nh&agrave; nước c&oacute; thẩm quyền</li>\r\n	<li>Đầu tư x&acirc;y dựng kết cấu hạ tầng để chuyển nhượng hoặc cho thu&ecirc;, kh&ocirc;ng ph&acirc;n biệt đất trong hay ngo&agrave;i khu c&ocirc;ng nghiệp, khu chế xuất; đầu tư x&acirc;y dựng nh&agrave; để kinh doanh.</li>\r\n	<li>C&aacute;c trường hợp n&agrave;y nếu chuyển nhượng th&igrave; tổ chức, c&aacute; nh&acirc;n nhận chuyển nhượng phải nộp lệ ph&iacute; trước bạ; nếu cho thu&ecirc; hoặc tự sử dụng th&igrave; tổ chức, c&aacute; nh&acirc;n được Nh&agrave; nước giao đất phải nộp lệ ph&iacute; trước bạ.</li>\r\n</ul>\r\n<br />\r\n- Đất được Nh&agrave; nước giao hoặc c&ocirc;ng nhận sử dụng v&agrave;o mục đ&iacute;ch sản xuất n&ocirc;ng nghiệp, l&acirc;m nghiệp, nu&ocirc;i trồng thủy sản v&agrave; l&agrave;m muối.<br />\r\n<br />\r\n- Đất n&ocirc;ng nghiệp chuyển đổi quyền sử dụng giữa c&aacute;c hộ gia đ&igrave;nh, c&aacute; nh&acirc;n theo chủ trương chung về dồn điền đổi thửa.<br />\r\n<br />\r\n- Đất n&ocirc;ng nghiệp do hộ gia đ&igrave;nh, c&aacute; nh&acirc;n tự khai hoang được cơ quan nh&agrave; nước c&oacute; thẩm quyền cấp giấy chứng nhận quyền sử dụng đất.<br />\r\n<br />\r\n- Đất thu&ecirc; của Nh&agrave; nước hoặc thu&ecirc; của tổ chức, c&aacute; nh&acirc;n đ&atilde; c&oacute; quyền sử dụng đất hợp ph&aacute;p.<br />\r\n<br />\r\n<br />\r\n- Nh&agrave;, đất sử dụng v&agrave;o mục đ&iacute;ch cộng đồng của c&aacute;c tổ chức t&ocirc;n gi&aacute;o, cơ sở t&iacute;n ngưỡng được Nh&agrave; nước c&ocirc;ng nhận hoặc được ph&eacute;p hoạt động.<br />\r\n<br />\r\n- Đất l&agrave;m nghĩa trang, nghĩa địa.<br />\r\n<br />\r\n- Nh&agrave;, đất nhận thừa kế hoặc l&agrave; qu&agrave; tặng giữa: vợ với chồng; cha đẻ, mẹ đẻ với con đẻ; cha nu&ocirc;i, mẹ nu&ocirc;i với con nu&ocirc;i; cha chồng, mẹ chồng với con d&acirc;u; cha vợ, mẹ vợ với con rể; &ocirc;ng nội, b&agrave; nội với ch&aacute;u nội; &ocirc;ng ngoại, b&agrave; ngoại với ch&aacute;u ngoại; anh, chị, em ruột với nhau nay được cơ quan nh&agrave; nước c&oacute; thẩm quyền cấp giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất lần đầu.<br />\r\n<br />\r\n- Nh&agrave; ở của hộ gia đ&igrave;nh, c&aacute; nh&acirc;n tạo lập th&ocirc;ng qua h&igrave;nh thức ph&aacute;t triển nh&agrave; ở ri&ecirc;ng lẻ.<br />\r\n<br />\r\n- T&agrave;i sản cho thu&ecirc; t&agrave;i ch&iacute;nh được chuyển quyền sở hữu cho b&ecirc;n thu&ecirc; khi kết th&uacute;c thời hạn thu&ecirc; th&ocirc;ng qua việc nhượng, b&aacute;n t&agrave;i sản cho thu&ecirc; th&igrave; b&ecirc;n thu&ecirc; kh&ocirc;ng phải nộp lệ ph&iacute; trước bạ; trường hợp c&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh mua t&agrave;i sản của đơn vị c&oacute; t&agrave;i sản đ&atilde; nộp lệ ph&iacute; trước bạ sau đ&oacute; cho ch&iacute;nh đơn vị b&aacute;n t&agrave;i sản thu&ecirc; lại th&igrave; c&ocirc;ng ty cho thu&ecirc; t&agrave;i ch&iacute;nh kh&ocirc;ng phải nộp lệ ph&iacute; trước bạ.<br />\r\n<br />\r\n- Nh&agrave;, đất, t&agrave;i sản đặc biệt, t&agrave;i sản chuy&ecirc;n d&ugrave;ng, t&agrave;i sản phục vụ c&ocirc;ng t&aacute;c quản l&yacute; chuy&ecirc;n d&ugrave;ng phục vụ quốc ph&ograve;ng, an ninh.<br />\r\n<br />\r\n- Nh&agrave;, đất thuộc t&agrave;i sản nh&agrave; nước d&ugrave;ng l&agrave;m trụ sở cơ quan của cơ quan nh&agrave; nước, đơn vị vũ trang nh&acirc;n d&acirc;n, đơn vị sự nghiệp c&ocirc;ng lập, tổ chức ch&iacute;nh trị, tổ chức ch&iacute;nh trị - x&atilde; hội, tổ chức ch&iacute;nh trị x&atilde; hội - nghề nghiệp, tổ chức x&atilde; hội, tổ chức x&atilde; hội - nghề nghiệp.<br />\r\n<br />\r\n- Nh&agrave;, đất được bồi thường (kể cả nh&agrave;, đất mua bằng tiền được bồi thường, hỗ trợ) khi Nh&agrave; nước thu hồi nh&agrave;, đất theo quy định của ph&aacute;p luật.<br />\r\n<br />\r\n- T&agrave;i sản của tổ chức, c&aacute; nh&acirc;n đ&atilde; được cấp giấy chứng nhận quyền sở hữu, sử dụng khi đăng k&yacute; lại quyền sở hữu, sử dụng kh&ocirc;ng phải nộp lệ ph&iacute; trước bạ trong những trường hợp sau đ&acirc;y:<br />\r\n<br />\r\n+ T&agrave;i sản đ&atilde; được cơ quan c&oacute; thẩm quyền của Nh&agrave; nước Việt Nam d&acirc;n chủ cộng h&ograve;a, Ch&iacute;nh phủ c&aacute;ch mạng l&acirc;m thời Cộng h&ograve;a miền Nam Việt Nam, Nh&agrave; nước Cộng h&ograve;a x&atilde; hội chủ nghĩa Việt Nam hoặc cơ quan c&oacute; thẩm quyền thuộc chế độ cũ cấp giấy chứng nhận quyền sở hữu, sử dụng nay đổi giấy chứng nhận quyền sở hữu, sử dụng mới m&agrave; kh&ocirc;ng thay đổi chủ sở hữu t&agrave;i sản<br />\r\n<br />\r\n+ T&agrave;i sản của doanh nghiệp nh&agrave; nước được cổ phần h&oacute;a th&agrave;nh sở hữu c&ocirc;ng ty cổ phần hoặc c&aacute;c h&igrave;nh thức sắp xếp lại doanh nghiệp nh&agrave; nước kh&aacute;c theo quy định của ph&aacute;p luật<br />\r\n<br />\r\n+ T&agrave;i sản đ&atilde; được cấp giấy chứng nhận quyền sở hữu, sử dụng của hộ gia đ&igrave;nh khi ph&acirc;n chia t&agrave;i sản đ&oacute; cho c&aacute;c th&agrave;nh vi&ecirc;n hộ gia đ&igrave;nh đăng k&yacute; lại<br />\r\n<br />\r\n+ T&agrave;i sản của tổ chức, c&aacute; nh&acirc;n đ&atilde; được cấp giấy chứng nhận quyền sở hữu, sử dụng phải đăng k&yacute; lại quyền sở hữu, sử dụng do giấy chứng nhận bị mất, r&aacute;ch n&aacute;t, hư hỏng<br />\r\n<br />\r\n+ Trường hợp khi cấp lại giấy chứng nhận quyền sử dụng đất c&oacute; ph&aacute;t sinh ch&ecirc;nh lệch diện t&iacute;ch đất tăng th&ecirc;m nhưng ranh giới của thửa đất kh&ocirc;ng thay đổi th&igrave; kh&ocirc;ng phải nộp lệ ph&iacute; trước bạ đối với phần diện t&iacute;ch đất tăng th&ecirc;m.<br />\r\n<br />\r\n- T&agrave;i sản của tổ chức, c&aacute; nh&acirc;n đ&atilde; nộp lệ ph&iacute; trước bạ (trừ trường hợp kh&ocirc;ng phải nộp hoặc được miễn nộp lệ ph&iacute; trước bạ) sau đ&oacute; chuyển cho tổ chức, c&aacute; nh&acirc;n kh&aacute;c đăng k&yacute; quyền sở hữu, sử dụng kh&ocirc;ng phải nộp lệ ph&iacute; trước bạ trong những trường hợp sau đ&acirc;y:<br />\r\n<br />\r\n+ Tổ chức, c&aacute; nh&acirc;n, x&atilde; vi&ecirc;n hợp t&aacute;c x&atilde; đem t&agrave;i sản của m&igrave;nh g&oacute;p vốn v&agrave;o doanh nghiệp, tổ chức t&iacute;n dụng, hợp t&aacute;c x&atilde; hoặc khi doanh nghiệp, tổ chức t&iacute;n dụng, hợp t&aacute;c x&atilde; giải thể hoặc ph&acirc;n chia hoặc r&uacute;t vốn cho tổ chức, c&aacute; nh&acirc;n th&agrave;nh vi&ecirc;n t&agrave;i sản m&agrave; tổ chức, c&aacute; nh&acirc;n th&agrave;nh vi&ecirc;n đ&atilde; g&oacute;p trước đ&acirc;y<br />\r\n<br />\r\n+ T&agrave;i sản của doanh nghiệp điều động cho c&aacute;c đơn vị th&agrave;nh vi&ecirc;n hoặc doanh nghiệp điều động t&agrave;i sản giữa c&aacute;c đơn vị th&agrave;nh vi&ecirc;n thuộc doanh nghiệp theo h&igrave;nh thức ghi tăng, ghi giảm vốn hoặc cơ quan, đơn vị h&agrave;nh ch&iacute;nh sự nghiệp điều động t&agrave;i sản trong nội bộ một cơ quan, đơn vị dự to&aacute;n theo quyết định của cấp c&oacute; thẩm quyền.<br />\r\n<br />\r\n- T&agrave;i sản của tổ chức, c&aacute; nh&acirc;n đ&atilde; nộp lệ ph&iacute; trước bạ được chia hay g&oacute;p do chia, t&aacute;ch, cổ phần h&oacute;a, hợp nhất, s&aacute;p nhập, đổi t&ecirc;n tổ chức theo quyết định của cơ quan c&oacute; thẩm quyền.<br />\r\n<br />\r\n- T&agrave;i sản của tổ chức, c&aacute; nh&acirc;n đ&atilde; nộp lệ ph&iacute; trước bạ chuyển đến địa phương nơi sử dụng m&agrave; kh&ocirc;ng thay đổi chủ sở hữu.<br />\r\n<br />\r\n+ Nh&agrave; t&igrave;nh nghĩa, nh&agrave; đại đo&agrave;n kết, nh&agrave; được hỗ trợ mang t&iacute;nh chất nh&acirc;n đạo, kể cả đất k&egrave;m theo nh&agrave; được đăng k&yacute; sở hữu, sử dụng t&ecirc;n người được tặng.<br />\r\n<br />\r\n- Xe chuy&ecirc;n d&ugrave;ng: xe cứu hỏa, xe cứu thương, xe cứu hộ, xe chở r&aacute;c, xe phun tưới, xe qu&eacute;t đường, xe chiếu chụp X-quang; xe chuy&ecirc;n d&ugrave;ng cho thương binh, bệnh binh, người t&agrave;n tật, đăng k&yacute; quyền sở hữu t&ecirc;n thương binh, bệnh binh, người t&agrave;n tật.<br />\r\n<br />\r\n- T&agrave;u bay sử dụng cho mục đ&iacute;ch kinh doanh vận chuyển h&agrave;ng h&oacute;a, h&agrave;nh kh&aacute;ch.<br />\r\n<br />\r\n- T&agrave;u, thuyền đ&aacute;nh bắt thủy, hải sản.<br />\r\n<br />\r\n- Vỏ, tổng th&agrave;nh khung, tổng th&agrave;nh m&aacute;y của t&agrave;i sản quy định tại khoản 8 Điều 2 Nghị định n&agrave;y được thay thế m&agrave; phải đăng k&yacute; lại trong thời hạn bảo h&agrave;nh.<br />\r\n<br />\r\n- Nh&agrave; xưởng của cơ sở sản xuất, kinh doanh.<br />\r\n<br />\r\n- T&agrave;i sản l&agrave; h&agrave;ng h&oacute;a kinh doanh của c&aacute;c tổ chức, c&aacute; nh&acirc;n đăng k&yacute; hoạt động kinh doanh theo quy định của ph&aacute;p luật m&agrave; kh&ocirc;ng đăng k&yacute; quyền sở hữu, quyền sử dụng với cơ quan nh&agrave; nước c&oacute; thẩm quyền.<br />\r\n<br />\r\nNhư vậy, đối với trường hợp của b&aacute;c l&agrave; thương binh c&oacute; tỷ l&ecirc;̣ thương tật 21%, được giao đất th&igrave; kh&ocirc;ng thuộc trường hợp kh&ocirc;ng phải đ&oacute;ng tiền lệ ph&iacute; trước bạ. Do đ&oacute; b&aacute;c vẫn phải đ&oacute;ng lệ ph&iacute; trước bạ khi l&agrave;m thủ tục cấp giấy chứng nhận quyền sử dụng đất.<br />\r\n<br />\r\nCăn cứ Khoản 1 Điều 13 Th&ocirc;ng tư 76/2014/TT-BTC quy định miễn tiền sử dụng đất như sau:<br />\r\n<br />\r\nViệc miễn tiền sử dụng đất được thực hiện theo quy định tại Điều 11 Nghị định số 45/2014/NĐ-CP; một số nội dung tại Điều n&agrave;y được hướng dẫn cụ thể như sau:<br />\r\n<br />\r\n1. Miễn tiền sử dụng đất trong hạn mức giao đất ở trong trường hợp được Nh&agrave; nước giao đất, cho ph&eacute;p chuyển mục đ&iacute;ch sử dụng đất, c&ocirc;ng nhận quyền sử dụng đất cho người c&oacute; c&ocirc;ng với C&aacute;ch mạng được thực hiện theo quy định của ph&aacute;p luật về người c&oacute; c&ocirc;ng. Việc miễn tiền sử dụng đất đối với người c&oacute; c&ocirc;ng với C&aacute;ch mạng chỉ được thực hiện khi c&oacute; quyết định của cơ quan nh&agrave; nước c&oacute; thẩm quyền về việc cho ph&eacute;p miễn tiền sử dụng đất theo ph&aacute;p luật về người c&oacute; c&ocirc;ng.<br />\r\n<br />\r\nB&aacute;c l&agrave; thương binh c&oacute; tỷ lệ thương tật 21% th&igrave; b&aacute;c sẽ được miễn tiền sử dụng đất trong hạn mức c&ocirc;ng nhận quyền sử dụng đất, đối với diện t&iacute;ch vượt hạn mức th&igrave; b&aacute;c vẫn phải đ&oacute;ng tiền sử dụng đất. Tuy nhi&ecirc;n, việc miễn tiền sử dụng đất đối với trường hợp của b&aacute;c th&igrave; phải c&oacute; quyết định của cơ quan nh&agrave; nước c&oacute; thẩm quyền về việc cho ph&eacute;p miễn tiền sử dụng đất, do đ&oacute; b&aacute;c phải l&agrave;m hồ sơ đề nghị miễn, giảm tiền sử dụng đất<br />\r\n<br />\r\n- Đơn đề nghị được miễn, giảm tiền sử dụng đất; trong đ&oacute; ghi r&otilde;: diện t&iacute;ch, l&yacute; do miễn, giảm<br />\r\n<br />\r\n- Giấy tờ chứng minh thuộc một trong c&aacute;c trường hợp được miễn, giảm tiền sử dụng đất (bảnsao c&oacute; chứng thực), cụ thể:<br />\r\n<br />\r\n+ Trường hợp người c&oacute; c&ocirc;ng với C&aacute;ch mạng phải c&oacute; giấy tờ li&ecirc;n quan đến chế độ được miễn, giảm tiền sử dụng đất hướng dẫn tại Khoản 1 Điều 13 v&agrave; Khoản 2 Điều 14 Th&ocirc;ng tư n&agrave;y; (Giấy tờ bệnh binh,...)<br />\r\n<br />\r\n+ C&aacute;c giấy tờ c&oacute; li&ecirc;n quan về thửa đất (nếu c&oacute;).<br />\r\n<br />\r\nNơi thực hiện thủ tục h&agrave;nh ch&iacute;nh: Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất thuộc ph&ograve;ng t&agrave;i nguy&ecirc;n m&ocirc;i trường.<br />\r\n<br />\r\n<strong><span style=\"font-size:18px\">► C&aacute;c trường hợp kh&ocirc;ng được cấp Giấy chứng nhận quyền sử dụng đất</span></strong><br />\r\n<br />\r\n- Người đang quản l&yacute;, sử dụng đất n&ocirc;ng nghiệp thuộc quỹ đất c&ocirc;ng &iacute;ch của x&atilde;, phường, thị trấn.<br />\r\n<br />\r\n- Người thu&ecirc;, thu&ecirc; lại đất của người sử dụng đất, trừ trường hợp thu&ecirc;, thu&ecirc; lại đất của nh&agrave; đầu tư x&acirc;y dựng, kinh doanh kết cấu hạ tầng trong khu c&ocirc;ng nghiệp, cụm c&ocirc;ng nghiệp, khu chế xuất, khu c&ocirc;ng nghệ cao, khu kinh tế.<br />\r\n<br />\r\n- Người nhận kho&aacute;n đất trong c&aacute;c n&ocirc;ng trường, l&acirc;m trường, doanh nghiệp n&ocirc;ng, l&acirc;m nghiệp, ban quản l&yacute; rừng ph&ograve;ng hộ, ban quản l&yacute; rừng đặc dụng.<br />\r\n<br />\r\n- Người đang sử dụng đất kh&ocirc;ng đủ điều kiện cấp giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất.<br />\r\n<br />\r\n- Người sử dụng đất c&oacute; đủ điều kiện cấp giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất nhưng đ&atilde; c&oacute; th&ocirc;ng b&aacute;o hoặc quyết định thu hồi đất của cơ quan nh&agrave; nước c&oacute; thẩm quyền.&nbsp;<br />\r\n<br />\r\n- Tổ chức, UBND x&atilde; được Nh&agrave; nước giao đất kh&ocirc;ng thu tiền sử dụng đất để sử dụng v&agrave;o mục đ&iacute;ch x&acirc;y dựng c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng gồm đường giao th&ocirc;ng, c&ocirc;ng tr&igrave;nh dẫn nước, dẫn xăng, dầu, kh&iacute;; đường d&acirc;y truyền tải điện, truyền dẫn th&ocirc;ng tin; khu vui chơi giải tr&iacute; ngo&agrave;i trời; nghĩa trang, nghĩa địa kh&ocirc;ng nhằm mục đ&iacute;ch kinh doanh.<br />\r\n<br />\r\n- V&agrave; cuối c&ugrave;ng l&agrave; c&aacute;c tổ chức, cộng đồng d&acirc;n cư được Nh&agrave; nước giao đất để quản l&yacute; thuộc c&aacute;c trường hợp quy định tại điều 8 của Luật Đất đai cũng kh&ocirc;ng được cấp giấy chứng nhận quyền sử dụng đất.<br />\r\n<br />\r\nB&ecirc;n cạnh đ&oacute;, Nghị định cũng quy định một số trường hợp c&aacute; biệt nhưng được hoặc kh&ocirc;ng được cấp giấy chứng nhận quyền sử dụng đất.<br />\r\n<br />\r\nCụ thể, đối với một số trường hợp thửa đất sử dụng h&igrave;nh th&agrave;nh từ trước thời điểm quy định của địa phương c&oacute; hiệu lực m&agrave; diện t&iacute;ch đất nhỏ hơn diện t&iacute;ch tối thiểu theo quy định của UBND cấp tỉnh nhưng c&oacute; đủ điều kiện cấp giấy chứng nhận th&igrave; người đang sử dụng sẽ được cấp &ldquo;sổ đỏ&rdquo;<br />\r\n<br />\r\nTrong trường hợp tự chia t&aacute;ch thửa đất đ&atilde; đăng k&yacute;, đ&atilde; được cấp giấy chứng nhận th&agrave;nh hai hoặc nhiều thừa đất m&agrave; trong đ&oacute; c&oacute; &iacute;t nhất một thửa đất c&oacute; diện t&iacute;ch nhỏ hơn diện t&iacute;ch tối thiểu th&igrave; sẽ kh&ocirc;ng được cấp giấy chứng nhận cũng như kh&ocirc;ng được c&ocirc;ng chứng, chứng thực<br />\r\n<br />\r\nTrường hợp người sử dụng đất xin t&aacute;ch thửa th&agrave;nh thửa c&oacute; diện t&iacute;ch nhỏ hơn diện t&iacute;ch tối thiểu đồng thời với việc xin được hợp thửa đ&oacute; với thửa đất kh&aacute;c liền kề c&oacute; diện t&iacute;ch bằng hoặc lớn hơn diện t&iacute;ch tối thiểu th&igrave; được cấp giấy chứng nhận quyền sử dụng đất cho thửa mới.', 0, NULL, NULL, 1, 0, 98, '2017-07-17 15:15:21', '2017-07-17 15:15:21', 2, 2),
(59, 'Trình tự thủ tục và thời gian giải quyết tách thửa như thế nào', 'trinh-tu-thu-tuc-va-thoi-gian-giai-quyet-tach-thua-nhu-the-nao', 'Trinh tu thu tuc va thoi gian giai quyet tach thua nhu the nao', 'Nếu quý vị đang có một mảnh đất và muốn tách thửa ra. Nhưng lại không biết trình tự thủ tục và thời gian giải quyết tách thửa như thế nào. Hãy tham khảo bài viết dưới đây để biết rõ hơn về các thủ tục cần thiết.', '2017/07/17/trinh-tu-thu-tuc-va-thoi-gian-giai-quyet-tach-thua-nhu-the-nao-1500281733.png', 5, '&nbsp;\r\n<div style=\"text-align:center\"><img alt=\"Trình tự thủ tục và thời gian giải quyết tách thửa như thế nào\" src=\"/uploads/images/trinh-tu-thu-tuc-va-thoi-gian-giai-quyet-tach-thua-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n<strong><span style=\"font-size:18px\">► Điều kiện t&aacute;ch thửa như thế n&agrave;o</span></strong><br />\r\n<br />\r\nĐiều 29 Nghị định 43/2014/NĐ-CP quy định như sau: Cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất đối với trường hợp thửa đất c&oacute; diện t&iacute;ch nhỏ hơn diện t&iacute;ch tối thiểu<br />\r\n<br />\r\n1. Thửa đất đang sử dụng được h&igrave;nh th&agrave;nh từ trước ng&agrave;y văn bản quy định của Ủy ban nh&acirc;n d&acirc;n cấp tỉnh về diện t&iacute;ch tối thiểu được t&aacute;ch thửa c&oacute; hiệu lực thi h&agrave;nh m&agrave; diện t&iacute;ch thửa đất nhỏ hơn diện t&iacute;ch tối thiểu theo quy định của Ủy ban nh&acirc;n d&acirc;n cấp tỉnh nhưng c&oacute; đủ điều kiện cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất th&igrave; người đang sử dụng đất được cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất.<br />\r\n<br />\r\n2. Kh&ocirc;ng được c&ocirc;ng chứng, chứng thực, cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất v&agrave; kh&ocirc;ng được l&agrave;m thủ tục thực hiện c&aacute;c quyền của người sử dụng đất đối với trường hợp tự chia t&aacute;ch thửa đất đ&atilde; đăng k&yacute;, đ&atilde; được cấp Giấy chứng nhận th&agrave;nh hai hoặc nhiều thửa đất m&agrave; trong đ&oacute; c&oacute; &iacute;t nhất một thửa đất c&oacute; diện t&iacute;ch nhỏ hơn diện t&iacute;ch tối thiểu theo quy định của Ủy ban nh&acirc;n d&acirc;n cấp tỉnh.<br />\r\n<br />\r\n3. Trường hợp người sử dụng đất xin t&aacute;ch thửa đất th&agrave;nh thửa đất c&oacute; diện t&iacute;ch nhỏ hơn diện t&iacute;ch tối thiểu đồng thời với việc xin được hợp thửa đất đ&oacute; với thửa đất kh&aacute;c liền kề để tạo th&agrave;nh thửa đất mới c&oacute; diện t&iacute;ch bằng hoặc lớn hơn diện t&iacute;ch tối thiểu được t&aacute;ch thửa th&igrave; được ph&eacute;p t&aacute;ch thửa đồng thời với việc hợp thửa v&agrave; cấp giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho thửa đất mới.<br />\r\n<br />\r\n<strong>► Tr&igrave;nh tự thủ tục t&aacute;ch thửa quy định tại điều 75 Nghị định 43/2014/NĐ-CP sửa đổi bổ sung bởi khoản 49 điều 2 Nghị định 01/2017/NĐ-CP như sau:</strong><br />\r\n<br />\r\nĐiều 75. Tr&igrave;nh tự, thủ tục thực hiện t&aacute;ch thửa hoặc hợp thửa đất<br />\r\n<br />\r\n1. Người sử dụng đất nộp 01 bộ hồ sơ đề nghị t&aacute;ch thửa hoặc hợp thửa.<br />\r\n<br />\r\n2. Văn ph&ograve;ng đăng k&yacute; đất đai c&oacute; tr&aacute;ch nhiệm thực hiện c&aacute;c c&ocirc;ng việc sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đo đạc địa ch&iacute;nh để chia t&aacute;ch thửa đất</li>\r\n	<li>Lập hồ sơ tr&igrave;nh cơ quan c&oacute; thẩm quyền cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho người sử dụng đất đối với thửa đất mới t&aacute;ch, hợp thửa</li>\r\n	<li>Chỉnh l&yacute;, cập nhật biến động v&agrave;o hồ sơ địa ch&iacute;nh, cơ sở dữ liệu đất đai; trao Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho người được cấp hoặc gửi ủy ban nh&acirc;n d&acirc;n cấp x&atilde; để trao đối với trường hợp nộp hồ sơ tại cấp x&atilde;.</li>\r\n</ul>\r\n<br />\r\n3. Trường hợp t&aacute;ch thửa do chuyển quyền sử dụng một phần thửa đất hoặc do giải quyết tranh chấp, khiếu nại, tố c&aacute;o, đấu gi&aacute; đất hoặc do chia t&aacute;ch hộ gia đ&igrave;nh, nh&oacute;m người sử dụng đất; do xử l&yacute; hợp đồng thế chấp, g&oacute;p vốn, k&ecirc; bi&ecirc;n b&aacute;n đấu gi&aacute; quyền sử dụng đất để thi h&agrave;nh &aacute;n (sau đ&acirc;y gọi l&agrave; chuyển quyền) th&igrave; Văn ph&ograve;ng đăng k&yacute; đất đai thực hiện c&aacute;c c&ocirc;ng việc sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đo đạc địa ch&iacute;nh để chia t&aacute;ch thửa đất v&agrave; chuyển Bản tr&iacute;ch đo thửa đất mới t&aacute;ch cho người sử dụng đất để thực hiện k&yacute; kết hợp đồng, văn bản giao dịch về quyền sử dụng một phần thửa đất mới t&aacute;ch</li>\r\n	<li>Thực hiện thủ tục đăng k&yacute; biến động theo quy định tại Nghị định n&agrave;y đối với phần diện t&iacute;ch chuyển quyền; đồng thời x&aacute;c nhận thay đổi v&agrave;o Giấy chứng nhận đ&atilde; cấp hoặc tr&igrave;nh cơ quan c&oacute; thẩm quyền cấp</li>\r\n</ul>\r\n<br />\r\nGiấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất đối với phần diện t&iacute;ch c&ograve;n lại của thửa đất kh&ocirc;ng chuyển quyền; chỉnh l&yacute;, cập nhật biến động v&agrave;o hồ sơ địa ch&iacute;nh, cơ sở dữ liệu đất đai; trao cho người sử dụng đất hoặc gửi Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; để trao đối với trường hợp nộp hồ sơ tại cấp x&atilde;.<br />\r\n<br />\r\n4. Trường hợp t&aacute;ch thửa do Nh&agrave; nước thu hồi một phần thửa đất th&igrave; cơ quan t&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường c&oacute; tr&aacute;ch nhiệm chỉ đạo Văn ph&ograve;ng đăng k&yacute; đất đai căn cứ quyết định thu hồi của cơ quan nh&agrave; nước c&oacute; thẩm quyền thực hiện c&aacute;c c&ocirc;ng việc sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đo đạc chỉnh l&yacute; bản đồ địa ch&iacute;nh, hồ sơ địa ch&iacute;nh v&agrave; cơ sở dữ liệu đất đai</li>\r\n	<li>X&aacute;c nhận thay đổi v&agrave;o Giấy chứng nhận đ&atilde; cấp v&agrave; trao cho người sử dụng đất hoặc gửi Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; để trao đối với trường hợp nộp hồ sơ tại cấp x&atilde;</li>\r\n</ul>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Thời gian giải quyết t&aacute;ch thửa</strong></span><br />\r\n<br />\r\nThời hạn giải quyết kh&ocirc;ng qu&aacute; 20 ng&agrave;y (điểm đ, khoản 2, Điều 61 Nghị định 43/2014/NĐ-CP). Trong trường hợp n&agrave;y bạn c&oacute; thể li&ecirc;n hệ tới Văn ph&ograve;ng đăng k&yacute; đất đai hoặc Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; để được giải quyết trong trường hợp bạn nộp hồ sơ tại x&atilde;.', 0, NULL, NULL, 1, 0, 99, '2017-07-17 15:56:26', '2017-07-17 15:59:15', 2, 2),
(60, 'Tách thửa chia đất cho con như thế nào', 'tach-thua-chia-dat-cho-con-nhu-the-nao', 'Tach thua chia dat cho con nhu the nao', 'Nếu cha mẹ hay quý vị đang muốn tách thửa chia đất cho con nhưng lại không điều kiện, lệ phí hay trình tự tách thửa chia đất cho con như thế nào. Hãy tham khảo ngay bài viết dưới đây để biết rõ hơn.', '2017/07/17/tach-thua-chia-dat-cho-con-nhu-the-nao-1500282620.png', 5, '&nbsp;\r\n<div style=\"text-align:center\"><img alt=\"Tách thửa chia đất cho con như thế nào\" src=\"/uploads/images/tach-thua-chia-dat-cho-con-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nVề điều kiện để t&aacute;ch thửa, để được t&aacute;ch thửa th&igrave; l&ocirc; đất của gia đ&igrave;nh bạn phải thỏa m&atilde;n điều kiện diện t&iacute;ch tối thiểu để được t&aacute;ch thửa theo quy định của từng địa phương để đảm bảo quy định, quy hoạch chung đối với quỹ đất của từng địa phương.<br />\r\n<br />\r\nTr&igrave;nh tự, thủ tục thực hiện t&aacute;ch thửa hoặc hợp thửa đất<br />\r\n<br />\r\n1. Người sử dụng đất nộp 01 bộ hồ sơ đề nghị t&aacute;ch thửa hoặc hợp thửa.<br />\r\n<br />\r\n2. Văn ph&ograve;ng đăng k&yacute; đất đai c&oacute; tr&aacute;ch nhiệm thực hiện c&aacute;c c&ocirc;ng việc sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đo đạc địa ch&iacute;nh để chia t&aacute;ch thửa đất</li>\r\n	<li>Lập hồ sơ tr&igrave;nh cơ quan c&oacute; thẩm quyền cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho người sử dụng đất đối với thửa đất mới t&aacute;ch, hợp thửa</li>\r\n	<li>Chỉnh l&yacute;, cập nhật biến động v&agrave;o hồ sơ địa ch&iacute;nh, cơ sở dữ liệu đất đai; trao Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho người được cấp hoặc gửi Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; để trao đối với trường hợp nộp hồ sơ tại cấp x&atilde;.</li>\r\n</ul>\r\n<br />\r\n3. Trường hợp t&aacute;ch thửa do chuyển quyền sử dụng một phần thửa đất hoặc do giải quyết tranh chấp, khiếu nại, tố c&aacute;o, đấu gi&aacute; đất hoặc do chia t&aacute;ch hộ gia đ&igrave;nh, nh&oacute;m người sử dụng đất; do xử l&yacute; hợp đồng thế chấp, g&oacute;p vốn, k&ecirc; bi&ecirc;n b&aacute;n đấu gi&aacute; quyền sử dụng đất để thi h&agrave;nh &aacute;n (sau đ&acirc;y gọi l&agrave; chuyển quyền) th&igrave; Văn ph&ograve;ng đăng k&yacute; đất đai thực hiện c&aacute;c c&ocirc;ng việc sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đo đạc địa ch&iacute;nh để chia t&aacute;ch thửa đất v&agrave; chuyển Bản tr&iacute;ch đo thửa đất mới t&aacute;ch cho người sử dụng đất để thực hiện k&yacute; kết hợp đồng, văn bản giao dịch về quyền sử dụng một phần thửa đất mới t&aacute;ch</li>\r\n	<li>Thực hiện thủ tục đăng k&yacute; biến động theo quy định tại Nghị định n&agrave;y đối với phần diện t&iacute;ch chuyển quyền; đồng thời x&aacute;c nhận thay đổi v&agrave;o Giấy chứng nhận đ&atilde; cấp hoặc tr&igrave;nh cơ quan c&oacute; thẩm quyền cấp</li>\r\n</ul>\r\n<br />\r\nGiấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất đối với phần diện t&iacute;ch c&ograve;n lại của thửa đất kh&ocirc;ng chuyển quyền; chỉnh l&yacute;, cập nhật biến động v&agrave;o hồ sơ địa ch&iacute;nh, cơ sở dữ liệu đất đai; trao cho người sử dụng đất hoặc gửi Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; để trao đối với trường hợp nộp hồ sơ tại cấp x&atilde;.<br />\r\n<br />\r\n4. Trường hợp t&aacute;ch thửa do Nh&agrave; nước thu hồi một phần thửa đất th&igrave; cơ quan t&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường c&oacute; tr&aacute;ch nhiệm chỉ đạo Văn ph&ograve;ng đăng k&yacute; đất đai căn cứ quyết định thu hồi của cơ quan nh&agrave; nước c&oacute; thẩm quyền thực hiện c&aacute;c c&ocirc;ng việc sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đo đạc chỉnh l&yacute; bản đồ địa ch&iacute;nh, hồ sơ địa ch&iacute;nh v&agrave; cơ sở dữ liệu đất đai</li>\r\n	<li>X&aacute;c nhận thay đổi v&agrave;o Giấy chứng nhận đ&atilde; cấp v&agrave; trao cho người sử dụng đất hoặc gửi Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; để trao đối với trường hợp nộp hồ sơ tại cấp x&atilde;</li>\r\n</ul>\r\n<br />\r\nKhi c&oacute; nhu cầu t&aacute;ch thửa, bạn sẽ phải nộp c&aacute;c khoản thuế, ph&iacute;, lệ ph&iacute; sau:<br />\r\n<br />\r\n<strong>► Lệ ph&iacute; địa ch&iacute;nh</strong><br />\r\n<br />\r\nĐ&acirc;y l&agrave; khoản thu v&agrave;o tổ chức, hộ gia đ&igrave;nh, c&aacute; nh&acirc;n khi được cơ quan nh&agrave; nước c&oacute; thẩm quyền hoặc tổ chức được ủy quyền giải quyết c&aacute;c c&ocirc;ng việc về địa ch&iacute;nh.<br />\r\n<br />\r\nT&ugrave;y từng điều kiện cụ thể của từng địa b&agrave;n v&agrave; ch&iacute;nh s&aacute;ch ph&aacute;t triển kinh tế - x&atilde; hội của địa phương m&agrave; quy định mức thu cho ph&ugrave; hợp, đảm bảo nguy&ecirc;n tắc:<br />\r\n<br />\r\nMức thu tối đa &aacute;p dụng đối với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n tại c&aacute;c quận thuộc th&agrave;nh phố trực thuộc Trung ương; phường nội th&agrave;nh thuộc th&agrave;nh phố hoặc thị x&atilde; trực thuộc tỉnh, như sau:<br />\r\n<br />\r\nCấp giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản gắn liền với đất<br />\r\n<br />\r\nMức thu tối đa kh&ocirc;ng qu&aacute; 100.000 đồng/giấy đối với cấp mới; tối đa kh&ocirc;ng qu&aacute; 50.000 đồng/lần cấp đối với cấp lại (kể cả cấp lại giấy chứng nhận do hết chỗ x&aacute;c nhận), cấp đổi, x&aacute;c nhận bổ sung v&agrave;o giấy chứng nhận.<br />\r\n<br />\r\nTrường hợp giấy chứng nhận cấp cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n chỉ c&oacute; quyền sử dụng đất (kh&ocirc;ng c&oacute; nh&agrave; v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất) th&igrave; &aacute;p dụng mức thu tối đa kh&ocirc;ng qu&aacute; 25.000 đồng/giấy cấp mới; tối đa kh&ocirc;ng qu&aacute; 20.000 đồng/lần cấp đối với cấp lại (kể cả cấp lại giấy chứng nhận do hết chỗ x&aacute;c nhận), cấp đổi, x&aacute;c nhận bổ sung v&agrave;o giấy chứng nhận.<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Chứng nhận đăng k&yacute; biến động về đất đai: Kh&ocirc;ng qu&aacute; 28.000 đồng/1 lần.</li>\r\n	<li>Tr&iacute;ch lục bản đồ địa ch&iacute;nh, văn bản, số liệu hồ sơ địa ch&iacute;nh: Kh&ocirc;ng qu&aacute; 15.000 đồng/1 lần.</li>\r\n</ul>\r\n<br />\r\nMức thu &aacute;p dụng đối với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n tại c&aacute;c khu vực kh&aacute;c: Tối đa kh&ocirc;ng qu&aacute; 50% mức thu &aacute;p dụng đối với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n tại c&aacute;c quận thuộc th&agrave;nh phố trực thuộc Trung ương; phường nội th&agrave;nh thuộc th&agrave;nh phố hoặc thị x&atilde; trực thuộc tỉnh.<br />\r\n<br />\r\nLệ ph&iacute; trước bạ nh&agrave; đất: mức thu lệ ph&iacute; trước bạ của nh&agrave; đất l&agrave; 0,5% gi&aacute; trị t&agrave;i sản t&iacute;nh lệ ph&iacute; trước bạ.<br />\r\n<br />\r\nNgo&agrave;i ra, người xin cấp Giấy chứng nhận quyền sử dụng đất phải nộp c&aacute;c loại ph&iacute; kh&aacute;c như: Ph&iacute; thẩm định địa ch&iacute;nh, ph&iacute; tr&iacute;ch đo địa ch&iacute;nh.', 0, NULL, NULL, 1, 0, 100, '2017-07-17 16:10:47', '2017-07-17 16:10:47', 2, 2),
(61, 'Chia tài sản thừa kế cho con chung và con riêng như thế nào', 'chia-tai-san-thua-ke-cho-con-chung-va-con-rieng-nhu-the-nao', 'Chia tai san thua ke cho con chung va con rieng nhu the nao', 'Nếu quý vị có tài sản đang muốn lập di chúc thừa kế chia lại cho con chung và riêng thì cần phải chuẩn bị những giấy tờ thủ tục gì, quy trình như thế nào. Hãy tham khảo bài viết dưới đây, để biết thêm về việc chia tài sản thừa kế cho con chung và con riêng như thế nào.', '2017/07/17/chia-tai-san-thua-ke-cho-con-chung-va-con-rieng-nhu-the-nao-1500286077.png', 5, '<div style=\"text-align:center\"><img alt=\"Chia tài sản thừa kế cho con chung và con riêng như thế nào\" src=\"/uploads/images/chia-tai-san-thua-ke-cho-con-chung-va-con-rieng-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nBộ luật d&acirc;n sự 2005 thừa nhận c&oacute; 3 trường hợp thừa kế, đ&oacute; l&agrave;: Thừa kế theo di ch&uacute;c; thừa kế theo ph&aacute;p luật v&agrave; thừa kế kh&ocirc;ng theo nội dung ph&aacute;p luật.<br />\r\n<br />\r\nV&igrave; theo Điều 646 Di ch&uacute;c l&agrave; sự thể hiện &yacute; ch&iacute; của c&aacute; nh&acirc;n nhằm chuyển t&agrave;i sản của m&igrave;nh cho người kh&aacute;c sau khi chết. B&ecirc;n cạnh đ&oacute; Điều 648 cũng quy định người lập di ch&uacute;c c&oacute; quyền<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Chỉ định người thừa kế; truất quyền hưởng di sản của người thừa kế</li>\r\n	<li>Ph&acirc;n định phần di sản cho từng người thừa kế</li>\r\n	<li>D&agrave;nh một phần t&agrave;i sản trong khối di sản để di tặng, thờ c&uacute;ng</li>\r\n	<li>Giao nghĩa vụ cho người thừa kế</li>\r\n	<li>Chỉ định người giữ di ch&uacute;c, người quản l&yacute; di sản, người ph&acirc;n chia di sản.</li>\r\n</ul>\r\n<br />\r\nNhững người thừa kế theo ph&aacute;p luật được quy định cụ thể tại Điều 676 như sau:<br />\r\n<br />\r\n1. Những người thừa kế theo ph&aacute;p luật được quy định theo thứ tự sau đ&acirc;y:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>a) H&agrave;ng thừa kế thứ nhất gồm: vợ, chồng, cha đẻ, mẹ đẻ, cha nu&ocirc;i, mẹ nu&ocirc;i, con đẻ, con nu&ocirc;i của người chết</li>\r\n	<li>b) H&agrave;ng thừa kế thứ hai gồm: &ocirc;ng nội, b&agrave; nội, &ocirc;ng ngoại, b&agrave; ngoại, anh ruột, chị ruột, em ruột của người chết; ch&aacute;u ruột của người chết m&agrave; người chết l&agrave; &ocirc;ng nội, b&agrave; nội, &ocirc;ng ngoại, b&agrave; ngoại</li>\r\n	<li>c) H&agrave;ng thừa kế thứ ba gồm: cụ nội, cụ ngoại của người chết; b&aacute;c ruột, ch&uacute; ruột, cậu ruột, c&ocirc; ruột, d&igrave; ruột của người chết; ch&aacute;u ruột của người chết m&agrave; người chết l&agrave; b&aacute;c ruột, ch&uacute; ruột, cậu ruột, c&ocirc; ruột, d&igrave; ruột, chắt ruột của người chết m&agrave; người chết l&agrave; cụ nội, cụ ngoại.</li>\r\n</ul>\r\n<br />\r\n2. Những người thừa kế c&ugrave;ng h&agrave;ng được hưởng phần di sản bằng nhau.<br />\r\n<br />\r\n3. Những người ở h&agrave;ng thừa kế sau chỉ được hưởng thừa kế, nếu kh&ocirc;ng c&ograve;n ai ở h&agrave;ng thừa kế trước do đ&atilde; chết, kh&ocirc;ng c&oacute; quyền hưởng di sản, bị truất quyền hưởng di sản hoặc từ chối nhận di sản.<br />\r\n<br />\r\n<strong>► Tr&igrave;nh tự khai nhận di sản thừa kế</strong><br />\r\n<br />\r\n<br />\r\n&diams; Giấy tờ chung cần chuẩn bị<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Phiếu y&ecirc;u cầu c&ocirc;ng chứng theo mẫu</li>\r\n	<li>Giấy chứng tử của người để lại di sản</li>\r\n	<li>C&aacute;c giấy tờ chứng minh về t&agrave;i sản của người để lại di sản bao gồm: Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất; sổ tiết kiệm, Giấy đăng k&yacute; xe; Giấy chứng nhận cổ phần</li>\r\n	<li>&nbsp;Giấy tờ về nh&acirc;n th&acirc;n của người thừa kế: CMND, hộ khẩu</li>\r\n</ul>\r\n<br />\r\n&diams; &nbsp;Trường hợp c&oacute; di ch&uacute;c v&agrave; di ch&uacute;c ghi r&otilde; c&aacute;ch ph&acirc;n chia t&agrave;i sản, bạn cần cung cấp th&ecirc;m: di ch&uacute;c<br />\r\n<br />\r\n&diams; &nbsp;Trường hợp kh&ocirc;ng c&oacute; di ch&uacute;c hoặc c&oacute; di ch&uacute;c nhưng di ch&uacute;c kh&ocirc;ng ghi r&otilde; c&aacute;ch ph&acirc;n chia di sản, bạn cần cung cấp th&ecirc;m:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Tờ tường tr&igrave;nh v&agrave; cam kết về quan hệ nh&acirc;n th&acirc;n</li>\r\n	<li>Giấy tờ chứng minh quan hệ nh&acirc;n th&acirc;n giữa người thừa kế theo ph&aacute;p luật với người để lại di sản trường hợp kh&ocirc;ng c&oacute; di ch&uacute;c: hộ khẩu, giấy khai sinh, tờ khai nh&acirc;n khẩu</li>\r\n</ul>\r\n<br />\r\n<strong>► Thủ tục thực hiện khai nhận thừa kế</strong><br />\r\n<br />\r\nSau khi kiểm tra hồ sơ thấy đầy đủ, ph&ugrave; hợp quy định của ph&aacute;p luật, cơ quan c&ocirc;ng chứng tiến h&agrave;nh thụ l&yacute; c&ocirc;ng chứng văn bản thỏa thuận ph&acirc;n chia di sản, văn bản khai nhận di sản. Việc thụ l&yacute; phải được ni&ecirc;m yết trong thời hạn 15 ng&agrave;y, kể từ ng&agrave;y ni&ecirc;m yết.<br />\r\n<br />\r\nViệc ni&ecirc;m yết do tổ chức h&agrave;nh nghề c&ocirc;ng chứng thực hiện tại trụ sở của Ủy ban nh&acirc;n cấp x&atilde; nơi thường tr&uacute; cuối c&ugrave;ng của người để lại di sản; trường hợp kh&ocirc;ng x&aacute;c định được nơi thường tr&uacute; cuối c&ugrave;ng th&igrave; ni&ecirc;m yết tại nơi tạm tr&uacute; c&oacute; thời hạn cuối c&ugrave;ng của người đ&oacute;.<br />\r\n<br />\r\nTrường hợp di sản gồm cả bất động sản v&agrave; động sản hoặc di sản chỉ gồm c&oacute; bất động sản th&igrave; việc ni&ecirc;m yết được thực hiện theo quy định tại Khoản n&agrave;y v&agrave; tại Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi c&oacute; bất động sản.<br />\r\n<br />\r\nTrường hợp di sản chỉ gồm c&oacute; động sản, nếu trụ sở của tổ chức h&agrave;nh nghề c&ocirc;ng chứng v&agrave; nơi thường tr&uacute; hoặc tạm tr&uacute; c&oacute; thời hạn cuối c&ugrave;ng của người để lại di sản kh&ocirc;ng ở c&ugrave;ng một tỉnh, th&agrave;nh phố trực thuộc Trung ương th&igrave; tổ chức h&agrave;nh nghề c&ocirc;ng chứng c&oacute; thể đề nghị ủy ban nh&acirc;n d&acirc;n cấp x&atilde; nơi thường tr&uacute; hoặc tạm tr&uacute; c&oacute; thời hạn cuối c&ugrave;ng của người để lại di sản thực hiện việc ni&ecirc;m yết.<br />\r\n<br />\r\nSau 15 ng&agrave;y ni&ecirc;m yết, kh&ocirc;ng c&oacute; khiếu nại, tố c&aacute;o g&igrave; th&igrave; cơ quan c&ocirc;ng chứng chứng nhận văn bản thừa kế. C&aacute;c đồng thừa kế c&oacute; thể lập Văn bản thỏa thuận ph&acirc;n chia di sản thừa kế (Điều 57 Luật C&ocirc;ng chứng 2014) hoặc Văn bản khai nhận di sản thừa kế (Điều 58 Luật C&ocirc;ng chứng 2014). Sau khi c&ocirc;ng chứng văn bản thừa kế, người được hưởng di sản thực hiện thủ tục đăng k&yacute; quyền t&agrave;i sản tại Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất thuộc Ph&ograve;ng t&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường cấp huyện nơi c&oacute; đất.<br />\r\n<br />\r\nTr&ecirc;n đ&acirc;y l&agrave; &yacute; kiến tư vấn của ch&uacute;ng t&ocirc;i về c&acirc;u hỏi của qu&yacute; kh&aacute;ch h&agrave;ng. Việc đưa ra &yacute; kiến tư vấn n&ecirc;u tr&ecirc;n căn cứ v&agrave;o c&aacute;c quy định của ph&aacute;p luật v&agrave; th&ocirc;ng tin do qu&yacute; kh&aacute;ch h&agrave;ng cung cấp. Mục đ&iacute;ch đưa ra nội dung tư vấn n&agrave;y l&agrave; để c&aacute;c c&aacute; nh&acirc;n, tổ chức tham khảo.<br />\r\n<br />\r\nTrường hợp trong nội dung tư vấn c&oacute; điều g&igrave; g&acirc;y nhầm lẫn, chưa r&otilde; r&agrave;ng hoặc th&ocirc;ng tin n&ecirc;u trong nội dung tư vấn khiến qu&yacute; kh&aacute;ch chưa hiểu hết vấn đề hoặc/ v&agrave; c&oacute; sự vướng ngại, thắc mắc, ch&uacute;ng t&ocirc;i rất mong nhận được &yacute; kiến phản hồi của qu&yacute; kh&aacute;ch h&agrave;ng.', 0, NULL, NULL, 1, 0, 101, '2017-07-17 17:08:43', '2017-07-17 17:08:43', 2, 2);
INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(62, 'Cách hóa giải hướng nhà tuyệt mệnh như thế nào', 'cach-hoa-giai-huong-nha-tuyet-menh-nhu-the-nao', 'Cach hoa giai huong nha tuyet menh nhu the nao', 'Trước khi đi tìm cách hóa giải hướng nhà tuyệt mệnh như thế nào. Mọi người cần nên biết hướng nhà tuyệt mệnh là hướng nhà như thế nào và ảnh hưởng ra sao với gia chủ. Để biết được tất cả, mời quý vị tham khảo bài viết dưới đây.', '2017/07/19/cach-hoa-giai-huong-nha-tuyet-menh-nhu-the-nao-1500433149.png', 4, 'Hướng nh&agrave; tuyệt mệnh được cho l&agrave; hướng nh&agrave; xấu, mang lại những tai ương cho gia chủ. Khi gặp hướng nh&agrave; xấu, thường mọi người sẽ d&ugrave;ng hướng bếp, hướng giường ngủ, hướng ban thờ tốt để thể khắc phục những bất lợi do hướng nh&agrave; g&acirc;y ra.\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"Cách hóa giải hướng nhà tuyệt mệnh như thế nào\" src=\"/uploads/images/cach-hoa-giai-huong-nha-tuyet-menh-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nNếu như hướng nh&agrave; của qu&yacute; vị cũng rơi v&agrave;o t&igrave;nh trạng hướng nh&agrave; tuyệt mệnh th&igrave; qu&yacute; vị đừng qu&aacute; lo lắng, h&atilde;y tham khảo c&aacute;ch h&oacute;a giải dưới đ&acirc;y, qu&yacute; vị lưu &yacute; b&agrave;i viết n&agrave;y chỉ chia sẻ cho qu&yacute; vị tham khảo, về phương diện sẽ đ&uacute;ng được một số phần về hướng nh&agrave; của qu&yacute; vị. Để ch&iacute;nh x&aacute;c nhất, qu&yacute; vị n&ecirc;n tham khảo c&aacute;c chuy&ecirc;n gia phong thủy để được tư vấn chi tiết hơn cho hướng nh&agrave; của qu&yacute; vị.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>►&nbsp;C&aacute;ch h&oacute;a giải hướng nh&agrave; tuyệt mệnh như thế n&agrave;o</strong></span><br />\r\n<br />\r\nX&eacute;t về l&yacute; thuyết, c&aacute;c kh&aacute;i niệm n&agrave;y thuộc trường ph&aacute;i Phong thủy B&aacute;t trạch. Tức ph&aacute;i nghi&ecirc;n cứu sự tương t&aacute;c giữa tuổi của gia chủ với phương vị của ng&ocirc;i nh&agrave;. Đ&oacute; l&agrave; những thuật ngữ mang t&iacute;nh ước lệ để chỉ những hướng nh&agrave; chưa ph&ugrave; hợp với tuổi của chủ nh&agrave;.<br />\r\n<br />\r\nTrong phong thủy b&aacute;t trạch c&oacute; 8 cung với 8 &yacute; nghĩa kh&aacute;c nhau. Trong đ&oacute; c&oacute; 4 cung tốt l&agrave; Sinh kh&iacute;, Thi&ecirc;n y, Ni&ecirc;n duy&ecirc;n, Phục v&igrave;; 4 cung xấu l&agrave; Họa hại, Ngũ quỷ, Lục s&aacute;t, Tuyệt mạng.<br />\r\n<br />\r\nT&ugrave;y v&agrave;o tuổi của chủ nh&agrave; sẽ ứng với một hướng (cung) ph&ugrave; hợp tr&ecirc;n la b&agrave;n v&agrave; b&aacute;t trạch để x&aacute;c định. V&iacute; dụ, người chồng sinh năm 1976 th&igrave; hướng Ngũ quỷ l&agrave; ch&iacute;nh Đ&ocirc;ng, hay chồng sinh năm 1973 hướng ch&iacute;nh T&acirc;y l&agrave; hướng Ngũ quỷ.<br />\r\n<br />\r\nTrong đ&oacute;, t&ugrave;y v&agrave;o mỗi gia cảnh, ng&agrave;nh nghề cũng như từng vị tr&iacute; nh&agrave; sẽ thể hiện c&aacute;c điều kh&ocirc;ng tốt cho cuộc sống.<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Cung Ngũ quỷ dễ gặp điều thị phi, người nh&agrave; phải mổ xẻ, đặc biệt vợ chồng hay gặp trắc trở.</li>\r\n	<li>Cung Tuyệt mệnh xấu về đường con c&aacute;i, nh&agrave; thường c&oacute; chuyện u sầu. Cung Lục s&aacute;t thường l&agrave;m gia chủ hao t&agrave;i, tốn của, đặc biệt người nh&agrave; c&oacute; nguy cơ bị thương tật</li>\r\n</ul>\r\n<br />\r\n<strong>Đa c&aacute;t thắng tiểu hung</strong><br />\r\n<br />\r\nC&aacute;c chuy&ecirc;n gia phong thủy sẽ c&oacute; nhiều dụng cụ trong nghiệp vụ để c&oacute; thể x&aacute;c định khả năng tuyệt mệnh hay kh&ocirc;ng v&agrave; nguy cấp đến mức n&agrave;o t&ugrave;y theo từng hạn mục. Khi kh&ocirc;ng may rơi v&agrave;o hướng Tuyệt mệnh hay Lục s&aacute;t, Ngũ quỷ th&igrave; gia chủ n&ecirc;n t&igrave;m gặp chuy&ecirc;n gia phong thủy để c&oacute; biện ph&aacute;p h&oacute;a giải hữu hiệu nhất.<br />\r\n<br />\r\nTheo c&aacute;c chuy&ecirc;n gia phong thủy, d&ugrave; tr&ecirc;n l&yacute; thuyết c&aacute;c cung được xem l&agrave; kh&ocirc;ng mang lại may mắn cho gia chủ, nhưng nếu x&eacute;t chung ở phương diện khoa học th&igrave; hướng nh&agrave; chỉ l&agrave; một trong nhiều yếu tố của Phong thủy ng&ocirc;i nh&agrave;.<br />\r\n<br />\r\n<strong>Xem th&ecirc;m</strong><br />\r\n<br />\r\n&rArr; <a href=\"http://thanhphuthinhland.vn/tin-tuc/nhung-dieu-cam-ky-khi-xay-nha-p22.html\">Những điều cấm kỵ khi x&acirc;y nh&agrave;</a><br />\r\n&rArr; <a href=\"http://thanhphuthinhland.vn/tin-tuc/nhung-loai-cay-khong-nen-trong-trong-nha-p14.html\">Những loại c&acirc;y kh&ocirc;ng n&ecirc;n trồng trong nh&agrave;</a><br />\r\n&rArr; <a href=\"http://thanhphuthinhland.vn/tin-tuc/cach-khac-phuc-huong-nha-khong-tot-p12.html\">C&aacute;ch khắc phục hướng nh&agrave; kh&ocirc;ng tốt</a><br />\r\n<br />\r\nPhong thủy lu&ocirc;n c&oacute; biện ph&aacute;p h&oacute;a giải theo nguy&ecirc;n tắc &ldquo;đa c&aacute;t thắng tiểu hung&rdquo;. Tức nhiều yếu tố tốt trong nh&agrave; sẽ lấn &aacute;t số &iacute;t điều xấu.<br />\r\n<br />\r\nV&igrave; thế, d&ugrave; hướng nh&agrave; kh&ocirc;ng tốt vẫn c&oacute; thể h&oacute;a giải. Cụ thể, trong trường ph&aacute;i B&aacute;t trạch, khi gặp hướng nh&agrave; xấu, người ta vẫn c&oacute; thể d&ugrave;ng c&aacute;c yếu tố kh&aacute;c để bổ cứu như d&ugrave;ng hướng bếp, hướng giường ngủ, hướng ban thờ tốt c&oacute; thể khắc phục được những bất lợi do hướng nh&agrave; g&acirc;y ra. B&ecirc;n cạnh đ&oacute;, việc thiết kế hệ thống cửa ch&uacute; &yacute; sao cho c&oacute; sự th&ocirc;ng tho&aacute;ng. N&ecirc;n bố tr&iacute; cầu thang lấy được hướng tốt so với bản mệnh của chủ nh&agrave;, g&oacute;p phần tăng cường c&aacute;c d&ograve;ng năng lượng tốt l&agrave;nh c&aacute;c kh&ocirc;ng gian kh&aacute;c trong nh&agrave;.<br />\r\n<br />\r\nNgo&agrave;i ra, c&aacute;c chuy&ecirc;n gia phong thủy sẽ c&oacute; kinh nghiệm để nhận thức v&agrave; h&oacute;a giải c&aacute;c yếu tố, chẳng hạn như cần phải x&aacute;c định c&aacute;c ph&ograve;ng ốc hay vật dụng cho đ&uacute;ng phương vị v&agrave; hướng tốt nhất cho gia chủ, x&aacute;c định chiều cao, chiều d&agrave;i của bếp hay c&aacute;c c&aacute;nh cửa nh&agrave; cho đ&uacute;ng k&iacute;ch thước chuẩn x&aacute;c.<br />\r\n<br />\r\nNgo&agrave;i những yếu tố tr&ecirc;n khi chuẩn bị x&acirc;y một căn nh&agrave; ch&uacute;ng ta cần phải quan s&aacute;t rộng r&atilde;i về nh&agrave; đối diện, nh&agrave; b&ecirc;n h&ocirc;ng hay h&igrave;nh thể k&iacute;ch thước nh&agrave; ở, xem c&oacute; hung trạch hay kh&ocirc;ng, v&iacute; dụ, theo dạng nh&agrave; h&igrave;nh chữ Z, h&igrave;nh b&igrave;nh h&agrave;nh, g&oacute;c nhọn, g&oacute;c t&ugrave;, xuy&ecirc;n g&oacute;c, t&ugrave; h&atilde;m hoặc t&oacute;p hậu&hellip; v&agrave; xem được t&iacute;nh chất đất l&agrave;nh hay đất dữ, m&agrave; ch&uacute;ng ta cần phải h&oacute;a giải ho&agrave;n to&agrave;n. V&igrave; vậy, nếu hướng nh&agrave; c&oacute; phạm Tuyệt mạng, Ngũ quỷ hay Lục s&aacute;t th&igrave; cũng kh&ocirc;ng phải l&agrave; vấn đề qu&aacute; lo ngại.<br />\r\n<br />\r\n<strong><span style=\"font-size:18px\">► Những c&aacute;ch khắc phục hướng nh&agrave; kh&ocirc;ng tốt</span></strong><br />\r\n<br />\r\nTheo c&aacute;ch truyền thống hiện nay, sẽ c&oacute; 3 c&aacute;ch để cho c&aacute;c gia chủ khắc phục hướng nh&agrave; kh&ocirc;ng tốt của m&igrave;nh để ph&ugrave; hợp với phong thủy hơn. Để biết r&otilde; hơn, mời qu&yacute; vị tham khảo dưới đ&acirc;y.<br />\r\n<br />\r\n<strong>C&aacute;ch 1: Sử dụng thường xuy&ecirc;n một c&aacute;nh cửa kh&aacute;c c&oacute; hướng tốt</strong><br />\r\n<br />\r\nC&aacute;nh cửa n&agrave;y phải c&oacute; hướng đ&uacute;ng với hầu hết c&aacute;c trường hợp v&agrave; kh&ocirc;ng hề phức tạp. Đối với c&aacute;c ng&ocirc;i nh&agrave; kiểu hiện đại đường đi v&agrave;o trong nh&agrave; thường th&ocirc;ng qua gara để xe hoặc cửa sau. Do đ&oacute; cửa ch&iacute;nh chỉ mang &yacute; nghĩa tượng trưng v&agrave; bị dẹp sang một b&ecirc;n. Nếu cửa ch&iacute;nh c&oacute; hướng xấu &iacute;t khi được sử dụng th&igrave; những nguồn năng lượng ti&ecirc;u cực sẽ kh&ocirc;ng được k&iacute;ch hoạt, suy yếu v&agrave; kh&ocirc;ng c&oacute; khả năng lọt được v&agrave;o trong ng&ocirc;i nh&agrave;.<br />\r\n<br />\r\n<strong>C&aacute;ch 2: Tạo c&aacute;c năng lượng mạnh bảo vệ cửa ra v&agrave;o</strong><br />\r\n<br />\r\nSử dụng c&aacute;c vật trang tr&iacute; gi&uacute;p tạo ra nguồn năng lượng bảo vệ mạnh mẽ cho cửa ra v&agrave;o của ng&ocirc;i nh&agrave;. Vật trang tr&iacute; ở đ&acirc;y c&oacute; thể l&agrave; gương b&aacute;t qu&aacute;i hoặc c&aacute;c vật phẩm phong thủy truyền thống như tượng, ảnh Quan Thế &Acirc;m Bồ T&aacute;t, tượng Quan C&ocirc;ng,&hellip; ở gần cửa ra v&agrave;o. Ngo&agrave;i ra, sự lựa chọn tốt nhất l&agrave; sử dụng biểu tượng ri&ecirc;ng của m&igrave;nh m&agrave; vẫn c&oacute; thể tạo ra năng lượng mạnh để bảo vệ v&agrave; thể hiện được l&ograve;ng bao dung, che chở.<br />\r\n<br />\r\n<strong>C&aacute;ch 3: Sử dụng c&aacute;c yếu tố phong thủy c&oacute; thể l&agrave;m suy yếu nguồn năng lượng xấu</strong><br />\r\n<br />\r\nĐ&acirc;y l&agrave; phương thức tốt nhất để đối ph&oacute; với hướng nh&agrave; xấu hoặc kh&ocirc;ng may mắn. Theo phong thủy theo tuổi nếu hướng nh&agrave; l&agrave; hướng bắc th&igrave; c&oacute; thể sử dụng c&aacute;c đồ vật đại diện cho yếu tố Thổ để l&agrave;m giảm sức mạnh của nước &ndash; biểu tượng của hướng Bắc.<br />\r\n<br />\r\nĐể c&oacute; thể khắc chế được những nguồn năng lượng ti&ecirc;u cực do hướng nh&agrave; xấu đem lại, th&igrave; c&aacute;c c&aacute;ch thức tr&ecirc;n cần phải được thực hiện thật tinh tế để lu&ocirc;n lu&ocirc;n đảm bảo được sự c&acirc;n bằng của c&aacute;c yếu tố phong thủy cơ bản. Đồng thời, duy tr&igrave; năng lượng hỗ trợ sự nghiệp của hướng Bắc kết nối h&agrave;i h&ograve;a.<br />\r\n<br />\r\nSau khi xem b&agrave;i viết chia sẻ tr&ecirc;n, hi vọng qu&yacute; vị sẽ biết được những c&aacute;ch h&oacute;a giải hướng nh&agrave; tuyệt mệnh để ph&ugrave; hợp phong thủy hơn.', 0, NULL, NULL, 1, 0, 102, '2017-07-19 09:59:53', '2017-07-19 09:59:53', 2, 2),
(63, 'Đất nền Long An - Miếng mồi béo bỡ của giới đầu tư', 'dat-nen-long-an-mieng-moi-beo-bo-cua-gioi-dau-tu', 'Dat nen Long An - Mieng moi beo bo cua gioi dau tu', 'Tại sao lại gọi đất nền Long An là miếng mồi béo bỡ của giới đầu tư hiện nay. Bởi vì hiện tại đất nền TP.HCM có mức giá khá cao, trong khi ở Long An có giá còn khá hấp dẫn, tỷ suất lợi nhuận cao đang trở thành điểm ngắm của khá nhiều nhà đầu tư BĐS', '2017/07/19/dat-nen-long-an-mieng-moi-beo-bo-cua-gioi-dau-tu-1500434673.png', 7, 'Để biết r&otilde; hơn, h&atilde;y c&ugrave;ng ph&acirc;n t&iacute;ch thị trường đất nền tại Long An hiện nay để biết được tại sao lại gọi l&agrave; miếng mồi b&eacute;o bỡ cho c&aacute;c nh&agrave; đầu tư.\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"Đất nền Long An Miếng mồi béo bỡ của giới đầu tư\" src=\"/uploads/images/dat-nen-long-an-mieng-moi-beo-bo-cua-gioi-dau-tu.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n<strong>► Lợi thế kết nối hạ tầng</strong><br />\r\n<br />\r\nL&agrave; một trong số c&aacute;c tỉnh l&acirc;n cận TP.HCM, trong khi BĐS B&igrave;nh Dương, Đồng Nai đ&atilde; từng nổi s&oacute;ng, tạo sự ch&uacute; &yacute; của giới đầu tư kh&aacute; l&acirc;u, th&igrave; Long An được đ&aacute;nh gi&aacute; l&agrave; thị trường mới nổi, nhưng hứa hẹn nhiều tiềm năng.<br />\r\n<br />\r\nTại địa phương n&agrave;y đ&atilde; v&agrave; đang h&igrave;nh th&agrave;nh h&agrave;ng loạt dự &aacute;n khu đ&ocirc; thị mới được quy hoạch b&agrave;i bản. Đặc biệt, nhiều c&ocirc;ng tr&igrave;nh hạ tầng giao th&ocirc;ng được đầu tư x&acirc;y dựng, gi&uacute;p r&uacute;t ngắn khoảng c&aacute;ch di chuyển giữa Long An v&agrave; TP.HCM.<br />\r\n<br />\r\nThời gian qua v&agrave; sắp tới, hạ tầng giao th&ocirc;ng tại Long An cũng được ch&uacute; trọng x&acirc;y dựng, như đường cao tốc TP.HCM - Trung Lương, đường cao tốc Long An - T&acirc;n Sơn Nhất, Đại lộ Đ&ocirc;ng - T&acirc;y, c&aacute;c tuyến đường v&agrave;nh đai 1, 2, 3, 4, c&aacute;c dự &aacute;n n&acirc;ng cấp đường tỉnh lộ, đường xuy&ecirc;n t&acirc;m&hellip; đ&atilde; gi&uacute;p kết nối Long An với TP.HCM v&agrave; c&aacute;c tỉnh, th&agrave;nh l&acirc;n cận như T&acirc;y Ninh, B&igrave;nh Dương, Đồng Nai, B&agrave; Rịa Vũng T&agrave;u.<br />\r\n<br />\r\nTại hội nghị x&uacute;c tiến đầu tư v&agrave;o Long An được tổ chức v&agrave;o trung tuần th&aacute;ng 10/2016, UBND tỉnh Long An đ&atilde; k&yacute; kết 4 bi&ecirc;n bản ghi nhớ đầu tư với c&aacute;c c&ocirc;ng ty li&ecirc;n doanh nước ngo&agrave;i v&agrave; DN trong nước nhằm triển khai thực hiện c&aacute;c dự &aacute;n trục giao th&ocirc;ng nối Tiền Giang - Long An - TP.HCM, x&acirc;y dựng tuyến đường v&agrave;nh đai TP T&acirc;n An, đầu tư hạ tầng c&aacute;c khu c&ocirc;ng nghiệp, khu d&acirc;n cư tr&ecirc;n địa b&agrave;n huyện Bến Lức, huyện Đức H&ograve;a...<br />\r\n<br />\r\nTheo giới kinh doanh địa ốc, Long An đang trở th&agrave;nh điểm ngắm mới của c&aacute;c nh&agrave; đầu tư BĐS. Đ&acirc;y l&agrave; t&iacute;n hiệu khả quan để thị trường BĐS tại Long An khởi sắc trong thời gian tới.<br />\r\n<br />\r\n<strong>►&nbsp;Cuộc chơi đ&oacute;n đầu của đại gia</strong><br />\r\n<br />\r\nNhận diện được tiềm năng ph&aacute;t triển của Long An, thời gian qua, nhiều nh&agrave; đầu tư t&ecirc;n tuổi đ&atilde; quyết định &ldquo;đổ&rdquo; vốn v&agrave;o địa phương n&agrave;y. Điển h&igrave;nh như dự &aacute;n TP sinh th&aacute;i Năm Sao - Five Star Eco City (tại huyện Cần Giuộc) do Tập đo&agrave;n Quốc tế Năm Sao l&agrave;m chủ đầu tư. Với tổng diện t&iacute;ch 450ha, được đầu tư cơ sở hạ tầng đồng bộ với đầy đủ c&aacute;c dịch vụ tiện &iacute;ch cao cấp, Five Star Eco City đang thu h&uacute;t sự quan t&acirc;m rất lớn của c&aacute;c nh&agrave; đầu tư cũng như kh&aacute;ch h&agrave;ng c&oacute; nhu cầu ở.<br />\r\n<br />\r\nMới đ&acirc;y, l&atilde;nh đạo tỉnh Long An cũng đ&atilde; c&ocirc;ng bố 4 dự &aacute;n sẽ được đầu tư x&acirc;y dựng với số vốn l&ecirc;n tới 14.300 tỷ đồng v&agrave; 2,5 tỷ USD. Những dự &aacute;n gồm: Dự &aacute;n Trục giao th&ocirc;ng Tiền Giang - Long An - TP.HCM với vốn đầu tư 230 triệu USD theo h&igrave;nh thức PPP; Dự &aacute;n Cụm c&ocirc;ng nghiệp Mekong, tổng vấn đầu tư 2,5 tỷ USD; Dự &aacute;n đường V&agrave;nh đai TP. T&acirc;n An theo h&igrave;nh thức PPP với tổng vốn đầu tư 3.300 tỷ đồng; Dự &aacute;n trục giao th&ocirc;ng kết nối với đường Mai B&aacute; Hương (TP.HCM), đầu tư hạ tầng c&aacute;c khu c&ocirc;ng nghiệp, khu d&acirc;n cư tr&ecirc;n địa b&agrave;n 2 huyện Bến Lức v&agrave; Đức H&ograve;a, tổng vốn đầu tư khoảng 6.000 tỷ đồng&hellip;<br />\r\n<br />\r\nTheo ph&acirc;n t&iacute;ch của c&aacute;c chuy&ecirc;n gia địa ốc, đ&acirc;y được xem như lợi thế lớn để thị trường BĐS tỉnh Long An ph&aacute;t triển mạnh hơn trong thời gian tới. Đặc biệt l&agrave; c&aacute;c khu vực gi&aacute;p ranh với TP.HCM như Cần Giuộc, Đức H&ograve;a&hellip; sẽ c&oacute; nhiều lợi thế.<br />\r\n<br />\r\nLong An cũng được xem như l&agrave; &ldquo;s&acirc;n sau&rdquo; của TP.HCM trong xu thế gi&atilde;n d&acirc;n v&agrave; d&ugrave; gi&aacute;p ranh với TP.HCM, nhưng mặt bằng gi&aacute; BĐS ở c&aacute;c dự &aacute;n Long An kh&aacute; thấp, chỉ cần bỏ ra v&agrave;i trăm triệu đồng l&agrave; c&oacute; thể sở hữu được một nền đất. Hơn nữa, do phần lớn dự &aacute;n l&agrave; dự &aacute;n mới, n&ecirc;n tiến độ thanh to&aacute;n rất linh hoạt, chia th&agrave;nh nhiều giai đoạn, gi&uacute;p những người c&oacute; thu nhập trung b&igrave;nh thấp vẫn c&oacute; thể sở hữu.<br />\r\n<br />\r\n&ldquo;Thị trường BĐS Long An đang c&oacute; nhiều t&iacute;n hiệu khả quan trong thời gian tới. Hiện tại, Long An đ&atilde; c&oacute; rất nhiều dự &aacute;n lớn, từ 300 ha đến tr&ecirc;n 1.000 ha, được c&aacute;c chủ đầu tư lớn thực hiện. C&aacute;c dự &aacute;n n&agrave;y đang trở th&agrave;nh những điểm nhấn quan trọng của thị trường BĐS Long An. Đ&acirc;y sẽ l&agrave; thị trường lớn trong năm 2017&rdquo; - &ocirc;ng L&ecirc; Ho&agrave;ng Ch&acirc;u, Chủ tịch Hiệp hội Bất động sản TP.HCM đ&aacute;nh gi&aacute;.<br />\r\n<br />\r\nTheo (&nbsp;http://vietnamnet.vn/vn/bat-dong-san/du-an/dat-nen-long-an-diem-ngam-moi-cua-gioi-dau-tu-349904.html )<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Dự &aacute;n đang triển khai của Thanh Ph&uacute; Thịnh Land tại Long An</strong></span><br />\r\n<br />\r\n- Hiện tại, <strong>Thanh Ph&uacute; Thịnh Land</strong> đang triển khai mở b&aacute;n giai đoạn 1 dự &aacute;n <strong>KDC An Thắng - Rạch Kiến</strong>. Tọa lạc tại Ấp 1 X&atilde; T&acirc;n Trạch, Huyện Cần Đước, Tỉnh Long An.&nbsp;C&oacute; quy m&ocirc; diện t&iacute;ch 6000m2, đất thổ cư 100%. Nếu qu&yacute; vị quan t&acirc;m đến dự &aacute;n n&agrave;y h&atilde;y tham khảo b&agrave;i viết dưới đ&acirc;y để biết chi tiết hơn.<br />\r\n<br />\r\n<br />\r\n<span style=\"font-size:18px\">► Xem chi tiết dự &aacute;n: &nbsp;<strong><a href=\"http://thanhphuthinhland.vn/du-an/kdc-an-thang-rach-kien\"><span style=\"color:#ff0000\">KDC An Thắng - Rạch Kiến</span></a></strong></span>', 0, NULL, NULL, 1, 0, 103, '2017-07-19 10:25:02', '2017-07-19 17:25:33', 2, 2),
(65, 'Mua bán nhà đất cần chuẩn bị những giấy tờ gì', 'mua-ban-nha-dat-can-chuan-bi-nhung-giay-to-gi', 'Mua ban nha dat can chuan bi nhung giay to gi', 'Nếu quý vị chuẩn bị mua bán nhà đất, nhưng hai bên lại không biết nên chuẩn bị các giấy tờ gì, để quá trình mua bán diễn ra đúng quy định của pháp luật. Nếu quý vị đang thắc mắc việc mua bán nhà đất cần chuẩn bị những giấy tờ gì, hãy tham khảo bài viết dưới đây', '2017/07/19/mua-ban-nha-dat-can-chuan-bi-nhung-giay-to-gi-1500473389.png', 5, '<div style=\"text-align:center\"><img alt=\"Mua bán nhà đất cần chuẩn bị những giấy tờ gì\" src=\"/uploads/images/mua-ban-nha-dat-can-chuan-bi-nhung-giay-to-gi.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nĐể tiến h&agrave;nh thủ tục mua b&aacute;n nh&agrave; đất diễn ra đ&uacute;ng theo quy định, b&ecirc;n mua v&agrave; b&ecirc;n b&aacute;n cần chuẩn bị một bộ hồ sơ bao gồm:<br />\r\n<br />\r\n- GCN QSD đất (sổ đỏ);<br />\r\n<br />\r\n- Th&ocirc;ng b&aacute;o nộp lệ ph&iacute; trước bạ<br />\r\n<br />\r\n- CMND + Hộ khẩu của b&ecirc;n b&aacute;n<br />\r\n<br />\r\n- Giấy đăng k&yacute; kết h&ocirc;n của b&ecirc;n b&aacute;n hoặc giấy x&aacute;c nhận độc th&acirc;n hoặc văn bản thỏa thuận t&agrave;i sản ri&ecirc;ng chung của vợ chồng<br />\r\n<br />\r\n- &nbsp;CMND + Hộ khẩu của bạn.<br />\r\n<br />\r\nNgo&agrave;i ra, đối với thủ tục giao dịch bất động sản l&agrave; chuyển nhượng quyền sử dụng đất (QSDĐ) th&igrave; b&ecirc;n nhận chuyển nhượng nộp 1 bộ hồ sơ gồm c&oacute;:<br />\r\n<br />\r\na) Hợp đồng chuyển nhượng QSDĐ<br />\r\n<br />\r\nb) Giấy chứng nhận QSDĐ hoặc một trong c&aacute;c loại giấy tờ về QSDĐ quy định tại c&aacute;c khoản 1, 2 v&agrave; 5 Điều 50 của Luật đất đai (nếu c&oacute;).<br />\r\n<br />\r\nHợp đồng chuyển nhượng QSDĐ quy định tại điểm a n&ecirc;u tr&ecirc;n phải được lập th&agrave;nh văn bản v&agrave; c&oacute; chứng nhận của ph&ograve;ng c&ocirc;ng chứng hoặc chứng thực của UBND x&atilde;, phường, thị trấn nơi c&oacute; đất.<br />\r\n<br />\r\nVề tr&igrave;nh tự, thủ tục mua b&aacute;n nh&agrave; ở bao g&ocirc;̀m các bước sau:<br />\r\n<br />\r\n&ndash; Bước 1: C&aacute;c b&ecirc;n mua b&aacute;n đến lập hợp đồng mua b&aacute;n tại cơ quan c&ocirc;ng chứng (kh&ocirc;ng ph&acirc;n biệt địa b&agrave;n), k&egrave;m theo giấy tờ chứng minh quyền sở hữu nh&agrave; ở v&agrave; giấy tờ t&ugrave;y th&acirc;n. Hợp đồng c&oacute; thể được soạn sẵn hoặc do c&ocirc;ng chứng vi&ecirc;n soạn.<br />\r\n<br />\r\n&ndash; Bước 2: Một trong hai b&ecirc;n nộp hồ sơ mua b&aacute;n tại cơ quan quản l&yacute; nh&agrave; ở cấp huyện (nếu l&agrave; c&aacute; nh&acirc;n), cơ quan quản l&yacute; nh&agrave; ở cấp tỉnh (nếu l&agrave; tổ chức). Trường hợp b&aacute;n một phần nh&agrave; ở gắn với quyền sử dụng đất ở th&igrave; phải k&egrave;m theo bản vẽ sơ đồ diện t&iacute;ch nh&agrave; ở, đất ở c&oacute; thẩm tra của của cơ quan quản l&yacute; nh&agrave; ở nếu tại đ&ocirc; thị, c&oacute; x&aacute;c nhận của UBND cấp x&atilde; nếu tại n&ocirc;ng th&ocirc;n.<br />\r\n<br />\r\n&ndash; Bước 3: Căn cứ v&agrave;o hồ sơ, cơ quan quản l&yacute; nh&agrave; ở sẽ kiểm tra, x&aacute;c định vị tr&iacute; thửa đất v&agrave; gửi cho cơ quan thuế để x&aacute;c định nghĩa vụ t&agrave;i ch&iacute;nh (nếu c&oacute;).<br />\r\n<br />\r\n&ndash; Bước 4: Sau khi c&oacute; th&ocirc;ng b&aacute;o của cơ quan thuế, cơ quan quản l&yacute; nh&agrave; ở sẽ gửi th&ocirc;ng b&aacute;o nộp thuế để chủ nh&agrave; đi nộp nghĩa vụ t&agrave;i ch&iacute;nh tại cơ quan thuế.<br />\r\n<br />\r\n&ndash; Bước 5: Sau khi đ&atilde; nộp thuế, chủ nh&agrave; nộp bi&ecirc;n lai thu thuế, lệ ph&iacute; trước bạ cho cơ quan quản l&yacute; nh&agrave; ở để nhận giấy chứng nhận quyền sở hữu nh&agrave; ở v&agrave; quyền sử dụng đất ở.<br />\r\n<br />\r\nNhư vậy khi hai b&ecirc;n ra ph&ograve;ng c&ocirc;ng chứng k&yacute; t&ecirc;n, lăn tay th&igrave; QSDĐ đ&oacute; vẫn chưa thuộc về bạn, v&igrave; đối với t&agrave;i sản l&agrave; bất động sản th&igrave; việc chuyển quyền sở hữu c&oacute; hiệu lực kể từ thời điểm đăng k&yacute; quyền sở hữu (khoản 1 Điều 168 BLDS).<br />\r\n<br />\r\nViệc thanh to&aacute;n tiền v&agrave; phương thức thanh to&aacute;n do c&aacute;c b&ecirc;n thỏa thuận nhằm đảm bảo quyền lợi của m&igrave;nh.<br />\r\n<br />\r\nNếu hai b&ecirc;n thỏa thuận bạn phải trả hết tiền v&agrave; tự l&agrave;m thủ tục c&ograve;n lại th&igrave; l&uacute;c n&agrave;y bạn phải thực hiện nghĩa vụ thuế gồm lệ ph&iacute; trước bạ (đ&acirc;y l&agrave; loại ph&iacute; m&agrave; theo quy định của ph&aacute;p luật b&ecirc;n mua phải chịu), v&agrave; thuế thu nhập c&aacute; nh&acirc;n (đ&acirc;y l&agrave; loại thuế m&agrave; theo quy định của ph&aacute;p luật b&ecirc;n b&aacute;n chịu).', 0, NULL, NULL, 1, 0, 105, '2017-07-19 21:13:12', '2017-07-20 10:47:40', 2, 2),
(66, 'Xin cấp sỏ đỏ chung cư cần những thủ tục gì', 'xin-cap-so-do-chung-cu-can-nhung-thu-tuc-gi', 'Xin cap so do chung cu can nhung thu tuc gi', 'Nếu quý vị đang có một căn hộ chung cư nhưng chưa có sổ đỏ. Bây giờ muốn xin nhưng lại không biết xin cấp sỏ đỏ chung cư cần những thủ tục gì. Hãy tham khảo bài viết dưới đây để biết rõ hơn các thủ tục cần phải chuẩn bị.', '2017/07/19/xin-cap-so-do-chung-cu-can-nhung-thu-tuc-gi-1500475694.png', 5, '&nbsp;\r\n<div style=\"text-align:center\"><strong><img alt=\"Xin cấp sỏ đỏ chung cư cần những thủ tục gì\" src=\"/uploads/images/xin-cap-so-do-chung-cu-can-nhung-thu-tuc-gi.png\" style=\"height:400px; width:650px\" /></strong></div>\r\n<br />\r\n<strong>► Điều kiện xin đề nghị cấp giấy sở hữu căn hộ chung cư</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Phải thanh to&aacute;n đầy đủ tiền ghi trong Hợp đồng mua b&aacute;n căn hộ chung với nh&agrave; đầu tư (thanh to&aacute;n hợp đồng mua b&aacute;n với nh&agrave; đầu tư)</li>\r\n	<li>Đề nghị nh&agrave; đầu tư cấp đơn y&ecirc;u cầu cấp giấy chứng nhận quyền sở hữu căn hộ chung cư</li>\r\n	<li>Hợp đồng mua b&aacute;n căn hộ chung cư với nh&agrave; đầu tư..</li>\r\n</ul>\r\n<br />\r\n<strong>► Hồ sơ xin đề nghị cấp giấy chứng nhận quyền sở hữu căn hộ chung cư</strong><br />\r\n<br />\r\n&diams; Hồ sơ đề nghị cấp giấy chứng nhận quyền sở hữu nh&agrave; chung cư (sổ đỏ) gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>1 bản gốc, 1 bản ph&ocirc; t&ocirc; Hợp đồng mua b&aacute;n với nh&agrave; đầu tư (1 bản lưu tai cơ quan thuế)</li>\r\n	<li>1 bản gốc, 1 bản ph&ocirc; t&ocirc; Hợp đồng mua b&aacute;n với nh&agrave; đầu tư (1 bản lưu tai cơ quan địa ch&iacute;nh)</li>\r\n	<li>2 bản chứng thực chứng minh thư, hộ khẩu của người đề nghị cấy giấy (bao gồm những người đứng t&ecirc;n trong Hợp đồng mua b&aacute;n)</li>\r\n	<li>2 tờ khai Lệ ph&iacute; trước bạ, 03 tờ khai thuế thu nhập c&aacute; nh&acirc;n (chủ t&agrave;i sản k&ecirc; khai hoặc người được uỷ quyền)</li>\r\n</ul>\r\n<br />\r\n&diams; To&agrave;n bộ hồ sơ xin đề nghị cấp giấy quyền sở hữu căn hộ chung cư nộp tại Văn ph&ograve;ng Đăng k&yacute; đất v&agrave; nh&agrave; (cấp Huyện) nộp v&agrave; l&agrave;m thủ tục k&ecirc; khai nộp thuế, lệ ph&iacute; trước bạ cho cơ quan Nh&agrave; nước.<br />\r\n<br />\r\n&diams; Nộp bổ sung để ho&agrave;n tất thủ tục xin giấy chứng nhận quyền sở hữu căn hộ chung cư gồm c&oacute;:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>1 bản gốc, 1 bản ph&ocirc; t&ocirc; Th&ocirc;ng b&aacute;o nộp Lệ ph&iacute; trước bạ v&agrave; Bi&ecirc;n lại nộp Lệ ph&iacute; trước bạ</li>\r\n	<li>1 bản gốc, 1 bản ph&ocirc; t&ocirc; Th&ocirc;ng b&aacute;o nộp thuế thu nhận c&aacute; nh&acirc;n v&agrave; Bi&ecirc;n lai nộp thuế thu nhập c&aacute; nh&acirc;n</li>\r\n</ul>\r\n<br />\r\n4. Thời hạn l&agrave; 50 ng&agrave;y sau khi Văn ph&ograve;ng đăng k&yacute; đất v&agrave; nh&agrave; nhận đủ hồ sơ hợp lệ.', 0, NULL, NULL, 1, 0, 106, '2017-07-19 21:49:00', '2017-07-20 10:48:37', 2, 2),
(73, 'Thủ tục đổi sổ hồng cũ sang sổ mới như thế nào', 'thu-tuc-doi-so-hong-cu-sang-so-moi-nhu-the-nao', 'Thu tuc doi so hong cu sang so moi nhu the nao', 'Nếu quý vị đang muốn đổi sổ hồng mới nhưng lại không rành về các thủ tục giấy tờ. Hãy tham khảo bài viết dưới đây, quý vị sẽ biết được thủ tục đổi sổ hồng cũ sang sổ mới như thế nào để việc đổi mới đúng theo quy định.', '2017/07/20/thu-tuc-doi-so-hong-cu-sang-so-moi-nhu-the-nao-1500541008.png', 5, '<div style=\"text-align:center\"><img alt=\"Thủ tục đổi sổ hồng cũ sang sổ mới như thế nào\" src=\"/uploads/images/thu-tuc-doi-so-hong-cu-sang-so-moi-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n<br />\r\nViệc cấp đổi giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất sau đ&acirc;y gọi tắt l&agrave; giấy chứng nhận được quy định cụ thể tại Điều 23 Nghị định số 88/2009/NĐ-CP ng&agrave;y 19/10/2009 của Ch&iacute;nh phủ về cấp giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất v&agrave; c&aacute;c th&ocirc;ng tư hướng dẫn của Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường.&nbsp;<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>►.Điều kiện để được cấp đổi giấy chứng nhận cần c&oacute;</strong></span><br />\r\n<br />\r\n* C&aacute;c loại giấy chứng nhận đ&atilde; cấp trước ng&agrave;y 01 th&aacute;ng 8 năm 2009 m&agrave; người được cấp giấy c&oacute; nhu cầu cấp đổi sang giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất (sau đ&acirc;y gọi tắt l&agrave; giấy chứng nhận), bao gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Giấy chứng nhận quyền sử dụng đất</li>\r\n	<li>Giấy chứng nhận quyền sở hữu nh&agrave; ở v&agrave; quyền sử dụng đất ở</li>\r\n	<li>Giấy chứng nhận quyền sở hữu nh&agrave; ở</li>\r\n	<li>Giấy chứng nhận quyền sở hữu c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; được cấp theo quy định của ph&aacute;p luật về đất đai, ph&aacute;p luật về nh&agrave; ở, ph&aacute;p luật về x&acirc;y dựng v&agrave; ph&aacute;p luật về d&acirc;n sự.</li>\r\n</ul>\r\n<br />\r\n* Giấy chứng nhận đ&atilde; cấp bị ố, nh&ograve;e, r&aacute;ch, hư hỏng hoặc do đo đạc lại.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Tr&igrave;nh Tự Thủ Tục Đổi Sổ Hồng Như Thế N&agrave;o</strong></span><br />\r\n<br />\r\nNgười c&oacute; nhu cầu cấp đổi giấy chứng nhận lập 01 bộ hồ sơ nộp tại bộ phận nhận v&agrave; trả kết quả sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường (đối với tổ chức) hoặc bộ nhận v&agrave; trả kết quả thuộc Văn ph&ograve;ng UBND cấp huyện (đối với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n). Hồ sơ gồm c&oacute;:<br />\r\n<br />\r\n- Đơn đề nghị cấp lại, cấp đổi Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất &nbsp;(Mẫu số 02/ĐK-GCN).<br />\r\n<br />\r\n- Bản gốc giấy chứng nhận.<br />\r\n<br />\r\n* Trường hợp cấp đổi Giấy chứng nhận đ&atilde; cấp m&agrave; c&oacute; y&ecirc;u cầu bổ sung chứng nhận quyền sở hữu t&agrave;i sản gắn liền với đất th&igrave; nộp đơn đề nghị cấp đổi giấy v&agrave; Giấy chứng nhận đ&atilde; cấp c&ugrave;ng với hồ sơ chứng nhận quyền sở hữu t&agrave;i sản để giải quyết theo quy định tại &nbsp;Điều 15, Điều 20 của Nghị định số 88/2009/NĐ-CP.<br />\r\n<br />\r\n* Trường hợp người sử dụng đất đ&atilde; được cấp Giấy chứng nhận theo quy định của Nghị định 88/2009/NĐ-CP (Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất) m&agrave; c&oacute; y&ecirc;u cầu x&aacute;c nhận bổ sung quyền sở hữu t&agrave;i sản gắn liền với đất v&agrave;o Giấy chứng nhận đ&atilde; cấp th&igrave; nộp đơn đề nghị v&agrave; Giấy chứng nhận đ&atilde; cấp c&ugrave;ng với hồ sơ chứng nhận quyền sở hữu t&agrave;i sản để giải quyết theo quy định tại Điều 15, Điều 20 của Nghị định 88/2009/NĐ-CP.<br />\r\n<br />\r\nTr&ecirc;n cơ sở hồ sơ đ&atilde; nộp, Văn ph&ograve;ng Đăng k&yacute; quyền sử dụng đất thực hiện thủ tục cấp đổi giấy chứng nhận, tr&igrave;nh cơ quan nh&agrave; nước c&oacute; thẩm quyền k&yacute; giấy chứng nhận v&agrave; giao giấy chứng nhận đ&atilde; k&yacute; cho người đề nghị cấp đổi giấy chứng nhận.<br />\r\n<br />\r\nThời gian thực hiện thực hiện thủ tục cấp đổi giấy chứng nhận l&agrave; kh&ocirc;ng qu&aacute; 20 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y Văn ph&ograve;ng Đăng k&yacute; quyền sử dụng đất nhận hồ sơ hợp lệ, kh&ocirc;ng kể thời gian c&ocirc;ng khai kết quả thẩm tra, thời gian thực hiện nghĩa vụ t&agrave;i ch&iacute;nh, thời gian tr&iacute;ch đo địa ch&iacute;nh thửa đất.', 0, NULL, NULL, 1, 0, 113, '2017-07-20 15:57:10', '2017-07-20 15:57:10', 2, 2),
(74, 'Thủ tục và giấy tờ hoàn công nhà như thế nào', 'thu-tuc-va-giay-to-hoan-cong-nha-nhu-the-nao', 'Thu tuc va giay to hoan cong nha nhu the nao', 'Quý vị đang đang muốn hoàn công nhà nhưng lại không biết thủ tục và giấy tờ hoàn công nhà như thế nào. Hãy tham khảo bài viết dưới đây, quý vị sẽ biết được nên chuẩn bị các giấy tờ gì và thủ tục ra sao để xin hoàn công.', '2017/07/20/thu-tuc-va-giay-to-hoan-cong-nha-nhu-the-nao-1500541589.png', 5, '<div style=\"text-align:center\"><img alt=\"Thủ tục và giấy tờ hoàn công nhà như thế nào\" src=\"/uploads/images/thu-tuc-va-giay-to-hoan-cong-nha-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\nTh&ocirc;ng tư số 05/2015/TT-BXD ng&agrave;y 30 th&aacute;ng 10 năm 2015 của Bộ X&acirc;y dựngquy định về quản l&yacute; chất lượng x&acirc;y dựng v&agrave; bảo tr&igrave; nh&agrave; ở ri&ecirc;ng lẻ quy định tới 08 loại giấy tờ cần phải đ&aacute;p ứng khi tiến h&agrave;nh l&agrave;m thủ tục ho&agrave;n c&ocirc;ng.<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Giấy ph&eacute;p x&acirc;y dựng</li>\r\n	<li>Hợp đồng x&acirc;y dựng của chủ nh&agrave; k&yacute; với c&aacute;c nh&agrave; thầu khảo s&aacute;t, thiết kế, thi c&ocirc;ng, gi&aacute;m s&aacute;t thi c&ocirc;ng x&acirc;y dựng (nếu c&oacute;).</li>\r\n	<li>B&aacute;o c&aacute;o kết quả khảo s&aacute;t x&acirc;y dựng</li>\r\n	<li>Hồ sơ thiết kế bản vẽ thi c&ocirc;ng x&acirc;y dựng.</li>\r\n	<li>B&aacute;o c&aacute;o kết quả thẩm tra v&agrave; văn bản kết quả thẩm định thiết kế bản vẽ thi c&ocirc;ng x&acirc;y dựng.</li>\r\n	<li>Bản vẽ ho&agrave;n c&ocirc;ng (trong trường hợp việc thi c&ocirc;ng x&acirc;y dựng c&oacute; sai kh&aacute;c so với thiết kế bản vẽ thi c&ocirc;ng x&acirc;y dựng)</li>\r\n	<li>B&aacute;o c&aacute;o kết quả th&iacute; nghiệm, kiểm định (nếu c&oacute;).</li>\r\n	<li>Văn bản thỏa thuận, chấp thuận, x&aacute;c nhận của c&aacute;c tổ chức, cơ quan Nh&agrave; nước c&oacute; thẩm quyền (nếu c&oacute;) về an to&agrave;n ph&ograve;ng ch&aacute;y, chữa ch&aacute;y; an to&agrave;n vận h&agrave;nh thang m&aacute;y.</li>\r\n</ul>\r\n<br />\r\nDĩ nhi&ecirc;n kh&ocirc;ng phải bất cứ loại giấy tờ n&agrave;o n&ecirc;u tr&ecirc;n cũng đều phải c&oacute; mặt trong hồ sơ ho&agrave;n c&ocirc;ng bởi ph&aacute;p luật đ&atilde; dự liệu được những trường hợp kh&ocirc;ng cần thiết. Cần ch&uacute; &yacute; c&aacute;c loại giấy tờ c&oacute; quy định &ldquo;nếu c&oacute;&rdquo; tức l&agrave; phải rơi v&agrave;o c&aacute;c điều kiện nhất định th&igrave; chủ nh&agrave; mới cần phải cung cấp c&aacute;c loại giấy tờ n&ecirc;u tr&ecirc;n.<br />\r\n<br />\r\nNhư vậy chỉ c&oacute; một v&agrave;i loại giấy tờ l&agrave; cần bắt buộc trong mọi hồ sơ ho&agrave;n c&ocirc;ng như Giấy ph&eacute;p x&acirc;y dựng,B&aacute;o c&aacute;o kết quả khảo s&aacute;t x&acirc;y dựng v&agrave; hồ sơ thiết kế bản vẽ thi c&ocirc;ng x&acirc;y dựng.<br />\r\n<br />\r\nBước thứ hai: X&aacute;c định cơ quan c&oacute; thẩm quyền ho&agrave;n c&ocirc;ng để nộp hồ sơ.<br />\r\n<br />\r\nQuy định tại Luật x&acirc;y dựng 2014 &nbsp;v&agrave; c&aacute;c văn bản hướng dẫn thi h&agrave;nh đều quy định thẩm quyền cấp ph&eacute;p ho&agrave;n c&ocirc;ngcũng ch&iacute;nh cơ quan c&oacute; thẩm quyền cấp ph&eacute;p x&acirc;y dựng. Theo đ&oacute; cơ quan c&oacute; thẩm quyền giải quyết hồ sơ xin ph&eacute;p ho&agrave;n c&ocirc;ng l&agrave;:<br />\r\n<br />\r\nTại UBND quận, huyện: nh&agrave; ở ri&ecirc;ng lẻ của người d&acirc;n v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng kh&aacute;c thuộc địa giới h&agrave;nh ch&iacute;nh quận, huyện;<br />\r\n<br />\r\nUBND x&atilde;: nh&agrave; ở ri&ecirc;ng lẻ ở điểm khu d&acirc;n cư n&ocirc;ng th&ocirc;n đ&atilde; c&oacute; quy hoạch x&acirc;y dựng thuộc địa giới h&agrave;nh ch&iacute;nh x&atilde; hoặc x&acirc;y dựng trong khu bảo tồn, khu di t&iacute;ch lịch sử - văn h&oacute;a thuộc địa giới h&agrave;nh ch&iacute;nh x&atilde;.<br />\r\n<br />\r\nVấn đề t&agrave;i ch&iacute;nh trong ho&agrave;n c&ocirc;ng nh&agrave; ở tư nh&acirc;n được quy định tại &nbsp;Luật Thuế gi&aacute; trị gia tăng, Nghị định số 65/2013/NĐ-CP hướng dẫn Luật Thuế thu nhập c&aacute; nh&acirc;n v&agrave; Th&ocirc;ng tư số 111/2013/TT-BTC hướng dẫn thực hiện Luật Thuế thu nhập c&aacute; nh&acirc;n.<br />\r\n<br />\r\n&nbsp;Theo đ&oacute;, nếu k&yacute; hợp đồng với chủ thầu x&acirc;y dựng th&igrave; chủ thầu c&oacute; tr&aacute;ch nhiệm nộp c&aacute;c khoản thuế, chủ nh&agrave; chỉ phải chịu c&aacute;c ph&iacute; h&agrave;nh ch&iacute;nh của việc ho&agrave;n c&ocirc;ng. Nếu k&yacute; hợp đồng với c&aacute; nh&acirc;n hoặc nh&oacute;m x&acirc;y dựng ri&ecirc;ng lẻ th&igrave; chủ nh&agrave; phải chịu c&aacute;c khoản thuế li&ecirc;n quan đến việc ho&agrave;n c&ocirc;ng b&ecirc;n cạnh c&aacute;c ph&iacute; h&agrave;nh ch&iacute;nh theo quy định.', 0, NULL, NULL, 1, 0, 114, '2017-07-20 16:06:52', '2017-08-09 14:42:32', 2, 12),
(75, 'Thủ tục làm sổ đỏ và lệ phí như thế nào', 'thu-tuc-lam-so-do-va-le-phi-nhu-the-nao', 'Thu tuc lam so do va le phi nhu the nao', 'Quý vị đang muốn làm sỏ đỏ cho mảnh đất của gia đình nhưng lại không biết thủ tục làm sổ đỏ và lệ phí như thế nào. Hãy tham khảo bài viết dưới đây, để biết rõ hơn về thủ tục cũng như lệ phí cần phải đóng.', '2017/07/20/thu-tuc-lam-so-do-va-le-phi-nhu-the-nao-1500542610.png', 5, '<div style=\"text-align:center\"><img alt=\"Thủ tục làm sổ đỏ và lệ phí như thế nào\" src=\"/uploads/images/thu-tuc-lam-so-do-va-le-phi-nhu-the-nao.png\" style=\"height:400px; width:650px\" /></div>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Thủ Tục L&agrave;m Sổ Đỏ</strong></span><br />\r\n<br />\r\nCơ sở ph&aacute;p l&yacute;: Theo Luật đất đai 2013; Nghị định 43/2014/NĐ-CP hướng dẫn thi h&agrave;nh Luật Đất đai; Nghị định 01/2017/NĐ-CP sửa đổi nghị định hướng dẫn Luật đất đai; Th&ocirc;ng tư 111/2013/TT-BTC Hướng dẫn Luật thuế thu nhập c&aacute; nh&acirc;n v&agrave; Nghị định 65/2013/NĐ-CP do Bộ trưởng Bộ T&agrave;i ch&iacute;nh ban h&agrave;nh; Nghị định 140/2016/NĐ-CP về lệ ph&iacute; trước bạ v&agrave; &nbsp;Th&ocirc;ng tư 301/2016/TT-BTC hướrng dẫn về lệ ph&iacute; trước bạ do Bộ trưởng Bộ T&agrave;i ch&iacute;nh ban h&agrave;nh.&nbsp;Điều 60 Nghị định 43/2014/NĐ-CP quy định về thủ tục Nộp hồ sơ v&agrave; trả kết quả thực hiện thủ tục h&agrave;nh ch&iacute;nh về đất đai.&nbsp;<br />\r\n<br />\r\n&diams; Bước 1: Trước ti&ecirc;n bạn cần phải k&yacute; hợp đồng mua b&aacute;n quyền sử dụng đất tại văn ph&ograve;ng c&ocirc;ng chứng, giấy tờ cần chuẩn bị bao gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Giấy chứng nhận quyền sử dụng đất</li>\r\n	<li>Chứng minh nh&acirc;n d&acirc;n/hộ khẩu hai b&ecirc;n b&aacute;n v&agrave; b&ecirc;n mua</li>\r\n	<li>Giấy tờ chứng minh t&agrave;i sản chung/ri&ecirc;ng như giấy chứng nhận đăng k&yacute; kết h&ocirc;n hoặc giấy x&aacute;c nhận t&igrave;nh trạng độc th&acirc;n.</li>\r\n</ul>\r\n<br />\r\nSau khi nhận hồ sơ v&agrave; x&eacute;t thấy đủ điều kiện sẽ c&ocirc;ng chứng hợp đồng mua b&aacute;n cho c&aacute;c b&ecirc;n.<br />\r\n<br />\r\n&diams; Bước 2: Nộp hồ sơ tại Văn ph&ograve;ng đăng k&yacute; quyền sử dụng đất thuộc ph&ograve;ng T&agrave;i nguy&ecirc;n M&ocirc;i trường nơi c&oacute; đất.<br />\r\n<br />\r\n<strong>Hồ sơ chuẩn bị bao gồm như sau:</strong><br />\r\n&nbsp;\r\n<ul>\r\n	<li>Giấy chứng nhận quyền sử dụng đất (01 bản ch&iacute;nh + 02 bản photo c&oacute; chứng thực)</li>\r\n	<li>Hợp đồng chuyển nhượng quyền sử dụng đất (02 bản c&oacute; c&ocirc;ng chứng)</li>\r\n	<li>Chứng minh nh&acirc;n d&acirc;n/hộ khẩu hai b&ecirc;n b&aacute;n v&agrave; b&ecirc;n mua (02 bộ c&oacute; chứng thực)</li>\r\n	<li>Giấy tờ chứng minh t&agrave;i sản chung/ri&ecirc;ng như Giấy chứng nhận đăng k&yacute; kết h&ocirc;n hoặc giấy x&aacute;c nhận t&igrave;nh trạng độc th&acirc;n (02 bộ c&oacute; chứng thực)</li>\r\n	<li>Đơn đăng k&yacute; biến động đất đai, t&agrave;i sản gắn liền với đất (01 bản ch&iacute;nh)</li>\r\n	<li>Tờ khai lệ ph&iacute; trước bạ (02 bản ch&iacute;nh)</li>\r\n	<li>Tờ khai thuế thu nhập c&aacute; nh&acirc;n (02 bản ch&iacute;nh)</li>\r\n	<li>Tờ khai thuế sử dụng đất phi n&ocirc;ng nghiệp (02 bản ch&iacute;nh)</li>\r\n	<li>Tờ khai đăng k&yacute; thuế</li>\r\n	<li>Sơ đồ vị tr&iacute; nh&agrave; đất (01 bản ch&iacute;nh)</li>\r\n</ul>\r\n<br />\r\nSau khi tiếp nhận hồ sơ, Ph&ograve;ng T&agrave;i nguy&ecirc;n M&ocirc;i trường lu&acirc;n chuyển hồ sơ sang cơ quan thuế để ra th&ocirc;ng b&aacute;o thuế, Ph&ograve;ng T&agrave;i nguy&ecirc;n M&ocirc;i trường thực hiện thủ tục sang t&ecirc;n.<br />\r\n<br />\r\nThời gian thực hiện thủ tục sang t&ecirc;n: 15 ng&agrave;y l&agrave;m việc. Nhưng từ ng&agrave;y 3/3/2017, thời gian thực hiện thủ tục l&agrave; 10 ngảy l&agrave;m việc kể từ ng&agrave;y tiếp nhận hồ sơ (điểm l khoản 2 Điều 61 Nghị định 01/2017).<br />\r\n<br />\r\n&diams; Bước 3: Căn cứ v&agrave;o giấy hẹn trả kết quả, bạn đến bộ phận trả kết quả để nhận th&ocirc;ng b&aacute;o thuế. Sau khi ho&agrave;n th&agrave;nh nghĩa vụ t&agrave;i ch&iacute;nh, bạn mang h&oacute;a đơn quay lại đ&acirc;y để nhận giấy chứng nhận quyền sử dụng đất đứng t&ecirc;n qu&yacute; vị.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Lệ ph&iacute; l&agrave;m sổ đỏ như thế n&agrave;o</strong></span><br />\r\n<br />\r\nC&aacute;c khoản tiền phải nộp th&igrave; l&agrave;m thủ tục cấp Giấy chứng nhận quyền sử dụng đất v&agrave; c&aacute;c t&agrave;i sản gắn liền với đất bao gồm: tiền sử dụng đất; lệ ph&iacute; trước bạ, lệ ph&iacute; cấp giấy chứng nhận v&agrave; c&aacute;c loại ph&iacute; kh&aacute;c như: ph&iacute; thẩm định địa ch&iacute;nh, ph&iacute; tr&iacute;ch đo địa ch&iacute;nh.<br />\r\n<br />\r\n<strong>&diams; Về tiền sử dụng đất</strong><br />\r\n<br />\r\nTheo Nghị định 45/2014/NĐ-CP th&igrave; c&aacute;c trường hợp cụ thể phải đ&oacute;ng tiền sử dụng đất bao gồm:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Thu tiền sử dụng đất khi Nh&agrave; nước giao đất</li>\r\n	<li>Thu tiền sử dụng đất khi chuyển mục đ&iacute;ch sử dụng đất</li>\r\n	<li>Thu tiền sử dụng đất khi c&ocirc;ng nhận quyền sử dụng đất cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n đối với đất đ&atilde; sử dụng ổn định từ 15/10/1993 m&agrave; kh&ocirc;ng c&oacute; một trong c&aacute;c giấy tờ được quy định tại Khoản 1 Điều 100 Luật đất đai 2013</li>\r\n	<li>Thu tiền sử dụng đất khi c&ocirc;ng nhận quyền sử dụng đất cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n đối với đất đ&atilde; sử dụng từ 15/10/1993 đến trước ng&agrave;y 01/7/2004 m&agrave; kh&ocirc;ng c&oacute; c&aacute;c giấy tờ theo quy định tại Khoản 1 Điều 100 Luật đất đai 2013</li>\r\n	<li>Thu tiền sử dụng đất khi c&ocirc;ng nhận quyền sử dụng đất (cấp Giấy chứng nhận) cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n đối với đất ở c&oacute; nguồn gốc được giao kh&ocirc;ng đ&uacute;ng thẩm quyền hoặc do lấn chiếm kể từ ng&agrave;y 01/7/2004 đến trước ng&agrave;y 01/7/2014 m&agrave; nay được Nh&agrave; nước x&eacute;t cấp Giấy chứng nhận</li>\r\n</ul>\r\n<br />\r\nT&ugrave;y từng trường hợp m&agrave; ph&aacute;p luật quy định mức tiền sử dụng đất kh&aacute;c nhau.<br />\r\n<br />\r\n<strong>&diams; Về Lệ Ph&iacute; Trước Bạ</strong><br />\r\n<br />\r\nCăn cứ theo quy định tại Khoản 1 Điều 2 Nghị định 45/2011/NĐ-CP th&igrave; nh&agrave;, đất l&agrave; một trong những đối tượng phải chịu lệ ph&iacute; trước bạ. V&agrave; mức thu lệ ph&iacute; trước bạ theo tỷ lệ (%) l&agrave; 0,5% (căn cứ theo Khoản 1 Điều 7 Nghị định n&agrave;y).<br />\r\n<br />\r\nVề lệ ph&iacute; cấp giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản gắn liền với đất. Theo quy định tại Th&ocirc;ng tư 02/2014/TT-BTC th&igrave;:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Đối với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n tại c&aacute;c quận thuộc th&agrave;nh phố trực thuộc trung ương; phường nội th&agrave;nh thuộc th&agrave;nh phố hoặc thị x&atilde; trực thuộc tỉnh, mức thu tối đa kh&ocirc;ng qu&aacute; 100.000 đồng/giấy đối với cấp mới; tối đa kh&ocirc;ng qu&aacute; 50.000 đồng/lần đối với cấp lại (kể cả cấp lại giấy chứng nhận do hết chỗ x&aacute;c nhận), cấp đổi, x&aacute;c nhận bổ sung v&agrave;o giấy chứng nhận.</li>\r\n	<li>Trường hợp giấy chứng nhận cấp cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n chỉ c&oacute; quyền sử dụng đất &nbsp;(kh&ocirc;ng c&oacute; nh&agrave; v&agrave; t&agrave;i sản gắn liền với đất) th&igrave; &aacute;p dụng mức thu tối đa kh&ocirc;ng qu&aacute; 25.000 đồng/giấy cấp mới; tối đa kh&ocirc;ng qu&aacute; 20.000 đồng/lần cấp đối với cấp lại (kể cả cấp lại giấy chứng nhận do hết chỗ x&aacute;c nhận), cấp đổi, x&aacute;c nhận bổ sung v&agrave;o giấy chứng nhận.</li>\r\n	<li>Đối với tổ chức, mức thu tối đa kh&ocirc;ng qu&aacute; 500.000 đồng/giấy.</li>\r\n	<li>Trường hợp giấy chứng nhận cấp cho tổ chức chỉ c&oacute; quyền sử dụng đất (kh&ocirc;ng c&oacute; nh&agrave; v&agrave; t&agrave;i sản gắn liền với đất) th&igrave; &aacute;p dụng mức thu tối đa kh&ocirc;ng qu&aacute; 100.000 đồng/giấy.</li>\r\n	<li>Trường hợp cấp lại (kể cả cấp lại giấy chứng nhận do hết chổ x&aacute;c nhận), cấp đổi, x&aacute;c nhận bổ sung v&agrave;o giấy chứng nhận: Mức thu tối đa kh&ocirc;ng qu&aacute; 50.000 đồng/lần cấp.</li>\r\n</ul>\r\n<br />\r\nPh&aacute;p luật cũng quy định cụ thể những trường hợp n&agrave;o được miễn tiền sử dụng đất, trường hợp n&agrave;o được x&eacute;t giảm tiền sử dụng đất;, trường hợp n&agrave;o kh&ocirc;ng phải đ&oacute;ng lệ ph&iacute; trước bạ.<br />\r\n<br />\r\nTr&ecirc;n đ&acirc;y l&agrave; c&aacute;c khoản tiền ch&iacute;nh phải nộp khi l&agrave;m thủ tục xin cấp giấy chứng nhận quyền sử dụng đất v&agrave; c&aacute;c t&agrave;i sản gắn liền với đất. T&ugrave;y từng trường hợp cụ thể m&agrave; c&aacute;c khoản tiền phải nộp tăng giảm kh&aacute;c nhau.', 0, NULL, NULL, 1, 0, 115, '2017-07-20 16:23:57', '2017-07-20 16:23:57', 2, 2),
(76, 'Diện tích bao nhiêu mới được cấp giấy phép xây dựng nhà ở', 'dien-tich-bao-nhieu-moi-duoc-cap-giay-phep-xay-dung-nha-o', 'Dien tich bao nhieu moi duoc cap giay phep xay dung nha o', 'Nếu quý vị đang muốn xin giấy phép xây dựng nhà nhưng lại không biết diện tích bao nhiêu mới được cấp giấy phép xây dựng nhà ở. Hãy tham khảo bài viết chia sẻ dưới đây để nắm được các thông tin.', '2017/07/20/dien-tich-bao-nhieu-moi-duoc-cap-giay-phep-xay-dung-nha-o-1500545241.png', 5, '<span style=\"font-size:18px\"><strong>3463634</strong></span>\r\n<div style=\"text-align:center\"><span style=\"font-size:18px\"><strong><img alt=\"Diện tích bao nhiêu mới được cấp giấy phép xây dựng nhà ở\" src=\"/uploads/images/dien-tich-bao-nhieu-moi-duoc-cap-giay-phep-xay-dung-nha-o.png\" style=\"height:400px; width:650px\" /></strong></span></div>\r\n\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Căn cứ ph&aacute;p l&yacute;</strong></span><br />\r\n<br />\r\n+ Quyết định 04/2008/QĐ-BXD Về việc ban h&agrave;nh quy chuẩn kỹ thuật quốc gia về quy hoạch x&acirc;y dựng.<br />\r\n<br />\r\n<span style=\"font-size:18px\"><strong>► Diện t&iacute;ch tối thiểu được cấp giấy ph&eacute;p x&acirc;y dựng nh&agrave; ở</strong></span><br />\r\n<br />\r\nCăn cứ v&agrave;o Điều 2.8.9 Quyết định số 04/2008/QĐ-BXD quy định về k&iacute;ch thước l&ocirc; đất quy hoạch x&acirc;y dựng nh&agrave; như sau:<br />\r\n<br />\r\n&diams;&nbsp;K&iacute;ch thước l&ocirc; đất quy hoạch x&acirc;y dựng nh&agrave; ở được x&aacute;c định cụ thể theo nhu cầu v&agrave; đối tượng sử dụng, ph&ugrave; hợp với c&aacute;c giải ph&aacute;p tổ chức kh&ocirc;ng gian v&agrave; được quản l&yacute; theo quy định về quản l&yacute; x&acirc;y dựng của khu vực lập quy hoạch.<br />\r\n<br />\r\n&diams;&nbsp;L&ocirc; đất x&acirc;y dựng nh&agrave; ở trong c&aacute;c khu ở quy hoạch x&acirc;y dựng mới, khi tiếp gi&aacute;p với đường phố c&oacute; lộ giới &ge;20m, phải đồng thời đảm bảo c&aacute;c y&ecirc;u cầu về k&iacute;ch thước tối thiểu như sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Diện t&iacute;ch của l&ocirc; đất x&acirc;y dựng nh&agrave; ở gia đ&igrave;nh &ge;45m2</li>\r\n	<li>Bề rộng của l&ocirc; đất x&acirc;y dựng nh&agrave; ở &ge;5m</li>\r\n	<li>Chiều s&acirc;u của l&ocirc; đất x&acirc;y dựng nh&agrave; ở &ge;5m</li>\r\n</ul>\r\n<br />\r\n&diams;&nbsp;L&ocirc; đất x&acirc;y dựng nh&agrave; ở trong c&aacute;c khu ở quy hoạch x&acirc;y dựng mới, khi tiếp gi&aacute;p với đường phố c&oacute; lộ giới &lt;20m, phải đồng thời đảm bảo c&aacute;c y&ecirc;u cầu về k&iacute;ch thước tối thiểu như sau:<br />\r\n&nbsp;\r\n<ul>\r\n	<li>Diện t&iacute;ch của l&ocirc; đất x&acirc;y dựng nh&agrave; ở gia đ&igrave;nh &ge;36m2</li>\r\n	<li>Bề rộng của l&ocirc; đất x&acirc;y dựng nh&agrave; ở &ge;4m</li>\r\n	<li>Chiều s&acirc;u của l&ocirc; đất x&acirc;y dựng nh&agrave; ở &ge;4m</li>\r\n</ul>\r\n<br />\r\n&diams;&nbsp;Chiều d&agrave;i tối đa của một d&atilde;y nh&agrave; li&ecirc;n kế hoặc ri&ecirc;ng lẻ c&oacute; cả hai mặt tiếp gi&aacute;p với c&aacute;c tuyến đường cấp đường ch&iacute;nh khu vực trở xuống l&agrave; 60m. Giữa c&aacute;c d&atilde;y nh&agrave; phải bố tr&iacute; đường giao th&ocirc;ng ph&ugrave; hợp với c&aacute;c quy định về quy hoạch mạng lưới đường giao th&ocirc;ng, hoặc phải bố tr&iacute; đường đi bộ với bề rộng tối thiểu l&agrave; 4m.<br />\r\n<br />\r\nV&igrave; th&ocirc;ng tin bạn cung cấp chưa r&otilde; mảnh đất bạn định x&acirc;y nh&agrave; tiếp gi&aacute;p với đường phố c&oacute; lộ giới l&agrave; bao nhi&ecirc;u m&eacute;t n&ecirc;n c&oacute; 2 trường hợp xảy ra như sau:<br />\r\n<br />\r\n<strong>&diams; Mảnh đất của bạn tiếp gi&aacute;p với đường phố c&oacute; lộ giới &ge;20m</strong><br />\r\n<br />\r\nTheo quy định, l&ocirc; đất x&acirc;y dựng nh&agrave; ở trong c&aacute;c khu ở quy hoạch x&acirc;y dựng mới, khi tiếp gi&aacute;p với đường phố c&oacute; lộ giới &ge;20m th&igrave; diện t&iacute;ch của l&ocirc; đất x&acirc;y dựng nh&agrave; ở gia đ&igrave;nh phải &ge;45m2. L&ocirc; đất của bạn c&oacute; diện t&iacute;ch l&agrave; 37.4 m2 n&ecirc;n bạn sẽ kh&ocirc;ng được cấp giấy ph&eacute;p x&acirc;y dựng<br />\r\n<br />\r\n<strong>&diams; Mảnh đất của bạn tiếp gi&aacute;p với đường phố c&oacute; lộ giới &lt;20m</strong><br />\r\n<br />\r\nL&ocirc; đất x&acirc;y dựng nh&agrave; ở trong c&aacute;c khu ở quy hoạch x&acirc;y dựng mới khi tiếp gi&aacute;p với đường phố c&oacute; lộ giới &lt;20m phải c&oacute; diện t&iacute;ch &ge;36m2, bề rộng &ge;4m, chiều s&acirc;u &ge;4m. Theo th&ocirc;ng tin bạn cung cấp, l&ocirc; đất của bạn c&oacute; diện t&iacute;ch l&agrave; 37.4 m2, chiều rộng l&agrave; 4 m, như vậy, l&ocirc; đất của bạn c&oacute; đủ điều kiện để được cấp giấy ph&eacute;p x&acirc;y dựng nh&agrave; ở.<br />\r\n<br />\r\nTr&ecirc;n đ&acirc;y l&agrave; một số th&ocirc;ng tin được cung cấp cho qu&yacute; vị tham khảo. Diện t&iacute;ch c&oacute; thể thay đổi n&ecirc;n qu&yacute; vị n&ecirc;n t&igrave;m hiểu th&ocirc;ng tin ch&iacute;nh x&aacute;c từ c&aacute;c quyết định mới nhất được ban h&agrave;nh gần đ&acirc;y.', 0, NULL, NULL, 1, 0, 116, '2017-07-20 17:10:26', '2017-07-20 17:10:26', 2, 2);
INSERT INTO `articles` (`id`, `title`, `slug`, `alias`, `description`, `image_url`, `cate_id`, `content`, `is_hot`, `project_id`, `tab_id`, `status`, `display_order`, `meta_id`, `created_at`, `updated_at`, `created_user`, `updated_user`) VALUES
(98, 'Chuyện lạ có thật đất Long An đắt hơn đất Sài Gòn', 'chuyen-la-co-that-dat-long-an-dat-hon-dat-sai-gon', 'Chuyen la co that dat Long An dat hon dat Sai Gon', 'Chuyện lạ có thật đất Long An đắt hơn đất Sài Gòn, tưởng chừng đó là một câu chuyện nghịch lý nhưng nó hoàn toàn đang diễn ra trong thời điểm hiện nay.  Cơn sốt đất nền vùng ven lan rộng thời gian gần đây, đang kéo theo nhiều diễn biến lạ trên thị trường. Việc đất ở Long An đắt hơn đất ở Sài Gòn là một trong những diễn biến lạ đây. Để biết rõ hơn, mời quý vị tham khảo bài viết dưới đây.', '2017/07/27/235-1501131353.jpg', 7, 'Theo B&aacute;o c&aacute;o thị trường qu&yacute; 2/2017 của DKRA, cho biết, mức gi&aacute; đất nền tại c&aacute;c huyện v&ugrave;ng ven TP.HCM như H&oacute;c M&ocirc;n, Củ Chi dao động ở mức 4 - 10 triệu/m2. Trong khi đ&oacute;, khảo s&aacute;t tại một số dự &aacute;n thuộc tỉnh Long An, như Vista Land, Bella Vista, L&agrave;ng Sen Việt Nam, Ph&uacute;c An City.... mức gi&aacute; b&aacute;n phổ biến từ 6 - 12 triệu/m2. Dựa v&agrave;o đ&acirc;u để những dự &aacute;n &ldquo;ăn theo&rdquo; vị tr&iacute; gi&aacute;p ranh TP.HCM, nhưng lại đẩy gi&aacute; cao như vậy?<br />\r\n<br />\r\nAnh B&igrave;nh, một nh&agrave; đầu tư t&igrave;m mua đất nền tại TP.HCM, cho biết, c&ograve; đất tỉnh lẻ c&oacute; rất nhiều chi&ecirc;u tr&ograve;, miễn sao t&igrave;m được kh&aacute;ch chứ kh&ocirc;ng cần biết th&ocirc;ng tin đưa ra đ&uacute;ng hay sai. Trước đ&oacute;, anh t&igrave;m mua đất nền ở khu vực Củ Chi. Qua lời m&ocirc;i giới, cho biết một dự &aacute;n ở trung t&acirc;m đ&ocirc; thị T&acirc;y Bắc TP.HCM, gần trường học, bệnh viện Xuy&ecirc;n &Aacute;, si&ecirc;u thị Coop Mart, S&acirc;n golf Nh&acirc;n Sư, ph&ugrave; hợp ở, đầu tư kinh doanh&hellip; Nghe rất hấp dẫn, nhưng mất thời gian t&igrave;m hiểu v&agrave; đi thực tế mới biết khu đất n&agrave;y l&agrave; dự &aacute;n Bella Vista, thuộc địa phận tỉnh Long An, của C&ocirc;ng ty Địa ốc Trần Anh.<br />\r\n<br />\r\nCũng theo anh B&igrave;nh, để đẩy gi&aacute; b&aacute;n, nhiều dự &aacute;n tỉnh lẻ thường d&ugrave;ng chi&ecirc;u tạo tiện &iacute;ch &ldquo;b&aacute;nh vẽ&rdquo;. Đơn cử như dự &aacute;n Bella Vista, ở khu vực v&ugrave;ng ven Long An, d&acirc;n cư thưa thớt nhưng lại được &ldquo;vẽ&rdquo; nhiều tiện &iacute;ch lung linh, kh&ocirc;ng thua k&eacute;m Singapore. Ngay tại trung t&acirc;m TP.HCM, một số dự &aacute;n x&acirc;y bến du thuyền nhưng hiếm khi thấy nhiều du thuyền neo đậu. Tuy nhi&ecirc;n, dự &aacute;n Bella Vista cũng được vẽ tiện &iacute;ch bến du thuyền, c&ugrave;ng với đ&oacute; l&agrave; khu phức hợp đa năng, hồ bơi ch&acirc;n m&acirc;y, phố thương mại&hellip;<br />\r\n<br />\r\n&ldquo;C&ocirc;ng thức b&aacute;n &ldquo;b&aacute;nh vẽ&rdquo; đ&atilde; từng được &aacute;p dụng tại Th&agrave;nh phố mới B&igrave;nh Dương rất phổ biến hồi năm 2007. Tuy nhi&ecirc;n, sau h&agrave;ng chục năm, h&agrave;ng loạt tiện &iacute;ch như phố thương mại, trường học quốc tế, khu phức hợp&hellip; vẫn c&ograve;n nằm tr&ecirc;n giấy. T&igrave;nh trạng n&agrave;y đang c&oacute; nguy cơ lặp lại tại nhiều dự &aacute;n như L&agrave;ng Sen Việt Nam, Five Star Eco City&hellip; chứ kh&ocirc;ng ri&ecirc;ng g&igrave; Bella Vista&rdquo; - anh B&igrave;nh n&oacute;i.<br />\r\n<br />\r\nNhiều chuy&ecirc;n gia cho rằng, chi ph&iacute; đền b&ugrave; giải tỏa, tiền sử dụng đất ở TP.HCM c&oacute; mặt bằng cao hơn Long An, n&ecirc;n gi&aacute; th&agrave;nh mỗi m2 đất thương mại ở TP.HCM thường cao hơn. Tuy nhi&ecirc;n, theo &ocirc;ng Trần Minh Nhật, Tổng Gi&aacute;m đốc C&ocirc;ng ty Nh&agrave; Thời Đại, kh&aacute;ch h&agrave;ng thường quan t&acirc;m đến tổng gi&aacute; b&aacute;n. M&agrave; ở c&aacute;c tỉnh th&igrave; lại dễ d&agrave;ng hơn trong việc ph&acirc;n l&ocirc; nhỏ, n&ecirc;n c&aacute;c doanh nghiệp thường &ldquo;b&oacute;p nhỏ&rdquo; diện t&iacute;ch v&agrave; tăng đơn gi&aacute; b&aacute;n tr&ecirc;n m2. C&aacute;c dự &aacute;n ở TP.HCM th&igrave; rất kh&oacute; l&agrave;m điều n&agrave;y v&igrave; ti&ecirc;u chuẩn ở th&agrave;nh phố cao hơn.<br />\r\n<br />\r\n&ldquo;Việc đầu tư đất nền ở Long An thường ăn theo 2 yếu tố: Gi&aacute;p ranh TP.HCM v&agrave; gần khu c&ocirc;ng nghiệp. Tuy nhi&ecirc;n, hiện c&oacute; nhiều dự &aacute;n bị ảnh hưởng bởi &ocirc; nhiễm từ ch&iacute;nh khu c&ocirc;ng nghiệp l&acirc;n cận, hoặc bị vướng điện cao thế, gần khu nghĩa trang&hellip; l&agrave;m giảm gi&aacute; trị.<br />\r\n<br />\r\nMặt kh&aacute;c, để vẽ tiện &iacute;ch ho&agrave;ng tr&aacute;ng th&igrave; dự &aacute;n phải c&oacute; quy m&ocirc; lớn. Số lượng sản phẩm nhiều sẽ tạo n&ecirc;n sự cạnh tranh lớn tr&ecirc;n thị trường mua b&aacute;n lại. Đặc biệt, nếu dự &aacute;n vẫn c&ograve;n đang b&aacute;n h&agrave;ng, th&igrave; việc b&aacute;n chốt lời của kh&aacute;ch h&agrave;ng sẽ rất kh&oacute;, v&igrave; cạnh tranh với h&agrave;ng của chủ đầu tư đang b&aacute;n&rdquo;.<br />\r\n<br />\r\nTrước việc rao một đằng, b&aacute;n một nẻo như thế n&agrave;y. Nếu muốn đầu tư cũng sẽ rất kh&oacute; khăn v&igrave; hiện tại th&ocirc;ng tin tr&ecirc;n thị trường đ&atilde; bị loạn, kh&ocirc;ng ch&iacute;nh x&aacute;c tuyệt đối. Nếu qu&yacute; vị quan t&acirc;m đến thị trường <a href=\"http://thanhphuthinhland.vn/nha-dat-long-an.html\">đất nền long an</a> th&igrave; kh&ocirc;ng n&ecirc;n bỏ qua những địa chỉ c&ocirc;ng ty nh&agrave; đất uy t&iacute;n, c&aacute;c chủ đầu tư c&aacute;c dự &aacute;n hấp dẫn hiện nay dưới Long An. Nếu qu&yacute; vi quan t&acirc;m, h&atilde;y tham khảo c&aacute;c th&ocirc;ng tin dưới đ&acirc;y.<br />\r\n<br />\r\n<span style=\"font-size:20px\"><strong>►&nbsp;Dự &aacute;n đất nền gi&aacute; cực rẻ - hấp dẫn giới đầu tư</strong></span><br />\r\n<br />\r\nHiện tại, <strong>Thanh Ph&uacute; Thịnh Land</strong> đang mở b&aacute;n giai đoạn 1 với 29 nền của dự &aacute;n KDC An Thắng - Rạch Kiến. Với gi&aacute; <strong>260 triệu/100m2</strong> ( cam kết gi&aacute; rẻ nhất so với c&aacute;c dự &aacute;n gần huyện Cần Đước).\r\n<div style=\"text-align:center\"><br />\r\n<img alt=\"\" src=\"/uploads/images/235.jpg\" style=\"height:400px; width:600px\" /></div>\r\n<br />\r\nSổ hồng ri&ecirc;ng từng nền, sang t&ecirc;n c&ocirc;ng chứng nhanh ch&oacute;ng. Nếu qu&yacute; vị quan t&acirc;m h&atilde;y tham khảo ngay dự &aacute;n n&agrave;y.<br />\r\n<br />\r\n<span style=\"font-size:20px\"><strong>► Xem chi tiết&nbsp;&rArr;</strong></span><strong><span style=\"font-size:16px\">&nbsp;<a href=\"http://thanhphuthinhland.vn/du-an/kdc-an-thang-rach-kien\"><span style=\"color:#ff0000\">Dự &Aacute;n KDC An Thắng - Rạch Kiến</span></a></span></strong>', 0, NULL, NULL, 1, 0, 161, '2017-07-27 11:56:37', '2017-07-27 11:57:47', 2, 2);

